INSERT INTO `prescript` VALUES (59323, '35741', '白鲜皮散', '白鲜皮半两，川升麻半两，川朴消1两，茵陈1两，黄芩半两，栀子仁半两，大青半两，川大黄2两（锉碎，微炒），葛根半两（锉）。', '每服3钱，新汲水调下。须臾当利1-2行，如人行10里未利，即再服。', '《圣惠》卷五十五', '急黄，头目四肢烦热疼痛，小便赤，大便难，心躁不得睡。', '-', '-', '升麻', '[\'白鲜皮\', \'半两\', \'升麻\', \'半两\', \'川朴\', \'消两\', \'茵陈\', \'黄芩\', \'半两\', \'栀子\', \'半两\', \'大青\', \'半两\', \'大黄\', \'葛根\', \'半两\']');
