INSERT INTO `prescript` VALUES (59327, '35596', '八珍汤', '当归3钱，白芍2钱，黄耆3钱，白术3钱，柴胡5分，熟地5钱，升麻5分，人参1钱，茯苓1钱，川芎1钱。', '水煎服。', '《石室秘录》卷二', '气血平补。主气沉血滞，而成呕逆、躄废之症。', '-', '-', '升麻', '[\'当归\', \'白芍\', \'黄耆\', \'白术\', \'柴胡\', \'熟地\', \'升麻\', \'人参\', \'茯苓\', \'川芎\']');
INSERT INTO `prescript` VALUES (59328, '35492', '决明子丸', '决明子（微炒）1两，菊花1两，秦皮1两（去粗皮，锉），黄连（去须）1两，车前子1两半，地骨皮1两半，羚羊角屑1两，黄芩1两（去黑心），萎蕤1两，山栀子1两（去皮），生干地黄1两（焙），秦艽（去苗土）1两，青葙1两半，白茯苓（去黑皮）1两半，升麻1两。', '每服30丸，食后以温水送下，临卧再服。', '《圣济总录》卷一一○', '肝肺热毒，气攻两眼，生疮赤痛。', '-', '-', '升麻', '[\'决明子\', \'菊花\', \'秦皮\', \'两去\', \'粗皮\', \'黄连\', \'车前子\', \'两半\', \'地骨皮\', \'两半\', \'羚羊角\', \'黄芩\', \'两去\', \'黑心\', \'萎蕤\', \'两山\', \'栀子\', \'去皮\', \'生干\', \'地黄\', \'两焙\', \'秦艽\', \'苗土\', \'两青\', \'两半\', \'白茯苓\', \'黑皮\', \'两半\', \'升麻\']');
INSERT INTO `prescript` VALUES (59329, '35433', '甲字化毒丸', '升麻2钱，牛黄4分，生生乳1钱（配矾石，用佐药炼百日而成），雄黄1钱（须择旧坑所产透明不臭者），朱砂1钱7分，乳香1钱7分，月月红1钱5分，白僵蚕1钱5分，川山甲1钱5分，白鲜皮（各取头末）1钱5分，广木香2钱5分，熟大黄2钱5分，牡丹皮2钱5分。', '每早空心服13丸，每晚空腹服9丸，人参汤送下；炒米汤亦可。病重者逢3、6、9日加服3丸，元弱者不必加，病去药减，如余邪未尽，药不可撤。', '《疮疡经验全书》卷六', '疮疡，肝经内外前后形症；梅毒。', '忌恼怒焦躁、茶、酒。', '-', '升麻', '[\'升麻\', \'牛黄\', \'生乳\', \'钱配\', \'矾石\', \'用佐药炼\', \'百日\', \'而成\', \'雄黄\', \'须择\', \'所产\', \'透明\', \'不臭者\', \'朱砂\', \'钱分\', \'乳香\', \'钱分\', \'月月红\', \'钱分\', \'白僵蚕\', \'钱分\', \'川山甲\', \'钱分\', \'白鲜皮\', \'各取\', \'分广\', \'木香\', \'钱分\', \'大黄\', \'钱分\', \'牡丹皮\', \'钱分\']');
INSERT INTO `prescript` VALUES (59330, '35423', '白花蛇煮酒', '全蝎（炒）1钱，当归1钱，防风（去芦）1钱，羌活1钱，芍药5钱，升麻5钱，白芷5钱，天麻5钱，独活5钱，甘草5钱。', '用白花蛇温水洗净，去头尾各3寸及骨，刺取净肉1两，先用糯米2斗，如法造白酒，将前药囊贮置酒缸中，俟酒来，春5、夏3、秋7、冬10日，取酒同药囊一并煮熟，空心热饮，初饮1杯，至3日加半杯，3日后2杯，渐至3杯为常，不可多服，多则反生变。', '《扶寿精方》', '诸风，无问新久，手足腰腿缓弱，行步不正，精神昏运，口眼斜，语言謇涩，痰涎盛；或筋脉挛急，肌肉顽麻，皮肤燥痒，骨节烦疼；或生恶疮，疼痛无常；或风气上攻，面浮耳鸣，腰痛体重；一切风湿疮疥。', '-', '-', '升麻', '[\'全蝎\', \'当归\', \'防风\', \'芦钱\', \'羌活\', \'芍药\', \'升麻\', \'白芷\', \'天麻\', \'独活\', \'甘草\']');
INSERT INTO `prescript` VALUES (59331, '35356', '羚羊角丸', '羚羊角2两（镑），茯神2两（去木），木香2两，防风2两（去叉），升麻2两，赤茯苓3两（去黑皮），大麻仁（别捣如膏）3两，独活（去芦头）2两半，大腹7枚（连皮子锉），旋复花1两1分。', '每日空腹酒送下40丸，每日1服。腹内先冷，加附子（炮裂，去皮脐）2两；大肠秘涩，加大黄（锉，炒）2两半；兼气，加槟榔仁（锉）2两半；心胸间热，加麦门冬（去心）3两；筋急掣痛，加牛膝（切，焙）2两半；皮肤风痒，心下妨闷，加枳壳（去瓤，炒黄）1两半；脏气羸弱，加黄耆（锉，炒）3两。', '《圣济总录》卷八十二', '脚气。心腹妨闷，两肋虚胀，不思食，渐觉心满，气膈不通。', '-', '-', '升麻', '[\'羚羊角\', \'两镑\', \'茯神\', \'两去\', \'木香\', \'防风\', \'两去\', \'升麻\', \'两赤\', \'茯苓\', \'两去\', \'黑皮\', \'麻仁\', \'如膏\', \'独活\', \'芦头\', \'两半\', \'大腹\', \'皮子\', \'旋复花\', \'两分\']');
