INSERT INTO `prescript` VALUES (59340, '34984', '龙齿饮', '龙齿（碎）2两半，黄芩（去黑心）1两半，防风（去叉）1两半，赤芍药1两半，白茯苓（去黑皮）1两半，升麻1两，大青3分，大腹皮（锉）2两。', '每服5钱匕，以水1盏半，加生姜2枣大者（拍碎），竹沥半合，银半两许，同煎至8分，去滓，食后温服。', '《圣济总录》卷十三', '劳风，肺热气壅，卧即多惊，时复头旋。', '-', '-', '升麻', '[\'龙齿\', \'两半\', \'黄芩\', \'黑心\', \'两半\', \'防风\', \'两半\', \'芍药\', \'两半\', \'白茯苓\', \'黑皮\', \'两半\', \'升麻\', \'两大青分\', \'大腹皮\']');
