INSERT INTO `prescript` VALUES (59351, '34238', '加味升麻汤', '升麻（痰喘不用）、葛根、枳壳、桔梗、橘红、桑皮、茯神、甘草、杏仁、蒌仁（痰喘、大便闭者，可用45钱）。', '水煎服。头痛，加川芎；发紫黑斑，磨入犀角2-3分；风寒阻闭，加苏叶、麻黄；伤食不食，加山楂；微热而不发，少加桂枝；腹痛，药汁磨槟榔少许；焦紫加红花2-3分；表面不起，加僵蚕；发痒，加蝉蜕；疹不起发，风寒甚急者，加全蝎（洗净）；发呕，加藿香；鼻衄，去升麻，加茅花。', '《治疹全书》卷中', '麻疹难发者。', '-', '-', '升麻', '[\'升麻\', \'痰喘\', \'不用\', \'葛根\', \'枳壳\', \'桔梗\', \'橘红\', \'桑皮\', \'茯神\', \'甘草\', \'杏仁\', \'痰喘\', \'大便\', \'闭者\', \'可用\']');
