INSERT INTO `prescript` VALUES (59365, '33670', '地骨皮散', '地骨皮1两，葳蕤1两，玄参1两，黄耆1两（锉），子芩1两，麦门冬1两（去心），川升麻1两，甘草半两（炙微赤，锉）。', '每服3钱，用水1中盏，加竹叶7片，煎至5分，去滓，加生地黄汁、蜜各半合，更煎1两沸，不拘时候温服。', '方出《圣惠》卷四，名见《普济方》卷十七', '心气盛实，气血壅涩，阴阳不通，荣卫隔塞，上焦壅滞，心胸烦热，口干颊赤。', '-', '-', '升麻', '[\'地骨皮\', \'葳蕤\', \'玄参\', \'两黄耆\', \'锉子\', \'麦门冬\', \'两去\', \'心川\', \'升麻\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (59366, '33652', '连翘散', '连翘1两半，射干1两，沉香1两，紫檀香1两，犀角屑1两，川升麻1两，川芒消5两，玄参2两，甘草1两（炙微赤，锉）。', '每服4钱，以水1中盏，煎至6分，去滓，每于食后良久温服。', '《圣惠》卷六十四', '风肿，欲结成脓。', '-', '-', '升麻', '[\'连翘\', \'两半\', \'射干\', \'沉香\', \'紫檀\', \'犀角\', \'两川\', \'升麻\', \'两川芒\', \'消两\', \'玄参\', \'甘草\', \'两炙微\']');
INSERT INTO `prescript` VALUES (59367, '33648', '龙胆散', '龙胆1两（去芦头），川升麻半两，麦门冬3分（去心），犀角屑1两，甘草半两（炙微赤，锉），栀子仁半两。', '每服5钱，以水1大盏，加生姜半分，煎至5分，去滓，加生地黄汁1合，更煎1-2沸，不拘时候温服。', '《圣惠》卷十八', '热病黄疸，热渴，额上汗出，手足热，小便赤涩。', '-', '-', '升麻', '[\'龙胆\', \'两去\', \'芦头川\', \'升麻\', \'半两\', \'麦门冬\', \'分去\', \'犀角\', \'甘草\', \'半两\', \'炙微\', \'栀子\', \'半两\']');
INSERT INTO `prescript` VALUES (59368, '33644', '加味益气汤', '黄耆（蜜炒）1钱5分，人参1钱，白术（去芦，炒）2钱，陈皮8分，当归1钱，柴胡5分，升麻5分，茵陈4分，苍术（米泔浸，炒）4分，栀子（炒）4分，猪苓4分，赤茯苓1钱，泽泻4分，黄连4分，滑石4分，甘草（炙）4分。', '加生姜，水煎服。以六味地黄丸加苍术、白术、茵陈、黄柏各2两，蜜为丸，相兼而进之。', '《寿世保元》卷三', '五疸，遍身发黄如栀子水染，病延日久，身体黑瘦，四肢沉困，憎寒发热，不思饮食。', '-', '-', '升麻', '[\'黄耆\', \'钱分\', \'人参\', \'白术\', \'去芦\', \'陈皮分\', \'当归\', \'柴胡\', \'升麻\', \'茵陈\', \'苍术\', \'泔浸\', \'炒分\', \'栀子\', \'炒分\', \'猪苓\', \'分赤\', \'茯苓\', \'泽泻\', \'黄连\', \'滑石\', \'甘草\', \'炙分\']');
INSERT INTO `prescript` VALUES (59369, '33605', '龙胆散', '龙胆2分（去芦头），甘草3分（炙微赤，锉），牡蛎1两（烧为粉），麦门冬3分（去心），柴胡3两（去苗），川升麻3分，犀角屑3分。', '龙胆汤（《圣济总录》卷六十）。', '《圣惠》卷五十五', '劳黄，额上汗出，手足中热，四肢烦疼，薄暮寒热，小便自利。', '-', '-', '升麻', '[\'龙胆\', \'分去\', \'芦头\', \'甘草\', \'分炙微\', \'牡蛎\', \'两烧\', \'麦门冬\', \'分去\', \'柴胡\', \'两去\', \'苗川\', \'升麻\', \'犀角\']');
INSERT INTO `prescript` VALUES (59370, '33523', '龙脑散', '龙脑（研）1分，蔓荆实1两，细辛（去苗叶）1两，升麻1两。', '每用半钱匕，揩牙良久，以温汤漱口。', '《圣济总录》卷一二一', '去口气，辟风冷。主牙齿疼痛，龈间出血。', '-', '-', '升麻', '[\'龙脑\', \'研分\', \'荆实\', \'细辛\', \'苗叶\', \'升麻\']');
INSERT INTO `prescript` VALUES (59371, '33493', '加减补中益气汤', '党参5钱，白术1钱（炒），黄耆3钱（炙），炙甘草1钱，当归身5钱（炒），升麻1钱（炙），生地5钱，乌梅肉3个。', '-', '《医门八法》卷三', '痔证已成，随肛脱出，累累然如珠贯穿，不能收复，血水常洗，痛如火炙，虚热相兼者。', '-', '-', '升麻', '[\'党参\', \'白术\', \'黄耆\', \'甘草\', \'当归\', \'身钱\', \'升麻\', \'生地\', \'乌梅\', \'肉个\']');
