INSERT INTO `prescript` VALUES (59373, '33486', '白花蛇酒', '白花蛇1条，乌梢蛇1条（去头尾皮骨），升麻1两，紫苏1两，枳实1两，当归1两，香附1两，熟地1两，黄耆1两，天冬1两，丹皮1两，粟壳1两，川芎1两，茯苓1两，厚朴1两，枳壳1两，三棱1两，苍术1两，牛膝1两，芍药1两，玄胡1两，杏仁1两，红花1两，肉桂1两，逢术1两，防风1两，草果1两，杜仲1两，木香1两，陈皮1两，青皮1两，半夏1两，桔梗1两，荆芥1两，藿香1两，僵蚕2两，麦冬2两，人参2两，白芷3两，枫藤3两，麻黄3两，乳香5钱，没药5钱，核桃肉4钱，益智仁5钱。', '上药入在酒内封口，晨煮至晚，取出埋土中，去火毒7日，分作4小瓶，半月开饮。先服《千金》托里散，或用加减天麻散，后用本方。如疮口烂，外贴阿魏膏药。', '《秘传大麻疯方》', '拓子风。其形遍身如拓子，细突而起，久不出血，其痛难忍。', '-', '-', '升麻', '[\'白花蛇\', \'乌梢蛇\', \'条去\', \'头尾\', \'皮骨\', \'升麻\', \'紫苏\', \'枳实\', \'当归\', \'香附\', \'熟地\', \'两黄耆\', \'两天\', \'冬两\', \'丹皮\', \'两粟壳\', \'川芎\', \'茯苓\', \'厚朴\', \'枳壳\', \'三棱\', \'苍术\', \'牛膝\', \'芍药\', \'两玄\', \'胡两\', \'杏仁\', \'红花\', \'肉桂\', \'两逢术\', \'防风\', \'草果\', \'杜仲\', \'木香\', \'陈皮\', \'青皮\', \'半夏\', \'桔梗\', \'荆芥\', \'藿香\', \'僵蚕\', \'麦冬\', \'人参\', \'白芷\', \'两枫\', \'麻黄\', \'乳香\', \'没药\', \'核桃肉\', \'益智仁\']');
INSERT INTO `prescript` VALUES (59374, '33456', '补母汤', '当归1钱，茯苓1钱，桔梗1钱，柴胡1钱，木香1钱，芍药1钱，莪术3钱，藿香3钱，芎3钱，人参3钱，黄耆3钱，肉桂3钱，桂心3钱，熏陆3钱，沉香3钱，乳香3钱，熟地黄3钱，丁子3钱，石膏3钱，滑石3钱，大黄3钱，升麻3钱，缩砂3钱，槟榔3钱，黄芩3钱，甘草3钱，安息香3钱。', '水煎服。', '《名家方选》', '产前产后，或金疮打扑，凡从血症变出者。', '-', '-', '升麻', '[\'当归\', \'茯苓\', \'桔梗\', \'柴胡\', \'木香\', \'芍药\', \'莪术\', \'藿香\', \'人参\', \'黄耆\', \'肉桂\', \'钱桂心\', \'熏陆\', \'沉香\', \'乳香\', \'熟地黄\', \'钱丁子\', \'石膏\', \'滑石\', \'大黄\', \'升麻\', \'钱缩\', \'槟榔\', \'黄芩\', \'甘草\', \'安息香\']');
INSERT INTO `prescript` VALUES (59375, '33403', '加味补中益气汤', '黄耆1钱，人参1钱，甘草（炙）1钱，白术1钱，当归1钱，陈皮1钱，升麻3分，柴胡3分，生地黄8分，天花粉8分。', '作1服。水煎服。', '《济阴纲目》卷二', '饮食劳倦，损伤脾胃，气弱体倦，发热作渴，饮食减少，而不生血者。', '-', '-', '升麻', '[\'黄耆\', \'人参\', \'甘草\', \'白术\', \'当归\', \'陈皮\', \'升麻\', \'柴胡\', \'生地黄\', \'天花粉\']');
INSERT INTO `prescript` VALUES (59376, '33370', '加味当归饮子', '当归5钱，生地黄5钱，升麻5钱，防风2钱半，荆芥穗2钱，何首乌2钱，白芍药3钱，柴胡3钱，川芎3钱，羌活3钱，黄耆3钱，红花1钱，苏木1钱，甘草1钱。', '加味当归饮（《疡科选粹》卷二）。', '《普济方》卷二七二', '诸疮痛痒。', '-', '-', '升麻', '[\'当归\', \'生地黄\', \'升麻\', \'防风\', \'钱半\', \'荆芥\', \'何首乌\', \'钱白\', \'芍药\', \'柴胡\', \'川芎\', \'羌活\', \'黄耆\', \'花钱\', \'苏木\', \'甘草\']');
