INSERT INTO `prescript` VALUES (59378, '33337', '地黄汤', '生地黄（细切）2两半，竹茹1两，玄参1两，鸡苏苗1两，赤茯苓（去黑皮）1两半，升麻1两半，麦门冬（去心，焙）1两半。', '每服3钱匕，水1盏，煎至5分，去滓，食后、临卧温服；如不能多服，细细含咽。', '《圣济总录》卷一二三', '咽喉中生疮，唾血不下食。', '-', '-', '升麻', '[\'生地黄\', \'细切\', \'两半\', \'竹茹\', \'玄参\', \'两鸡\', \'苏苗\', \'茯苓\', \'黑皮\', \'两半\', \'升麻\', \'两半\', \'麦门冬\', \'两半\']');
INSERT INTO `prescript` VALUES (59379, '33327', '防风散', '防风1两（去芦头），木通1两（锉），麦门冬1两（去心），川升麻1两，甘草3分（炙微赤，锉），虎杖1两（锉），石膏2两，葛根1两（锉）。', '每服5钱，以水1大盏，煎至5分，去滓温服，不拘时候。', '《圣惠》卷十', '伤寒阳痉，壮热不歇，筋脉拘急，牙关急痛。', '-', '-', '升麻', '[\'防风\', \'两去\', \'芦头\', \'木通\', \'麦门冬\', \'两去\', \'心川\', \'升麻\', \'甘草\', \'分炙微\', \'虎杖\', \'石膏\', \'葛根\']');
INSERT INTO `prescript` VALUES (59380, '33324', '保安膏', '当归（切，焙）1两，附子（去皮脐）1两，芎1两，防风（去叉）1两，白蔹1两，升麻1两，细辛（去苗叶）1两，侧柏1两，萆薢1两，桃仁（去皮）半两，甘草半两，桑根白皮半两，垂柳枝半两，白芨半两，黄耆半两，白芷半两，白僵蚕半两，铅丹（研）5两，雄黄（研）半两，麝香（研）半两，硫黄（研）半两，杏仁（去皮）3分，丹砂（研）1分。', '发背，酒调2匙，每日2服，外贴，2日1换；瘰疬瘘疮、疽疮、风肿、干癣、奶癣、肾癣、发鬓、发脑、发牙、蛇虫咬，皆贴之；折伤筋骨，酒服半匙；箭入骨，贴之自出；喉闭，含之即通；难产并胎死腹中，并酒化下半两；血气冲心，生姜自然汁加小便同煎，温酒化下1匙；但诸恶疮，数年不痊者，以盐汤先洗，然后贴之。', '《圣济总录》卷一三○', '一切疮肿。发背，瘰疬，瘘疮，疽疮，风肿，干癣，奶癣，肾癣，发鬓，发脑，发牙，蛇虫咬，折伤筋骨，箭入骨，喉闭，难产并胎死腹中，血气冲心，及诸恶疮，数年不痊者。', '-', '-', '升麻', '[\'当归\', \'切焙\', \'附子\', \'去皮\', \'两芎\', \'防风\', \'白蔹\', \'升麻\', \'细辛\', \'苗叶\', \'两侧\', \'柏两\', \'桃仁\', \'去皮\', \'半两\', \'甘草\', \'半两\', \'桑根\', \'白皮\', \'半两\', \'柳枝\', \'半两\', \'白芨\', \'半两\', \'黄耆\', \'半两\', \'白芷\', \'半两\', \'白僵蚕\', \'半两\', \'铅丹\', \'研两\', \'雄黄\', \'半两\', \'麝香\', \'半两\', \'硫黄\', \'半两\', \'杏仁\', \'去皮\', \'丹砂\', \'研分\']');
INSERT INTO `prescript` VALUES (59381, '33305', '加味补血汤', '黄耆2两，当归1两，升麻5分，益母草3钱。', '水煎服。', '《辨证录》卷十二', '妇人气虚，产后5-6日，胞衣留于腹中，经治仍胞衣不下，又无烦躁昏晕之状者。', '-', '-', '升麻', '[\'黄耆\', \'当归\', \'升麻\', \'益母草\']');
INSERT INTO `prescript` VALUES (59382, '33217', '补中益气汤', '黄耆半钱，人参（去芦）3钱，甘草半钱，红花1分，白芍药3分（秋冬之月未有，只用白术3分，代之），葛根半钱，当归身2分（酒洗，焙干），橘皮（不去白）2-3分，升麻2-3分，柴胡6分或3分，黄柏（酒洗，去皮）1-2分，黄芩2-3分，生甘草梢3分。', '上(口父)咀，作1服。水2盏，量气弱气盛加减水盏大小，去滓，食远稍热服，伤重者不2服。病劳复热甚者，黄耆用1钱；如觉胸中热者，去黄柏；如觉胸中不热者，去生甘草梢；腹中痛，加白芍药半分、甘草3分；恶寒冷痛，加去皮中桂1-2分（即桂心）；恶热喜寒而痛者，于已加白芍药、甘草二味中，更加生黄芩1-2分，治时热也；如天凉时恶热而痛，于已加白芍药、甘草、黄芩中，更少加桂1分；如腹痛在天气寒冷时，则去芍药，以其味酸而寒故也，别加热药用之；天气寒时腹痛，加益智仁2-3分，或加半夏半钱、生姜3片，忌用芍药；头痛加蔓荆子2分', '《普济方》卷二十四引《内外伤辨》', '补元气，泻心火。主饮食劳倦所伤，气高身热，烦喘短气，鼻息不调，嗜卧困倦少言，皆为热伤元气耗神。其初肌肤间必大热燥闷，心烦而渴，久后则不渴，头痛大作，四肢疼痛，表虚不任风寒，目不欲开。', '-', '-', '升麻', '[\'黄耆\', \'半钱\', \'人参\', \'芦钱\', \'甘草\', \'半钱\', \'红花\', \'分白\', \'芍药\', \'秋冬\', \'未有\', \'只用\', \'白术\', \'分代\', \'葛根\', \'半钱\', \'当归\', \'身分\', \'酒洗\', \'焙干\', \'橘皮\', \'不去\', \'升麻\', \'柴胡\', \'黄柏\', \'酒洗\', \'去皮\', \'黄芩\', \'生甘草\']');
INSERT INTO `prescript` VALUES (59383, '33195', '牢牙散', '羊筒骨灰3钱，升麻3钱，生地黄1钱，黄连1钱，石膏1钱，白茯苓5分，人参5分，胡桐泪3分。', '牢牙齿散（《普济方》卷七十）。', '《卫生宝鉴》卷八', '牙齿无力，不能嚼物。', '-', '-', '升麻', '[\'羊筒\', \'骨灰\', \'升麻\', \'生地黄\', \'黄连\', \'石膏\', \'白茯苓\', \'人参\', \'分胡桐\', \'泪分\']');
INSERT INTO `prescript` VALUES (59384, '33161', '地肤饮', '地肤子3两，知母（焙）2两，猪苓（去黑皮）2两，瞿麦（去梗）2两，黄芩（去黑心）升麻2两，木通（锉）2两，冬葵子1两半，海藻（洗去咸，焙）1两。', '每服3钱匕，水1盏，煎7分，去滓温服，不拘时候。', '《圣济总录》卷九十八', '卒淋，小便不通，秘涩疼痛。', '-', '-', '升麻', '[\'地肤子\', \'知母\', \'猪苓\', \'黑皮\', \'瞿麦\', \'黄芩\', \'黑心\', \'升麻\', \'木通\', \'冬葵子\', \'两半\', \'海藻\', \'洗去\', \'咸焙\']');
INSERT INTO `prescript` VALUES (59385, '33149', '加减补中益气汤', '黄耆3钱，党参3钱，白术2钱，陈皮2钱，升麻1钱，柴胡1钱，阿胶（冲化）2钱，焦艾2钱，甘草1钱。', '水煎服。大便溏薄，胃纳不佳，加砂仁2钱，扁豆4钱。', '《中医妇科治疗学》', '补气安胎。主平素体质不强，妊娠4-5月，忽然腰酸腹胀，或有下坠感，精神疲乏，胎动不安，阴道有少许出血，脉滑无力，属气虚下陷者。', '-', '-', '升麻', '[\'黄耆\', \'党参\', \'白术\', \'陈皮\', \'升麻\', \'柴胡\', \'阿胶\', \'化钱\', \'焦艾钱\', \'甘草\']');
INSERT INTO `prescript` VALUES (59386, '33129', '加减泻黄散', '山栀子1两，防风1两，藿香7钱，石膏5钱，连翘，甘草7钱半，升麻3钱。', '方中连翘用量原缺。小便短涩，加滑石、木通。', '《赤水玄珠》卷二十五', '心脾热甚口疮。', '-', '-', '升麻', '[\'栀子\', \'防风\', \'藿香\', \'石膏\', \'连翘\', \'甘草\', \'钱半\', \'升麻\']');
INSERT INTO `prescript` VALUES (59387, '33119', '甘菊花散', '甘菊花3分，茯神1两，犀角屑3分，防风1两（去芦头），川升麻3分，石膏2两，白芷半两，芎藭半两，甘草半两（炙微赤，锉），牡荆子1两，葛根1两（锉），枳壳半两（麸炒微黄，去瓤）。', '每服3钱，以水1中盏，加生姜半分，竹叶2-7片，煎至6分，去滓温服，不拘时候。', '《圣惠》卷二十二', '头风目眩痛。', '-', '-', '升麻', '[\'甘菊花\', \'茯神\', \'犀角\', \'防风\', \'两去\', \'芦头川\', \'升麻\', \'石膏\', \'白芷\', \'半两\', \'半两\', \'甘草\', \'半两\', \'炙微\', \'牡荆\', \'葛根\', \'枳壳\', \'半两\', \'微黄\']');
INSERT INTO `prescript` VALUES (59388, '33102', '甘菊花散', '甘菊花1两，川升麻1两，芎藭1两半，细辛1两，防风（去芦头）3分，石膏2两，羚羊角屑1两半，川大黄1两（锉碎，微炒），黄连1两（去须），甘草3分（炙微赤，锉）。', '每服4钱，以水1中盏，煎至6分，去滓，食后温服。', '《圣惠》卷三十二', '诸风毒攻头目，睛中如针刺痛，及欲成障翳。', '-', '-', '升麻', '[\'甘菊花\', \'两川\', \'升麻\', \'两半\', \'细辛\', \'防风\', \'芦头分\', \'石膏\', \'羚羊角\', \'两半\', \'大黄\', \'黄连\', \'两去\', \'甘草\', \'分炙微\']');
INSERT INTO `prescript` VALUES (59389, '33089', '当归龙胆散', '香白芷5分，当归梢5分，羊胫骨灰5分，生地黄5分，麻黄1钱，草豆蔻皮1钱，草龙胆1钱，升麻1钱，黄连1钱。', '以热浆水漱牙外，以粗末熬浆水刷牙。', '《兰室秘藏》卷中', '寒热牙痛。', '-', '-', '升麻', '[\'白芷\', \'当归\', \'分羊\', \'胫骨\', \'灰分\', \'生地黄\', \'麻黄\', \'草豆蔻\', \'皮钱\', \'龙胆\', \'升麻\', \'黄连\']');
INSERT INTO `prescript` VALUES (59390, '33084', '地骨皮汤', '地骨皮3分，升麻3分，犀角（镑）3分，玄参3分，常山1两（细锉）。', '每服3钱匕，以水1盏，煎至7分，去滓，未发前空腹温服。欲吐须忍，俟不能禁即吐。如此吐下即愈。', '《圣济总录》卷三十五', '久疟不愈，发不以时，或朝或夜，肌瘦食少。', '-', '-', '升麻', '[\'地骨皮\', \'升麻\', \'犀角\', \'玄参\', \'常山\', \'两细\']');
INSERT INTO `prescript` VALUES (59391, '33070', '龙花蕊散', '龙花蕊2两（出安南者），川升麻1两，郁李根1两（切），生干地黄1两，地骨皮1两，白蒺藜1两，杏仁（汤浸，去皮尖双仁，麸炒微黄）1两，细辛半两，龙脑半钱（细研），麝香半钱（细研）。', '每欲贴时，先以柳枝净揩齿，以新汲水漱口，更以盐花于齿龈内外揩之，有涎即吐却，不要漱口，便取白薄纸剪作片子，阔如薤叶，以水蘸纸，掺药末少许，贴齿龈上，便闭口勿语，有药汁咽之。', '《圣惠》卷三十四', '齿黄黑。', '-', '-', '升麻', '[\'花蕊\', \'两出\', \'安南\', \'升麻\', \'两郁\', \'李根\', \'两切\', \'生干\', \'地黄\', \'地骨皮\', \'两白\', \'蒺藜\', \'杏仁\', \'汤浸\', \'皮尖\', \'双仁麸\', \'微黄\', \'细辛\', \'半两\', \'龙脑\', \'半钱\', \'细研\', \'麝香\', \'半钱\', \'细研\']');
INSERT INTO `prescript` VALUES (59392, '33035', '连翘汤', '连翘3两，漏芦3两，射干3两，白蔹3两，升麻3两，栀子（擘）3两，芍药3两，羚羊角（屑）3两，黄芩3两，生地黄8两，寒水石5两（碎），甘草2两（炙）。', '上（口父）咀。以水1斗，煮取4升，去滓，分4服。', '《千金翼》卷二十二', '背脊痈疖，举身壮热。', '-', '-', '升麻', '[\'连翘\', \'漏芦\', \'射干\', \'白蔹\', \'升麻\', \'栀子\', \'芍药\', \'羚羊角\', \'黄芩\', \'生地黄\', \'寒水石\', \'两碎\', \'甘草\']');
INSERT INTO `prescript` VALUES (59393, '32971', '昆布丸', '昆布2两（洗去咸味），羚羊角屑1两，陈橘皮1两（汤浸，去白瓤，焙），赤茯苓2两，木香1两，射干1两，旋覆花1两，前胡2两（去芦头），川升麻1两，郁李仁2两（汤浸，去皮，微炒），桔梗2两（去芦头），紫菀1两（去苗土）。', '每服20丸，不拘时候，以温酒送下。', '《圣惠》卷五十', '膈气，咽喉噎塞，全不思食，肩背气壅，四肢烦疼。', '-', '-', '升麻', '[\'昆布\', \'两洗\', \'咸味\', \'羚羊角\', \'橘皮\', \'两汤\', \'浸去\', \'茯苓\', \'木香\', \'射干\', \'旋覆花\', \'两前\', \'胡两去\', \'芦头川\', \'升麻\', \'郁李仁\', \'两汤\', \'去皮\', \'桔梗\', \'两去\', \'芦头\', \'紫菀\', \'两去\', \'苗土\']');
INSERT INTO `prescript` VALUES (59394, '32884', '龙脑丹砂丸', '龙脑（研）1钱，丹砂（研）半两，人参1两，白茯苓（去黑皮）1两，羚羊角半两（镑），犀角半两（镑），甘草半两（炙，锉），升麻半两，恶实（炒）半两，麦门冬（去心，焙）1两半，马牙消1分（研），黄药1分。', '含化龙脑丸（《医方类聚》卷七十五引《御医撮要》）。', '《圣济总录》卷一二二', '咽喉肿痛，连舌颊、牙根赤肿，心烦，咽干多渴，眠睡不稳。', '-', '-', '升麻', '[\'龙脑\', \'研钱\', \'丹砂\', \'半两\', \'人参\', \'白茯苓\', \'黑皮\', \'羚羊角\', \'半两\', \'犀角\', \'半两\', \'甘草\', \'半两\', \'升麻\', \'半两\', \'恶实\', \'半两\', \'麦门冬\', \'两半\', \'马牙\', \'消分研\', \'黄药分\']');
INSERT INTO `prescript` VALUES (59395, '32868', '甘露汤', '人参2钱，白术2钱，升麻2钱，附子2钱，黄耆2钱，丹皮2钱。', '加大枣2枚，煎8分，食远服。', '《玉案》卷三', '中焦虚火，服凉药反盛者。', '-', '-', '升麻', '[\'人参\', \'白术\', \'升麻\', \'附子\', \'黄耆\', \'丹皮\']');
INSERT INTO `prescript` VALUES (59396, '32825', '夺命丹', '辰砂（择腰面者，以纱囊盛之，用升麻、麻黄、紫草、连翘4味同入砂罐，以新汲水、桑柴火煮1日夜，取出，将砂细研，仍将药汁去滓，飞，取末待干，听用）2钱，麻黄（连根节，酒蜜拌，炒焦）8分，蝉蜕（洗净，去足）3分，紫草（酒洗）5分，红花子3分，穿山甲（酒洗）5分，真蟾酥3分。', '周岁者半丸，2岁者1丸，最多者以3丸为止，热酒化服，厚盖取汗，汗出痘随出也。', '《麻疹全书》卷三', '风邪倒陷，及痘毒入里。', '-', '-', '升麻', '[\'辰砂\', \'择腰\', \'面者\', \'以纱囊\', \'之用\', \'升麻\', \'麻黄\', \'紫草\', \'连翘\', \'味同入\', \'砂罐\', \'汲水\', \'柴火\', \'日夜\', \'取出\', \'砂细研\', \'药汁\', \'飞取\', \'用钱\', \'麻黄\', \'连根节\', \'酒蜜\', \'焦分\', \'蝉蜕\', \'洗净\', \'足分\', \'紫草\', \'酒洗\', \'分红\', \'花子\', \'穿山甲\', \'酒洗\', \'分真\', \'蟾酥\']');
INSERT INTO `prescript` VALUES (59397, '32750', '红花子汤', '红花子1两，紫草茸1两，麻黄半两（去根节），升麻半两。', '紫草散（《杨氏家藏方》卷十九）。本方方名，原书人卫本作“红子汤”。', '《幼幼新书》（古籍本）卷十八引张涣方', '平调疮疹。主疮疱已出，色不红润，身热喘急，神志昏困。', '-', '-', '升麻', '[\'红花\', \'紫草茸\', \'麻黄\', \'半两\', \'根节\', \'升麻\', \'半两\']');
INSERT INTO `prescript` VALUES (59398, '32697', '凤麟膏', '荆芥1钱，薄荷1钱，玄参1钱，防风1钱，白芷1钱，蕤仁1钱，白芍1钱，栀子1钱，干葛1钱，连翘1钱，当归1钱，黄芩1钱，柴胡1钱，升麻1钱，车前子1钱，生地1钱，木贼1钱，细辛1钱，枸杞1钱，桑白皮1钱，夏枯草1钱，黄连1钱，菊花1钱，石决明1钱，蒺藜1钱，白术1钱，香附米1钱，黄柏1钱。', '本方用法中的“龙砂”、“虎液”，均出原着。参阅“龙砂”、“虎液”条。', '《眼科阐微》卷四', '降火，拨云去翳。主', '-', '-', '升麻', '[\'荆芥\', \'薄荷\', \'玄参\', \'防风\', \'白芷\', \'蕤仁\', \'白芍\', \'栀子\', \'干葛钱\', \'连翘\', \'当归\', \'黄芩\', \'柴胡\', \'升麻\', \'车前子\', \'生地\', \'木贼\', \'细辛\', \'枸杞\', \'桑白皮\', \'夏枯草\', \'黄连\', \'菊花\', \'石决明\', \'蒺藜\', \'白术\', \'香附\', \'黄柏\']');
INSERT INTO `prescript` VALUES (59399, '32692', '地骨皮散', '地骨皮1两，当归1两，川升麻半两，寒水石半两，桂心半两，芎穷半两，黄药1两，沉香1两，麝香1分（细研）。', '用贴齿根，重者以绵裹，含如弹子大，每日2-3丸。', '《圣惠》卷三十四', '牙齿动摇，吃食不稳。', '-', '-', '升麻', '[\'地骨皮\', \'当归\', \'两川\', \'升麻\', \'半两\', \'寒水石\', \'半两\', \'桂心\', \'半两\', \'半两\', \'黄药\', \'沉香\', \'麝香\', \'分细研\']');
