INSERT INTO `prescript` VALUES (59428, '32067', '连翘散', '连翘3分，黄耆3分（锉），木香半两，川升麻3分，葛根3分（锉），地骨皮3分，红雪2两，麦门冬3分（去心），犀角屑3分，甘草半两（生用），石膏1两，沉香半两，黄芩3分，防风半两（去芦头）。', '每服4钱，以水1中盏，入竹叶3-7片，煎至6分，去滓温服，每日3-4次。', '《圣惠》卷三十八', '乳石发毒生痈肿，烦热疼痛，口干心燥，筋脉拘急，头项强硬。', '-', '-', '升麻', '[\'连翘\', \'分黄耆\', \'木香\', \'半两\', \'升麻\', \'葛根\', \'地骨皮\', \'分红\', \'麦门冬\', \'分去\', \'犀角\', \'甘草\', \'半两\', \'生用\', \'石膏\', \'沉香\', \'半两\', \'黄芩\', \'防风\', \'半两\', \'芦头\']');
INSERT INTO `prescript` VALUES (59429, '32065', '甘草汤', '甘草2分，升麻2分，当归1分，椒1分，鳖甲1两。', '阴毒汤（《千金》卷九）、当归汤（《圣济总录》卷二十七）、阴毒甘草汤（《普济方》卷一三五）。', '方出《肘后方》卷二，名见《千金》卷九', '阴毒，身重背强，蛰蛰如被打，腹中痛，心下强，短气呕逆，唇青面黑，四肢冷，脉沉细而紧数。', '-', '-', '升麻', '[\'甘草\', \'升麻\', \'当归\', \'分椒分\', \'鳖甲\']');
INSERT INTO `prescript` VALUES (59430, '32061', '含化升麻丸', '川升麻1分，川大黄1分（锉，微炒），玄参1分，甘草半两（炙微赤，锉），射干1分，马牙消3分，杏仁半两（汤浸，去皮尖双仁，麸炒微黄）。', '每服以绵裹1丸咽津，1日5-6次。', '《圣惠》卷三十五', '热毒在肺脾，上焦壅滞，咽喉肿痛，心神烦闷。', '-', '-', '升麻', '[\'升麻\', \'分川\', \'大黄\', \'玄参\', \'甘草\', \'半两\', \'炙微\', \'射干\', \'分马牙\', \'消分\', \'杏仁\', \'半两\', \'汤浸\', \'皮尖\', \'双仁麸\', \'微黄\']');
