INSERT INTO `prescript` VALUES (59434, '31976', '地骨皮汤', '地骨皮（去土）1两，甘菊花（择）1两，升麻1两，黄连（去须）1两，防风（去叉）1两，决明子（微炒）1两，细辛（去苗叶）1两，竹叶（洗）。', '每服5钱匕，以水1盏半，加竹叶7片，煎至1盏，去滓，食后、临卧温服。', '《圣济总录》卷一○三', '风毒冲眼，赤痛干碜。', '-', '-', '升麻', '[\'地骨皮\', \'甘菊花\', \'升麻\', \'黄连\', \'防风\', \'决明子\', \'细辛\', \'苗叶\', \'竹叶\']');
INSERT INTO `prescript` VALUES (59435, '31973', '地黄养血汤', '熟地1钱2分，归身1钱，柴胡5分，茯苓1钱，白芍1钱，蔓荆8分，枣仁1钱，丹皮1钱，炙草5分，远志肉1钱2分，川芎1钱，黄耆1钱2分，升麻3分。', '-', '《陈素庵妇科补解》卷一', '血虚气陷，经行发热，兼头重目暗。', '-', '-', '升麻', '[\'熟地\', \'钱分\', \'归身\', \'柴胡\', \'茯苓\', \'白芍\', \'荆分\', \'枣仁\', \'丹皮\', \'草分\', \'远志\', \'肉钱分\', \'川芎\', \'黄耆\', \'钱分\', \'升麻\']');
INSERT INTO `prescript` VALUES (59436, '31967', '当归汤', '当归（切，焙）半两，柴胡（去芦头）3分，黄芩（去黑心）半两，细辛（去苗叶）3分，大黄（锉，炒）1两，升麻半两，五味子半两，紫菀（去苗土）1两，牛黄（研）1分，杏仁20个（汤浸，退去皮尖双仁，炒，别研）。', '2-3岁儿，每服1钱匕，以水7分，煎至5分，去滓温服，日3次。', '《圣济总录》卷一六八', '小儿挟实温壮，惕惕微惊。', '-', '-', '升麻', '[\'当归\', \'切焙\', \'半两\', \'柴胡\', \'芦头分\', \'黄芩\', \'黑心\', \'半两\', \'细辛\', \'苗叶分\', \'大黄\', \'升麻\', \'半两\', \'五味子\', \'半两\', \'紫菀\', \'苗土\', \'牛黄\', \'研分\', \'杏仁\', \'退去\', \'皮尖\', \'双仁\', \'别研\']');
INSERT INTO `prescript` VALUES (59437, '31953', '防风立效散', '柴胡1两，升麻1两，牛蒡子1两（炒），全蝎1两，石膏1两（生用），干葛1两，赤芍药1两，甘草1两，北防风半两，郁金半两，薄荷叶半两，半夏半两（泡），赤茯苓半两，北细辛半两，川芎半两，羌活半两，桔梗2两，荆芥2两。', '每服4大钱，水1盏半，葱头1个，煎至1盏，食后卧时温服，滓合煎服。候发散后，又相间服《局方》解毒雄黄丸。', '《朱氏集验方》卷九', '咽喉病。', '忌酒、毒物。', '-', '升麻', '[\'柴胡\', \'升麻\', \'牛蒡子\', \'全蝎\', \'石膏\', \'两生用\', \'干葛\', \'芍药\', \'甘草\', \'两北\', \'防风\', \'半两\', \'郁金\', \'半两\', \'薄荷叶\', \'半两\', \'半夏\', \'半两\', \'茯苓\', \'半两\', \'细辛\', \'半两\', \'川芎\', \'半两\', \'羌活\', \'半两\', \'桔梗\', \'荆芥\']');
INSERT INTO `prescript` VALUES (59438, '31942', '防风汤', '防风（去叉）1两，芎1两半，麻黄（去节，煎，掠去沫，焙干）1两半，独活（去芦头）1两半，桂（去粗皮）1两，升麻1两，羚羊角（镑）1两。', '每用10钱匕，水3盏，煮取2盏，加竹沥1合，更煎3沸，去滓，分3服，日2夜1，微热服之。若手足冷，加生姜3两。', '《圣济总录》卷九', '偏风不随。', '-', '-', '升麻', '[\'防风\', \'两芎\', \'两半\', \'麻黄\', \'去节\', \'掠去\', \'焙干\', \'两半\', \'独活\', \'芦头\', \'两半\', \'桂去\', \'粗皮\', \'升麻\', \'羚羊角\']');
INSERT INTO `prescript` VALUES (59439, '31920', '连翘散', '连翘1两，沉香1两，玄参1两，川大黄2两（锉碎，散炒），川升麻1两，桑根白皮1两（研），蓝子1两，犀角屑2两，寒水石3两，露蜂房1两（散炙，研），川朴消2两。', '每服4钱，以水1中盏，煎至6分，去滓，不拘时候温服。以长利为度。', '《圣惠》卷六十二', '积热毒气攻腑脏，出于皮肤，为发背壅肿。', '-', '-', '升麻', '[\'连翘\', \'沉香\', \'玄参\', \'两川\', \'大黄\', \'炒川\', \'升麻\', \'两桑根\', \'白皮\', \'两研\', \'蓝子\', \'犀角\', \'寒水石\', \'露蜂房\', \'两散\', \'研川朴\', \'消两\']');
INSERT INTO `prescript` VALUES (59440, '31919', '甘桔升麻汤', '甘草5分，桔梗1钱，升麻7分，杏仁2钱5分，当归2钱5分，玄明粉3钱。', '-', '《治疹全书》卷下', '疹后脱肛，由大肠积热，下攻脱肛，肿硬疼痛，或时下血，或粪细小，唇赤齿燥，其腹坚实，其脉洪数有力者。', '-', '-', '升麻', '[\'甘草\', \'桔梗\', \'升麻\', \'杏仁\', \'钱分\', \'当归\', \'钱分\', \'玄明粉\']');
INSERT INTO `prescript` VALUES (59441, '31915', '赤茯苓散', '赤茯苓3分，当归半两（锉，微炒），赤芍药半两，鬼箭羽3分，桂心3分，生干地黄半两，川升麻3分，木香半两，芎半两，桃仁3分（汤浸，去皮尖双仁，麸炒微黄）。', '每服3钱，以水一中盏，煎至5分，去滓温服。', '《圣惠》卷五十六', '尸疰。发作无时，心胸痛，喘息急。尸疰，喘急，气上冲，傍攻两胁。', '-', '-', '升麻', '[\'茯苓\', \'当归\', \'半两\', \'芍药\', \'半两\', \'鬼箭羽\', \'分桂心\', \'分生\', \'地黄\', \'半两\', \'升麻\', \'木香\', \'半两\', \'半两\', \'桃仁\', \'分汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\']');
INSERT INTO `prescript` VALUES (59442, '31883', '决明子汤', '决明子（炒）3分，柴胡（去苗）3分，黄连（去须）3分，防风（去叉）3分，升麻3分，苦竹叶3分，甘草（炙，锉）半两，菊花半两，细辛（去苗叶）1分。', '每服5钱匕，水1盏半，煎至8分，去滓，食后温服。', '《圣济总录》卷一○二', '肝脏实热，目眦生赤肉涩痛。', '-', '-', '升麻', '[\'决明子\', \'炒分\', \'柴胡\', \'苗分\', \'黄连\', \'防风\', \'去叉分\', \'升麻\', \'苦竹叶\', \'甘草\', \'半两\', \'菊花\', \'半两\', \'细辛\', \'苗叶分\']');
INSERT INTO `prescript` VALUES (59443, '31852', '加减益气汤', '黄耆5分，人参5分，白术1钱，陈皮1钱，当归7分，白芍药1钱，升麻3分，甘草（炙）3分，泽泻5分，砂仁5分，木香3分，白豆蔻3分，地榆5分，御米壳（醋炒）3分。', '水2盏，煎至8分，滤去滓，空心温服。', '《古今医鉴》卷五', '痢疾日久不愈，不能起床虚弱者。', '-', '-', '升麻', '[\'黄耆\', \'人参\', \'白术\', \'陈皮\', \'当归\', \'分白\', \'芍药\', \'升麻\', \'甘草\', \'炙分\', \'泽泻\', \'砂仁\', \'木香\', \'白豆蔻\', \'地榆\', \'分御\', \'米壳\', \'炒分\']');
INSERT INTO `prescript` VALUES (59444, '31828', '参杏膏', '人参半钱（去芦），杏仁半钱（去，皮尖），川升麻半钱（煨），甘草2钱（炙）。', '百日以前每服1字，用麦门冬（去心）前汤，食远调服。', '《幼科类萃》卷三', '小儿变蒸潮热。', '-', '-', '升麻', '[\'参半\', \'杏仁\', \'半钱\', \'皮尖\', \'升麻\', \'半钱\', \'甘草\']');
INSERT INTO `prescript` VALUES (59445, '31784', '地参菊花汤', '熟地60g，玄参60g，菊花60g，生石膏10-30g，升麻0.5-5g，蜂蜜60g。', '上加水1000毫升，煎成300毫升，徐徐服之。', '《古今名方》引杨慎修方', '补阴，清热，止痛。主阴虚胃热牙痛。', '-', '-', '升麻', '[\'熟地\', \'玄参\', \'菊花\', \'生石膏\', \'升麻\', \'蜂蜜\']');
INSERT INTO `prescript` VALUES (59446, '31747', '地黄散', '生干地黄2两，地骨皮1两，升麻3分，牛膝（酒浸，切，焙）1两半，羌活（去芦头）1两，芎穷1两，藁本（去土）1两，细辛（去苗叶）1两，胡桐泪半两（研）。', '先用温盐汤净漱口，次以药揩之，揩了更不漱，早起、临卧用之。', '《圣济总录》卷一二一', '口齿宣露，（匿虫）蚀肿痒。', '-', '-', '升麻', '[\'生干\', \'地黄\', \'地骨皮\', \'升麻\', \'牛膝\', \'浸切\', \'两半\', \'羌活\', \'芦头\', \'两芎\', \'藁本\', \'细辛\', \'苗叶\', \'胡桐泪\', \'半两\']');
INSERT INTO `prescript` VALUES (59447, '31667', '芦根散', '芦根2两（锉），地骨皮1两，茅根1两（锉），甘草3分（炙微赤，锉），葛根1两（锉），麦门冬1两半（去心，焙），黄芩1两，川升麻1两。', '每服4钱，以水1中盏，加竹茹1分，煎至6分，去滓温服，不拘时候。', '《圣惠》卷十八', '热病口干，烦热。', '-', '-', '升麻', '[\'芦根\', \'地骨皮\', \'两茅根\', \'甘草\', \'分炙微\', \'葛根\', \'麦门冬\', \'两半\', \'黄芩\', \'两川\', \'升麻\']');
INSERT INTO `prescript` VALUES (59448, '31637', '当归和血散', '川芎4分，青皮6分，槐花6分，荆芥穗6分，熟地黄6分，白术6分，当归身1钱，升麻1钱。', '槐花散（《兰室秘藏》卷下）、当归和血汤（《济阳纲目》卷六十三）。', '《脾胃论》卷下', '肠澼下血，湿毒下血。', '-', '-', '升麻', '[\'川芎\', \'青皮\', \'槐花\', \'荆芥\', \'穗分\', \'熟地黄\', \'白术\', \'当归\', \'身钱\', \'升麻\']');
INSERT INTO `prescript` VALUES (59449, '31608', '安痘汤', '玄参5钱，当归3钱，连翘1钱，白芍2钱，丹皮2钱，荆芥2钱，甘菊花2钱，升麻5分，天花粉1钱。', '水煎服。', '《辨证录》卷十四', '散风热。主痘症发全，痘毒全无，收痂大愈之后，放心纵欲，饮食过伤，又兼风热，而致数日之后身复发热，通身发出红斑，痒甚，愈抓愈痒，先出大小不一如粟米之状，渐渐长大如红云片。', '-', '-', '升麻', '[\'玄参\', \'当归\', \'连翘\', \'白芍\', \'丹皮\', \'荆芥\', \'甘菊花\', \'升麻\', \'天花粉\']');
INSERT INTO `prescript` VALUES (59450, '31604', '防葵散', '防葵1两，代赭1两（细研），人参1两（去芦头），铅丹1两半，钩藤1两，茯神1两，雷丸1两，虎头骨1两半（涂酥炙令黄），远志1两（去心），白僵蚕1两（微炒），生猪齿1两，防风1两（去芦头），卷柏1两，川升麻1两，附子1两（炮裂，去皮脐），虎掌3分（汤洗7遍，生姜汁拌炒令黄），朱砂1两（细研），牡丹1两，牛黄半两（细研），龙齿2两，蚱蝉14枚（微炒），蛇蜕皮1条（烧为灰），白蔹1两，白马眼睛1对（炙令微黄）。', '每服1钱，以温酒调下，不拘时候。', '《圣惠》卷二十二', '风癫，精神错乱，发作无时。', '-', '-', '升麻', '[\'防葵\', \'两代\', \'两细研\', \'人参\', \'两去\', \'芦头\', \'铅丹\', \'两半\', \'钩藤\', \'茯神\', \'雷丸\', \'两虎\', \'头骨\', \'两半\', \'炙令\', \'远志\', \'两去\', \'白僵蚕\', \'两微\', \'生猪\', \'防风\', \'两去\', \'芦头\', \'卷柏\', \'两川\', \'升麻\', \'附子\', \'两炮裂\', \'去皮\', \'虎掌\', \'分汤\', \'洗遍生\', \'姜汁\', \'炒令\', \'朱砂\', \'两细研\', \'牡丹\', \'牛黄\', \'半两\', \'细研\', \'龙齿\', \'蚱蝉\', \'蛇蜕\', \'皮条\', \'烧为\', \'白蔹\', \'白马\', \'眼睛\', \'对炙令\', \'微黄\']');
INSERT INTO `prescript` VALUES (59451, '31598', '甲香散', '甲香1两，犀角屑1两，射干1两，木香1两，熏陆香1两，丁香1两，黄连1两，川升麻1两，鳖甲1两（涂醋炙令黄，去裙襕），牡蛎1两（烧为粉），羚羊角屑1两，甘草1两（炙微赤，锉），黄芩1两，黄柏1两（锉），吴茱萸1分（汤浸7遍，焙干，微炒）。', '犀角散（《圣济总录》卷一四九）。', '《圣惠》卷五十七', '射工毒肿生疮。', '-', '-', '升麻', '[\'甲香\', \'犀角\', \'射干\', \'木香\', \'两熏\', \'陆香\', \'丁香\', \'黄连\', \'两川\', \'升麻\', \'鳖甲\', \'两涂\', \'炙令\', \'黄去\', \'牡蛎\', \'两烧\', \'羚羊角\', \'甘草\', \'两炙微\', \'黄芩\', \'黄柏\', \'吴茱萸\', \'分汤\', \'浸遍\', \'焙干\']');
INSERT INTO `prescript` VALUES (59452, '31532', '车前子散', '车前子1两，川升麻1两，羚羊角屑1两，赤芍药1两，黄芩1两，川大黄（挫锉，微炒）1两，麦门冬1两半（去心，焙），甘草半两（炙微赤，锉）。', '每服4钱，以水1中盏，加竹叶2-7片，煎至6分，去滓温服，不拘时候。', '《圣惠》卷三十二', '丹石毒上攻眼黑白睛，肿胀疼痛，开张不得，心神烦闷。', '-', '-', '升麻', '[\'车前子\', \'两川\', \'升麻\', \'羚羊角\', \'芍药\', \'黄芩\', \'两川\', \'大黄\', \'麦门冬\', \'两半\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (59453, '31513', '加味补中益气汤', '人参3钱，黄耆5钱，白术1钱，当归3钱，柴胡8分，升麻4分，生草1钱，陈皮1钱，金银花1两。', '水煎服。纵色者，加熟地1两，山萸肉4钱；动怒者，加白芍药1两，当归2钱，丹皮3钱，熟地5钱。', '《洞天奥旨》卷十二', '脐漏疮。', '-', '-', '升麻', '[\'人参\', \'黄耆\', \'白术\', \'当归\', \'柴胡\', \'升麻\', \'分生\', \'草钱\', \'陈皮\', \'金银花\']');
INSERT INTO `prescript` VALUES (59454, '31499', '利膈汤', '防风1两，鸡苏叶1两，桔梗1两，牛蒡子1两，荆芥穗1两，川升麻半两，人参半两，甘草（炙）半两。', '上（口父）咀。每服4钱，水1盏半，加生姜5片，煎至8分，去滓温服，不拘时候。', '《济生》卷五', '上膈壅热，口苦咽干，痰唾稠粘，心烦喜冷，咽喉生疮疼痛，一切上壅之证。', '-', '-', '升麻', '[\'防风\', \'两鸡\', \'苏叶\', \'桔梗\', \'牛蒡子\', \'荆芥\', \'两川\', \'升麻\', \'半两\', \'人参\', \'半两\', \'甘草\', \'半两\']');
INSERT INTO `prescript` VALUES (59455, '31486', '白鲜皮散', '白鲜皮半两，川升麻半两，黄芩半两，玄参半两，麦门冬1两（去心，焙）犀角屑半两，栀子仁半两，赤芍药半两，川大黄半两（锉碎，微炒），甘草半两（炙微赤，锉），杏仁半两（汤浸，去皮尖双仁，麸炒微黄）。', '每服4钱，以水1中盏，煎至6分，去滓温服，不拘时候。', '《圣惠》卷十八', '热病，毒气不散，遍身生热毒疮。', '-', '-', '升麻', '[\'白鲜皮\', \'半两\', \'升麻\', \'半两\', \'黄芩\', \'半两\', \'玄参\', \'半两\', \'麦门冬\', \'两去\', \'犀角\', \'半两\', \'栀子\', \'半两\', \'芍药\', \'半两\', \'大黄\', \'半两\', \'甘草\', \'半两\', \'炙微\', \'杏仁\', \'半两\', \'汤浸\', \'皮尖\', \'双仁麸\', \'微黄\']');
INSERT INTO `prescript` VALUES (59456, '31460', '加减升麻汤', '升麻1钱，防风6分，桔梗5钱，川芎5钱，陈皮5钱，牛蒡8分，连翘8分，山楂8分，柴胡8分，蝉退4分，赤芍6分，甘草3分，木通5分。', '-', '《种痘新书》卷四', '痘疮初热，外感风寒，憎寒壮热，咳嗽流涕，体性旺者。', '-', '-', '升麻', '[\'升麻\', \'防风\', \'桔梗\', \'川芎\', \'陈皮\', \'牛蒡\', \'连翘\', \'山楂\', \'柴胡\', \'分蝉\', \'退分\', \'赤芍\', \'甘草\', \'木通\']');
INSERT INTO `prescript` VALUES (59457, '31450', '龙脑散', '龙脑1两（细研），栀子仁半两，黄芩半两，麦门冬（去心，焙）半两，地骨皮半两，川升麻半两，犀角屑半两，牛黄1分（细研），川大黄1两（锉，微炒），甘草1分（炙微赤，锉）。', '每服半钱，食后以温水调下。5岁以下可服1字。', '《圣惠》卷八十九', '小儿肝脏壅热，两眼赤痛。', '-', '-', '升麻', '[\'龙脑\', \'两细研\', \'栀子\', \'半两\', \'黄芩\', \'半两\', \'麦门冬\', \'半两\', \'地骨皮\', \'半两\', \'升麻\', \'半两\', \'犀角\', \'半两\', \'牛黄\', \'分细\', \'研川\', \'大黄\', \'甘草\', \'分炙微\']');
INSERT INTO `prescript` VALUES (59458, '31394', '沉香散', '沉香3分，地骨皮1两，麦门冬1两（去心），当归1两，川大黄1两（锉碎，微炒），川升麻1两，木香3分，玄参1两，枳壳1两（麸炒微黄，去瓤），羚羊角屑1两，独活1两，甘草1两（生锉），赤芍药1两，防风3两（去芦头）。', '沉香汤（《圣济总录》一二八）。', '《圣惠》卷六十一', '石痈。', '-', '-', '升麻', '[\'沉香\', \'地骨皮\', \'麦门冬\', \'两去\', \'当归\', \'两川\', \'大黄\', \'碎微\', \'炒川\', \'升麻\', \'木香\', \'玄参\', \'枳壳\', \'两麸\', \'微黄\', \'羚羊角\', \'独活\', \'甘草\', \'两生\', \'芍药\', \'防风\', \'两去\', \'芦头\']');
INSERT INTO `prescript` VALUES (59459, '31381', '升麻栀子汤', '升麻（锉）3两，栀子仁3两，射干3两，赤茯苓（去黑皮）3两，白术5两，芍药4两。', '每服5钱匕，水1盏，煎至8分，加地黄汁1合，赤蜜1匙，更煎1-2沸，去滓温服。', '《圣济总录》卷四十七', '胃实热。', '-', '-', '升麻', '[\'升麻\', \'栀子\', \'射干\', \'两赤\', \'茯苓\', \'黑皮\', \'白术\', \'芍药\']');
INSERT INTO `prescript` VALUES (59460, '31299', '苍术升麻汤', '苍术1钱半，半夏1钱，厚朴7分，陈皮7分，枳实7分，桔梗7分，川芎7分，升麻7分，柴胡7分，木通7分，黄连5分，黄芩5分，木香5分，甘草5分，生姜3片。', '-', '《杂病源流犀烛》卷二十', '瘴疫。岭南春秋时月，山岚雾瘴之毒中于人，发为寒热温疟者。', '-', '-', '升麻', '[\'苍术\', \'钱半\', \'半夏\', \'厚朴\', \'陈皮分\', \'枳实\', \'桔梗\', \'川芎\', \'升麻\', \'柴胡\', \'木通\', \'黄连\', \'黄芩\', \'木香\', \'甘草\', \'生姜\']');
INSERT INTO `prescript` VALUES (59461, '31282', '防风汤', '防风（去叉）1两半，黄耆（锉）1两半，犀角（镑）1两半，升麻1两半，漏芦（去芦头）1两半，秦艽（去土）1两半，乌蛇（酒炙，去皮骨）2两，芒消（研）2两，枳壳（去瓤，麸炒）2两。', '每服5钱匕，水1盏半，煎至1盏，去滓温服，日2次。', '《圣济总录》卷十一', '风瘙瘾疹，皮肤痒痛，心神烦闷。', '-', '-', '升麻', '[\'防风\', \'两半\', \'黄耆\', \'两半\', \'犀角\', \'两半\', \'升麻\', \'两半\', \'漏芦\', \'芦头\', \'两半\', \'秦艽\', \'两半\', \'乌蛇\', \'去皮\', \'两芒\', \'消研\', \'枳壳\']');
