INSERT INTO `prescript` VALUES (59463, '30940', '瓜霜紫雪丹', '白犀角5钱，羚羊角5钱，青木香5钱，上沉香5钱，寒水石5两，石膏5两，灵磁石5两，飞滑石5两，玄参1两6钱，升麻1两6钱，飞朱砂5钱，生甘草8钱，公丁香2钱，麝香1钱2分，金箔1两，西瓜消8钱，冰片3钱。', '照《苏恭方》紫雪。', '《湿温时疫治疗法》引方省庵方', '时疫血热生风，热深厥深，手足反冷，咽干舌燥，头颈动摇，口噤齘齿，腿脚挛急，时发瘛疭，甚或睾丸上升，宗筋下注，少腹里急，阴中拘挛，或肠燥，有似板硬，按之痛甚，弯曲难伸，冲任脉失营养，当脐上下左右按之坚硬，动跃震手，虚里穴及心房亦必动跃异常。', '-', '-', '升麻', '[\'犀角\', \'羚羊角\', \'青木香\', \'沉香\', \'寒水石\', \'石膏\', \'两灵\', \'磁石\', \'两飞\', \'滑石\', \'玄参\', \'升麻\', \'朱砂\', \'生甘草\', \'钱公\', \'丁香\', \'麝香\', \'钱分\', \'金箔\', \'西瓜\', \'消钱\', \'冰片\']');
INSERT INTO `prescript` VALUES (59464, '30749', '芦根汤', '芦根（锉）1两，麦门冬（去心，焙）3分，升麻3分，葛根（锉）3分，山栀子（去皮）半两，石膏1两。', '每服5钱匕，水1盏半，加竹叶10片，煎取1盏，去滓温服。未发前连3服。', '《圣济总录》卷三十四', '温疟，初壮热，后寒战，骨节酸痛，口干烦渴。足少阳疟，热多汗出。', '-', '-', '升麻', '[\'芦根\', \'麦门冬\', \'心焙分\', \'升麻\', \'葛根\', \'分山\', \'栀子\', \'去皮\', \'半两\', \'石膏\']');
INSERT INTO `prescript` VALUES (59465, '30706', '连翘消毒饮', '连翘1钱，川芎1钱，当归1钱，赤芍药1钱，牛蒡子1钱，薄荷1钱，黄芩1钱，天花粉1钱，甘草1钱，枳壳1钱，桔梗1钱，升麻5分。', '水2钟，煎8分，食后服。便燥者，加酒炒大黄。', '《外科正宗》卷二', '时毒表里二症俱罢，余肿不消，疼痛不退者。', '-', '-', '升麻', '[\'连翘\', \'川芎\', \'当归\', \'芍药\', \'牛蒡子\', \'薄荷\', \'黄芩\', \'天花粉\', \'甘草\', \'枳壳\', \'桔梗\', \'升麻\']');
INSERT INTO `prescript` VALUES (59466, '30700', '地榆丹', '地榆（炙，锉）1分，黄连1分，干蓝叶1分，川升麻1两，川楝子半两，苦楝根半两。', '他榆丸（《卫生总微》卷十一）。', '《幼幼新书》卷二十九引张涣方', '消毒止痢。主小儿蛊痢。肠中蓄毒，下血如豆汁，或诸恶物。', '-', '-', '升麻', '[\'地榆\', \'黄连\', \'分干\', \'蓝叶\', \'分川\', \'升麻\', \'川楝子\', \'半两\', \'苦楝\', \'半两\']');
INSERT INTO `prescript` VALUES (59467, '30658', '加减败毒散', '防风1钱5分，荆芥2钱，羌活2钱，独活2钱，前胡2钱，升麻5分，干葛1钱，赤芍2钱，桔梗8分，川芎1钱5分，白芷2钱，薄荷8分，牛蒡子3钱，甘草8分，柴胡8分。', '加生姜、葱，水煎，热服出汗。', '《寿世保元》卷二', '天行时疫，头面肿大，咽喉不利，舌干口燥，憎寒壮热。四时瘟疫，皆可通用。', '-', '-', '升麻', '[\'防风\', \'钱分\', \'荆芥\', \'羌活\', \'独活\', \'钱前\', \'胡钱\', \'升麻\', \'分干葛钱\', \'赤芍\', \'桔梗\', \'川芎\', \'钱分\', \'白芷\', \'薄荷\', \'牛蒡子\', \'甘草\', \'柴胡\']');
INSERT INTO `prescript` VALUES (59468, '30622', '连翘漏芦汤', '漏芦1钱，麻黄（去根节）1钱，连翘1钱，升麻1钱，黄芩1钱，白敛1钱，甘草半钱，枳壳半钱。', '每服1钱，以水1小盏，煎至5分，去滓，量儿大小，不拘时候温服。热甚，加大黄、朴消。', '《婴童百问》卷四', '小儿痈疮，丹毒、疮疖，咽喉肿痛，腮肿。', '-', '-', '升麻', '[\'漏芦\', \'麻黄\', \'根节\', \'连翘\', \'升麻\', \'黄芩\', \'钱白\', \'敛钱\', \'甘草\', \'半钱\', \'枳壳\', \'半钱\']');
INSERT INTO `prescript` VALUES (59469, '30534', '防风升麻汤', '防风5钱，升麻3钱，半夏7钱（汤泡7次），苍术（清水漂，不炒）7钱，石膏1两（煅），黄芩（酒炒）1两，白芍2钱，甘草2钱，枳实（麸炒）5钱。', '每服2-3钱，用薄荷、生姜煎水调服。', '《治痘全书》卷十四', '痘因痰郁，咳嗽而出不快者。', '-', '-', '升麻', '[\'防风\', \'升麻\', \'半夏\', \'泡次\', \'苍术\', \'水漂\', \'石膏\', \'黄芩\', \'白芍\', \'甘草\', \'枳实\']');
INSERT INTO `prescript` VALUES (59470, '30498', '安胎饮', '绵耆3-4钱（炙），生地炭3钱，归身炭2钱，茯苓3钱，泽泻2钱，升麻（炒）5分，银花3钱，条岑（酒炒）1钱5分，川连（酒炒）8分，广木香5分，范制面2钱。', '加荷叶1角，水煎服。如噤口，水汤不进而呕吐频频，加石莲子3钱，石菖蒲3分，生姜3片，去生地炭；口渴者，加青果1枚，乌梅1枚；如赤痢加地榆炭3钱；白痢，加白槿花1钱；如腹痛甚者，加川椒20粒，去升麻，更加白芍1钱；如赤白兼者，加天仙藤2钱、驴胶1钱，去广木香；或外加扁豆叶2片，以醒胃气。', '《医方简义》卷五', '淡渗利湿，清热安胎。主妊妇患痢，名子痢。腰痛气滞，里急后重，少腹（？？）痛。', '口渴者，切忌生冷水果。', '-', '升麻', '[\'绵耆\', \'生地\', \'炭钱\', \'身炭\', \'茯苓\', \'泽泻\', \'升麻\', \'炒分\', \'花钱\', \'条岑\', \'分川\', \'连酒\', \'分广\', \'木香\', \'分范\', \'制面\']');
INSERT INTO `prescript` VALUES (59471, '30494', '连翘散', '连翘1两，何首乌（米泔浸一宿，焙）1两，干白薄荷1两，麝香（研）半钱，升麻半两，恶实（炒）半两，白茯苓（去黑皮）半两，蛇蜕皮（酒浸，炙）半两。', '每服1钱匕，食前温酒调下，日3次。', '《圣济总录》卷一二六', '瘰疬恶核肿痛。', '-', '-', '升麻', '[\'连翘\', \'何首乌\', \'泔浸\', \'一宿\', \'两干白\', \'薄荷\', \'麝香\', \'研半钱\', \'升麻\', \'半两\', \'恶实\', \'半两\', \'白茯苓\', \'黑皮\', \'半两\', \'蜕皮\', \'酒浸\', \'半两\']');
