INSERT INTO `prescript` VALUES (59474, '30467', '决明散', '决明子半两，子芩半两，柴胡半两（去苗），川大黄半两（锉，微炒），川升麻半两，栀子仁半两，羚羊角屑半两，甘草（炙微赤，锉）半两，石膏1两。', '决明汤（《圣济总录》卷一八一）。', '《圣惠》卷八十九', '膈中有热，赤眼涩痛不开。', '-', '-', '升麻', '[\'决明子\', \'半两\', \'子芩\', \'半两\', \'柴胡\', \'半两\', \'苗川\', \'大黄\', \'半两\', \'锉微\', \'炒川\', \'升麻\', \'半两\', \'栀子\', \'半两\', \'羚羊角\', \'半两\', \'甘草\', \'炙微\', \'半两\', \'石膏\']');
INSERT INTO `prescript` VALUES (59475, '30455', '苦参十二味丸', '苦参6两，干姜6两，芎6两，玄参5两，丹参5两，人参5两，沙参5两，白术5两，地骨白皮4两，独活4两，薏苡仁2升，蜀升麻2升。', '初服15丸，稍加至30丸，薏苡仁饮送下，1日2次。若觉冷，即去玄参、沙参，加桂心4两，细辛3两；若觉热，别加10两主地黄；若觉有痛处，去沙参，加当归6两；若觉有气，去玄参，加橘皮4两；若大便涩，加大槟榔仁20枚。', '《外台》卷十四引许仁则方', '风热未退，频服汤饮，力不能及。', '忌桃、李、生葱、生菜、芜荑。', '-', '升麻', '[\'苦参\', \'干姜\', \'两芎\', \'玄参\', \'丹参\', \'人参\', \'沙参\', \'白术\', \'两地\', \'白皮\', \'独活\', \'薏苡仁\', \'升蜀\', \'升麻\']');
INSERT INTO `prescript` VALUES (59476, '30439', '白鲜皮散', '白鲜皮1两，百合1两，石膏1两半，羚羊角屑1分，木通1两（锉），防风1两（去芦头），川升麻1两，龙齿1两半。', '每服3钱，以水1中盏，加葱白7寸，豉50粒，煎至6分，去滓温服，不拘时候。', '《圣惠》卷十', '伤寒阳痉，壮热不渴，筋脉不能舒展，牙关疼急，不欲见食。', '-', '-', '升麻', '[\'白鲜皮\', \'百合\', \'石膏\', \'两半\', \'羚羊角\', \'木通\', \'防风\', \'两去\', \'芦头川\', \'升麻\', \'龙齿\', \'两半\']');
INSERT INTO `prescript` VALUES (59477, '30424', '灵效散', '当归1两，生地1两，赤芍6钱，川芎6钱，山栀6钱，血余（煅存性）3钱，升麻3钱，龙骨3钱（煅，黄芩水浸），艾叶5钱。', '每服2钱，空心童便送下。', '《玉案》卷五', '妊娠尿血，比崩漏更甚。', '-', '-', '升麻', '[\'当归\', \'生地\', \'赤芍\', \'川芎\', \'钱山\', \'栀钱\', \'血余\', \'存性\', \'升麻\', \'龙骨\', \'黄芩\', \'水浸\', \'艾叶\']');
INSERT INTO `prescript` VALUES (59478, '30419', '加味补中益气汤', '人参（随便），黄耆（蜜炒）2钱，白术钱半，当归钱半，炙草8分，陈皮8分，柴胡（酒炒）3分，升麻（蜜炒）3分，杜仲钱半，续断钱半，淮山药（炒）钱半，百合2钱，五味子15粒。', '胎至七月，肺经养之，前此或堕，肺经受伤，后孕宜预于六月调补之，必须大剂，不可间断，保过七月，则无虞也。如肺虚有热，或口渴溺赤，或咳嗽喉燥，加麦冬1-2钱；如脾寒泄泻，加炮干姜1钱；如气滞而胀，加腹皮8分，或加枳壳7分。', '《会约》卷十四', '堕胎。', '-', '-', '升麻', '[\'人参\', \'随便\', \'黄耆\', \'白术\', \'钱半\', \'当归\', \'钱半炙草分\', \'陈皮分\', \'柴胡\', \'炒分\', \'升麻\', \'炒分\', \'杜仲\', \'钱半\', \'续断\', \'钱半淮\', \'山药\', \'百合\', \'五味子\']');
