INSERT INTO `prescript` VALUES (59490, '30233', '固胎饮', '人参2钱，黄耆2钱，白术2钱，条芩2钱，当归1钱，川芎7分，芍药7分，熟地黄5分，陈皮5分，黄连、升麻、柴胡，糯米81粒。', '本方方名，据剂型，当作“固胎丸”。方中黄连、升麻、柴胡用量原缺。', '《医统》卷八十五', '妊娠气血俱虚，3个月内惯要坠胎。', '-', '-', '升麻', '[\'人参\', \'黄耆\', \'白术\', \'钱条\', \'当归\', \'川芎\', \'芍药\', \'熟地黄\', \'陈皮分\', \'黄连\', \'升麻\', \'柴胡\', \'糯米\']');
