INSERT INTO `prescript` VALUES (59492, '30164', '绛宫丸', '大黄（酒蒸）2两，山楂1两，连翘1两，川芎1两，当归（酒洗）1两，麦芽1两，桃仁1两，芦荟1两，甘草1两，芸苔子1两，黄连（酒浸）1两半，南星（酒浸）1两半，海藻（酒洗）1两半，升麻半两，羌活半两，桔梗半两，防风半两，白术2两，黄芩（酒炒）半两。', '已破，加人参1两作膏；用甘草节、僵蚕同煎。', '《医学纲目》卷十九', '瘰疬。', '-', '-', '升麻', '[\'黄酒\', \'山楂\', \'连翘\', \'川芎\', \'当归\', \'酒洗\', \'麦芽\', \'桃仁\', \'芦荟\', \'甘草\', \'两芸\', \'苔子\', \'黄连\', \'酒浸\', \'两半\', \'南星\', \'酒浸\', \'两半\', \'海藻\', \'酒洗\', \'两半\', \'升麻\', \'半两\', \'羌活\', \'半两\', \'桔梗\', \'半两\', \'防风\', \'半两\', \'白术\', \'黄芩\', \'半两\']');
INSERT INTO `prescript` VALUES (59493, '30163', '当归散', '当归半两，细辛半两，川升麻半两，防风（去芦头）1分，藁本1分，莽草1分，芎1分，白杨枝1两。', '每用5钱，以水1大盏，煎至7分，去滓，热含冷吐。', '《圣惠》卷三十四', '牙齿宣露挺出，齿断，肿痛且痒。', '-', '-', '升麻', '[\'当归\', \'半两\', \'细辛\', \'半两\', \'升麻\', \'半两\', \'防风\', \'芦头分\', \'藁本\', \'莽草\', \'分芎\', \'白杨\']');
INSERT INTO `prescript` VALUES (59494, '30161', '八仙散', '白术2两，荆芥穗1两，黄柏7钱，甘草7钱，黄连9钱，升麻5钱。', '每服30丸，空心米饮下。以熊胆3钱，片脑4分，为末，用猪胆汁调擦患处。', '《摄生众妙方》卷七', '痔漏。', '-', '-', '升麻', '[\'白术\', \'荆芥\', \'黄柏\', \'甘草\', \'黄连\', \'升麻\']');
