INSERT INTO `prescript` VALUES (59496, '30023', '补中地黄汤', '黄耆1钱，人参8分，当归8分，白术8分，山萸8分，山药8分，陈皮8分，茯苓8分，泽泻5分，丹皮5分，熟地1钱5分，升麻3分。', '加生姜、大枣，水煎服。', '《嵩崖尊生》卷十一', '气血虚弱，无精神，体乏，腰腿酸。', '-', '-', '升麻', '[\'黄耆\', \'人参\', \'当归\', \'白术\', \'分山萸\', \'山药\', \'陈皮分\', \'茯苓\', \'泽泻\', \'丹皮\', \'熟地\', \'钱分\', \'升麻\']');
INSERT INTO `prescript` VALUES (59497, '29998', '当归膏', '当归1两，射干1两，升麻1两，附子半两，白蜜4两。', '取如杏仁大含之，日4-5遍，輙咽之。', '方出《千金》卷六，名见《圣济总录》卷一一七', '热病口烂，咽喉生疮，水浆不得入。', '-', '-', '升麻', '[\'当归\', \'射干\', \'升麻\', \'附子\', \'半两\', \'白蜜\']');
INSERT INTO `prescript` VALUES (59498, '29966', '决明子丸', '决明子1两半，防风（去叉）半两，黄连（去须）半两，车前子1两，升麻1两，黄芩1两（去黑心），大黄1两（锉，炒），玄参1两，萎蕤1两。', '每服30丸，食后温浆水送下，临卧再服。', '《圣济总录》卷一○三', '风毒冲目，虚热赤痛。', '-', '-', '升麻', '[\'决明子\', \'两半\', \'防风\', \'半两\', \'黄连\', \'半两\', \'车前子\', \'升麻\', \'黄芩\', \'两去\', \'黑心\', \'大黄\', \'玄参\', \'萎蕤\']');
INSERT INTO `prescript` VALUES (59499, '29900', '连翘散', '连翘1两半，葛根1两（锉），川升麻1两，枳壳1两（麸炒微黄，去瓤），黄芩2两，蓝叶1两，赤芍药1两，玄参1两，白蔹1两，羚羊角屑1两，木通1两（锉），黄耆1两（锉），川大黄1两（锉碎，微炒），甘草1两（锉）。', '每服4钱，以水1中盏，煎至6分，去滓，不拘时候温服。', '《圣惠》卷六十一', '痈肿焮痛，口干烦渴，不欲饮食。', '-', '-', '升麻', '[\'连翘\', \'两半\', \'葛根\', \'锉川\', \'升麻\', \'枳壳\', \'两麸\', \'微黄\', \'黄芩\', \'两蓝叶\', \'芍药\', \'玄参\', \'白蔹\', \'羚羊角\', \'木通\', \'黄耆\', \'锉川\', \'大黄\', \'甘草\']');
