INSERT INTO `prescript` VALUES (59501, '29843', '火郁汤', '羌活半两，升麻半两，葛根半两，参半两，白芍半两，柴胡3钱，甘草（炙）3钱，防风2钱半，葱白3寸。', '水煎服。', '《脉因证治》卷上', '四肢热，五心烦热，因热伏土中，或血虚得之，或胃虚多飧冷物，抑遏阳气于土中。', '-', '-', '升麻', '[\'羌活\', \'半两\', \'升麻\', \'半两\', \'葛根\', \'半两\', \'参半\', \'白芍\', \'半两\', \'柴胡\', \'甘草\', \'防风\', \'钱半\', \'葱白\']');
INSERT INTO `prescript` VALUES (59502, '29758', '安神镇心琥珀丸', '琥珀1两（细研），真珠1两（细研，水飞过），牛黄半两（细研），天竹黄1两（细研），铁粉1两，光明砂3分（细研，水飞过），金箔50片（细研），银箔50片（细研），龙齿1两（细研如粉），腻粉半两（研入），麝香1分（细研），犀角屑3分，露蜂房半两（微炒），龙胆半两，川升麻半两，天门冬3分（去心，焙），钩藤3分，茯神3分，菖蒲3分，远志3分（去心），麦门冬3分（去心，焙），人参3分（去芦头），白藓皮3分，黄芩半两，蚱蝉半两（微炒），干蝎半两（微炒），甘草半两（炙微赤，锉）。', '每服15丸，以竹叶汤送下。', '《圣惠》卷六十九', '妇人风邪凌心，言语不定，精神恍惚，乃成癫狂，发歇无时。', '-', '-', '升麻', '[\'琥珀\', \'两细研\', \'真珠\', \'两细\', \'研水\', \'飞过\', \'牛黄\', \'半两\', \'细研\', \'天竹黄\', \'两细研\', \'铁粉\', \'光明\', \'砂分细\', \'研水\', \'飞过\', \'金箔\', \'片细\', \'研银\', \'箔片\', \'细研\', \'龙齿\', \'两细\', \'研如粉\', \'半两\', \'研入\', \'麝香\', \'分细研\', \'犀角\', \'露蜂房\', \'半两\', \'龙胆\', \'半两\', \'升麻\', \'半两\', \'天门冬\', \'分去\', \'钩藤\', \'茯神\', \'菖蒲\', \'远志\', \'分去\', \'麦门冬\', \'分去\', \'人参\', \'分去\', \'芦头\', \'白藓\', \'皮分\', \'黄芩\', \'半两\', \'蚱蝉\', \'半两\', \'半两\', \'甘草\', \'半两\', \'炙微\']');
