INSERT INTO `prescript` VALUES (59504, '29670', '补中益气汤', '人参1钱，白术1钱，当归1钱，陈皮1钱，甘草5分，黄耆1钱5分，升麻5分，柴胡5分。', '加地榆1钱同煎，用芝麻油蘸纸点火烧荆芥穗灰，研末，调和前药温服。', '《幼科金针》卷下', '大人肠风，及痔疮出血不止者。', '-', '-', '升麻', '[\'人参\', \'白术\', \'当归\', \'陈皮\', \'甘草\', \'分黄耆\', \'钱分\', \'升麻\', \'柴胡\']');
INSERT INTO `prescript` VALUES (59505, '29612', '含化犀角丸', '犀角屑半两，射干3分，黄药半两，子芩半两，郁金半两，川大黄半两（锉碎，微炒），天门冬1两（去心，焙），玄参半两，川升麻半两，络石叶3分，甘草半两（炙微赤，锉），马牙消1两。', '常含1丸咽津，不拘时候。', '《圣惠》卷十八', '热病，心脾虚热，肺气暴壅，咽中肿痛，口舌干燥，咽津有妨，不下饮食。', '-', '-', '升麻', '[\'犀角\', \'半两\', \'射干\', \'分黄药\', \'半两\', \'子芩\', \'半两\', \'郁金\', \'半两\', \'大黄\', \'半两\', \'天门冬\', \'两去\', \'玄参\', \'半两\', \'升麻\', \'半两\', \'络石\', \'叶分\', \'甘草\', \'半两\', \'炙微\', \'马牙消\']');
INSERT INTO `prescript` VALUES (59506, '29605', '地黄汤', '生地黄2两（切），甘草（炙，锉）半两，大黄半两，升麻3分，车前子1两。', '每服5钱匕，水1盏半，煎至8分，去滓，下朴消末1钱匕，搅匀。食后温服，每日3次。先针舌下两边出血。', '《圣济总录》卷三十二', '伤寒后，心脾虚热，舌根肿塞，喉痹。', '-', '-', '升麻', '[\'生地黄\', \'两切\', \'甘草\', \'半两\', \'大黄\', \'半两\', \'升麻\', \'车前子\']');
INSERT INTO `prescript` VALUES (59507, '29517', '龙胆丸', '龙胆1分，升麻1分，麝1分，水银（枣肉研）1分，干蝎1分，铁粉1分，熊胆1分，雄黄1分，朱砂1分，芦荟半两，胡黄连半两，天麻半两。', '每服3丸，汤送下。', '《幼幼新书》卷二十三引《万全方》', '肝疳，体热烦渴，心躁，夜不得卧。', '-', '-', '升麻', '[\'龙胆\', \'升麻\', \'麝分\', \'水银\', \'枣肉\', \'研分\', \'干蝎分\', \'铁粉\', \'熊胆\', \'雄黄\', \'朱砂\', \'芦荟\', \'半两\', \'胡黄连\', \'半两\', \'天麻\', \'半两\']');
INSERT INTO `prescript` VALUES (59508, '29514', '龙花蕊散', '龙花蕊2两，升麻1两，黄芩1两（去黑心），白蒺藜1两（炒），郁李仁根1两（刮去皮，切），地柏1两（炙，切），地骨皮1两（锉碎，微炒），吴蓝（去根）1两，龙脑（别研）半钱，麝香（别研）半钱。', '上药除脑,麝外，捣罗为散，次入脑、麝细研。每欲贴时，先以柳枝揩齿，以新汲水漱口，更以盐花于齿龈内外揩之，有涎唾即吐，不要漱口，便取白薄纸剪作片子，阔如薤时，以水蘸纸，掺药末少许，贴齿龈上，咽津亦得。', '《圣济总录》卷一二一', '齿黄黑。', '-', '-', '升麻', '[\'花蕊\', \'升麻\', \'黄芩\', \'两去\', \'黑心\', \'蒺藜\', \'郁李仁\', \'根两\', \'去皮\', \'切地柏\', \'两炙切\', \'地骨皮\', \'吴蓝去\', \'龙脑\', \'研半钱\', \'麝香\', \'研半钱\']');
INSERT INTO `prescript` VALUES (59509, '29503', '补中益气汤', '黄耆1钱，甘草（炙）5分，人参（去芦）3分，升麻3分，柴胡3分，橘皮3分，当归身（酒洗）3分，白术3分。', '医王汤（《伤寒论今释》卷七引《方函口诀》）。①《小儿痘疹》有生姜、大枣。②本方改为丸剂，名“补中益气丸”（见《中药成方配本》苏州方）；本方改为片剂，名“补中益气片”（见《天津市中成药规范》）。手扪之肌表热，服补中益气汤1-2服后，若再烦乱，腹中或周身有刺痛，皆血涩不足，加当归身5分或1钱；如精神短少，加人参5分，五味子20个；头痛，加蔓荆子3分，痛甚，加川芎5分；顶痛脑痛，加藁本5分，细辛3分；如头痛有痰，沉重懒倦者，乃太阴痰厥头痛，加半夏5分，生姜3分；耳鸣，目黄，颊颔肿，颈、肩、臑、肘、臂外后廉痛，面', '《内外伤辨》卷中', '补中益气，升阳举陷。主脾胃气虚，发热，自汗出，渴喜温饮，少气懒言，体倦肢软，面色（白光）白，大便稀溏，脉洪而虚，舌质淡，苔薄白。或气虚下陷，脱肛，子宫下垂，久泻，久痢，久疟等，以及清阳下陷诸证。', '下元虚者禁用。', '-', '升麻', '[\'黄耆\', \'甘草\', \'炙分\', \'人参\', \'芦分\', \'升麻\', \'柴胡\', \'橘皮\', \'当归\', \'身酒\', \'洗分\', \'白术\']');
INSERT INTO `prescript` VALUES (59510, '29499', '甘草饮子', '甘草1两（炙微赤），陈橘皮1两（汤浸，去白瓤，焙），川升麻1两，生姜1两，葛根1两，人参1两（去芦头）。', '每服半两，以水1大盏，煎至5分，去滓温服，不拘时候。', '《圣惠》卷十七', '热病，毒气攻胃，呕哕不止。', '-', '-', '升麻', '[\'甘草\', \'两炙微\', \'橘皮\', \'两汤\', \'浸去\', \'焙川\', \'升麻\', \'生姜\', \'葛根\', \'人参\', \'两去\', \'芦头\']');
INSERT INTO `prescript` VALUES (59511, '29490', '达郁汤', '升麻2钱，元参9钱，干葛8钱，青蒿4钱，黄耆3钱。', '水煎服。', '《石室秘录》卷四', '火丹。', '-', '-', '升麻', '[\'升麻\', \'钱元\', \'参钱\', \'干葛钱\', \'青蒿\', \'黄耆\']');
INSERT INTO `prescript` VALUES (59512, '29466', '龙胆泻肝汤', '龙胆草8分，升麻3分，柴胡3分，羌活根1钱，酒黄柏1钱，防风根2钱，麻黄根2钱，苍术5分，猪苓3分，泽泻3分，藁本2分，红花2分，当归2分，黄芩5分，炙甘草3分。', '上(口父)咀，作1服。水2盏煎，稍热服。', '《医统》卷六十', '尿黄，臊臭淋沥，两丸如水，汗浸两胯，阴头亦冷。', '忌酒。面。', '-', '升麻', '[\'龙胆草\', \'升麻\', \'柴胡\', \'羌活\', \'根钱\', \'黄柏\', \'防风\', \'根钱\', \'麻黄根\', \'苍术\', \'猪苓\', \'泽泻\', \'藁本\', \'红花\', \'当归\', \'黄芩\', \'甘草\']');
INSERT INTO `prescript` VALUES (59513, '29460', '加减补中益气汤', '人参1钱半（去芦），黄耆1钱半（蜜炙），白术1钱，当归1钱（酒洗），草（炙）7分，陈皮8分，升麻5分，柴胡5分，加半夏1钱2分，黄柏8分，茯神1钱2分，枣仁1钱2分，贝母1钱2分，甘枸杞1钱2分。', '加生姜3片，大枣1枚，水2钟，煎8分，食远服。或加黄柏5分。如身大热，只1服，气和微汗而愈。夏月神短，加麦门冬、五味子；口干，加葛根；身刺痛乃少血，加当归；头痛，加川芎、蔓荆子，头顶痛，加藁本、细辛，诸头痛并用此四味；有痰加半夏、生姜；咳嗽，春加川芎、佛耳草，夏加黄芩、麦门冬、五味子，秋加黄芩、麻黄、金佛草，冬加款冬花、马兜铃；久嗽，乃肺中伏火，去参、耆；饮食不下，乃胃中有寒，或气滞，春加青皮、陈皮、木香，冬加益智仁、草豆蔻仁，夏加芩、连，秋加槟榔、砂仁；心下痞，加枳实、黄连、白芍药；腹胀，加枳实、木香、', '《医便》卷二', '饮食劳力，读书刻苦，勤政伤神，饥饱失时，症类疟状，发热头疼恶寒，身强体痛，苦劳极复感风寒，则头疼如破，全似外感伤寒之症，误表伤正者。', '-', '-', '升麻', '[\'人参\', \'钱半去\', \'芦黄耆\', \'半蜜\', \'白术\', \'当归\', \'钱酒\', \'草炙分\', \'陈皮分\', \'升麻\', \'柴胡\', \'分加\', \'半夏\', \'钱分\', \'黄柏\', \'茯神\', \'钱分\', \'枣仁\', \'钱分\', \'贝母\', \'钱分\', \'枸杞\', \'钱分\']');
INSERT INTO `prescript` VALUES (59514, '29402', '安胎饮', '白术1钱5分，陈皮4分（去白），甘草4分，人参1钱，川芎8分，当归2钱，生地1钱5分，柴胡4分，半夏6分（姜制），升麻4分。', '加生姜为引，空心屡服。盐汤探吐，则气升尿下行。', '《胎产指南》卷一', '孕妇脾胃气虚，胎压尿胞而脐腹作胀，或小便淋闭。', '-', '-', '升麻', '[\'白术\', \'钱分\', \'陈皮分\', \'甘草\', \'人参\', \'川芎\', \'当归\', \'生地\', \'钱分\', \'柴胡\', \'半夏\', \'分姜制\', \'升麻\']');
INSERT INTO `prescript` VALUES (59515, '29361', '防风汤', '防风1两，蔓荆子1两，细辛半两，川升麻半两，地骨皮半两，赤茯苓半两，芎1两。', '每用半两，以水1大盏，酒1盏，同煎至1盏，去滓，热含就于患处，良久冷即吐，含尽为度，每日2次。', '《圣济总录》卷一二一', '齿根出露，摇动疼痛。', '-', '-', '升麻', '[\'防风\', \'蔓荆子\', \'细辛\', \'半两\', \'升麻\', \'半两\', \'地骨皮\', \'半两\', \'茯苓\', \'半两\']');
INSERT INTO `prescript` VALUES (59516, '29299', '补中益气汤', '黄耆3钱，当归3钱，白术3钱，广陈皮3钱，川升麻8分，人参6分，柴胡6分，甘草6分，桂枝、防风、木通、木瓜。', '方中桂枝、防风、木通、木瓜用量原缺。', '《一盘珠》卷三', '升清降浊。主体虚冒风发肿。', '-', '-', '升麻', '[\'黄耆\', \'当归\', \'白术\', \'钱广\', \'陈皮\', \'钱川\', \'升麻\', \'人参\', \'柴胡\', \'甘草\', \'桂枝\', \'防风\', \'木通\', \'木瓜\']');
INSERT INTO `prescript` VALUES (59517, '29208', '百部丸', '百部（炒）3两，升麻1两，桂（去粗皮）1两，五味子（炒）1两，甘草（炙，锉）1两，紫菀（去苗土）1两，桑根白皮（炙，锉）1两。', '每服20丸，粥饮送下，日3次，夜1次。', '《圣济总录》卷六十六', '咳嗽唾脓血。', '-', '-', '升麻', '[\'百部\', \'升麻\', \'两桂去\', \'粗皮\', \'五味子\', \'甘草\', \'紫菀\', \'苗土\', \'两桑根\', \'白皮\']');
INSERT INTO `prescript` VALUES (59518, '29191', '防风汤', '防风（去叉）2两，麻黄（去根节）2两，半夏（汤洗7遍去滑，切，焙）2两，秦芎（去苗土）2两，独活（去芦头）2两，当归（切，焙）1两，远志（去心）1两，甘草（炙，锉）1两，防己1两，人参1两，黄芩（去黑心）1两，升麻1两，芍药1两，石膏（碎）半两。', '每服5钱匕，水1盏半，加麝香末少许，生姜1枣大（拍碎），煎至1盏，去滓温热服，不拘时候。', '《圣济总录》卷四十一', '肝气壅逆，肢体沉重，面色多青，时欲嗔怒，甚者恍惚狂言，心神不安。', '-', '-', '升麻', '[\'防风\', \'麻黄\', \'根节\', \'半夏\', \'洗遍\', \'滑切\', \'秦芎\', \'苗土\', \'独活\', \'芦头\', \'当归\', \'切焙\', \'远志\', \'甘草\', \'防己\', \'人参\', \'黄芩\', \'黑心\', \'升麻\', \'芍药\', \'石膏\', \'半两\']');
INSERT INTO `prescript` VALUES (59519, '29173', '甘菊花汤', '甘菊花1两，地骨皮1两，升麻1两半，黄连（去须）2两，茯神（去木皮）2两，葳蕤2两，防风（去叉）2两，木通（锉）2两。', '本方方名，原书（文瑞楼本）作“甘菊汤”。', '《圣济总录》卷一○四', '风毒冲眼，赤眼。', '-', '-', '升麻', '[\'甘菊花\', \'地骨皮\', \'升麻\', \'两半\', \'黄连\', \'茯神\', \'木皮\', \'葳蕤\', \'防风\', \'木通\']');
INSERT INTO `prescript` VALUES (59520, '29139', '防风散', '防风3分（去芦头），沙参半两（去芦头），犀角屑1两，川升麻1两，木通1两（锉），羌活1两，秦艽1两半（去苗），枳壳3分（麸炒微黄，去瓤），甘草1两（炙微赤，锉），茯神1两，龙齿1两，前胡1两（去芦头）。', '每服3钱，以水1中盏，煎至5分，去滓，下生地黄汁1合，更煎1-2沸，不拘时候温服。', '《圣惠》卷二十一', '热毒风，痰壅头目晕闷，心神不宁。', '-', '-', '升麻', '[\'防风\', \'分去\', \'芦头\', \'沙参\', \'半两\', \'芦头\', \'犀角\', \'两川\', \'升麻\', \'木通\', \'羌活\', \'秦艽\', \'两半\', \'去苗\', \'枳壳\', \'分麸\', \'微黄\', \'甘草\', \'两炙微\', \'茯神\', \'龙齿\', \'两前\', \'胡两去\', \'芦头\']');
INSERT INTO `prescript` VALUES (59521, '29103', '白牙药', '零陵香半两，香白芷半两，青盐半两，升麻半两，细辛2钱，麝香（另研）半钱，砂锅（细末）1两，石膏（细末）1两。', '每日早晨，以指蘸药擦牙后，用温水漱口。', '《御药院方》卷九', '牙齿黄黑不莹净。', '-', '-', '升麻', '[\'零陵\', \'半两\', \'白芷\', \'半两\', \'青盐\', \'半两\', \'升麻\', \'半两\', \'细辛\', \'麝香\', \'研半钱\', \'砂锅\', \'细末\', \'石膏\', \'细末\']');
INSERT INTO `prescript` VALUES (59522, '29083', '加味甘桔汤', '桔梗1钱，甘草1钱，升麻1钱，连翘1钱，防风1钱，牛蒡子1钱，黄芩（酒炒）1钱。', '水煎，加薄荷3叶，煎8分，食后细细呷之。', '《保命歌括》卷六', '大毒流行，咽痛喉痹。', '-', '-', '升麻', '[\'桔梗\', \'甘草\', \'升麻\', \'连翘\', \'防风\', \'牛蒡子\', \'黄芩\']');
INSERT INTO `prescript` VALUES (59523, '29035', '护心夺命丹', '肉豆蔻1两5钱，白芍药（酒炒）6两，炙甘草1两，广橘红3两，白扁豆（炒）3两，滑石6两，赤曲（炒，研）4两，莲肉（去心，炒焦黄）5两，绿色升麻（醋炒）2两5钱，川黄连（切片，拌好酒，同吴茱萸浸二宿，瓦上炒干，分开连、萸各贮，用净黄连）3两。', '每服3钱，白汤送下。如噤回痢并虚弱人，即以前方中去豆蔻，另以人参3钱煎浓汤送下。白痢，加茱萸1两。', '《广笔记》卷一', '虚弱人患痢及痢久脾虚者。', '-', '-', '升麻', '[\'肉豆蔻\', \'白芍\', \'药酒\', \'甘草\', \'两广\', \'橘红\', \'白扁豆\', \'滑石\', \'两赤曲\', \'研两\', \'莲肉\', \'焦黄\', \'绿色\', \'升麻\', \'两钱川\', \'黄连\', \'切片\', \'酒同\', \'吴茱萸\', \'浸二宿\', \'分开\', \'连萸\', \'贮用\', \'黄连\']');
INSERT INTO `prescript` VALUES (59524, '29034', '防风汤', '防风（去叉）1两，升麻1两，黄芩（去黑心）1两，芍药1两，石膏（生）1两，葛根（锉）1两，芍1两，羌活1两。', '每服3钱匕，水1盏，煎至7分，去滓服，不拘时候。', '《圣济总录》卷一六二', '产后风热，头痛，目掣动。', '-', '-', '升麻', '[\'防风\', \'升麻\', \'黄芩\', \'黑心\', \'芍药\', \'石膏\', \'生两\', \'葛根\', \'两芍\', \'羌活\']');
INSERT INTO `prescript` VALUES (59525, '28967', '扶元汤', '人参2钱，白术2钱，石斛？？钱5分，白茯苓？？钱5分，肉桂1钱，升麻1钱，山茱萸1钱2分，黄连1钱2分。', '加大枣2个，水煎，食前服。', '《玉案》卷五', '脾胃久虚，泄泻不止，神思倦怠，饮食少进，四肢酸软。', '-', '-', '升麻', '[\'人参\', \'白术\', \'石斛\', \'钱分\', \'白茯苓\', \'钱分\', \'肉桂\', \'升麻\', \'山茱萸\', \'钱分\', \'黄连\', \'钱分\']');
INSERT INTO `prescript` VALUES (59526, '28896', '防风浴汤', '防风3两，羊桃根3两，石南1两，秦艽1两，菵草1两，蒺藜子1两，蛇床子1两，白及1两，枳壳1两，苦参3两，升麻1两。', '上件合煎，浴之。', '《医方类聚》卷二十四引《御医撮要》', '诸风顽痹，皮肤不仁，瘙痒麻木。', '-', '-', '升麻', '[\'防风\', \'两羊\', \'桃根\', \'石南\', \'秦艽\', \'蒺藜\', \'蛇床子\', \'两白及\', \'枳壳\', \'苦参\', \'升麻\']');
INSERT INTO `prescript` VALUES (59527, '28825', '沉香散', '沉香1两，川升麻1两，细辛1两，白芷1两，地骨皮1两，黑附子（生用）1分。', '每用1钱，白汤温服，冷即吐了。', '《鸡峰》卷二十一', '老人久患冷牙疼不可忍者。', '-', '-', '升麻', '[\'沉香\', \'两川\', \'升麻\', \'细辛\', \'白芷\', \'地骨皮\', \'两黑\', \'附子\', \'生用\']');
INSERT INTO `prescript` VALUES (59528, '28798', '麦门冬汤', '麦门冬（去心，焙）2两，甘草（炙，锉）2两，白茯苓（去黑皮）1两，羌活（去芦头）1两，旋覆花1两，玄参1两，白术1两，芍药1两，柴胡（去苗）1两，人参1两，升麻1两，当归（切，焙）1两，桑根白皮（锉）1两，胡黄连1分，熟干地黄（焙）1两半，木香半两。', '每服3钱匕，水1盏，入甘草1寸，同煎至8分，去滓温服，不拘时候。', '《圣济总录》卷四十七', '胃热肠寒，善食数饥，少腹胀痛。', '-', '-', '升麻', '[\'麦门冬\', \'甘草\', \'白茯苓\', \'黑皮\', \'羌活\', \'芦头\', \'旋覆花\', \'玄参\', \'白术\', \'芍药\', \'柴胡\', \'人参\', \'升麻\', \'当归\', \'切焙\', \'两桑根\', \'白皮\', \'胡黄连\', \'分熟\', \'地黄\', \'两半\', \'木香\', \'半两\']');
