INSERT INTO `prescript` VALUES (59549, '28116', '防风汤', '防风（去叉）半两，菊花1两，升麻半两，独活（去芦头）半两，知母（焙）半两，黄芩（去黑心）半两，玄参半两，藁木（去苗土）半两，大黄（锉，炒）半两，栀子（去皮）半两，前胡（去芦头）半两，桔梗（锉，炒）半两，甘草（炙，锉）半两，麦门冬（去心，焙）半两，生干地黄（焙）半两。', '每用3钱匕，水1盏，煎至7分，去滓，食后服，日3次。', '《圣济总录》卷一一八', '脾胃蕴结热气，复为风冷相搏，唇边生核，结硬疼痛。', '-', '-', '升麻', '[\'防风\', \'半两\', \'菊花\', \'升麻\', \'半两\', \'独活\', \'芦头\', \'半两\', \'知母\', \'半两\', \'黄芩\', \'黑心\', \'半两\', \'玄参\', \'半两\', \'藁木去\', \'苗土\', \'半两\', \'大黄\', \'半两\', \'栀子\', \'去皮\', \'半两\', \'胡去\', \'芦头\', \'半两\', \'桔梗\', \'半两\', \'甘草\', \'半两\', \'麦门冬\', \'半两\', \'生干\', \'地黄\', \'半两\']');
INSERT INTO `prescript` VALUES (59550, '28094', '当归散', '当归1两（锉，微炒），川升麻半两，川乌头半两（炮裂，去皮脐），天门冬1两（去心，焙），五味子半两，赤芍药半两，远志半两（去心），独活半两，麻黄1两（去根节），防风半两（去芦头），芎半两，干姜半两（炮裂，锉），秦艽1两（去苗），桂心半两，大豆1合（炒熟），石斛半两（去根节），甘草1分（炙微赤，锉），人参半两（去芦头），白茯苓2两，紫菀半两（洗，去苗土），石膏1两，黄耆半两（锉），杏仁半两（汤浸，去皮尖双仁，麸炒微黄）。', '每服4钱，以水1中盏，煎至5分，去滓，入酒1合，更煎1两沸，温服，不拘时候。', '《圣惠》卷十九', '风湿痹亸曳，或手脚不遂，或风入五脏，恍恍惚惚，多语喜忘，又时恐怖，或肢节疼痛，头眩烦闷，或腰脊强直，腹满不食。', '-', '-', '升麻', '[\'当归\', \'炒川\', \'升麻\', \'半两\', \'乌头\', \'半两\', \'炮裂\', \'去皮\', \'天门冬\', \'两去\', \'五味子\', \'半两\', \'芍药\', \'半两\', \'远志\', \'半两\', \'去心\', \'独活\', \'半两\', \'麻黄\', \'两去\', \'根节\', \'防风\', \'半两\', \'芦头\', \'半两\', \'干姜\', \'半两\', \'炮裂\', \'秦艽\', \'两去\', \'苗桂心\', \'半两\', \'大豆\', \'炒熟\', \'石斛\', \'半两\', \'根节\', \'甘草\', \'分炙微\', \'人参\', \'半两\', \'芦头\', \'白茯苓\', \'紫菀\', \'半两\', \'苗土\', \'石膏\', \'两黄耆\', \'半两\', \'杏仁\', \'半两\', \'汤浸\', \'皮尖\', \'双仁麸\', \'微黄\']');
INSERT INTO `prescript` VALUES (59551, '28089', '甘菊花散', '甘菊花1两，旋覆花半两，人参1两（去芦头），川升麻3分，防风半两（去芦头），车前子半两，石膏1两，羚羊角屑半两，黄芩半两，决明子1两，杏仁半两（汤浸，去皮尖双仁，麸炒微黄），甘草1分（炙微赤，锉）。', '本方方名，《普济方》引作“甘菊散”。', '《圣惠》卷三十三', '肝脏风毒，上攻眼目，始即昏暗，久成内障。', '忌炙煿、热面。', '-', '升麻', '[\'甘菊花\', \'旋覆花\', \'半两\', \'人参\', \'两去\', \'芦头川\', \'升麻\', \'防风\', \'半两\', \'芦头\', \'车前子\', \'半两\', \'石膏\', \'羚羊角\', \'半两\', \'黄芩\', \'半两\', \'决明子\', \'杏仁\', \'半两\', \'汤浸\', \'皮尖\', \'双仁麸\', \'微黄\', \'甘草\', \'分炙微\']');
INSERT INTO `prescript` VALUES (59552, '28027', '快斑散', '贯众1两（拣，洗，焙干），赤芍药1两，甘草半两，川升麻半两，枳壳（麸炒，去瓤）半两。', '快斑汤（《治痘全书》卷十四）。', '《卫生总微》卷八', '平和疮疹。主痘疹出快肥红，烦渴，咽燥，喘急，大便闭，小便赤涩，口干目赤者。', '-', '-', '升麻', '[\'贯众\', \'两拣洗\', \'焙干\', \'芍药\', \'甘草\', \'半两\', \'升麻\', \'半两\', \'枳壳\', \'半两\']');
INSERT INTO `prescript` VALUES (59553, '27923', '和解汤', '羌活1钱，防风1钱，人参1钱，川芎5分，干葛5分，升麻5分，甘草5分，芍药3分，荆芥3分。', '上加生姜、大枣，水煎服。如无汗，加麻黄；嗽甚，加杏仁、桔梗。', '《诚书》卷十四', '感寒壮热，烦躁，鼻塞多涕，惊悸。', '-', '-', '升麻', '[\'羌活\', \'防风\', \'人参\', \'川芎\', \'分干葛分\', \'升麻\', \'甘草\', \'芍药\', \'荆芥\']');
INSERT INTO `prescript` VALUES (59554, '27887', '大青散', '大青1两，黄芩1两，川升麻1两，麦门冬（去心，焙）1两，栀子仁1两，甘草（炙微赤，锉）1两。', '每服4钱，以水1中盏，加竹叶6-7片，煎至6分，去滓温服，不拘时候。', '《圣惠》卷十五', '时气，咽痛口疮，烦躁头重。', '-', '-', '升麻', '[\'大青\', \'黄芩\', \'两川\', \'升麻\', \'麦门冬\', \'栀子\', \'甘草\', \'炙微\']');
