INSERT INTO `prescript` VALUES (59557, '27780', '龙齿散', '龙齿2两，犀角屑1两，川升麻1两，茯神1两半，玄参1两，麦门冬1两（去心，焙），甜竹根3分（锉），赤芍药1两半，马牙消1两，生干地黄2两。', '每服4钱，以水1中盏，加生姜半分，煎至6分，去滓，不拘时候温服。', '《圣惠》卷十一', '伤寒心热，狂言恍惚，卧不安席。', '-', '-', '升麻', '[\'龙齿\', \'犀角\', \'两川\', \'升麻\', \'茯神\', \'两半\', \'玄参\', \'麦门冬\', \'两去\', \'竹根\', \'芍药\', \'两半\', \'马牙消\', \'两生\', \'地黄\']');
INSERT INTO `prescript` VALUES (59558, '27777', '补中益气汤', '当归1钱，黄耆1钱，人参5分，白术8分，柴胡1钱，升麻1钱，干葛1钱，甘草5分。', '加生姜1片，水煎服。', '《回春》卷七', '痘疮结痴而误犯风寒，恶寒发热者。', '-', '-', '升麻', '[\'当归\', \'黄耆\', \'人参\', \'白术\', \'柴胡\', \'升麻\', \'干葛钱\', \'甘草\']');
INSERT INTO `prescript` VALUES (59559, '27731', '地骨皮散', '地骨皮3分，麦门冬1两（去心），柴胡1两半（去苗），川升麻1两，赤芍药3分，甘草半两（炙微赤，锉），射干1两，石膏2两，龙胆3分（去芦头）。', '每服3钱，以水1中盏，煎至5分，去滓，加牛蒡汁1合，更煎1两沸，食后温服。', '《圣惠》卷五', '脾实热，唇肿心烦，咽喉不利，体热烦疼。', '-', '-', '升麻', '[\'地骨皮\', \'麦门冬\', \'两去\', \'柴胡\', \'两半\', \'苗川\', \'升麻\', \'两赤\', \'芍药\', \'甘草\', \'半两\', \'炙微\', \'射干\', \'石膏\', \'龙胆\', \'分去\', \'芦头\']');
INSERT INTO `prescript` VALUES (59560, '27616', '地肤汤', '地肤草3两，知母2两，猪苓（去皮）2两，瞿麦2两，黄芩2两，升麻2两，通草2两，海藻1两，葵子1升，枳实2两（炙）。', '地肤子汤（《千金》卷二十一）、地肤子散（《玉机微义》卷二十八引《济生》）。大小便皆闭者，加大黄3两；妇人房劳，肾中有热，小便难不利，腹满痛，脉沉细者，加猪肾1具。', '《外台》卷二十七引《小品方》', '诸淋。下焦诸结热，小便赤黄，数起出少，大痛或便血；温病后余热，及霍乱后当风，取热过度，饮酒房劳，及步行冒热，冷饮逐热，热结下焦，及乳石热动关格，少腹坚，胞胀如斗大。', '-', '-', '升麻', '[\'地肤\', \'知母\', \'猪苓\', \'去皮\', \'瞿麦\', \'黄芩\', \'升麻\', \'通草\', \'海藻\', \'葵子\', \'枳实\']');
INSERT INTO `prescript` VALUES (59561, '27615', '连翘散', '连翘1两，川升麻1两，汉防己1两，黄芩1两，川大黄1两（锉碎，微炒），川芒消1两，柴胡1两（去苗），赤芍药2两，甘草1两（炙微赤，锉），犀角屑1两，杏仁1两（汤浸，去皮尖双仁，麸炒微黄）。', '每服3钱，以水1中盏，煎至6分，去滓，每于食后温服。', '《圣惠》卷八十一', '产后妒乳，肿痛此热，欲结成痈。', '-', '-', '升麻', '[\'连翘\', \'两川\', \'升麻\', \'两汉\', \'防己\', \'黄芩\', \'两川\', \'大黄\', \'碎微\', \'炒川芒\', \'消两\', \'柴胡\', \'两去\', \'芍药\', \'甘草\', \'两炙微\', \'犀角\', \'杏仁\', \'两汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\']');
INSERT INTO `prescript` VALUES (59562, '27595', '附桂汤', '附子3钱，肉桂1钱，黄柏5分，知母5分，升麻5分，甘草5分，黄耆1钱半，人参7分。', '水煎服。', '《医学入门》卷八', '虚寒带下，白带腥臭，多悲不乐，大寒；兼治浊淫。', '-', '-', '升麻', '[\'附子\', \'肉桂\', \'黄柏\', \'知母\', \'升麻\', \'甘草\', \'分黄耆\', \'人参\']');
INSERT INTO `prescript` VALUES (59563, '27565', '八白散', '白丁香3两，白及3两，白僵蚕3两，白牵牛3两，杜蒺藜3两，新升麻（肉白者佳）3两，三赖子2两，白蔹2两，白芷2两，白附子半两，白茯苓半两。', '至夜津调涂面，明旦以莹肌如玉散洗之。', '《卫生宝鉴》卷二十', '劳汗当风，郁乃痤。', '-', '-', '升麻', '[\'丁香\', \'两白及\', \'白僵蚕\', \'两白\', \'牵牛\', \'两杜\', \'蒺藜\', \'两新\', \'升麻\', \'肉白者\', \'两三\', \'赖子\', \'白蔹\', \'白芷\', \'白附子\', \'半两\', \'白茯苓\', \'半两\']');
INSERT INTO `prescript` VALUES (59564, '27522', '地骨皮散', '地骨白皮（微炒）1两，当归（切，焙干）3分，升麻半两，桂（去粗皮）1分,甘草（炙黄赤色）半两，芎藭3分，紫矿（炙）半两，寒水石2两半，莨菪子（炒香熟）半两。', '每用1钱匕，涂齿根下；甚者绵裹如弹子大，日吞2-3丸，口中含化亦妙。', '《圣济总录》卷一二一', '齿动，吃食不稳。', '-', '-', '升麻', '[\'地骨\', \'白皮\', \'当归\', \'焙干\', \'升麻\', \'半两\', \'桂去\', \'粗皮分\', \'甘草\', \'赤色\', \'半两\', \'半两\', \'寒水石\', \'两半\', \'莨菪子\', \'炒香熟\', \'半两\']');
INSERT INTO `prescript` VALUES (59565, '27484', '当归拈痛汤', '羌活半两，防风3钱，升麻1钱，葛根2钱，白术1钱，苍术3钱，当归身3钱，人参2钱，甘草5钱，苦参（酒浸）2钱，黄芩1钱（炒），知母3钱（酒洗），茵陈5钱（酒炒），猪芩3钱，泽泻3钱。', '拈痛汤（《兰室秘藏》卷中）、当归止痛汤（《便览》卷一）。《便览》有茯苓。', '《医学启源》卷下', '湿热为病，肢节烦痛，肩背沉重，胸膈不利，遍身疼，下注于胫，肿痛不可忍。', '-', '-', '升麻', '[\'羌活\', \'半两\', \'防风\', \'升麻\', \'葛根\', \'白术\', \'苍术\', \'当归\', \'身钱\', \'人参\', \'甘草\', \'苦参\', \'酒浸\', \'黄芩\', \'知母\', \'钱酒\', \'茵陈\', \'钱酒\', \'泽泻\']');
INSERT INTO `prescript` VALUES (59566, '27469', '地黄煎', '生地黄汁3升，生葛汁1升，生玄参汁1升，大黄2两，升麻2两，栀子仁3两，麻黄3两，犀角3两，石膏5两，芍药4两。', '生地黄汤（《圣济总录》卷九十二）。', '《千金》卷十一', '筋实极，手足爪甲或青、或黄、或黑乌黯，四肢筋急烦满。', '-', '-', '升麻', '[\'生地黄\', \'升生\', \'葛汁\', \'升生\', \'玄参\', \'汁升\', \'大黄\', \'升麻\', \'栀子\', \'麻黄\', \'犀角\', \'石膏\', \'芍药\']');
INSERT INTO `prescript` VALUES (59567, '27465', '白术汤', '白术1两，葛根1两，升麻半两，黄芩半两，芍药2两，甘草2钱半。', '上(口父)咀。每服1两，水1盏半，煎至1盏，去滓温服，不拘时候。', '《保命集》卷中', '破伤风，大汗不止，筋挛搐搦。', '-', '-', '升麻', '[\'白术\', \'葛根\', \'升麻\', \'半两\', \'黄芩\', \'半两\', \'芍药\', \'甘草\', \'钱半\']');
INSERT INTO `prescript` VALUES (59568, '27464', '八仙丹', '白附子1两，天麻1两，升麻1两，丹参1两，威灵仙1两，细辛1两，赤箭1两，蜈蚣1对（酥炙，去头足）。', '每日食后米饮嚼下1丸，日进3服。服之10日或1月至2月，筋骨疼痛，是其验也。', '《御药院方》卷一', '大癞病。', '比至有验，且忌出入行动。', '-', '升麻', '[\'白附子\', \'天麻\', \'升麻\', \'丹参\', \'威灵仙\', \'细辛\', \'两赤箭\', \'蜈蚣\', \'头足\']');
INSERT INTO `prescript` VALUES (59569, '27459', '决明子散', '决明子1两，黄连1两（去须），川升麻1两，枳壳1两（麸炒微黄，去瓤），玄参1两，黄芩1两，车前子半两，栀子仁半两，地肤子半两，人参半两（去芦头）。', '每服3钱，以水1中盏，煎至6分，去滓，食后温服。', '《圣惠》卷三十三', '眼中卒生翳膜，视物昏暗，及翳覆裹瞳仁。', '-', '-', '升麻', '[\'决明子\', \'黄连\', \'两去\', \'须川\', \'升麻\', \'枳壳\', \'两麸\', \'微黄\', \'玄参\', \'黄芩\', \'车前子\', \'半两\', \'栀子\', \'半两\', \'地肤子\', \'半两\', \'人参\', \'半两\', \'芦头\']');
INSERT INTO `prescript` VALUES (59570, '27434', '栀子大青汤', '生地5钱，升麻8分，栀子1钱半，黄芩1钱半，大青3钱，葱白3个。', '水煎，去滓温服。', '《医略六书》卷二十八', '妊娠伤寒，火郁不解，营阴受伤而夹湿热，故斑发青紫，胎动不安，脉数弦大者。', '-', '-', '升麻', '[\'生地\', \'升麻\', \'栀子\', \'钱半\', \'黄芩\', \'钱半大青\', \'葱白\']');
INSERT INTO `prescript` VALUES (59571, '27405', '绛雪散', '木通（锉）2两，桔梗（锉，炒）2两，槟榔2两，枳壳（去瓤，麸炒）1两半，犀角（镑）1两半，柴胡（去苗）2两，升麻2两，木香2两，赤茯苓（去黑皮）2两，桑根白皮（锉）4两，山栀子仁4两，桂（去粗皮）2两，人参2两，苏枋木5两，朴消（研）1斤，丹砂（研）1两，麝香（研）1分，诃黎勒（去核）5枚。', '每服1钱或2钱，食后临卧，以冷蜜汤调下。', '《圣济总录》卷一二二', '热结喉间，连颊肿不消，心膈烦满。', '-', '-', '升麻', '[\'木通\', \'桔梗\', \'槟榔\', \'枳壳\', \'两半\', \'犀角\', \'两半\', \'柴胡\', \'升麻\', \'木香\', \'两赤\', \'茯苓\', \'黑皮\', \'两桑根\', \'白皮\', \'两山\', \'栀子\', \'两桂去\', \'粗皮\', \'人参\', \'两苏枋\', \'两朴\', \'消研斤\', \'丹砂\', \'研两\', \'麝香\', \'研分诃黎勒\', \'去核\']');
INSERT INTO `prescript` VALUES (59572, '27401', '赤茯苓汤', '赤茯苓（去黑皮）1分，凝水石（研）1分，龙齿（研）半两，石膏（碎）1两，麦门冬（去心，焙）3分，升麻3分。', '1-2岁儿，每服半钱匕，水3分，竹沥3分，同煎至3分，去滓温服，早晨、晚后各1次。', '《圣济总录》卷一六九', '小儿惊热，神气不安，手足掣缩。', '-', '-', '升麻', '[\'茯苓\', \'黑皮\', \'分凝水石\', \'研分\', \'龙齿\', \'半两\', \'石膏\', \'麦门冬\', \'心焙分\', \'升麻\']');
INSERT INTO `prescript` VALUES (59573, '27396', '红雪通中散', '大青（别研）1两，消石5斤（煎药后下），丹砂（别研）半两，桑根白皮1两，羚羊角（镑）半两，苏枋木2两，栀子仁15枚，槐花1两，升麻1两半，淡竹叶1握，诃黎勒15枚（去核），大腹5枚（锉），槟榔5枚（锉）。', '每服2钱匕，小儿半钱匕，临卧用温水调下。', '《圣济总录》卷五', '风疾，及一切滞闷不通，亦治伤寒。', '-', '-', '升麻', '[\'大青别\', \'研两\', \'消石斤\', \'煎药\', \'丹砂\', \'别研\', \'半两\', \'桑根\', \'白皮\', \'羚羊角\', \'半两\', \'苏枋\', \'栀子\', \'仁枚\', \'槐花\', \'升麻\', \'两半\', \'淡竹叶\', \'握诃黎勒枚\', \'去核\', \'大腹\', \'槟榔\']');
INSERT INTO `prescript` VALUES (59574, '27320', '防风散', '防风半两（去芦头），芎半两，川升麻半两，犀角屑半两，羚羊角屑半两，赤芍药半两，前胡半两（去芦头），细辛1分，秦皮1分，朱砂1分（细研），甘草（炙微赤，锉）1分，牛黄2钱（细研）。', '每服1钱，食后煎地黄汤调下。', '《圣惠》卷三十二', '退上焦壅热，止痛消肿。主眼赤肿痛，多眵泪。', '-', '-', '升麻', '[\'防风\', \'半两\', \'芦头\', \'半两\', \'升麻\', \'半两\', \'犀角\', \'半两\', \'羚羊角\', \'半两\', \'芍药\', \'半两\', \'半两\', \'芦头\', \'细辛\', \'分秦\', \'皮分\', \'朱砂\', \'分细研\', \'甘草\', \'炙微\', \'牛黄\', \'钱细研\']');
INSERT INTO `prescript` VALUES (59575, '27307', '百花丸', '防风1两半，人参1两半，苁蓉1两半，干地黄1两半，羚羊角1两半，麦冬1两半，天门冬1两半，芍药20铢，独活20铢，干姜20铢，白术20铢，丹参20铢，山茱萸20铢，甘草20铢，茯神20铢，升麻20铢，黄耆20铢，菊花20铢，地骨皮20铢，石斛20铢，牛膝20铢，五加皮20铢，薯蓣20铢，秦艽1两，芎1两，桂心1两，防己1两，生姜1两，黄芩1两，附子18铢，石膏3两，寒水石2两。', '每服20丸，生姜蜜汤送下，日3次。稍加至30丸。', '《医部全录》卷二二一', '中风后虚热翕翕然。', '忌油、面、蒜、生冷、酢滑，及猪、羊、鸡、鱼等肉。', '-', '升麻', '[\'防风\', \'两半\', \'人参\', \'两半\', \'苁蓉\', \'两半\', \'地黄\', \'两半\', \'羚羊角\', \'两半\', \'麦冬\', \'两半\', \'天门冬\', \'两半\', \'芍药\', \'独活\', \'干姜\', \'白术\', \'丹参\', \'山茱萸\', \'甘草\', \'茯神\', \'升麻\', \'黄耆\', \'菊花\', \'地骨皮\', \'石斛\', \'牛膝\', \'五加皮\', \'薯蓣\', \'秦艽\', \'两芎\', \'两桂心\', \'防己\', \'生姜\', \'黄芩\', \'附子\', \'石膏\', \'寒水石\']');
INSERT INTO `prescript` VALUES (59576, '27245', '沉香散', '沉香半两，升麻半两，白芷半两，藁本（去苗土）半两，细辛（去苗叶）半两，丁香半两，寒水石（研）2两。', '每日早取柳枝，咬枝头令软，摭药揩齿，暖水漱，复以绵揩之令净。', '《普济方》卷五十八', '口臭。', '-', '-', '升麻', '[\'沉香\', \'半两\', \'升麻\', \'半两\', \'白芷\', \'半两\', \'藁本\', \'苗土\', \'半两\', \'细辛\', \'苗叶\', \'半两\', \'丁香\', \'半两\', \'寒水石\', \'研两\']');
INSERT INTO `prescript` VALUES (59577, '27187', '沉香散', '沉香1两，桑寄生1两，射干1两，川升麻1两，防风3分（去芦头），熏陆香3分，麝香1分（细研），川大黄1两半（锉碎，微炒），藿香3分，连翘1两。', '每服4钱，以水1中盏，煎至6分，去滓温服，每日3-4次。', '《圣惠》卷六十六', '瘰疬寒热，结肿疼痛，心胸壅滞。', '-', '-', '升麻', '[\'沉香\', \'桑寄生\', \'射干\', \'两川\', \'升麻\', \'防风\', \'分去\', \'芦头\', \'陆香分\', \'麝香\', \'分细\', \'研川\', \'大黄\', \'两半\', \'藿香\', \'连翘\']');
INSERT INTO `prescript` VALUES (59578, '27138', '陈皮枳实汤', '陈皮1钱2分，鼠粘子1钱1分，厚朴1钱1分，枳实1钱，青皮1钱，乌药1钱，紫草茸1钱，砂仁1钱，神曲1钱，槟榔1钱，草果1钱，桔梗1钱，升麻8分。', '每服4-5钱，水煎，食远服。', '《准绳·幼科》卷五', '小儿痘疹，宿食不消。', '-', '-', '升麻', '[\'陈皮\', \'分鼠\', \'粘子\', \'钱分\', \'厚朴\', \'钱分\', \'枳实\', \'青皮\', \'乌药\', \'紫草茸\', \'砂仁\', \'神曲\', \'槟榔\', \'草果\', \'桔梗\', \'升麻\']');
INSERT INTO `prescript` VALUES (59579, '27106', '虎睛丸', '虎睛1对（酒浸，炙微黄），茯神1两，龙齿1两，石膏1两，防风1两（去芦头），黄芩1两，秦艽1两（去苗），川升麻1两，汉防己1两，铁粉1两（细研），川大黄1两（锉碎，微炒），人参1两（去芦头），防葵1两，独活1两，远志3分（去心），白鲜皮3分，鬼臼3分，细辛3分，银箔50片（细研），金箔50片（细研），天雄3分（炮裂，去皮脐），干姜半两（炮裂，锉），芎3分，麝香半两（细研），露蜂房半两（微炒），牛黄半两（细研），蛇蜕皮10条（烧灰）。', '每服20丸，温酒送下，薄荷汤下亦得，不拘时候。', '《圣惠》卷二十二', '风癫，口眼开张，多吐白沫，或作恶声，恍惚虚悸。', '-', '-', '升麻', '[\'虎睛\', \'微黄\', \'茯神\', \'龙齿\', \'石膏\', \'防风\', \'两去\', \'芦头\', \'黄芩\', \'秦艽\', \'两去\', \'苗川\', \'升麻\', \'两汉\', \'防己\', \'铁粉\', \'两细\', \'研川\', \'大黄\', \'人参\', \'两去\', \'芦头\', \'防葵\', \'独活\', \'远志\', \'分去\', \'白鲜皮\', \'鬼臼\', \'细辛\', \'分银\', \'箔片\', \'细研\', \'金箔\', \'片细研\', \'天雄\', \'分炮裂\', \'去皮\', \'干姜\', \'半两\', \'炮裂\', \'麝香\', \'半两\', \'细研\', \'露蜂房\', \'半两\', \'牛黄\', \'半两\', \'细研\', \'蛇蜕\', \'皮条\', \'烧灰\']');
INSERT INTO `prescript` VALUES (59580, '27100', '红雪', '川朴消10斤，羚羊角屑3两，川升麻3两，黄芩3两，枳壳2两（麸炒微黄，去瓤），赤芍药2两，人参2两（去芦头），淡竹叶2两，甘草2两（生用），木香2两，槟榔2两，葛根1两半，大青1两半，桑根白皮1两半，蓝叶1两半，木通1两半，栀子1两半，朱砂1两（细研），苏枋3两（捶碎），麝香半两（细研）。', '通中散（原书同卷）、红雪通中散（《局方》卷六）、红雪煎（《圣济总录》卷一一九）。', '《圣惠》卷九十五', '解酒毒，消宿食，开三焦，利五脏，爽精神，除毒热，破积滞，去脑闷。主烦热黄疸，脚气温瘴，眼昏头痛，鼻塞口疮，重舌，喉闭，肠痈；伤寒狂躁，胃烂发斑。', '-', '-', '升麻', '[\'川朴\', \'消斤\', \'羚羊角\', \'两川\', \'升麻\', \'黄芩\', \'枳壳\', \'两麸\', \'微黄\', \'芍药\', \'人参\', \'两去\', \'芦头\', \'淡竹叶\', \'甘草\', \'两生用\', \'木香\', \'槟榔\', \'葛根\', \'两半\', \'大青\', \'两半\', \'桑根\', \'白皮\', \'两半\', \'蓝叶\', \'两半\', \'木通\', \'两半\', \'栀子\', \'两半\', \'朱砂\', \'两细\', \'研苏枋\', \'麝香\', \'半两\', \'细研\']');
INSERT INTO `prescript` VALUES (59581, '27055', '白鲜皮汤', '白鲜皮1两，桑根白皮（锉）1两，玄参1两，漏芦（去芦头）1两，升麻1两，犀角屑半两，败酱3分。', '每服5钱匕，以水1盏半，煎至1盏，入芒消半钱匕，滤去滓，空心温服，晚再服。稍觉疮痛止，即去芒消。', '《圣济总录》卷一二八', '痈疽日月久远，脓水不尽，心中烦闷。', '-', '-', '升麻', '[\'白鲜皮\', \'两桑根\', \'白皮\', \'玄参\', \'漏芦\', \'芦头\', \'升麻\', \'犀角\', \'半两\', \'败酱\']');
INSERT INTO `prescript` VALUES (59582, '27039', '地肤子汤', '地肤草1钱，车前子1钱，知母（去毛，炒）7分，黄芩7分，赤茯苓7分，白芍药7分，枳壳（麸炒黄色）7分，升麻3分，通草3分，甘草3分。', '水1盏半，煎至1盏，温服。', '《医学正传》卷七引《录验》', '妊娠子淋，小便涩数。', '-', '-', '升麻', '[\'地肤\', \'草钱\', \'车前子\', \'知母\', \'炒分\', \'黄芩\', \'分赤\', \'茯苓\', \'分白\', \'芍药\', \'枳壳\', \'黄色\', \'升麻\', \'通草\', \'甘草\']');
INSERT INTO `prescript` VALUES (59583, '26993', '地骨皮汤', '地骨皮1两半，甘菊花1两，升麻1两，黄连（去须）1两，防风（去叉）1两，木通（锉）1两，萎蕤，大黄（锉，炒）1两，甘草（炙，锉）1两，蕤仁（去皮）1两。', '每服5钱匕，水1盏半，煎至7分，去滓，食后、临卧温服。', '《圣济总录》卷一○七', '心肺风热，目干涩痛痒。', '-', '-', '升麻', '[\'地骨皮\', \'两半\', \'甘菊花\', \'升麻\', \'黄连\', \'防风\', \'木通\', \'萎蕤\', \'大黄\', \'甘草\', \'蕤仁\', \'去皮\']');
INSERT INTO `prescript` VALUES (59584, '26987', '独活丸', '独活（去芦头）半两，防风（去叉）半两，黄芩（去黑心）半两，零陵香半两，芎半两，细辛（去苗叶）半两，当归（切，焙）半两，沉香（锉）1两，鸡舌香1两，升麻1两，甘草（炙，锉）1两。', '用绵裹1粒，于痛处含化咽津，消尽即再用。', '《圣济总录》卷一二一', '齿龋龈肿，脓出疼痛。', '-', '-', '升麻', '[\'独活\', \'芦头\', \'半两\', \'防风\', \'半两\', \'黄芩\', \'黑心\', \'半两\', \'零陵\', \'半两\', \'半两\', \'细辛\', \'苗叶\', \'半两\', \'当归\', \'切焙\', \'半两\', \'沉香\', \'两鸡\', \'舌香\', \'升麻\', \'甘草\']');
INSERT INTO `prescript` VALUES (59585, '26951', '钓藤饮', '钓藤半两，黄芩（去黑心）半两，犀角（镑）半两，石膏（碎）1两，龙齿1两，升麻3分，甘草（炙，锉）3分，竹叶40片。', '本方方名，原书作“钩藤饮”。', '《圣济总录》卷一七一（人卫本）', '小儿痫疾。', '-', '-', '升麻', '[\'钓藤\', \'半两\', \'黄芩\', \'黑心\', \'半两\', \'犀角\', \'半两\', \'石膏\', \'龙齿\', \'升麻\', \'甘草\', \'分竹\', \'叶片\']');
INSERT INTO `prescript` VALUES (59586, '26887', '连翘升麻汤', '连翘1钱，升麻1钱，黄芩1钱，葛根1钱，麦门冬（去心）2钱。', '水煎服。', '《赤水玄珠》卷二十八', '轻其表而凉其内。主痘一发，密如针头，形势重者。', '-', '-', '升麻', '[\'连翘\', \'升麻\', \'黄芩\', \'葛根\', \'麦门冬\', \'心钱\']');
INSERT INTO `prescript` VALUES (59587, '26860', '百部散', '百部1两，桔梗1两（去芦头），射干1两，川升麻1两，天门冬1两（去心），木通1两（锉），甘草半两（炙微赤，锉），沙参半两（去芦头），川大黄半两，（锉碎，微炒）。', '每服4钱，以水1中盏，加竹叶2-7片，煎至6分，去滓，不拘时候温服。', '《圣惠》卷六', '肺痿咳嗽，涕唾稠粘，咽喉不利，心神烦热。', '-', '-', '升麻', '[\'百部\', \'桔梗\', \'两去\', \'芦头\', \'射干\', \'两川\', \'升麻\', \'天门冬\', \'两去\', \'木通\', \'甘草\', \'半两\', \'炙微\', \'沙参\', \'半两\', \'芦头川\', \'大黄\', \'半两\']');
INSERT INTO `prescript` VALUES (59588, '26847', '防风散', '防风1两（去芦头），羌活2两，川升麻1两，桂心1两，芎2两，羚羊角屑3分，麻黄1两（去根节），杏仁1两（汤浸，去皮尖双仁，麸炒微黄），薏苡仁1两。', '每服4钱，以水1中盏，煎至5分，去滓，加竹沥1合，重煎1-2沸，不拘时候稍热服。如人行5-7里再服，以衣盖之，汗出为度。', '《圣惠》卷十九', '中风口面僻，手足不遂，风入于脏，则语不得转，心神昏闷。', '-', '-', '升麻', '[\'防风\', \'两去\', \'芦头\', \'羌活\', \'两川\', \'升麻\', \'两桂心\', \'两芎\', \'羚羊角\', \'麻黄\', \'两去\', \'根节\', \'杏仁\', \'两汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\', \'薏苡仁\']');
INSERT INTO `prescript` VALUES (59589, '26823', '参耆桂麻汤', '人参2钱，甘草1钱，黄耆1钱，桂枝1钱，升麻1钱。', '流水煎至半杯，温服。', '《四圣悬枢》卷三', '痘顶平者。', '-', '-', '升麻', '[\'人参\', \'甘草\', \'黄耆\', \'桂枝\', \'升麻\']');
INSERT INTO `prescript` VALUES (59590, '26752', '沉香汤', '沉香（锉）1分，丁香1分，木香1分，麝香（研）1分，连翘半两，黄芩（去黑心）半两，犀角（镑）1两半，升麻1两。', '每服5钱匕，水1盏半，煎至8分，去滓温服，空心、日午、夜卧各1次。', '《圣济总录》卷一二六', '瘰疬毒肿。', '-', '-', '升麻', '[\'沉香\', \'锉分\', \'丁香\', \'木香\', \'麝香\', \'研分\', \'连翘\', \'半两\', \'黄芩\', \'黑心\', \'半两\', \'犀角\', \'两半\', \'升麻\']');
INSERT INTO `prescript` VALUES (59591, '26722', '连翘升麻汤', '连翘1钱，升麻7分，葛根7分，桔梗7分，甘草7分，牛蒡子1钱，木通8分，白芍药5分，薄荷叶少许。', '加淡竹叶、灯心，水1盏半，煎1盏，去滓温服，不拘时候。', '《痘疹心要》卷十一', '解毒清火利小便。主痘疹身热如火，疮势稠密，其毒盛者。', '-', '-', '升麻', '[\'连翘\', \'升麻\', \'葛根\', \'桔梗\', \'甘草\', \'牛蒡子\', \'木通\', \'分白\', \'芍药\', \'薄荷叶\', \'少许\']');
