INSERT INTO `prescript` VALUES (59593, '26706', '吹云膏', '细辛1分，升麻3分，蕤仁3分，青皮4分，连翘4分，防风4分，柴胡5分，生甘草6分，当归身6分，荆芥穗1钱（微取浓汁），生地黄1钱5分，拣黄连3钱。', '点之。', '《兰室秘藏》卷上', '目中泪下，及迎风寒泣，羞明怕日，常欲闭目，喜在暗室，塞其户牖，翳膜岁久遮睛。', '-', '-', '升麻', '[\'细辛\', \'升麻\', \'蕤仁\', \'青皮\', \'连翘\', \'防风\', \'柴胡\', \'生甘草\', \'当归\', \'身分\', \'荆芥\', \'微取\', \'浓汁\', \'生地黄\', \'分拣\', \'黄连\']');
INSERT INTO `prescript` VALUES (59594, '26699', '连翘丸', '连翘1两半，玄参1两半，木香1两半，升麻1两半，大黄（蒸）半两，昆布（洗去咸，焙）2两，大麻子（微炒，别捣研）2两，枳壳（去瓤，麸炒）1两半。', '每服15丸，加至20丸，食前米饮送下。', '《圣济总录》卷一二六', '瘰疬寒热结核，在颈腋之下，坚痛。', '-', '-', '升麻', '[\'连翘\', \'两半\', \'玄参\', \'两半\', \'木香\', \'两半\', \'升麻\', \'两半\', \'大黄\', \'半两\', \'昆布\', \'洗去\', \'咸焙\', \'大麻子\', \'炒别\', \'研两\', \'枳壳\', \'两半\']');
INSERT INTO `prescript` VALUES (59595, '26647', '补脾胃泻阴火升阳汤', '柴胡1两5钱，甘草（炙）1两，黄耆1两，苍术（泔浸，去黑皮，切作片子，晒干，锉碎，炒）1两，羌活1两，升麻8钱，人参7钱，黄芩7钱，黄连（去须，酒制）5钱（炒），石膏少许（长夏微用，过时去之，从权）。', '泻阴火升阳汤（《玉机微义》卷十）、升阳泄火汤（《准绳·类方》卷一）、升阳降火汤（《便览》卷一）。', '《脾胃论》卷上', '饮食损胃，劳倦伤脾，火邪乘之而生大热。', '服药之时宜减食，宜美食。服药讫忌语话1-2时辰许，及酒、湿面大料之类，恐大热之物，复助火邪而愈损元气也。亦忌冷水及寒凉淡渗之物及诸果，恐阳气不能生旺也。宜温食及薄滋味以助阳气。', '-', '升麻', '[\'柴胡\', \'甘草\', \'黄耆\', \'苍术\', \'泔浸\', \'黑皮\', \'切作\', \'片子\', \'晒干\', \'羌活\', \'升麻\', \'人参\', \'黄芩\', \'黄连\', \'制钱\', \'石膏\', \'少许\', \'长夏\', \'微用\', \'过时\', \'从权\']');
INSERT INTO `prescript` VALUES (59596, '26607', '防风饮', '防风（去叉）2两，升麻2两，桂（去粗皮）2两，白石脂（研）2两，当归（切，焙）2两，槟榔（锉）2两，桑根白皮（锉，炒）2两，干木瓜2两，人参2两，黄连（去须）2两，羌活（去芦头）2两，芎（锉）2两，天雄（炮裂，去皮脐）2两，黄芩（去黑心）1两，远志（去心）半两。', '每服3钱匕，以水1盏，加生姜5片，煎取7分，去滓温服。', '《圣济总录》卷一一九', '齿痛舌痒，食物不得。', '-', '-', '升麻', '[\'防风\', \'升麻\', \'两桂去\', \'粗皮\', \'白石\', \'脂研\', \'当归\', \'切焙\', \'槟榔\', \'两桑根\', \'白皮\', \'两干\', \'木瓜\', \'人参\', \'黄连\', \'羌活\', \'芦头\', \'两芎\', \'两天\', \'雄炮裂\', \'去皮\', \'黄芩\', \'黑心\', \'远志\', \'去心\', \'半两\']');
INSERT INTO `prescript` VALUES (59597, '26583', '金丸药', '防风4两，桔梗4两，羌活4两，全蝎4两，独活4两，天麻4两，灵仙4两，升麻4两，陈皮4两，首乌4两，麻黄4两，狗脊4两，川芎4两，牛蒡4两，蔓荆（去花）4两，荆芥8两，风藤4两，川归4两，蝉壳2两，胡麻2两，枫子肉2斤半，雄黄2两。', '1日3服，清茶荆芥汤送下。鼻塞，加牙皂2两；有痰，加南星2两（制）。', '《秘传大麻疯方》', '麻风。肺经受病，其色白，初起粉色，眉毛先落，面若虫行，遍身起癣如鳞。', '-', '-', '升麻', '[\'防风\', \'桔梗\', \'羌活\', \'全蝎\', \'独活\', \'天麻\', \'两灵仙\', \'升麻\', \'陈皮\', \'首乌\', \'麻黄\', \'狗脊\', \'川芎\', \'牛蒡\', \'两蔓\', \'荆去\', \'荆芥\', \'两风藤\', \'两川归\', \'两蝉壳\', \'胡麻\', \'两枫子\', \'斤半\', \'雄黄\']');
