INSERT INTO `prescript` VALUES (59599, '26558', '栀子大青汤', '升麻2两，栀子仁2两，大青1两半，杏仁（去皮尖）1两半，黄芩1两半。', '每服5钱匕，以水1盏半，细切葱白3寸，煎至1盏，去滓温服。', '《活人书》卷十九', '妊妇发斑，变为黑色；妊娠人七月，伤寒壮热，赤斑变为黑斑，溺血。', '-', '-', '升麻', '[\'升麻\', \'栀子\', \'两大青\', \'两半\', \'杏仁\', \'皮尖\', \'两半\', \'黄芩\', \'两半\']');
