INSERT INTO `prescript` VALUES (59601, '26525', '地肤大黄汤', '地肤草3两，大黄3两，知母2两，黄芩2两，茯苓（一作猪苓）2两，芍药2两，枳实（炙）2两，升麻2两，通草2两，甘草（炙）2两。', '大黄散（《圣惠》卷七十四）。', '《外台》卷三十三引《小品方》', '实热子淋；妊娠小便淋涩，脐腹妨闷。', '忌海藻、菘菜、酢物。', '-', '升麻', '[\'地肤\', \'大黄\', \'知母\', \'黄芩\', \'茯苓\', \'一作\', \'猪苓\', \'芍药\', \'枳实\', \'升麻\', \'通草\', \'甘草\']');
INSERT INTO `prescript` VALUES (59602, '26494', '络石散', '络石1两半，木通2两，川升麻、射干1两，犀角屑1两，玄参1两，栀子仁半两，桔梗1两半（去芦头），赤芍药1两，马牙消2两。', '络石汤、络石叶饮（《圣济总录》卷一二二）。', '《圣惠》卷三十五', '咽喉肿痛，热毒气在于胸心，及一切风热。', '-', '-', '升麻', '[\'络石\', \'两半\', \'木通\', \'两川\', \'升麻\', \'射干\', \'犀角\', \'玄参\', \'栀子\', \'半两\', \'桔梗\', \'两半\', \'芦头\', \'芍药\', \'两马牙\', \'消两\']');
INSERT INTO `prescript` VALUES (59603, '26485', '白虎青龙各半汤', '柴胡（取银州者，去须及头，用银刀削去黄薄皮少许，粗布拭净，锉细，勿令犯火）7钱，升麻（不经雨阳者，形色翠碧，削去皮，用黄精汁浸1宿，晒干，锉，蒸，再晒）3钱，葛根（取洁白肥嫩者，用雪水或秋露润透，切片，阴干）9钱，羌活（去头，细锉，以淫羊藿拌浥3日，晒干，去藿）5钱，防风（勿用叉头叉尾者，叉头令人发狂，叉尾发人痼疾，取肥大柔润色黄通理者锉细）5钱，甘草（取黄中通理者，去头尾尖处各45寸，仅取中节，切作寸许长，入瓷器中好酒浸蒸，从巳至午，取出晒干，锉细）7钱5分，知母（槐砧上锉细，于木臼杵捣数千下，勿犯铁器', '上以水3升5合，先煮粳米减半升，去粳米，同诸药煮取1升半，去滓，分3次服。寅卯时取初服：再煮数沸，俟病者睡熟推醒服，服毕覆盖取微似汗；2服未发前半时许服，服毕温覆，勿使寒栗大作，热亦渐减；3服发后半时许服，服毕再半时许方啜热粥饮1盏许，以充营卫，勿食他物损伤药力也。', '《痎疟论疏》', '痎疟', '-', '-', '升麻', '[\'柴胡\', \'银州\', \'头用\', \'刀削\', \'黄薄皮\', \'少许\', \'粗布\', \'拭净\', \'细勿令\', \'犯火\', \'升麻\', \'不经雨阳者\', \'形色\', \'翠碧削\', \'去皮\', \'精汁\', \'浸宿\', \'晒干\', \'葛根\', \'洁白\', \'肥嫩\', \'雪水\', \'秋露\', \'润透\', \'切片\', \'阴干\', \'羌活\', \'头细\', \'日晒\', \'干去\', \'防风\', \'用叉头\', \'尾者\', \'令人\', \'发狂\', \'发人\', \'痼疾\', \'肥大\', \'柔润\', \'黄通理者\', \'细钱\', \'甘草\', \'黄中\', \'通理者\', \'头尾\', \'尖处\', \'各寸仅\', \'取中\', \'节切\', \'作寸\', \'许长入\', \'瓷器\', \'中好\', \'浸蒸\', \'至午\', \'取出\', \'晒干\', \'细钱分\', \'知母\', \'槐砧\', \'细于\', \'臼杵\', \'数千\', \'下勿犯\', \'铁器\']');
INSERT INTO `prescript` VALUES (59604, '26446', '龙胆饮', '龙胆（去根）半两，犀角（镑屑）半两，升麻3分，大麻（锉，炒）3分，甘草（炙）3分，鳖甲（去裙襕，醋浸，炙黄色）3分，槟榔（煨，锉）1枚。', '每服1钱匕，以水半盏，煎至3分，去滓，分3次温服。', '《圣济总录》卷一六八', '小儿壮热胀满，不饮乳。', '-', '-', '升麻', '[\'龙胆\', \'半两\', \'犀角\', \'半两\', \'升麻\', \'大麻\', \'炒分\', \'甘草\', \'炙分\', \'鳖甲\', \'黄色\', \'槟榔\']');
INSERT INTO `prescript` VALUES (59605, '26426', '防风汤', '防风（去叉）1两，防己1两，升麻1两，桂（去粗皮）1两，麻黄（去根节，煎掠去沫，焙干）1两，芎1两，羚羊角（镑）1两半。', '每服3钱匕，以水1盏，煎取6分，去滓，加竹沥半合，更煎2-3沸，空心热服，日午及临卧各1服。', '《圣济总录》卷六', '中风，口面斜，泪出失音。', '-', '-', '升麻', '[\'防风\', \'防己\', \'升麻\', \'两桂去\', \'粗皮\', \'麻黄\', \'根节\', \'掠去\', \'焙干\', \'两芎\', \'羚羊角\', \'两半\']');
INSERT INTO `prescript` VALUES (59606, '26418', '抱龙丸', '陈胆星4两，钩藤1两，桔梗2两，天麻2两，升麻5钱，陈皮1两，薄荷1两，僵蚕5钱，川贝母1两（去心）。', '乳孩每服半丸，大者每服1丸，白滚水送下；若外感风邪，用防风5分煎汤调下；若内热，用竹叶汤调下。', '《幼科直言》卷三', '痧症不拘前后，痰多咳嗽，有风有热者。', '-', '-', '升麻', '[\'陈胆星\', \'钩藤\', \'桔梗\', \'天麻\', \'升麻\', \'陈皮\', \'薄荷\', \'僵蚕\', \'川贝母\', \'两去\']');
INSERT INTO `prescript` VALUES (59607, '26417', '甘草散', '甘草1两（生用），川升麻半两，射干半两。', '都以水3大盏，煎至2盏，去滓，分为4服，日3服，夜1服。', '《圣惠》卷十', '伤寒2-3日，毒气攻咽喉痛肿。', '-', '-', '升麻', '[\'甘草\', \'两生\', \'用川\', \'升麻\', \'半两\', \'射干\', \'半两\']');
INSERT INTO `prescript` VALUES (59608, '26415', '肺热汤', '羚羊角3钱，玄参3钱，射干3钱，薄荷3钱，芍药3钱，升麻3钱，柏皮3钱，生地黄1合，栀子仁4钱，竹茹2钱。', '-', '《医方考》卷五', '肺鸣叶焦，令人色白毛败，发为痿（辟足），脉来短数。', '-', '-', '升麻', '[\'羚羊角\', \'玄参\', \'射干\', \'薄荷\', \'芍药\', \'升麻\', \'钱柏皮\', \'生地黄\', \'栀子\', \'仁钱\', \'竹茹\']');
INSERT INTO `prescript` VALUES (59609, '26337', '决明丸', '决明子1两（微炒），蕤仁1两（去皮，研），茯神1两（去木），桔梗1两（炒），麦门冬1两（去心，焙），黄连（去须）1两，青葙子3分，枳壳3分（去瓤，麸炒），防风3分（去叉），玄参3分，犀角3分（镑），槟榔3分（煨，锉），升麻3分，生干地黄3分（焙），龙胆3分，沙参3分，紫菀（去苗土）3分，百合半两。', '本方方名，原书人卫本作\"决明子丸\"。', '《圣济总录》（文瑞楼本）卷一○七', '肝肺热，毒风目昏。', '-', '-', '升麻', '[\'决明子\', \'两微\', \'蕤仁\', \'去皮\', \'茯神\', \'两去\', \'桔梗\', \'麦门冬\', \'两去\', \'黄连\', \'青葙子\', \'枳壳\', \'分去\', \'防风\', \'分去\', \'玄参\', \'犀角\', \'分镑\', \'槟榔\', \'升麻\', \'分生\', \'地黄\', \'分焙\', \'龙胆\', \'沙参\', \'紫菀\', \'苗土\', \'百合\', \'半两\']');
INSERT INTO `prescript` VALUES (59610, '26288', '参耆白术汤', '党参（去芦，米妙）2钱，炙耆1钱5分，白术（净炒）1钱5分，肉蔻霜1钱5分，茯苓1钱5分，淮山药（炒）2钱，升麻（密炙）6分，炙甘草7分。', '加生姜2片；煎服。或加制附子5分。', '《不知医必要》卷三', '泻痢与产育气虚脱肛。', '-', '-', '升麻', '[\'党参\', \'芦米妙\', \'钱炙耆\', \'钱分\', \'白术\', \'分肉蔻\', \'钱分\', \'茯苓\', \'钱分\', \'山药\', \'升麻\', \'密炙分\', \'甘草\']');
INSERT INTO `prescript` VALUES (59611, '26255', '赤茯苓散', '赤茯苓1钱，桑白皮1钱，贝母1钱，升麻半钱，甘草半钱，桔梗（微炒）半钱，杏仁半钱，甜葶苈（炒）半钱。', '每服3钱，水1盏，煎6分服，小儿作3服。', '《普济方》卷一九三引《经验良方》', '大人、小儿水气肿满，喘咳不止。', '-', '-', '升麻', '[\'茯苓\', \'桑白皮\', \'贝母\', \'升麻\', \'半钱\', \'甘草\', \'半钱\', \'桔梗\', \'半钱\', \'杏仁\', \'半钱\', \'半钱\']');
