INSERT INTO `prescript` VALUES (59613, '26106', '地黄汤', '生干地黄（焙）1两，地骨皮1两，赤茯苓（去黑皮）1两，甘草（炙，锉）1两，大黄（湿纸裹煨，锉）1两，玄参1两，黄芩（去黑心）1两，当归（切，焙）1两，麦门冬（去心，焙）1两，藿香（取叶）1两，升麻1两，紫菀（去苗土）1两，桑根白皮（锉）1两。', '每服5钱匕，水1盏半，煎至1盏，去滓温服，不拘时候。', '《圣济总录》卷六十九', '心肺壅热，上焦不利，吐血，胸中痞，口干。', '-', '-', '升麻', '[\'生干\', \'地黄\', \'地骨皮\', \'两赤\', \'茯苓\', \'黑皮\', \'甘草\', \'大黄\', \'湿纸\', \'玄参\', \'黄芩\', \'黑心\', \'当归\', \'切焙\', \'麦门冬\', \'藿香\', \'取叶\', \'升麻\', \'紫菀\', \'苗土\', \'两桑根\', \'白皮\']');
