INSERT INTO `prescript` VALUES (59615, '26094', '虎睛丸', '虎睛1对（酒浸，炙令黄），朱砂半两（细研，水飞），麻黄半两（去根节），钩藤半两，铁粉3分，防风3分（去芦头），子芩3分，川大黄3分（锉碎，微炒），龙齿1两（细研），银屑3分（细研），栀子仁3分，羌活3分，柴胡半两（去苗），白鲜皮半两，牛黄半两（细研），雷丸半两，沙参半两（去芦头），细辛1分，石膏1两（细研，水飞），川升麻半两，蚱蝉4枚（微炙，去翅足）。', '3岁以下，每服3丸，薄荷汤送下，每日3次；3岁以上，以意加丸服之。', '《圣惠》卷八十五', '小儿风痫，自3岁以来，至10岁不愈，发时口中白沫，大小便不觉。', '-', '-', '升麻', '[\'虎睛\', \'浸炙令\', \'朱砂\', \'半两\', \'细研\', \'水飞\', \'麻黄\', \'半两\', \'根节\', \'钩藤\', \'半两\', \'铁粉\', \'防风\', \'分去\', \'头子\', \'分川\', \'大黄\', \'龙齿\', \'两细研\', \'银屑\', \'分细研\', \'栀子\', \'仁分\', \'羌活\', \'柴胡\', \'半两\', \'去苗\', \'白鲜皮\', \'半两\', \'牛黄\', \'半两\', \'细研\', \'雷丸\', \'半两\', \'沙参\', \'半两\', \'芦头\', \'细辛\', \'石膏\', \'两细\', \'研水\', \'飞川\', \'升麻\', \'半两\', \'蚱蝉\', \'微炙去\']');
INSERT INTO `prescript` VALUES (59616, '26032', '肠风槐角丸', '槐角8两，地榆8两，黄耆8两，当归8两，川芎4两，阿胶2两，升麻8两，生地8两，条芩8两，连翘8两，秦艽8两，防风4两，白芷4两，川连4两。', '-', '《鳞爪集》卷二', '祛风消毒，解热润脏，宽肠利气，和血定痛。主肠风痔漏，痛痒火盛。', '-', '-', '升麻', '[\'槐角\', \'地榆\', \'两黄耆\', \'当归\', \'川芎\', \'阿胶\', \'升麻\', \'生地\', \'两条\', \'连翘\', \'秦艽\', \'防风\', \'白芷\', \'两川\']');
INSERT INTO `prescript` VALUES (59617, '26030', '沉香散', '沉香3分，麦门冬1两（去心），赤芍药1两，玄参1两，甘草1两（生，锉），枳实1两（麸炒微黄），川升麻1两，前胡1两（去芦头），葳蕤半两，黄耆半两（锉），生干地黄1两，犀角屑3分，川大黄2两（锉碎，微炒），麝香1分（细研）。', '沉香汤（《圣济总录》卷一三一）。', '《圣惠》卷六十二', '发脑，疮肿焮赤疼痛，烦躁。', '-', '-', '升麻', '[\'沉香\', \'麦门冬\', \'两去\', \'芍药\', \'玄参\', \'甘草\', \'两生\', \'枳实\', \'两麸\', \'微黄\', \'升麻\', \'两前\', \'胡两去\', \'芦头\', \'葳蕤\', \'半两\', \'黄耆\', \'半两\', \'锉生\', \'地黄\', \'犀角\', \'分川\', \'大黄\', \'麝香\', \'分细研\']');
