INSERT INTO `prescript` VALUES (59619, '25979', '苦参散', '苦查1两（锉），人参1两（去芦头），丹参1两，黄连3分（去须），沙参1两（去芦头），玄参1两，秦艽3分（去苗），白鲜皮1两，川升麻1两，枳壳1两（麸炒微黄，去瓤），栀子仁3分，犀角屑1两，黄芩1两，赤芍药1两，当归1两，白蒺藜1两（微炒，去刺），防风1两（去芦头），白花蛇2两（酒浸，炙令黄，去皮骨）。', '每服2钱，食后以温酒调下。', '《圣惠》卷六十四', '身体生风毒疮，或痒痛不止。', '-', '-', '升麻', '[\'苦查\', \'人参\', \'两去\', \'芦头\', \'丹参\', \'黄连\', \'分去\', \'沙参\', \'两去\', \'芦头\', \'玄参\', \'秦艽\', \'分去\', \'白鲜皮\', \'两川\', \'升麻\', \'枳壳\', \'两麸\', \'微黄\', \'栀子\', \'仁分\', \'犀角\', \'黄芩\', \'两赤\', \'芍药\', \'当归\', \'两白\', \'蒺藜\', \'两微\', \'防风\', \'两去\', \'芦头\', \'白花蛇\', \'两酒\', \'浸炙令\', \'去皮\']');
INSERT INTO `prescript` VALUES (59620, '25859', '空青丸', '空青（研细，水飞）1分，珍珠末1分，犀角屑半两，防风（去叉）半两，羚羊角屑半两，升麻（锉）半两，防己半两，人参1两，麦门冬（去心，焙）1两，茺蔚子1两，阳起石（研细）1两，前胡（去芦头）1两，虎睛1对。', '每服5丸，加至10丸，麦门冬煎汤送下，温椒汤亦得。', '《圣济总录》卷一○二', '肝肾久虚，目睹，渐生翳膜。', '-', '-', '升麻', '[\'空青研\', \'细水\', \'飞分\', \'珍珠\', \'末分\', \'犀角\', \'半两\', \'防风\', \'半两\', \'羚羊角\', \'半两\', \'升麻\', \'半两\', \'防己\', \'半两\', \'人参\', \'麦门冬\', \'茺蔚子\', \'阳起石\', \'研细\', \'两前\', \'胡去\', \'芦头\', \'两虎睛\']');
INSERT INTO `prescript` VALUES (59621, '25823', '绛雪散', '红雪半两，生麦门冬（去心）1两半，萎蕤1两半，秦皮（去粗皮）1两半，赤茯苓（去黑皮）1两半，升麻1两，淡竹叶50片。', '每服3钱匕，水2盏，煎至1盏，抄红雪半钱匕调匀，食后温服。', '《圣济总录》卷一○七', '肝心风热邪毒上攻，目赤痒。', '-', '-', '升麻', '[\'红雪\', \'半两\', \'麦门冬\', \'去心\', \'两半\', \'萎蕤\', \'两半\', \'秦皮\', \'粗皮\', \'两半\', \'茯苓\', \'黑皮\', \'两半\', \'升麻\', \'淡竹叶\']');
INSERT INTO `prescript` VALUES (59622, '25806', '陈橘皮散', '陈橘皮半两（汤浸，去白瓤，焙），川升麻1分，桑根白皮半两（锉），麦门冬半两（去心，焙），前胡1分（去芦头），川大黄1分（锉碎，微炒）。', '陈橘皮汤（《普济方》卷三八七）。', '《圣惠》卷八十四', '小儿痰壅结实，时欲呕吐。', '-', '-', '升麻', '[\'橘皮\', \'半两\', \'汤浸\', \'焙川\', \'升麻\', \'分桑根\', \'白皮\', \'半两\', \'麦门冬\', \'半两\', \'心焙前\', \'胡分\', \'芦头川\', \'大黄\']');
INSERT INTO `prescript` VALUES (59623, '25798', '和解散', '邵前胡（去芦，净）1两，川升麻1两，京芍药1两，赤芍药1两，白术1两，熟苍术1两，羌活1两，川独活1两，川芎1两，藁本1两，桔梗1两，防风1两，甘草1两，熟半夏1两，人参（去芦）1两，藿香1两，粉葛2两（去皮）。', '上(口父)咀。每服4钱，加生姜5片，水1盏半，煎至7分服，连进3服。得汗即愈。憎寒壮热，发渴，头痛，四肢酸疼强直，加去节麻黄1钱半，姜、葱（连须）；身热头疼，烦躁，小便赤，衄血，加麦门冬（去心）；疹子欲出未出，热烦，加当归、黄耆、紫草各半钱；感风头目不清，加北细辛半钱，茶芽少许；阴湿脚痛，大便秘，小便赤，加煨熟大黄半钱，槟榔半钱；气虚头疼目眩，加生川芎半钱；中风口眼喎斜，角弓反张，潮涎昏闷，不省人事，加附子、川芎、南星各半钱，姜、葱煎；下血，加槐花半钱；血痢，加乌梅、灯心；热嗽，加桑白皮、薄荷同煎。', '《医方类聚》卷五十九引《必用之书》', '四时伤寒初病。', '老人、孕妇、自汗自利者，不可服。', '-', '升麻', '[\'邵前\', \'胡去\', \'芦净\', \'两川\', \'升麻\', \'两京\', \'芍药\', \'两赤\', \'芍药\', \'白术\', \'两熟\', \'苍术\', \'羌活\', \'两川\', \'独活\', \'川芎\', \'藁本\', \'桔梗\', \'防风\', \'甘草\', \'两熟\', \'半夏\', \'人参\', \'去芦\', \'藿香\', \'粉葛\', \'去皮\']');
INSERT INTO `prescript` VALUES (59624, '25750', '灵仙枫藤汤', '灵仙5钱，枫藤3钱，桂枝1钱5分，石菖7分，升麻8分，细辛2分，吉根1钱5分，羌活1钱5分，防风1钱5分，槟榔1钱，全皮1钱，草节8分，乳没2钱，赤芍1钱5分，生地2钱。', '水煎服。老损，加甲珠1钱5分；肘加酒芩1钱、北召白芷、归尖内消红；骨损，加海马1条，（炙，研），自然铜1钱（醋炙，火煅），骨碎补3钱。', '《青囊全集》卷上', '肩、臂、肘痛肿，跌打损伤。', '-', '-', '升麻', '[\'灵仙\', \'枫藤钱\', \'桂枝\', \'钱分\', \'石菖分\', \'升麻\', \'细辛\', \'分吉根\', \'钱分\', \'羌活\', \'钱分\', \'防风\', \'钱分\', \'槟榔\', \'全皮\', \'草节\', \'分乳\', \'没钱\', \'赤芍\', \'钱分\', \'生地\']');
