INSERT INTO `prescript` VALUES (59626, '25619', '连石茱萸丸', '黄连（酒炒）1两，滑石（飞过）1两，黄芩8分，干葛8分，白芍8分，茱萸1两，升麻7分，甘草4分。', '原书用本方治上症时去升麻、干葛、甘草、白芍药，加枳壳、山楂肉、麦芽、青皮；方中茱萸，若非阴寒膈寒而挟有热者，亦须除去。', '《麻科活人全书》卷三', '麻疹收后，热毒未曾解尽，有下积滞者。', '-', '-', '升麻', '[\'黄连\', \'滑石\', \'飞过\', \'黄芩\', \'分干葛分\', \'白芍\', \'茱萸\', \'升麻\', \'甘草\']');
INSERT INTO `prescript` VALUES (59627, '25583', '防风汤', '防风（去叉）1两，甘菊花1两，萎蕤1两，旋覆花1两，升麻1两，决明子（微炒）1两，秦皮（去粗皮，锉）1两，黄连（去须）1两，栀子仁1两，麦门冬（去心，焙）1两，甘草（炙令赤，锉）1两。', '每服5钱匕，水1盏半，煎至7分，去滓，食后、临卧温服。', '《圣济总录》卷一○六', '肝脏风热，冲目赤涩痛，风泪肿合，目多倒睫。', '-', '-', '升麻', '[\'防风\', \'甘菊花\', \'萎蕤\', \'旋覆花\', \'升麻\', \'决明子\', \'秦皮\', \'粗皮\', \'黄连\', \'栀子\', \'麦门冬\', \'甘草\', \'炙令\']');
INSERT INTO `prescript` VALUES (59628, '25556', '茯神散', '茯神1两半，川升麻1两，玄参1两半，秦艽1两（去苗），寒水石2两，龙胆1两（去芦头），川芒消2两，川大黄3两（锉碎，微炒）。', '每服1钱，以水1小盏，煎至5分，去滓，分2次温服，早晨、午后各1服。', '《圣惠》卷八十五', '小儿心腹结实，身体壮热，四肢不利，心神多惊，欲发痫者。', '-', '-', '升麻', '[\'茯神\', \'两半\', \'升麻\', \'玄参\', \'两半\', \'秦艽\', \'两去\', \'寒水石\', \'龙胆\', \'两去\', \'芦头川芒\', \'消两川\', \'大黄\']');
INSERT INTO `prescript` VALUES (59629, '25494', '钓藤汤', '钓藤1两，黄芩（去黑心）1两，升麻1两，甘草（炙令赤色，锉）1两，鳖甲（去裙襕，醋浸，炙令黄色）半两，丁香半两，大黄（锉，微炒）4两。', '每服5钱匕，水1盏半，煎至1盏，去滓，每日食后分温2服，相去如人行7-8里，再服之。', '《圣济总录》卷九十三', '传尸痨，瘦羸骨蒸，复连殗殜，命在须臾，精神尚爽。', '-', '-', '升麻', '[\'钓藤两\', \'黄芩\', \'黑心\', \'升麻\', \'甘草\', \'炙令\', \'赤色\', \'鳖甲\', \'浸炙令\', \'黄色\', \'半两\', \'丁香\', \'半两\', \'大黄\']');
