INSERT INTO `prescript` VALUES (59631, '25410', '茯神散', '茯神半两，龙齿半两（细研），寒水石1两，川升麻半两，石膏1两（细研，水飞过），犀角屑半两，牛黄半分（细研）。', '每服半钱，以竹沥调下，不拘时候。', '《圣惠》卷八十五', '小儿惊热烦躁，手足抽掣，心悸。', '-', '-', '升麻', '[\'茯神\', \'半两\', \'龙齿\', \'半两\', \'细研\', \'寒水石\', \'两川\', \'升麻\', \'半两\', \'石膏\', \'两细\', \'研水\', \'飞过\', \'犀角\', \'半两\', \'牛黄\', \'半分\', \'细研\']');
INSERT INTO `prescript` VALUES (59632, '25402', '地黄汤', '生地黄1两，芍药1两，升麻1两，木通1两，甘草（炙）1两，大黄（锉，微炒）1两，知母1两，人参1两，赤茯苓（去黑皮）1两，当归（焙）1两，黄芩（去黑心）1两半，黄耆1两半，小麦半升，栝楼根2两，前胡（去芦头）1两半。', '生地黄汤、黄耆汤（原书卷一八三）。如小便不利，除木通；大热，加人参。', '《圣济总录》卷一三一', '痈疽，虚热大渴。乳石发动。', '-', '-', '升麻', '[\'生地黄\', \'芍药\', \'升麻\', \'木通\', \'甘草\', \'大黄\', \'知母\', \'人参\', \'两赤\', \'茯苓\', \'黑皮\', \'当归\', \'黄芩\', \'黑心\', \'两半\', \'黄耆\', \'两半\', \'小麦\', \'半升\', \'栝楼\', \'根两前\', \'胡去\', \'芦头\', \'两半\']');
INSERT INTO `prescript` VALUES (59633, '25368', '辰砂夺命丹', '辰砂（研细，用升麻、黄紫草、连翘煮汁，滤净，用汁煮砂一昼夜，收干听用）2钱，麻黄（不去报节，酒、蜜拌炒焦色）8分，蝉蜕（洗净）5分，紫草（酒炒）5分，红花子5分，山甲（酒浸，炒黑）5分，蟾酥（酒化）2分。', '周岁半丸，2岁1丸，热酒化服。盖暖出汗，痘即随出。', '《痘疹仁端录》卷十四', '痘疮血热毒拥不出。', '-', '-', '升麻', '[\'辰砂\', \'研细用\', \'麻黄\', \'紫草\', \'连翘\', \'煮汁\', \'滤净用\', \'昼夜\', \'收干\', \'用钱\', \'麻黄\', \'不去\', \'报节\', \'酒蜜\', \'焦色分\', \'蝉蜕\', \'洗净\', \'紫草\', \'分红\', \'花子\', \'山甲\', \'酒浸\', \'黑分\', \'蟾酥\', \'酒化分\']');
INSERT INTO `prescript` VALUES (59634, '25288', '补肝汤', '柴胡5分，升麻5分，藁本5分，白茯苓7分，炒神曲1钱，苍术1钱，半夏2钱，生姜10片。', '柴胡半夏汤（原书同卷）、半夏苍术汤（《张氏医通》卷十四）。', '《兰室秘藏》卷中', '素有风证，不敢见风，眼涩，头痛眼黑，胸中有痰，恶心，兀兀欲吐，遇风但觉皮肉紧，手足难举重物；如居暖室，少出微汗，其证乃减，再或遇风，病即复。', '-', '-', '升麻', '[\'柴胡\', \'升麻\', \'藁本\', \'白茯苓\', \'神曲\', \'苍术\', \'半夏\', \'生姜\']');
INSERT INTO `prescript` VALUES (59635, '25265', '连翘饮', '连翘2钱，葛根2钱，牛蒡子2钱，玄参2钱，黄芩2钱，桔梗2钱，赤芍1钱，栀仁1钱，淡竹叶1钱，甘草1钱，木通1钱，升麻6分。', '水煎服。', '《喉证指南》卷四', '肺胃邪热，咽喉疼痛。', '-', '-', '升麻', '[\'连翘\', \'葛根\', \'牛蒡子\', \'玄参\', \'黄芩\', \'桔梗\', \'赤芍\', \'栀仁钱\', \'淡竹叶\', \'甘草\', \'木通\', \'升麻\']');
INSERT INTO `prescript` VALUES (59636, '25263', '和胃汤', '升麻半钱，柴胡半钱，当归身2钱，草豆蔻半钱，半夏3分，干姜7分，甘草7分，红消7分，黄耆半钱。', '上都作1服。水2盏，煎至1盏，去滓稍热服，两饭间饮之。', '《普济方》卷二○八', '泄痢。', '-', '-', '升麻', '[\'升麻\', \'半钱\', \'柴胡\', \'半钱\', \'当归\', \'身钱\', \'草豆蔻\', \'半钱\', \'半夏\', \'干姜\', \'甘草\', \'分红\', \'消分\', \'黄耆\', \'半钱\']');
INSERT INTO `prescript` VALUES (59637, '25227', '连翘汤', '连翘1两，瞿麦穗1两，升麻3分，玄参3分，生干地黄（焙）3分，芍药3分，甘草（炙）1分，射干半两。', '每服5钱匕，水1盏半，煎至8分，去滓，食后温服。', '《圣济总录》卷一二八', '吹乳乳痈。', '-', '-', '升麻', '[\'连翘\', \'麦穗\', \'升麻\', \'玄参\', \'分生\', \'地黄\', \'焙分\', \'芍药\', \'甘草\', \'炙分\', \'射干\', \'半两\']');
INSERT INTO `prescript` VALUES (59638, '25193', '防风煮散', '防风1两3分，茯苓1两3分，萎蕤1两3分，白术1两3分，橘皮1两3分，丹参1两3分，细辛2两，甘草1两半，升麻1两半，黄芩1两半，大枣3-7枚，射干1两，酸枣仁3分。', '本方方名，《普济方》引作“防风散”。', '《千金》卷十一', '肝实热，梦怒，虚惊。', '-', '-', '升麻', '[\'防风\', \'两分\', \'茯苓\', \'两分\', \'萎蕤\', \'两分\', \'白术\', \'两分\', \'橘皮\', \'两分\', \'丹参\', \'两分\', \'细辛\', \'甘草\', \'两半\', \'升麻\', \'两半\', \'黄芩\', \'两半\', \'大枣\', \'射干\', \'酸枣仁\']');
INSERT INTO `prescript` VALUES (59639, '25191', '净液汤', '桂技2分，连翘5分，生地黄5分，桔梗5分，升麻5分，甘草5分，当归梢7分，麻黄1钱，草豆蔻仁1钱，羌活1钱，防风1钱，柴胡1钱，苍术1钱，酒黄芩2钱，红花少许。', '水2盏，煎至1盏，去滓，食后热服。', '《兰室秘藏》卷下', '皮肤痒，腋下疮，背上疮，耳聋耳鸣。', '-', '-', '升麻', '[\'桂技分\', \'连翘\', \'生地黄\', \'桔梗\', \'升麻\', \'甘草\', \'当归\', \'麻黄\', \'草豆蔻\', \'仁钱\', \'羌活\', \'防风\', \'柴胡\', \'苍术\', \'钱酒\', \'黄芩\', \'红花\', \'少许\']');
INSERT INTO `prescript` VALUES (59640, '25168', '茯神散', '茯神3分，白鲜皮半两，羚羊角屑半两，钩藤3分，甘草3分（炙微赤，锉），川升麻3分，石膏2两，龙齿1两，犀角屑3分，炸蝉3枚（微炒，去翅足）。', '每服1钱，以水1小盏，煎至5分，去滓温服。', '《圣惠》卷八十五', '小儿热痫，皮肉壮热，烦躁头痛。', '-', '-', '升麻', '[\'茯神\', \'白鲜皮\', \'半两\', \'羚羊角\', \'半两\', \'钩藤\', \'甘草\', \'分炙微\', \'锉川\', \'升麻\', \'石膏\', \'龙齿\', \'犀角\', \'分炸蝉\']');
INSERT INTO `prescript` VALUES (59641, '25069', '赤茯苓汤', '赤茯苓（去黑皮）1两，木通（锉）1两，升麻3分，羚羊角（镑）3分，前胡（去芦头）3分，马蔺根（锉）1两，桑根白皮（锉）1两，大黄（锉，炒）1两。', '每服5钱匕，以水2盏，煎至1盏，去滓，入芒消1钱匕，食后分温2服，晚再服。', '《圣济总录》卷一二四', '喉痹肿塞不通。', '-', '-', '升麻', '[\'茯苓\', \'黑皮\', \'木通\', \'升麻\', \'羚羊角\', \'分前\', \'胡去\', \'芦头分\', \'马蔺\', \'两桑根\', \'白皮\', \'大黄\']');
INSERT INTO `prescript` VALUES (59642, '25063', '八物麦门冬饮', '麦门冬3两（去心），甘草（炙）1分，人参1分，紫菀2两，升麻2两，贝母1分半。', '麦门冬散（《伤寒活人指掌》卷五）。本方方名，《医方类聚》引作“八物麦门冬散”。', '《活人书》卷二十', '小儿天行壮热，咳嗽心烦。', '-', '-', '升麻', '[\'麦门冬\', \'两去\', \'甘草\', \'炙分\', \'人参\', \'紫菀\', \'升麻\', \'贝母\', \'分半\']');
INSERT INTO `prescript` VALUES (59643, '25055', '虎睛丸', '虎睛1具（酒浸1宿，炙），防风4分，秦艽4分，防葵4分，龙齿4分，黄芩4分，雄黄4分，防己4分，山茱萸4分，茯苓4分，铁精4分，鬼臼4分，人参4分，干地黄（一方云干姜）4分，大黄4分，银屑4分，牛黄4分，独活3两，远志3两，细辛3两，贯众3两，麝香3两，白蔹（1作白薇）3两，升麻3两，白鲜皮3两，茯神5两，石膏5两，天雄5两，鬼箭羽2分，露蜂房2分，寒水石6分，蛇蜕1尺。', '按：《圣济总录》有芎、飞鸦头，无雄黄、茯神。', '《千金》卷十四', '风癫掣疭，口眼张大，口中出白沫，或作声，或死不知人。', '-', '-', '升麻', '[\'虎睛\', \'具酒\', \'浸宿\', \'防风\', \'秦艽\', \'分防\', \'葵分\', \'龙齿\', \'黄芩\', \'雄黄\', \'防己\', \'山茱萸\', \'茯苓\', \'分铁\', \'精分\', \'鬼臼\', \'人参\', \'分干\', \'地黄\', \'一方\', \'干姜\', \'大黄\', \'银屑\', \'牛黄\', \'独活\', \'远志\', \'细辛\', \'贯众\', \'麝香\', \'白蔹\', \'作白薇\', \'升麻\', \'白鲜皮\', \'茯神\', \'石膏\', \'两天\', \'鬼箭羽\', \'露蜂房\', \'寒水石\', \'蛇蜕\']');
INSERT INTO `prescript` VALUES (59644, '25009', '金色泻黄饮', '白芷3钱，升麻2钱，枳壳（炒）2钱，黄芩（炒）2钱，防风（去芦）2钱，半夏2钱，石斛2钱，甘草1钱。', '分作2帖。每贴加生姜3片。水煎，食后服。', '《明医指掌》卷八', '脾经风热，口唇燥裂。', '-', '-', '升麻', '[\'白芷\', \'升麻\', \'枳壳\', \'黄芩\', \'防风\', \'芦钱\', \'半夏\', \'石斛\', \'甘草\']');
INSERT INTO `prescript` VALUES (59645, '24985', '柴胡饮子', '柴胡2两（去苗），川升麻1两半，赤芍药1两，黄芩1两半，甘草1两（炙微赤，锉），枳壳1两半（麸炒微黄，去瓤），麦门冬2两（去心），竹叶2两，栀子仁1两。', '每服半两，以水1大盏，加豉50粒，葱白1茎，煎至5分，去滓温服，不拘时候。', '《圣惠》卷十七', '热病5日，已得汗，毒气不尽，犹乍寒乍热，昏昏如醉，胁下牢满，骨节疼痛，不能食下，舌本干燥，口内生疮。', '-', '-', '升麻', '[\'柴胡\', \'两去\', \'苗川\', \'升麻\', \'两半\', \'芍药\', \'黄芩\', \'两半\', \'甘草\', \'两炙微\', \'枳壳\', \'两半\', \'微黄\', \'麦门冬\', \'两去\', \'竹叶\', \'栀子\']');
INSERT INTO `prescript` VALUES (59646, '24958', '沉香散', '沉香1分（锉），麝香（研）半两，地骨皮1两，当归（切，焙）半两，升麻半两，防风（去叉）半两，芎3分，桂（去粗皮）1分，甘草（炙，锉）半两，黄柏（去粗皮，蜜炙）半两，凝水石（研）1两。', '每用1钱匕，敷齿根；或以绵裹，如弹子大，含化咽津。', '《圣济总录》卷一二○', '风疳龈肿，牙齿浮动。', '-', '-', '升麻', '[\'沉香\', \'麝香\', \'半两\', \'地骨皮\', \'当归\', \'切焙\', \'半两\', \'升麻\', \'半两\', \'防风\', \'半两\', \'分桂去\', \'粗皮分\', \'甘草\', \'半两\', \'黄柏\', \'粗皮蜜\', \'半两\', \'凝水\', \'石研\']');
INSERT INTO `prescript` VALUES (59647, '24923', '地肤汤', '地肤草3两，车前子3两，知母2两，黄芩2两，赤茯苓2两，赤芍2两，枳实（炙）2两，升麻2两，通草2两，甘草（炙）2两。', '每服4钱，水1盏半，煎8分，去滓，空心温服。', '《女科百问》卷下', '妊娠患子淋。', '-', '-', '升麻', '[\'地肤\', \'车前子\', \'知母\', \'黄芩\', \'两赤\', \'茯苓\', \'赤芍\', \'枳实\', \'升麻\', \'通草\', \'甘草\']');
INSERT INTO `prescript` VALUES (59648, '24912', '麦门冬汤', '麦门冬1钱，人参1钱，甘菊1钱，赤芍药1钱，赤茯苓1钱，升麻1钱，甘草5分，石膏3钱。', '水煎服。', '《准绳·幼科》卷五', '小儿癍疹，烦渴吐泻，及痂后余热。', '-', '-', '升麻', '[\'麦门冬\', \'人参\', \'甘菊\', \'芍药\', \'茯苓\', \'升麻\', \'甘草\', \'石膏\']');
INSERT INTO `prescript` VALUES (59649, '24860', '虎睛丸', '虎睛1对（微炙），秦艽半两（去苗），龙齿半两，防葵半两，黄芩半两，雄黄半两（细研，水飞过），汉防己半两，牛黄半两（细研），羌活1分，川升麻3分，寒水石3分，远志1分（去心），茯神半两，石膏1两（细研），天雄半两（炮裂，去皮脐），鬼箭羽1分，蛇蜕皮5寸（微炒），露蜂房1分，白鲜皮1分，白薇1分，贯众1分，麝香1分（细研）。', '本方方名，《普济方》引作“虎眼丸”。', '《圣惠》卷六十九', '妇人风邪，发癫狂及诸痫。', '-', '-', '升麻', '[\'虎睛\', \'秦艽\', \'半两\', \'去苗\', \'龙齿\', \'半两\', \'防葵\', \'半两\', \'黄芩\', \'半两\', \'雄黄\', \'半两\', \'细研水\', \'飞过\', \'防己\', \'半两\', \'牛黄\', \'半两\', \'细研\', \'羌活\', \'分川\', \'升麻\', \'寒水石\', \'远志\', \'分去\', \'茯神\', \'半两\', \'石膏\', \'两细研\', \'天雄\', \'半两\', \'炮裂\', \'去皮\', \'鬼箭羽\', \'分蛇\', \'蜕皮\', \'寸微\', \'露蜂房\', \'白鲜皮\', \'分白薇分\', \'贯众\', \'麝香\', \'分细研\']');
INSERT INTO `prescript` VALUES (59650, '24823', '牢牙散', '升麻1钱，细辛1钱，川芎1钱，防风1钱，槐角1钱，生地黄1钱，白芷1钱，木律1钱，青盐（研）1钱，皂角灰1钱，茯苓2钱，寒水石（烧赤，去石，研粉）。', '方中寒水石用量原缺。', '《鸡峰》卷二十一', '牢牙。主牙齿一切疼痛不可忍。', '-', '-', '升麻', '[\'升麻\', \'细辛\', \'川芎\', \'防风\', \'槐角\', \'生地黄\', \'白芷\', \'钱木律\', \'青盐\', \'研钱\', \'角灰钱\', \'茯苓\', \'寒水石\', \'去石\', \'研粉\']');
INSERT INTO `prescript` VALUES (59651, '24764', '还睛散', '独活（去芦头）1两1分，麻黄（去根节）1两1分，白茯苓（去黑皮）1两1分，厚朴（去粗皮，生姜汁炙）1两1分，五味子1两1分，蒺藜子（炒，去角）1两1分，槐子1两1分，枸杞子1两1分，菥蓂子1两1分，麦门冬（去心，焙）1两1分，人参1两1分，细辛（去苗叶）1两1分，白芷1两1分，决明子1两1分，车前子1两1分，茺蔚子1两1分，覆盆子1两1分，地肤子1两1分，丹参1两1分，芎1两1分，防风（去叉）1两1分，黄芩（去黑心）1两1分，升麻1两1分，黄连（去须）1两1分，远志（去心）2两，木通（锉）2两，柏子仁2', '每服方寸匕，食后以米饮调服，日2次。', '《圣济总录》卷一○九', '眼见黑花昏暗。或因饮热酒食五辛，致黑风入眼，或因重病后昏暗，或因赤眼不见物，或因虚损视物不明，但瞳子不破者。', '-', '-', '升麻', '[\'独活\', \'芦头\', \'两分\', \'麻黄\', \'根节\', \'两分\', \'白茯苓\', \'黑皮\', \'两分\', \'厚朴\', \'粗皮生\', \'姜汁\', \'两分\', \'五味子\', \'两分\', \'蒺藜\', \'两分\', \'槐子\', \'两分\', \'枸杞子\', \'两分\', \'两分\', \'麦门冬\', \'两分\', \'人参\', \'两分\', \'细辛\', \'苗叶\', \'两分\', \'白芷\', \'两分\', \'决明子\', \'两分\', \'车前子\', \'两分\', \'茺蔚子\', \'两分\', \'覆盆子\', \'两分\', \'地肤子\', \'两分\', \'丹参\', \'两分\', \'两分\', \'防风\', \'两分\', \'黄芩\', \'黑心\', \'两分\', \'升麻\', \'两分\', \'黄连\', \'两分\', \'远志\', \'木通\', \'柏子仁\']');
INSERT INTO `prescript` VALUES (59652, '24754', '补中胜毒饼', '黄耆1钱，人参3分，甘草5分，当归3分，生地3分，熟地3分，白芍3分，陈皮3分，升麻5分，柴胡5分，连翘1钱，防风5分。', '本方改为汤剂，名“补中胜毒汤”（见《寿世保元》卷九）。如足阳明部疮多，倍升麻，加漏芦1钱，干葛5分；手、足太阳项脊背腰强者，加羌活1钱，独活5分；肿甚加鼠粘子3分；坚硬加昆布，硬甚加三棱、莪术各2分；寒月身凉，或有腹痛，加肉桂2分；暑日身热，或有烦闷，加酒黄连、黄柏各3分；肠胃有瘀血，加牡丹皮2分；少食，加麦芽、神曲各2分；便秘，加酒大黄或麻仁、桃仁、秦艽；阴寒秘结，去诸苦药，加附子1钱，姜煎冷服。', '《医学入门》卷八', '瘰疬马刀挟瘿，在足少阳、阳明部分，受心脾之邪而作。', '-', '-', '升麻', '[\'黄耆\', \'人参\', \'甘草\', \'当归\', \'生地\', \'熟地\', \'白芍\', \'陈皮分\', \'升麻\', \'柴胡\', \'连翘\', \'防风\']');
INSERT INTO `prescript` VALUES (59653, '24724', '决明子丸', '决明子（炒）1两半，秦皮（去粗皮，锉）1两，甘菊花1两，升麻1两半，黄芩（去黑心）1两，车前子1两半，白茯苓（去黑皮）1两半，秦艽（去苗土）1两，赤芍药1两半，地骨皮1两半，山栀子仁1两半，黄连（去须）1两半，青葙子1两半，萎蕤1两半，牵牛子（炒）1两半，蕤仁（去皮）1两半，大黄（锉，炒）1两，甘草（炙，锉）1两。', '每服20丸，食后以温水送下，临卧再服。', '《圣济总录》卷一○六', '目赤昏暗涩痛，心躁恍惚。', '-', '-', '升麻', '[\'决明子\', \'两半\', \'秦皮\', \'粗皮\', \'甘菊花\', \'升麻\', \'两半\', \'黄芩\', \'黑心\', \'车前子\', \'两半\', \'白茯苓\', \'黑皮\', \'两半\', \'秦艽\', \'苗土\', \'两赤\', \'芍药\', \'两半\', \'地骨皮\', \'半山\', \'栀子\', \'两半\', \'黄连\', \'两半\', \'青葙子\', \'两半\', \'萎蕤\', \'两半\', \'牵牛子\', \'两半\', \'蕤仁\', \'去皮\', \'两半\', \'大黄\', \'甘草\']');
INSERT INTO `prescript` VALUES (59654, '24722', '保命丹', '苦参皮4两，荆芥穗4两，羌活4两，蒺藜4两，胡麻4两，明天麻4两，风藤4两，元参4两，独活4两，连翘4两，白芷4两，厚朴4两，紫萍4两，牛膝4两，苍术3两，乌药3两，藁本3两，麻黄3两，甘草3两，红花3两，苍耳子3两，川芎3两，升麻3两，薄荷3两，半夏3两，牛蒡子3两，木瓜3两，僵蚕3两，桔梗3两，大黄3两，蒲黄3两，巴戟3两，防风3两，萆薢3两，蝉壳3两，牙皂3两，全蝎3两，续断3两，蔓荆子3两，石斛2两，甘松1两，猴姜1两，菖蒲1两，草乌1两，贝母1两，木香1两，檀香1两，沉香1两，银柴胡1两，柏子仁', '每服80丸，酒送下，每日3次。手足挛痛，加葳蕤半斤，香蛇1条；阳痿，加仙灵脾6两；身浮肿，加白花蛇1条，紫萍8两；黑斑，加广陵香、地骨皮、血见愁各4两；眼赤烂，加珠粉、知母、胡黄连各4两；破音，加木通12两，诃子6两。', '《解围元薮》卷三', '诸般风证。', '-', '-', '升麻', '[\'苦参\', \'荆芥\', \'羌活\', \'蒺藜\', \'胡麻\', \'两明\', \'天麻\', \'两风藤\', \'两元\', \'参两\', \'独活\', \'连翘\', \'白芷\', \'厚朴\', \'紫萍\', \'牛膝\', \'苍术\', \'乌药\', \'藁本\', \'麻黄\', \'甘草\', \'红花\', \'苍耳子\', \'川芎\', \'升麻\', \'薄荷\', \'半夏\', \'牛蒡子\', \'木瓜\', \'僵蚕\', \'桔梗\', \'大黄\', \'两蒲\', \'两巴戟\', \'防风\', \'两蝉壳\', \'两牙皂\', \'全蝎\', \'续断\', \'蔓荆子\', \'石斛\', \'两甘松\', \'两猴\', \'菖蒲\', \'草乌\', \'贝母\', \'木香\', \'檀香\', \'沉香\', \'银柴胡\', \'柏子仁\']');
INSERT INTO `prescript` VALUES (59655, '24718', '决明子丸', '决明子1两，地肤子3分，车前子1两半，黄连（去须）1两，人参1两，玄参1两，槐子1两，青葙子3分，地骨皮2两，升麻1两，白茯苓（去黑皮）1两，沙参1两，苦参3分。', '本方方名，《普济方》引作\"决明丸\"。', '《圣济总录》卷一○二', '肝肾气虚，风热上攻，目赤肿痛。', '-', '-', '升麻', '[\'决明子\', \'地肤子\', \'车前子\', \'两半\', \'黄连\', \'人参\', \'玄参\', \'两槐子\', \'青葙子\', \'地骨皮\', \'升麻\', \'白茯苓\', \'黑皮\', \'沙参\', \'苦参\']');
INSERT INTO `prescript` VALUES (59656, '24696', '栀子人参散', '赤石脂1两，乌梅肉1两，栀子仁1两，人参1两（去芦头），甘草半两（炙微赤，锉），川升麻1两。', '每服5钱，以水1大盏，煎至5分，去滓温服，不拘时候。', '方出《圣惠》卷四十七，名见《普济方》卷四十三', '走哺。下焦壅热，气逆不续，呕吐不禁。', '-', '-', '升麻', '[\'赤石脂\', \'乌梅\', \'栀子\', \'人参\', \'两去\', \'芦头\', \'甘草\', \'半两\', \'炙微\', \'锉川\', \'升麻\']');
INSERT INTO `prescript` VALUES (59657, '24693', '大青丸', '大青（去根）3分，甘草（炙、锉）3分，枳壳（去瓤，麸炒）3分，苦参（锉）3分，黄连（去须）1两，生干地黄（焙）1两，升麻1两。', '每服20丸，食后热水送下，1日2次。', '《圣济总录》卷一一七', '心脾中热，口糜生疮，乍发乍退，久不瘥。', '-', '-', '升麻', '[\'大青去\', \'根分\', \'甘草\', \'枳壳\', \'炒分\', \'苦参\', \'锉分\', \'黄连\', \'两生\', \'地黄\', \'升麻\']');
INSERT INTO `prescript` VALUES (59658, '24685', '角蒿散', '角蒿半两，细辛半两，川升麻半两，地骨皮1分，牛膝（去苗）1分。', '每用半钱，掺于湿纸片上贴之。以愈为度。', '《圣惠》卷三十四', '牙齿急疳，出脓血不止。', '-', '-', '升麻', '[\'角蒿\', \'半两\', \'细辛\', \'半两\', \'升麻\', \'半两\', \'地骨皮\', \'牛膝\', \'苗分\']');
INSERT INTO `prescript` VALUES (59659, '24658', '甘草石膏汤', '生地黄1分，细辛1分，熟地黄3分，黄连3分，甘草5分，石膏6分，柴胡7分，黄柏1钱，知母1钱，当归身1钱，桃仁（炒，去皮尖）1钱，荆芥穗1钱，防风1钱，升麻1钱5分，红花少许，杏仁6个，小椒2个。', '都作1服，水2盏，煎至1盏，食后温服。', '《兰室秘藏》卷上', '渴病久愈，又添舌白滑微肿，咽喉咽津觉痛，嗌肿时时有，渴喜冷饮，口中白沫如胶。', '-', '-', '升麻', '[\'生地黄\', \'细辛\', \'熟地黄\', \'黄连\', \'甘草\', \'石膏\', \'柴胡\', \'黄柏\', \'知母\', \'当归\', \'身钱\', \'桃仁\', \'炒去\', \'皮尖\', \'荆芥\', \'防风\', \'升麻\', \'钱分\', \'红花\', \'少许\', \'杏仁\', \'小椒个\']');
INSERT INTO `prescript` VALUES (59660, '24648', '赤小豆散', '赤小豆3合（炒干，纳醋中，如此7遍），人参半两，甘草半两（炙令赤色，锉），瞿麦穗半两，白蔹半两，当归半两（切，焙），黄芩半两（去黑心），猪苓半两（去黑皮），防风半两（去叉），黄耆3分（锉），薏苡仁3分，升麻3分。', '每服3钱匕，空心以粥饮下，日2次，夜1次。', '《圣济总录》卷一三六', '干湿疥。', '-', '-', '升麻', '[\'赤小豆\', \'干纳\', \'如此\', \'人参\', \'半两\', \'甘草\', \'半两\', \'炙令\', \'赤色\', \'麦穗\', \'半两\', \'白蔹\', \'半两\', \'当归\', \'半两\', \'切焙\', \'黄芩\', \'半两\', \'黑心\', \'猪苓\', \'半两\', \'黑皮\', \'防风\', \'半两\', \'黄耆\', \'薏苡仁\', \'升麻\']');
INSERT INTO `prescript` VALUES (59661, '24619', '佛手祛毒膏', '大黄2两，山栀子2两，白蔹1两，连翘1两，升麻半两，蒴藋半两。', '涂敷或摊贴之。', '《杨氏家藏方》卷十二', '风热毒气，留滞荣卫，血气壅盛，聚结痈肿，烦疼不止，肌肉败溃，及诸疮焮赤疼痛。', '-', '-', '升麻', '[\'大黄\', \'两山\', \'栀子\', \'白蔹\', \'连翘\', \'升麻\', \'半两\', \'半两\']');
INSERT INTO `prescript` VALUES (59662, '24579', '茯苓汤', '白茯苓（去黑皮）半两，大黄（锉，炒）半两，升麻半两，麦门冬（去心，焙）半两，远志（去心）半两，人参半两，葛根（锉）半两，甘草（炙，锉）半两。', '每服3钱匕，水1盏，煎至7分，去滓温服，不拘时候。', '《圣济总录》卷&mdash;一七', '心热，舌干烦躁。', '-', '-', '升麻', '[\'白茯苓\', \'黑皮\', \'半两\', \'大黄\', \'半两\', \'升麻\', \'半两\', \'麦门冬\', \'半两\', \'远志\', \'去心\', \'半两\', \'人参\', \'半两\', \'葛根\', \'半两\', \'甘草\', \'半两\']');
INSERT INTO `prescript` VALUES (59663, '24561', '地黄汤', '生地黄汁1升，川升麻2两，白蔹2两，栀子仁2两，黄连2两，黄柏1两，当归2两，赤芍药2两，射干2两，川大黄2两，甘草2两半（锉）。', '以水1斗，煮至6升，去滓，下地黄汁，搅令微温，以故帛纳汤中，蘸搨于肿上。', '《圣惠》卷六十二', '发背成疮烂坏。', '-', '-', '升麻', '[\'生地黄\', \'升川\', \'升麻\', \'白蔹\', \'栀子\', \'黄连\', \'黄柏\', \'当归\', \'两赤\', \'芍药\', \'射干\', \'两川\', \'大黄\', \'甘草\', \'两半\']');
INSERT INTO `prescript` VALUES (59664, '24560', '连翘散', '连翘1两，射干3分，玄参3分，赤芍药半两，木香半两，川芒消1两，川升麻2分，栀子仁半两，前胡半两（去芦头），当归3分，甘草半两（炙微赤，锉），川大黄1两（锉碎，微炒）。', '射干连翘汤（《圣济总录》卷一二六）、射干连翘散（《外科发挥》卷五）。', '《圣惠》卷六十六', '瘰疬结肿疼痛，时发寒热。', '-', '-', '升麻', '[\'连翘\', \'射干\', \'玄参\', \'分赤\', \'芍药\', \'半两\', \'木香\', \'半两\', \'川芒\', \'消两川\', \'升麻\', \'栀子\', \'半两\', \'半两\', \'芦头\', \'当归\', \'甘草\', \'半两\', \'炙微\', \'锉川\', \'大黄\']');
INSERT INTO `prescript` VALUES (59665, '24553', '虎骨丸', '虎头骨（酥炙）半两，乌梅肉（炒）半两，桂（去粗皮）半两，甘草（炙，锉）半两，人参半两，桃仁（去皮尖双仁，炒，研）3分，常山3分，升麻3分，肉苁蓉（酒浸，切，焙）3分，附子（炮裂，去皮脐）半两，麝香（研）1钱，豉（炒）1合。', '每服30丸，未发前米饮送下。', '《圣济总录》卷三十四', '寒疟，无问远近。', '-', '-', '升麻', '[\'头骨\', \'半两\', \'乌梅\', \'半两\', \'桂去\', \'粗皮\', \'半两\', \'甘草\', \'半两\', \'人参\', \'半两\', \'桃仁\', \'皮尖\', \'双仁\', \'研分\', \'常山\', \'升麻\', \'肉苁蓉\', \'浸切\', \'焙分\', \'附子\', \'炮裂\', \'去皮\', \'半两\', \'麝香\', \'研钱豉\', \'炒合\']');
INSERT INTO `prescript` VALUES (59666, '24513', '柴胡饮子', '柴胡半两，黄芩半两，人参半两，甘草半两，升麻半两，地骨皮半两，赤茯苓半两，赤芍药半两，草龙胆半两。', '上(口父)咀。每服4钱，水1盏，加薄荷、沙糖，同煎7分，温服，不拘时候，小儿分作3服。', '《普济方》卷一三二', '大人、小儿伤寒，余热不已。', '-', '-', '升麻', '[\'柴胡\', \'半两\', \'黄芩\', \'半两\', \'人参\', \'半两\', \'甘草\', \'半两\', \'升麻\', \'半两\', \'地骨皮\', \'半两\', \'茯苓\', \'半两\', \'芍药\', \'半两\', \'龙胆\', \'半两\']');
INSERT INTO `prescript` VALUES (59667, '24493', '经验固崩汤', '当归1钱，川芎1钱，白芍（酒炒）1钱，熟地8钱，杜仲1钱，川断1钱，山药1钱，升麻（甚者倍用）1钱5分，地榆1钱5分，山栀（炒黑）1钱5分，荆芥（炒黑）1钱5分，干姜（炒黑倍用）。', '水煎，空心服。', '《宋氏女科》', '血崩不止，旁徨之甚。', '-', '-', '升麻', '[\'当归\', \'川芎\', \'白芍\', \'熟地\', \'杜仲\', \'钱川断\', \'山药\', \'升麻\', \'甚者\', \'用钱\', \'榆钱\', \'分山\', \'黑钱\', \'荆芥\', \'黑钱\', \'干姜\', \'黑倍\']');
INSERT INTO `prescript` VALUES (59668, '24374', '红雪', '升麻3两，大青3两，桑白皮2两，犀角屑1两半，羚羊角1两，诃子30枚（去核），槟榔30枚，苏木6两，山栀子30个，槐花末1两，淡竹叶4两。', '神仙红雪（《鸡峰》卷四）。', '《脚气治法总要》卷下', '脚气，气毒，身内外烦热，疮肿狂叫，及诸般石毒，瘴疠时行，一切风热。', '-', '-', '升麻', '[\'升麻\', \'两大青\', \'桑白皮\', \'犀角\', \'两半\', \'羚羊角\', \'诃子\', \'去核\', \'槟榔\', \'苏木\', \'两山\', \'栀子\', \'槐花\', \'淡竹叶\']');
INSERT INTO `prescript` VALUES (59669, '24369', '赤茯苓汤', '赤茯苓（去黑皮）1两半，石膏1两，犀角屑1两，升麻1两半，麦门冬（去心，焙）1两半，木香1两半。', '每服4钱匕，水1盏，加竹沥半小盏，同煎至8分，去滓温服。', '《圣济总录》卷八十二', '脚气冲心，烦闷膝痛。', '-', '-', '升麻', '[\'茯苓\', \'黑皮\', \'两半\', \'石膏\', \'犀角\', \'升麻\', \'两半\', \'麦门冬\', \'两半\', \'木香\', \'两半\']');
INSERT INTO `prescript` VALUES (59670, '24283', '参耆汤', '人参1钱，当归1钱，苍术1钱，白术1钱，陈皮1钱，神曲1钱，炙草1钱，五味子1钱，炙耆4钱，酒柏，升麻4钱。', '加生姜、大枣，水煎服。', '《痘疹仁端录》卷十', '诸毒脓血，大溃，大痛，大臭。', '-', '-', '升麻', '[\'人参\', \'当归\', \'苍术\', \'白术\', \'陈皮\', \'神曲\', \'钱炙草\', \'五味子\', \'钱炙耆\', \'酒柏\', \'升麻\']');
INSERT INTO `prescript` VALUES (59671, '24197', '麦门冬汤', '麦门冬（去心，焙）2两，黄芩（去黑心）1两，柴胡（去苗）1两，升麻1两，芍药1两，甘草（炙，锉）1两。', '每服5钱匕，水1盏半，加苦竹叶3片，煎至1盏，去滓，分2次温，空腹、食后各1服。', '《圣济总录》卷九十三', '骨蒸疼烦，翕翕发热，骨节酸痛，口干烦渴。', '-', '-', '升麻', '[\'麦门冬\', \'黄芩\', \'黑心\', \'柴胡\', \'升麻\', \'芍药\', \'甘草\']');
INSERT INTO `prescript` VALUES (59672, '24171', '赤茯苓散', '赤茯苓1两，麦门冬1两（去心），木通3分（锉），川升麻3分，葳蕤3分，甘草半两（炙微赤，锉），紫菀3分（去苗土），川大黄3分（锉碎，微炒），子芩1两。', '每服3钱，以水一中盏，加淡竹茹1分，煎至6分，去滓，食前温服。', '《圣惠》卷四', '心气实热，烦闷不安。', '-', '-', '升麻', '[\'茯苓\', \'麦门冬\', \'两去\', \'木通\', \'锉川\', \'升麻\', \'葳蕤\', \'甘草\', \'半两\', \'炙微\', \'紫菀\', \'苗土\', \'大黄\', \'碎微\', \'炒子\']');
INSERT INTO `prescript` VALUES (59673, '24167', '补气泻荣汤', '升麻6分，连翘6分，苏木3分，当归3分，全蝎3分，黄连3分，地龙3分，黄耆3分，生黄芩4分，甘草1钱半，人参2分，生地黄4分，桃仁3个，桔梗半钱，麝香少许，胡桐泪1分，虻虫（去翅足，微炒）2个，水蛭（炒令烟尽）2个。', '除连翘另锉，梧桐泪研白豆蔻2分为细末，2味另放；麝香、虻虫、水蛭3味为细末另放外，都作1服，水2大盏，酒1匙，入连翘煎至1盏6分，再入白豆蔻2味并麝香等3味，再上火煎1-2沸，去滓，早饭后、午饭前稍热服。', '《东垣试效方》卷九', '疠风，满面连须极痒，眉毛脱落。', '忌酒、湿面、生冷硬物。', '-', '升麻', '[\'升麻\', \'连翘\', \'苏木\', \'当归\', \'全蝎\', \'黄连\', \'分地\', \'龙分\', \'黄耆\', \'分生\', \'黄芩\', \'甘草\', \'钱半\', \'人参\', \'生地黄\', \'桃仁\', \'桔梗\', \'半钱\', \'麝香\', \'少许\', \'胡桐\', \'泪分\', \'足微\', \'炒个\', \'水蛭\', \'炒令烟\', \'尽个\']');
INSERT INTO `prescript` VALUES (59674, '24160', '连翘解毒饮', '木通1钱2分，防风1钱2分，羌活1钱2分，连翘1钱2分，丁香1钱，乳香1钱，沉香1钱，升麻1钱，大黄7分，黄芩7分，甘草3分，木香3分，桑寄生1钱，麝香5厘。', '水煎服。', '《续名家方选》', '大人、小儿头疮及臁疮。', '-', '-', '升麻', '[\'木通\', \'钱分\', \'防风\', \'钱分\', \'羌活\', \'钱分\', \'连翘\', \'钱分\', \'丁香\', \'乳香\', \'沉香\', \'升麻\', \'大黄\', \'黄芩\', \'甘草\', \'木香\', \'桑寄生\', \'麝香\']');
INSERT INTO `prescript` VALUES (59675, '24073', '钓藤饮', '麻黄（去节）3钱，粉草3钱，蝉蜕5个（去足翼），升麻3钱，龙胆草2钱，川芎3钱，天竺黄3钱，钓藤3钱，羌活3钱，独活3钱，防风3钱。', '本方方名，《普济方》引作“钩藤饮”。', '《得效》卷十一', '小儿一切惊风，潮搐，眼视昏迷，以及惊风变易诸症。', '-', '-', '升麻', '[\'麻黄\', \'节钱\', \'粉草\', \'蝉蜕\', \'足翼\', \'升麻\', \'龙胆草\', \'川芎\', \'天竺黄\', \'钓藤钱\', \'羌活\', \'独活\', \'防风\']');
INSERT INTO `prescript` VALUES (59676, '24069', '连翘汤', '连翘2两，蜀升麻2两，黄芩3两，枳实2两（炙），干蓝3两，芍药2两，玄参2两，白蔹2两，甘草2两（炙），羚羊角（屑）2两，通草2两，黄耆2两，大黄3两。', '以水8升，煮取2升半，分3服。利1-2行后，更服去大黄、干蓝，即不利。', '《外台》卷二十四引《崔氏方》', '患疮肿而渴。', '忌海藻、菘菜。', '-', '升麻', '[\'连翘\', \'两蜀\', \'升麻\', \'黄芩\', \'枳实\', \'两炙干\', \'芍药\', \'玄参\', \'白蔹\', \'甘草\', \'羚羊角\', \'通草\', \'两黄耆\', \'大黄\']');
INSERT INTO `prescript` VALUES (59677, '24017', '补中益气汤', '黄耆1钱半，人参1钱，甘草（炙）1钱，当归身（酒洗，焙干）半钱，柴胡半钱，陈皮半钱，白术半钱，升麻3分，葛根半钱。', '上作1服。水煎，午前稍热服。嗽者，黄耆用半钱，并去人参，不渴者，去葛根；头痛，加蔓荆子3分，痛甚，加川芎5分；顶痛、脑痛者，加藁本5分，细辛3分；头痛有痰，沉重懒倦者，乃太阴、厥阴头痛，加半夏半钱或1钱，生姜3片；耳鸣目黄，颊颔肿，颈肩臑肘臂外后廉痛，面赤，脉洪大者，加羌活1钱，防风7分，甘草3分，藁本5分，通其经血，加黄芩、黄连各3分，消其肿；咽痛颔肿，脉洪大，面赤，加黄芩3分，桔梗7分，甘草3分；口干、咽干或渴者，加葛根5分，升胃气上行以润之；心下痞，瞀闷者，加芍药、黄连各1钱；如痞腹胀，加枳实3分，', '《丹溪心法》卷三', '补元气，泻火邪。主内伤，喜怒过度，饮食失节，寒温不适，劳役所伤，以致中气不足，阴火独旺，上乘阳分，荣卫失守，气高而喘，身热而烦，短气上逆，鼻息不调，怠情嗜卧，四肢困倦不收，无气以动，亦无气以言。', '-', '-', '升麻', '[\'黄耆\', \'人参\', \'甘草\', \'当归\', \'身酒\', \'焙干\', \'半钱\', \'柴胡\', \'半钱\', \'陈皮\', \'半钱\', \'白术\', \'半钱\', \'升麻\', \'葛根\', \'半钱\']');
INSERT INTO `prescript` VALUES (59678, '23995', '连翘饮', '连翘1两，山栀子仁1两，黄耆（锉，炒）1两，防风（去叉）1两，升麻1两，羚羊角（镑）1两，漏芦（去芦头）1两，甘草（炙，锉）1两，大黄（锉，炒）1两，枸杞根皮1两。', '每服5钱匕，用水1盏半，煎取8分，去滓，空心温服。', '《圣济总录》卷一三一', '痈疽发背疮肿，或已溃，或未溃。', '-', '-', '升麻', '[\'连翘\', \'两山\', \'栀子\', \'黄耆\', \'防风\', \'升麻\', \'羚羊角\', \'漏芦\', \'芦头\', \'甘草\', \'大黄\', \'枸杞\', \'根皮\']');
INSERT INTO `prescript` VALUES (59679, '23988', '苦竹叶散', '苦竹叶半两，知母1分，川升麻1分，川大黄（锉，微炒）1分，甘草（炙微赤，锉）1分，栀子仁1分。', '儿3-5岁者每服半钱，食后以温淡浆水调下。', '《圣惠》卷八十九', '小儿眼生翳膜下垂。', '-', '-', '升麻', '[\'苦竹叶\', \'半两\', \'知母\', \'分川\', \'升麻\', \'分川\', \'大黄\', \'锉微\', \'炒分\', \'甘草\', \'炙微\', \'栀子\', \'仁分\']');
INSERT INTO `prescript` VALUES (59680, '23951', '独活汤', '独活（去芦头）2两，芍药2两，远志（去心）1两半，薏苡仁（炒）半升，甘草（炙，锉）2两，麻黄（去节，煎掠去沫，焙）1两，丹参2两，陈橘皮（汤浸，去白，焙）1两半，熟干地黄（焙）3两，桂（去粗皮）1两，甘菊花半升（未开者良，微炒），人参2两，防风（去叉）2两，茯神（去木）2两，山茱萸2两，天门冬（去心，焙）2两，厚朴（去粗皮，生姜汁炙，锉）2两，牛膝（去苗，酒浸，切，焙）2两，五加皮（锉）2两，羚羊角（镑）2两，麦门冬（去心，焙）2两，山芋2两，白术2两，秦艽（去苗土）2两，黄耆（锉）2两，芎2两，附子（', '每用10钱匕，以水3盏，加生姜10片，煎取1盏半，去滓，分温2服，空心、临卧各1次。取微有汗，慎外风。若心中热者，每日平旦、临卧服此汤，午时服荆沥汤。', '《圣济总录》卷九', '偏风。半身不随，肌肉偏枯。', '-', '-', '升麻', '[\'独活\', \'芦头\', \'芍药\', \'远志\', \'去心\', \'两半\', \'薏苡仁\', \'炒半升\', \'甘草\', \'麻黄\', \'去节\', \'掠去\', \'丹参\', \'两陈\', \'橘皮\', \'汤浸\', \'两半\', \'熟干\', \'地黄\', \'两桂去\', \'粗皮\', \'甘菊花\', \'半升\', \'开者\', \'良微\', \'人参\', \'防风\', \'茯神\', \'山茱萸\', \'天门冬\', \'厚朴\', \'粗皮生\', \'姜汁\', \'牛膝\', \'苗酒\', \'浸切\', \'五加皮\', \'羚羊角\', \'麦门冬\', \'山芋\', \'白术\', \'秦艽\', \'苗土\', \'两黄耆\', \'两芎\', \'附子\']');
INSERT INTO `prescript` VALUES (59681, '23945', '补中益气加姜桂汤', '炙耆1钱5分，白术（净炒）1钱5分，当归1钱5分，升麻（蜜炙）3分，柴胡5分，党参（去芦，米炒）3钱，干姜5分，肉桂（去皮另炖）3分，陈皮1钱，炙草7分。', '水煎服。若疟疾复作，则去姜、桂，加制附。', '《不知医必要》卷三', '疟止而痢更甚者。', '-', '-', '升麻', '[\'炙耆\', \'钱分\', \'白术\', \'钱分\', \'当归\', \'钱分\', \'升麻\', \'蜜炙分\', \'柴胡\', \'党参\', \'芦米\', \'干姜\', \'肉桂\', \'去皮\', \'另炖\', \'陈皮\', \'草分\']');
INSERT INTO `prescript` VALUES (59682, '23933', '补中益气加减汤', '炙耆1钱5分，白术（土炒）1钱5分，党参（去芦，米炒）1钱5分，淮山（炒）1钱5分，扁豆（炒，杵）1钱，升麻（蜜炙）3分，陈皮6分，炙草7分。', '加生姜2片，红枣2枚，煎服。如不止，加肉蔻霜1钱；寒，加干姜4分。', '《不知医必要》卷四', '产后泄泻。', '不可利小便。', '-', '升麻', '[\'炙耆\', \'钱分\', \'白术\', \'钱分\', \'党参\', \'芦米\', \'钱分\', \'淮山\', \'炒钱分\', \'扁豆\', \'升麻\', \'蜜炙分\', \'陈皮\', \'分炙草分\']');
INSERT INTO `prescript` VALUES (59683, '23786', '补元汤', '人参3钱，川芎1钱2分，熟地1钱2分，白术1钱2分，紫河车1钱2分，白芍1钱2分，五味子3分，升麻3分。', '加大枣10枚，水煎去滓，不拘时服。', '《玉案》卷五', '产后产门不闭。', '-', '-', '升麻', '[\'人参\', \'川芎\', \'钱分\', \'熟地\', \'钱分\', \'白术\', \'钱分\', \'紫河车\', \'钱分\', \'白芍\', \'钱分\', \'五味子\', \'升麻\']');
INSERT INTO `prescript` VALUES (59684, '23769', '龟柏丸', '龟版2两，侧柏叶1两半，芍药1两半，椿根皮7钱半，升麻5钱，香附5钱。', '椿皮丸（《明医指掌》卷六）。', '方出《丹溪心法》卷二，名见《医学入门》卷七', '便血久而致虚，腰脚软痛，及麻风疮疡见血。', '-', '-', '升麻', '[\'龟版\', \'侧柏叶\', \'两半\', \'芍药\', \'两半\', \'根皮\', \'升麻\', \'香附\']');
INSERT INTO `prescript` VALUES (59685, '23745', '大快斑丸', '辰砂1钱，紫草茸半两，川升麻半两，钓藤钩半两，赤小豆半两，甘草（炒）1钱，川地龙（去土，焙）3钱。', '每服1丸，石榴汤化下；或用温水与乳汁送下。', '《医方类聚》卷二六五引《施圆端效方》', '斑疹倒靥，黑陷恶候。', '-', '-', '升麻', '[\'辰砂\', \'紫草茸\', \'半两\', \'升麻\', \'半两\', \'钓藤钩\', \'半两\', \'赤小豆\', \'半两\', \'甘草\', \'炒钱川\', \'地龙\']');
INSERT INTO `prescript` VALUES (59686, '23703', '防风独活汤', '防风1两（去叉），独活1两（去芦头），秦艽1两（去苗土），黄耆1两，芍药1两，人参1两，茯神1两（去木），白术1两（锉，炒），芎1两，山茱萸1两，薯蓣1两，桂1两（去粗皮），天门冬1两（去心，焙），麦门冬（去心，焙）1两，厚朴4两（去粗皮，生姜汁炙），羚羊角4两（镑），升麻4两，甘草4两（炙），丹参4两，牛膝4两（去苗，酒浸，切，焙），五加皮4两，石斛4两（去根），地骨皮4两，远志（去心）4两，附子3两（炮裂，去皮脐），陈橘皮3两（汤浸，去白，焙），麻黄（去根节，先煎，掠去沫，焙）3两，甘菊花1升（半开', '每服5钱匕，水1盏半，入生姜半分（切），煎至8分，去滓，空心、日午、夜卧各温服。如觉心膈虚烦满闷，气喘面赤，即与荆沥汤相间服。', '《圣济总录》卷五', '中风初得，四肢不收，心神昏愦，眼不识人，不能言语，服防己竹沥汤后，风若未除者。', '-', '-', '升麻', '[\'防风\', \'两去\', \'独活\', \'两去\', \'芦头\', \'秦艽\', \'两去\', \'苗土\', \'黄耆\', \'芍药\', \'人参\', \'茯神\', \'两去\', \'白术\', \'山茱萸\', \'薯蓣\', \'两桂\', \'两去\', \'粗皮\', \'天门冬\', \'两去\', \'麦门冬\', \'厚朴\', \'两去\', \'粗皮生\', \'姜汁\', \'羚羊角\', \'两镑\', \'升麻\', \'甘草\', \'丹参\', \'牛膝\', \'两去\', \'苗酒\', \'浸切\', \'五加皮\', \'石斛\', \'两去\', \'地骨皮\', \'远志\', \'附子\', \'两炮裂\', \'去皮\', \'橘皮\', \'两汤\', \'浸去\', \'麻黄\', \'根节\', \'掠去\', \'甘菊花\', \'半开\']');
INSERT INTO `prescript` VALUES (59687, '23667', '百效汤', '升麻1钱，葛根1钱，白芷1钱，酒芩1钱，桔梗1钱，防风5分，薄荷5分，石膏3钱，甘草6分，天花粉8分，细辛4分。', '加生姜3片，水煎服，食后频频少服之。头疼及项颈痛者，加羌活1钱；发寒热者，加柴胡1钱。', '《赤水玄珠》卷三', '肠胃壅热、风热，牙龈肿痛，颊车皆肿。', '-', '-', '升麻', '[\'升麻\', \'葛根\', \'白芷\', \'钱酒\', \'桔梗\', \'防风\', \'薄荷\', \'石膏\', \'甘草\', \'天花粉\', \'细辛\']');
