INSERT INTO `prescript` VALUES (59696, '23167', '补中益气汤', '牛蒡子1钱半，元参3钱，蜜耆3钱，白术1钱半（蜜炒），广陈皮1钱半，当归1钱半，甘草1钱，麦冬3钱半，苦桔梗1钱，红枣1枚，生姜3片，柴胡2钱（酒炒），升麻8分（酒炒）。', '水煎服。', '《喉科种福》卷四', '慢喉风，平素体虚，更兼暴怒，或过食五辛而生，或忧思太过而成。其发缓，其色淡，其肿微，咽干，舌滑而白，大便自利，脉细而微，唇如矾色，午前痛者。', '-', '-', '升麻', '[\'牛蒡子\', \'钱半元\', \'参钱\', \'蜜耆\', \'白术\', \'钱半蜜\', \'炒广\', \'陈皮\', \'当归\', \'钱半\', \'甘草\', \'麦冬\', \'钱半苦\', \'桔梗\', \'红枣\', \'生姜\', \'柴胡\', \'钱酒\', \'升麻\', \'分酒\']');
