INSERT INTO `prescript` VALUES (59699, '23093', '赤茯苓散', '赤茯苓1两，川升麻1两，甘草3分（炙微赤，锉），知母1两，犀角屑3分，木香3分，前胡3分（去芦头），石膏1两半，旋覆花半两，麦门冬1两（去心），紫苏茎叶1两，槟榔1两。', '每服4钱，以水一中盏，加姜汁半分，煎6分，去滓温服，不拘时候。', '方出《圣惠》卷四十五，名见《普济方》卷二四六', '瘴毒脚气，胸膈气不通，乍寒乍热，头痛心烦，不下饮食。', '-', '-', '升麻', '[\'茯苓\', \'两川\', \'升麻\', \'甘草\', \'分炙微\', \'知母\', \'犀角\', \'木香\', \'分前\', \'胡分\', \'芦头\', \'石膏\', \'两半\', \'旋覆花\', \'半两\', \'麦门冬\', \'两去\', \'紫苏\', \'槟榔\']');
