INSERT INTO `prescript` VALUES (59701, '23080', '连翘散', '连翘5钱，当归尾5钱，羌活5钱，独活5钱，防风5钱，赤芍药5钱，小赤豆5钱，大黄2钱，木香3钱，菇荑3钱，茨菇3钱，薄荷3钱，红内消3钱，杜白芷3钱，升麻3钱，甘草3钱，忍冬草3钱。', '方中菇荑，《普济方》作“辛荑”。若潮热不退，加黄芩、栀子仁各3钱，朴消4钱；喘，加人参。', '《得效》卷十九', '疔疮泻后。', '-', '-', '升麻', '[\'连翘\', \'当归\', \'尾钱\', \'羌活\', \'独活\', \'防风\', \'芍药\', \'赤豆\', \'大黄\', \'木香\', \'钱菇\', \'钱茨菇\', \'薄荷\', \'钱红内\', \'消钱\', \'白芷\', \'升麻\', \'甘草\', \'忍冬\', \'草钱\']');
INSERT INTO `prescript` VALUES (59702, '23068', '固真汤', '升麻1钱，羌活1钱，柴胡1钱，炙甘草1钱5分，草龙胆1钱5分，泽泻1钱5分，黄柏2钱，知母2钱。', '《普济方》有苍术；《证治宝鉴》有汉防已。', '《兰室秘藏》卷下', '两丸冷，前阴痿弱，阴汗如水，小便后有余滴，尻臀并前阴冷，恶寒而喜热，膝下亦冷。', '-', '-', '升麻', '[\'升麻\', \'羌活\', \'柴胡\', \'甘草\', \'分草\', \'龙胆\', \'钱分\', \'泽泻\', \'钱分\', \'黄柏\', \'知母\']');
