INSERT INTO `prescript` VALUES (59706, '23016', '参麻汤', '玄参1两，升麻3分，犀角（镑）半两，干蓝叶1两，甘草（炙，锉）3分。', '每服5钱匕，水1盏半，加葱白3寸，豉100粒，同煎至1盏，去滓温服，1日2次。', '《圣济总录》卷二十八', '伤寒热病生豌豆疮并疱疮，烦闷昏迷。', '-', '-', '升麻', '[\'玄参\', \'升麻\', \'犀角\', \'半两\', \'干蓝叶\', \'甘草\']');
