INSERT INTO `prescript` VALUES (59709, '22852', '赤茯苓散', '赤茯苓半两，桑根白皮半两（锉），川升麻1分，甜葶苈1分（隔纸炒令紫色），杏仁1分（汤浸，去皮尖双仁，麸炒微黄），桔梗1分（去芦头），贝母半两（煨令微黄）。', '每服1钱，以水一小盏，煎至5分，去滓温服，日3-4次。', '《圣惠》卷八十八', '小儿水气肿满，喘咳不止。', '-', '-', '升麻', '[\'茯苓\', \'半两\', \'桑根\', \'白皮\', \'半两\', \'锉川\', \'升麻\', \'分甜\', \'分隔\', \'炒令\', \'紫色\', \'杏仁\', \'分汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\', \'桔梗\', \'分去\', \'芦头\', \'贝母\', \'半两\', \'微黄\']');
INSERT INTO `prescript` VALUES (59710, '22846', '和顺汤', '升麻5分，防风3分，白芷3分，黄耆3钱，人参3钱，甘草3分，白芍3钱，白术5钱，茯神3钱，炮姜5分。', '水煎，午前服。连服10剂，黑色尽除，再服10剂，诸病全愈。', '《辨证录》卷九', '补中益气，升提阳气。主忧思不已，加之饮食失节，阴阳相逆，脾胃有伤，面色黧黑不泽，环唇尤甚，心中如饥，然见食则恶，气短而促。', '-', '-', '升麻', '[\'升麻\', \'防风\', \'白芷\', \'分黄耆\', \'人参\', \'甘草\', \'白芍\', \'白术\', \'茯神\', \'钱炮\', \'姜分\']');
