INSERT INTO `prescript` VALUES (59712, '22715', '定痛羌活汤', '羌活1钱，防风1钱，川芎1钱，生地1钱，升麻1钱2分，细辛6分，荆芥6分，独活6分，薄荷6分，石膏2钱，甘草5分。', '水煎，食后服。恶热饮者，加龙胆草（酒洗）1钱半；恶风作痛，加白豆蔻、黄连各5分；湿热甚者，加黄连1钱，山栀1钱。', '《赤水玄珠》卷三', '风热攻注，牙根肿痛。', '-', '-', '升麻', '[\'羌活\', \'防风\', \'川芎\', \'生地\', \'升麻\', \'钱分\', \'细辛\', \'荆芥\', \'独活\', \'薄荷\', \'石膏\', \'甘草\']');
INSERT INTO `prescript` VALUES (59713, '22712', '定痛散', '当归1钱，川芎1钱，白芍1钱，官桂1钱，山柰3钱，麝香3分，红花5钱，紫丁香根5钱，升麻1钱，防风1钱。', '老葱捣汁合敷患处，再用熨法。', '《金鉴》卷八十八', '定痛消肿，舒筋和络。主一切打仆损伤。', '-', '-', '升麻', '[\'当归\', \'川芎\', \'白芍\', \'官桂钱\', \'麝香\', \'分红\', \'花钱\', \'紫丁香\', \'根钱\', \'升麻\', \'防风\']');
INSERT INTO `prescript` VALUES (59714, '22694', '和解汤', '升麻1钱，干葛1钱，白芍1钱，川芎7分，防风7分，人参7分，甘草2分。', '生姜为引，水煎，热服。', '《种痘新书》卷十二', '发表解毒。主痘症初热。', '-', '-', '升麻', '[\'升麻\', \'干葛钱\', \'白芍\', \'川芎\', \'防风\', \'人参\', \'甘草\']');
