INSERT INTO `prescript` VALUES (59716, '22604', '补中益气汤', '黄耆4钱，甘草（炙）5分，白术1钱，人参3钱，升麻2分，柴胡5分，陈皮8分，黄柏6分，当归1钱，生姜3片。', '上(口父)咀。用水煎熟，食前温服。', '《杏苑》卷三', '伤寒、时疫愈后，劳役复热，自汗倦怠。', '-', '-', '升麻', '[\'黄耆\', \'甘草\', \'炙分\', \'白术\', \'人参\', \'升麻\', \'柴胡\', \'陈皮分\', \'黄柏\', \'当归\', \'生姜\']');
INSERT INTO `prescript` VALUES (59717, '22589', '胡黄连散', '胡黄连1分，升麻半两，铅霜（研）1分。', '每服半钱匕，绵裹含化咽津，1日3-5度，不拘时候。', '《圣济总录》卷一二二', '咽喉中壅塞如核，连颊肿痛。', '-', '-', '升麻', '[\'胡黄连\', \'升麻\', \'半两\', \'铅霜\', \'研分\']');
INSERT INTO `prescript` VALUES (59718, '22555', '凉血汤', '当归1钱，生地黄1钱，黄连（姜制）8分，黄芩8分，黄柏（酒炒）8分，知母（酒炒）8分，防风8分，荆芥8分，细辛6分，蔓荆子6分，羌活6分，藁本4分，甘草3分，升麻3分（炒）。', '水煎，食前服。', '《竹林女科》卷一', '妇人肾虚崩漏。', '-', '-', '升麻', '[\'当归\', \'生地黄\', \'黄连\', \'姜制\', \'黄芩\', \'黄柏\', \'炒分\', \'知母\', \'炒分\', \'防风\', \'荆芥\', \'细辛\', \'蔓荆子\', \'羌活\', \'藁本\', \'甘草\', \'升麻\']');
INSERT INTO `prescript` VALUES (59719, '22527', '络石射干汤', '络石3分，射干1两半，芍药1两1分，升麻1两1分，露蜂房（炙）1两，蒺藜子（炒去角）1两。', '每服3钱匕，水1盏，煎至6分，去滓，入马牙消1钱匕，搅匀，食后临卧温服。细细含咽亦得。', '《圣济总录》卷一二二', '咽喉肿痛，咽物不得。', '-', '-', '升麻', '[\'络石\', \'射干\', \'两半\', \'芍药\', \'两分\', \'升麻\', \'两分\', \'露蜂房\', \'蒺藜\']');
INSERT INTO `prescript` VALUES (59720, '22468', '均药', '栀子（炒）7钱，薄荷叶1两，连翘1两，赤小豆1两，升麻5钱，鸡内金（炙黄）1钱5分。', '吹患处。', '《喉科紫珍集》卷上', '咽喉诸症，下刀针后，不消不溃坚硬者。', '-', '-', '升麻', '[\'栀子\', \'薄荷叶\', \'连翘\', \'赤小豆\', \'升麻\', \'鸡内金\', \'黄钱分\']');
INSERT INTO `prescript` VALUES (59721, '22459', '和血补气饮', '防风1钱，黄芩1钱，蔓荆子1钱，白芷1钱，柴胡1钱，甘草5分，当归1钱5分，升麻6分。', '水煎服。', '《异授眼科》', '患目之后，多受风寒，而气血不通，九窍闭塞，以致肺气衰弱，心火太旺，故心血欺凌肺金，目有白珠多红，及眵泪少涩难开。', '-', '-', '升麻', '[\'防风\', \'黄芩\', \'蔓荆子\', \'白芷\', \'柴胡\', \'甘草\', \'当归\', \'钱分\', \'升麻\']');
INSERT INTO `prescript` VALUES (59722, '22456', '八岁汤', '芍药2两，栀子2两，柴胡1两6铢，升麻2两半，黄芩2两半，黄连2两半，竹叶（切）1升半，桔梗1两半，细辛15铢，知母2两，大黄2两（1本有枳实、杏仁各1两半，无桔梗、黄连）。', '上(口父)咀。以水6升，煮取1升8合，去滓，分4服，十岁儿为3服。', '方出《千金》卷五，名见《医部全录》卷四四一', '8岁以上儿，热结痰实，不能食，自下。', '-', '-', '升麻', '[\'芍药\', \'栀子\', \'柴胡\', \'两铢\', \'升麻\', \'两半\', \'黄芩\', \'两半\', \'黄连\', \'两半\', \'竹叶\', \'切升半\', \'桔梗\', \'两半\', \'细辛\', \'知母\', \'大黄\', \'两本\', \'枳实\', \'杏仁\', \'两半\', \'桔梗\', \'黄连\']');
INSERT INTO `prescript` VALUES (59723, '22417', '安胃散', '丁香5分，茱萸1钱，草蔻1钱，参1钱，炙甘草5分，耆1钱，柴胡5分，升麻7分，柏3钱，陈皮5分，归1钱5分，苍术1钱。', '水煎，温服。痰饮为患，呕吐痰涎者，加半夏、茯苓、陈皮。', '《脉因证治》卷下', '胃寒所致呕吐哕。', '-', '-', '升麻', '[\'丁香\', \'茱萸\', \'钱草蔻\', \'参钱\', \'甘草\', \'分耆\', \'柴胡\', \'升麻\', \'分柏钱\', \'陈皮分\', \'钱分\', \'苍术\']');
INSERT INTO `prescript` VALUES (59724, '22385', '举斑汤', '白芍药1钱，当归1钱，升麻5分，柴胡7分，白芷7分，穿山甲2钱，水姜1片。', '水煎，温服。如下后斑毒隐伏，反见循衣撮空，脉微者，加人参3钱，得补发出者不死。', '《温热暑疫全书》卷四', '托里举斑。主疫气留血分，里气壅闭，不下则斑不出，出则毒邪从外解矣。如下后斑渐出，更不可下，设有下证，宜少与承气缓服。倘大下则元气不振，斑毒内陷则危，宜此方。斑出不透而热不退。', '-', '-', '升麻', '[\'芍药\', \'当归\', \'升麻\', \'柴胡\', \'白芷\', \'穿山甲\', \'钱水\', \'姜片\']');
INSERT INTO `prescript` VALUES (59725, '22374', '补中治湿汤', '人参1钱，白术1钱，苍术7分，陈皮7分，赤茯苓7分，麦门冬7分，木通7分，当归7分，黄芩5分，厚朴3分，升麻3分。', '水煎服。', '《东医宝鉴》卷六引《医林》', '补中行湿。主水病。', '-', '-', '升麻', '[\'人参\', \'白术\', \'苍术\', \'陈皮分\', \'茯苓\', \'麦门冬\', \'木通\', \'当归\', \'黄芩\', \'厚朴\', \'升麻\']');
INSERT INTO `prescript` VALUES (59726, '22330', '补中参附汤', '黄耆6钱，白术6钱，广皮2钱，升麻2钱，柴胡2钱，泡参2两，秦归2钱，炙甘草2钱，肉桂1钱，附片3钱。', '水煎，空腹温服。手足温而兼漏下黑血如衃者，去桂、附，加阿胶珠3钱、乌贼骨1两、姜灰2钱。', '《中医妇科治疗学》', '补正。主瘀积日久，正虚邪实，身体羸弱，饮食不思，头晕目眩，神疲懒言，气短下陷，溲清便溏，甚或四肢不温，舌淡苔少，脉浮虚而涩。', '-', '-', '升麻', '[\'黄耆\', \'白术\', \'钱广皮\', \'升麻\', \'柴胡\', \'钱泡\', \'参两\', \'秦归钱\', \'甘草\', \'肉桂\', \'附片\']');
INSERT INTO `prescript` VALUES (59727, '22096', '拨云汤', '黄耆1分，细辛5分，生姜5分，葛根5分，川芎5分，柴胡7分，荆芥穗5钱，藁本5钱，生甘草5钱，升麻5钱，当归身5钱，知母5钱，羌活1钱5分，防风1钱5分，黄柏1钱5分。', '都作1服，水2盏，煎至1盏，去滓，食后热服。', '《兰室秘藏》卷上', '寒水翳，寒膜遮睛，隐涩难开，两目紧缩而无疼痛，两手寸脉细紧，按之洪大无力，呵欠，善悲健忘，嚏喷眵泪，时自泪下，面赤而白，能食不大便，小便数而欠，气上而喘。', '-', '-', '升麻', '[\'黄耆\', \'细辛\', \'生姜\', \'葛根\', \'川芎\', \'柴胡\', \'荆芥\', \'藁本\', \'生甘草\', \'升麻\', \'当归\', \'身钱\', \'知母\', \'羌活\', \'钱分\', \'防风\', \'钱分\', \'黄柏\', \'钱分\']');
INSERT INTO `prescript` VALUES (59728, '22062', '虎睛丸', '虎睛1对（细研），牛黄1分，麝香1分（细研），川升麻半两，钩藤半两，甘草半两（炙微赤，锉），犀角屑半两，天竹黄2分（细研），栀子仁半两，川大黄1两（锉碎，微炒），蚱蝉3枚（去翅足，微炒），蜣螂3枚（去翅足，微炒），蛇蜕皮5寸（烧灰）。', '《圣济总录》有桂，无甘草、天竺黄。', '《圣惠》卷八十五', '小儿惊痫邪气，皮肉壮热，呕吐心烦，不得安睡。小儿下痢五色。', '-', '-', '升麻', '[\'虎睛\', \'细研\', \'牛黄\', \'麝香\', \'分细\', \'研川\', \'升麻\', \'半两\', \'钩藤\', \'半两\', \'甘草\', \'半两\', \'炙微\', \'犀角\', \'半两\', \'天竹黄\', \'分细研\', \'栀子\', \'半两\', \'大黄\', \'蚱蝉\', \'足微\', \'蜣螂\', \'足微\', \'蜕皮\', \'烧灰\']');
INSERT INTO `prescript` VALUES (59729, '22048', '参耆汤', '人参1钱，黄耆（蜜水炒）1钱，当归1钱，生地黄1钱，白术（去芦）1钱，芍药（炒）1钱，茯苓（去皮）1钱，升麻5分，桔梗5分，陈皮5分，甘草（炙）3分。', '加生姜、大枣，水煎，食煎服。肺脏虚寒，加干姜（炒）5分。', '《回春》卷四', '泻痢久虚，或老人气血虚惫，或产妇用力过度，致患脱肛，以及小儿脱肛。', '-', '-', '升麻', '[\'人参\', \'黄耆\', \'蜜水\', \'当归\', \'生地黄\', \'白术\', \'芦钱\', \'芍药\', \'茯苓\', \'去皮\', \'升麻\', \'桔梗\', \'陈皮分\', \'甘草\', \'炙分\']');
INSERT INTO `prescript` VALUES (59730, '22004', '参耆补脾汤', '人参2钱，白术2钱，黄耆（炒）2钱5分，茯苓1钱，陈皮1钱，当归1钱，升麻3分，麦门冬7分，五味子4分，桔梗6分，甘草（炙）5分。', '上加生姜、大枣，水煎服。', '《外科枢要》卷四', '补脾土以生肺金。主肺疽，脾气亏损，久咳吐脓涎，或中满不食。肺痈。', '-', '-', '升麻', '[\'人参\', \'白术\', \'黄耆\', \'钱分\', \'茯苓\', \'陈皮\', \'当归\', \'升麻\', \'麦门冬\', \'五味子\', \'桔梗\', \'甘草\', \'炙分\']');
INSERT INTO `prescript` VALUES (59731, '21939', '赤茯苓散', '赤茯苓1两，桑寄生1两，知母1两，百合1两，麦门冬（去心）1两，川升麻1两，人参（去芦头）1两，柴胡（去苗）1两，甘草半两（炙微赤，锉）。', '每服4钱，以水一中盏，加甜竹茹1分，生姜半分，薤白7寸，煎至6分，去滓温服，不拘时候。', '《圣惠》卷七十四', '妊娠心烦，头项疼痛，不思饮食，手足多热。', '-', '-', '升麻', '[\'茯苓\', \'桑寄生\', \'知母\', \'百合\', \'麦门冬\', \'去心\', \'两川\', \'升麻\', \'人参\', \'芦头\', \'柴胡\', \'甘草\', \'半两\', \'炙微\']');
