INSERT INTO `prescript` VALUES (59734, '21850', '党参膏', '党参（去芦）480两，黄耆320两，升麻40两，桂圆肉80两，生地160两，熟地160两，当归160两，紫河车10具。', '每服3-5钱，开水冲服，每日2次。', '《北京市中药成方选集》', '大补气血，健脾养胃。主气血亏虚，脾胃虚弱，肢体酸软，精神疲倦。', '-', '-', '升麻', '[\'党参\', \'去芦\', \'黄耆\', \'升麻\', \'桂圆肉\', \'生地\', \'熟地\', \'当归\', \'紫河车\']');
INSERT INTO `prescript` VALUES (59735, '21764', '卷柏散', '卷柏1两，荆芥穗1两，川乌1两，大艾叶1两，升麻（去下根节）1两，露蜂房1两，晚蚕沙1两，藁本头1两。', '每服2两，水3大碗，煮取一半，滤去滓，先熏后洗，疮安即止。', '《普济方》卷三○一引孟诜方', '阴湿生疮，出汗痒甚。', '-', '-', '升麻', '[\'卷柏\', \'荆芥\', \'川乌\', \'两大\', \'艾叶\', \'升麻\', \'根节\', \'露蜂房\', \'两晚\', \'蚕沙\', \'藁本\']');
INSERT INTO `prescript` VALUES (59736, '21735', '参术柴苓汤', '人参1钱，白术1钱，茯苓1钱，陈皮1钱，柴胡7分，升麻7分，山栀（炒）8分，钩藤钩1钱，甘草（炒）5分。', '每次1-2钱，加生姜、大枣，水煎服。', '《保婴撮要》卷三', '肝经风热，脾土受克，其证善怒，睡中抽搐，遍身作痒，饮食少思；或疮疡脾气虚弱，肝气内动，肢体抽动。', '-', '-', '升麻', '[\'人参\', \'白术\', \'茯苓\', \'陈皮\', \'柴胡\', \'升麻\', \'分山\', \'炒分\', \'钩藤\', \'甘草\', \'炒分\']');
INSERT INTO `prescript` VALUES (59737, '21718', '固齿茯苓散', '龙骨1两，寒水石（烧熟）1两，升麻1两，香白芷1两，茯苓1两，细辛3钱，青盐3钱，石膏4两，麝香半钱，石燕子大者半对（火烧醋淬7次）。', '每日早晨以指蘸药擦牙后，用温水嗽口吐出。', '《丹溪心法附余》卷十二引《应验方》', '牢牙固齿，固密不生疳疾。主', '-', '-', '升麻', '[\'龙骨\', \'寒水石\', \'烧熟\', \'升麻\', \'两香\', \'白芷\', \'茯苓\', \'细辛\', \'青盐\', \'石膏\', \'麝香\', \'半钱石\', \'燕子\', \'大者\', \'火烧\', \'淬次\']');
INSERT INTO `prescript` VALUES (59738, '21712', '饭灰方', '制厚朴8两，焦茅术6两，制半夏6两，公丁香6两（忌火），白茯苓12两，小青皮6两，广藿香6两，新会皮16两，六神曲16两，黑楂肉16两，瓜蒌仁5两，鸡内金100两（不落水者），广木香4两（忌火），陈黄米150两（炒黑，另磨粉拌和），桂枝6两，防风6两，葛根6两，荆芥6两，枳实6两，苏叶5两，桔梗5两，升麻4两，川芎4两，独活4两，槟榔6两，麦芽16两，羌活4两，炮姜12两，秦艽4两，薄荷6两。', '以开水送下。', '《良方集腋》卷下', '消导运化。主大人、小儿风寒食积，头痛发热，大小便闭不畅。', '此药须藏干燥处，不可着湿，否则有霉变之患。', '-', '升麻', '[\'厚朴\', \'两焦\', \'茅术\', \'两制\', \'半夏\', \'两公\', \'丁香\', \'两忌火\', \'白茯苓\', \'两小\', \'青皮\', \'广藿香\', \'两新会\', \'两六\', \'神曲\', \'两黑\', \'两瓜\', \'鸡内金\', \'落水者\', \'木香\', \'两忌\', \'黄米\', \'磨粉\', \'拌和\', \'桂枝\', \'防风\', \'葛根\', \'荆芥\', \'枳实\', \'两苏叶\', \'桔梗\', \'升麻\', \'川芎\', \'独活\', \'槟榔\', \'麦芽\', \'羌活\', \'两炮\', \'秦艽\', \'薄荷\']');
INSERT INTO `prescript` VALUES (59739, '21562', '桂枝石膏汤', '桂枝2两，黄芩2两，甘草2两，升麻3两，葛根3两，生姜3两，芍药6两，石膏8两，栀子2-7枚。', '上(口父)咀。以水9升，煮取2升7合，分2次服，相去十里久。若前2服讫，即得汗后，服即停；不得汗，更进1服，得汗即止。不得汗者，明日去栀子，加麻黄2两，足水2升，再依方服。', '方出《千金》卷九，名见《活人书》卷十七', '伤寒忽发肿，或着四肢，或在胸背，虚肿浮如吹状，亦着头面唇口颈项，剧者偏着脚胫外如轴大而不痛不赤，着四肢者乃硬不遂，与五香麻黄汤不愈，阳气犹在经络，未入脏腑，脉势仍数者。', '-', '-', '升麻', '[\'桂枝\', \'黄芩\', \'甘草\', \'升麻\', \'葛根\', \'生姜\', \'芍药\', \'石膏\', \'栀子\']');
INSERT INTO `prescript` VALUES (59740, '21511', '参耆汤', '人参3钱，黄耆4钱，薄荷7分，黄连1钱，连翘7分，防风7分，升麻1钱。', '上(口父)咀。水煎，食前服。外以鳖头骨、五倍子焙干为末托之。', '《杏苑》卷七引洁古方', '气虚不能拘摄湿热，以致下流脱肛者。', '-', '-', '升麻', '[\'人参\', \'黄耆\', \'薄荷\', \'黄连\', \'连翘\', \'防风\', \'升麻\']');
INSERT INTO `prescript` VALUES (59741, '21487', '枫香散', '枫香半两，景天花半两，荠苨半两，贝母（煨令微黄）半两，甘草（炙微赤，锉）半两，天麻半两，防风（去芦头）半两，细辛半两，蔓荆子半两，甘菊花半两，羌活半两，川升麻半两，藁本半两，白鲜皮半两，荷叶半两，紫菀（洗去苗土）半两，石膏（细研）半两，枳实（麸炒，炙微黄）半两。', '每服1钱，温浆水调下，不拘时候。', '《圣惠》卷六', '肺脏风毒壅滞，皮肤及面上皶疱，或如麻豆，苦痒，搔之即赤痛，或破为疮。', '-', '-', '升麻', '[\'枫香\', \'半两\', \'天花\', \'半两\', \'半两\', \'贝母\', \'微黄\', \'半两\', \'甘草\', \'炙微\', \'半两\', \'天麻\', \'半两\', \'防风\', \'芦头\', \'半两\', \'细辛\', \'半两\', \'蔓荆子\', \'半两\', \'甘菊花\', \'半两\', \'羌活\', \'半两\', \'升麻\', \'半两\', \'藁本\', \'半两\', \'白鲜皮\', \'半两\', \'荷叶\', \'半两\', \'紫菀\', \'苗土\', \'半两\', \'石膏\', \'细研\', \'半两\', \'枳实\', \'微黄\', \'半两\']');
INSERT INTO `prescript` VALUES (59742, '21283', '麦门冬汤', '麦门冬（去心）2两，升麻3两，葛根3两，丁香1两半，零陵香1两，藿香1两。', '上（口父）咀。以水7升，煮取2升5合，分3服，1日令尽。', '《千金翼》卷二十二', '痈肿始觉，其肿五色，并为发背，痛欲死，肿上加灸不愈，腹内虚闷。', '-', '-', '升麻', '[\'麦门冬\', \'升麻\', \'葛根\', \'丁香\', \'两半\', \'零陵\', \'藿香\']');
INSERT INTO `prescript` VALUES (59743, '21281', '茯苓石脂汤', '茯苓3钱，丹皮3钱，桂枝3钱，芍药4钱，干姜2钱（炒），甘草2钱，赤石脂3钱，升麻1钱。', '煎大半杯，温服。肛热，加黄连；木燥，加阿胶。', '《四圣心源》卷十', '痔漏肿痛下血。', '-', '-', '升麻', '[\'茯苓\', \'丹皮\', \'桂枝\', \'芍药\', \'干姜\', \'甘草\', \'赤石脂\', \'升麻\']');
INSERT INTO `prescript` VALUES (59744, '21149', '连翘丸', '连翘1两，川大黄1两（锉碎，微炒），沉香1两，黛陆香1两，黄耆1两（锉），牛蒡子1两（微炒），枳壳1两（麸炒微黄，去瓤），赤芍药3分，玄参3分，川升麻3分，羌活3分，皂荚子仁49个（炒黄焦），占斯3分，芎3分，黄芩3分，红盐1分（波斯者）。', '每服30丸，食后以温酒送下。', '《圣惠》卷六十六', '瘰疬结肿不散，欲成脓，致寒热不退。', '-', '-', '升麻', '[\'连翘\', \'两川\', \'大黄\', \'沉香\', \'两黛陆\', \'黄耆\', \'牛蒡子\', \'两微\', \'枳壳\', \'两麸\', \'微黄\', \'芍药\', \'玄参\', \'分川\', \'升麻\', \'羌活\', \'皂荚\', \'子仁个\', \'黄焦\', \'占斯分\', \'黄芩\', \'分红\', \'盐分\', \'波斯\']');
INSERT INTO `prescript` VALUES (59745, '21112', '沉香汤', '沉香（锉）1两，木香1两，射干1两，防风（去叉）1两，升麻1两，甘草（炙）1两，当归（切，焙）1两，黄芩（去黑心）1两，熏陆香1两，藿香叶1两，鸡舌香1两，独活（去芦头）3两，麻黄（去根节，先煎，掠去沫，焙）3分，大黄（锉，生用）2两。', '每服3钱匕，水1盏，煎至6分，去滓，食后温服，日3次。', '《圣济总录》卷一二三', '咽喉肿痛不得语，卒中风毒，入于喉间，舌强，头面身体疼痛，咽喉闭塞，气欲绝者。', '-', '-', '升麻', '[\'沉香\', \'木香\', \'射干\', \'防风\', \'升麻\', \'甘草\', \'当归\', \'切焙\', \'黄芩\', \'黑心\', \'两熏\', \'陆香\', \'藿香\', \'叶两鸡\', \'舌香\', \'独活\', \'芦头\', \'麻黄\', \'根节\', \'掠去\', \'焙分\', \'大黄\', \'生用\']');
INSERT INTO `prescript` VALUES (59746, '21086', '补气汤', '柴胡2分，升麻3分，黄耆8分，当归身2钱，炙甘草4钱，红花少许。', '《医学纲目》有细辛少许，麻黄（炒）、苦丁香各半钱，无红花。', '《兰室秘藏》卷中', '年少时气弱，常于气海、三里灸之，节次约50-70壮，至年老添热厥头痛，虽冬天大寒，犹喜寒风，其头痛则愈。微来暖处，或见烟火，其痛复作，5-7年不愈者。服清上泻火汤后用此方。', '-', '-', '升麻', '[\'柴胡\', \'升麻\', \'分黄\', \'耆分\', \'当归\', \'身钱\', \'甘草\', \'红花\', \'少许\']');
INSERT INTO `prescript` VALUES (59747, '21083', '芦根散', '芦根1两（锉），连翘1两，玄参1两，射干1两，川升麻1两，栀子仁1两，赤芍药1两，羚羊角屑1两，寒水石2两，甘草3分（生，锉），生干地黄2两。', '每服4钱，以水1中盏，煎至6分，去滓温服，不拘时候。', '《圣惠》卷六十二', '一切痈疖，身体烦躁，热渴疼痛。', '-', '-', '升麻', '[\'芦根\', \'连翘\', \'玄参\', \'射干\', \'两川\', \'升麻\', \'栀子\', \'芍药\', \'羚羊角\', \'寒水石\', \'甘草\', \'分生\', \'生干\', \'地黄\']');
INSERT INTO `prescript` VALUES (59748, '21061', '洞虚丹', '藁本1两5钱，天麻1两5钱，川芎1两5钱，细辛1两5钱，牛膝3两，羌活3两，大枫子4两，蝉壳2两，胡麻2两，防风2两，独活2两，僵蚕2两，荆芥2两，苏木2两，风藤2两，石膏2两，蒺藜2两，山栀2两，芍药2两，菖蒲2两，石蚕2两，黄芩2两，连翘2两，草乌2两，紫萍2两，升麻2两，红花2两，麻黄2两，白芷2两，石斛2两，当归2两，威灵仙2两。', '每服100丸，用羊踯躅草根1斤4两，打碎，以酒20斤煮去滓，取1杯送下。', '《解围元薮》卷三', '恶风，麻木走注抽痛。', '忌食盐物。', '-', '升麻', '[\'藁本\', \'天麻\', \'川芎\', \'细辛\', \'牛膝\', \'羌活\', \'两大枫子\', \'两蝉壳\', \'胡麻\', \'防风\', \'独活\', \'僵蚕\', \'荆芥\', \'苏木\', \'两风藤\', \'石膏\', \'蒺藜\', \'两山\', \'芍药\', \'菖蒲\', \'石蚕\', \'黄芩\', \'连翘\', \'草乌\', \'紫萍\', \'升麻\', \'红花\', \'麻黄\', \'白芷\', \'石斛\', \'当归\', \'威灵仙\']');
INSERT INTO `prescript` VALUES (59749, '20994', '姜附御寒汤', '干姜（炮）1钱2分，半夏（汤洗）5分，柴胡（去苗）1钱，防风（去芦）半钱，羌活1钱，藁本（去土）8分，人参（去芦）半钱，白葵花5朵（去心萼），甘草（炙）8分，升麻7分，郁李仁（汤浸，去皮尖）半钱，当归身6分（酒制），桃仁半钱（汤浸，去皮尖，与郁李仁研如泥，入正药），黑附子（炮，去皮脐）4钱。', '上（口父）咀，都作1服。水5大盏，煎至3盏，入黄耆1钱，橘皮5分，草豆蔻1钱，再煎至2盏，再入酒制黄柏3分，酒制黄连3分，枳壳3分，酒地黄2分（此4味锉碎，预1日先用新水多半盏浸1宿），蔓荆子2分（亦预先1日用新水各另浸），将前正药去滓，入此3味，再上火同煎至1盏，去滓，空心热服之，待少时以美膳压之。', '《东垣试效方》卷二', '中气不足，遇冬天寒气客于脾胃之间，相引两胁缩急而痛，善嚏，鼻中流浊涕不止，不闻香臭，咳嗽脑痛，上热如火，下寒如冰，头时作阵痛，或暴痛，两目中流火，视物(目荒)(目荒)然，或耳鸣耳聋，喜晴明，恶阴寒，夜不得安卧，胸中痰涎，膈咽不通，饮食失味，口中沃沃，牙齿动摇，不能嚼物，腰脐间及尻肾膝足胻冷，阴汗自出，行步失力，风痹麻木，小便数，气短喘喝，少气不足以息，卒遗失无度。妇人白带，阴户中大痛，上牵心而痛，黧黑失色；男子控睾而痛，牵心腹阴阴而痛，面如赭色，食少，大小便不调，烦心，霍乱，逆气，里急，而腹皮白或黑，下气', '忌肉汤，宜食肉。', '-', '升麻', '[\'干姜\', \'炮钱分\', \'半夏\', \'洗分\', \'柴胡\', \'苗钱\', \'防风\', \'芦半钱\', \'羌活\', \'藁本\', \'土分\', \'人参\', \'芦半钱\', \'葵花\', \'心萼\', \'甘草\', \'炙分\', \'升麻\', \'郁李仁\', \'汤浸\', \'皮尖\', \'半钱\', \'当归\', \'身分\', \'酒制\', \'桃仁\', \'半钱\', \'浸去\', \'皮尖\', \'郁李仁\', \'研如\', \'泥入\', \'正药\', \'附子\', \'去皮\']');
INSERT INTO `prescript` VALUES (59750, '20973', '金不换神仙膏', '川芎5钱，白芷5钱，生地5钱，熟地5钱，当归5钱，白术5钱，苍术5钱，陈皮5钱，香附5钱，枳壳5钱，乌药5钱，半夏5钱，青皮5钱，细辛5钱，知母5钱，贝母5钱，杏仁5钱，桑白皮5钱，黄连5钱，黄芩5钱，黄柏5钱，栀子5钱，大黄5钱，柴胡5钱，薄荷5钱，赤芍5钱，木通5钱，桃仁5钱，玄参5钱，猪苓5钱，泽泻5钱，桔梗5钱，前胡5钱，升麻5钱，麻黄5钱，牛膝5钱，杜仲5钱，山药5钱，远志5钱，续断5钱，良姜5钱，何首乌5钱，甘草5钱，连翘5钱，藁本5钱，茵陈5钱，地榆5钱，防风5钱，荆芥5钱，羌活5钱，独活5', '临用时加细药：乳香、没药、血竭、轻粉、朝脑（即樟脑）、片脑、麝香、龙骨、海螵蛸、赤石脂，上为细末，瓷器内收贮，临摊膏药时掺上。五劳七伤，遍身筋骨疼痛，腰脚软弱，贴二膏盲穴、两肾俞穴、两足三里穴；痰喘气急，咳嗽，贴肺俞穴、华盖穴、膻中穴；左瘫右痪，手足麻木，贴两肩井穴、两曲池穴；男子遗精白浊，妇人赤白带下，月经不调，血山崩漏，贴两阴交穴、关元穴；赤白痢疾，贴丹田穴；小肠气、疝气，贴膀胱穴；疟疾，男子贴左肩，女子贴右肩；偏正头风。贴风门穴；腰痛，贴命门穴；心气疼痛，贴中脘穴；走气，贴二章门穴；寒湿脚气，贴两三', '《古今医鉴》卷十六', '生肌定痛，调血祛风湿。主劳伤筋骨疼痛，痰喘咳嗽，左瘫右痪，手足麻木，赤白痢疾，疝气，疟疾，偏正头风，心气疼痛，寒湿脚气，男子遗精白浊，女子赤白带下，一切无名肿毒，跌打损伤。', '-', '-', '升麻', '[\'川芎\', \'白芷\', \'生地\', \'熟地\', \'当归\', \'白术\', \'苍术\', \'陈皮\', \'香附\', \'枳壳\', \'乌药\', \'半夏\', \'青皮\', \'细辛\', \'知母\', \'贝母\', \'杏仁\', \'桑白皮\', \'黄连\', \'黄芩\', \'黄柏\', \'栀子\', \'大黄\', \'柴胡\', \'薄荷\', \'赤芍\', \'木通\', \'桃仁\', \'玄参\', \'猪苓\', \'泽泻\', \'桔梗\', \'钱前\', \'胡钱\', \'升麻\', \'麻黄\', \'牛膝\', \'杜仲\', \'山药\', \'远志\', \'续断\', \'钱良\', \'姜钱\', \'何首乌\', \'甘草\', \'连翘\', \'藁本\', \'茵陈\', \'钱地\', \'榆钱\', \'防风\', \'荆芥\', \'羌活\', \'独活\']');
INSERT INTO `prescript` VALUES (59751, '20971', '麦门冬丸', '麦门冬（去心，焙）1两，赤茯苓（去黑皮）1两，黄连（去须）1两，黄芩（去黑心）1两，石膏（煅）1两，萎蕤1两，人参1两，升麻1两，龙胆1两，栝楼根1两，枳壳（去瓤，麸炒）1两，生姜（切，焙）1两，枸杞根皮（洗，切）1两。', '每服30丸，粟米饮送下，不拘时候。', '《圣济总录》卷五十九', '久消渴。', '-', '-', '升麻', '[\'麦门冬\', \'茯苓\', \'黑皮\', \'黄连\', \'黄芩\', \'黑心\', \'石膏\', \'萎蕤\', \'人参\', \'升麻\', \'龙胆\', \'栝楼\', \'根两\', \'枳壳\', \'生姜\', \'切焙\', \'枸杞\', \'根皮\', \'洗切\']');
INSERT INTO `prescript` VALUES (59752, '20964', '连翘汤', '连翘3两，升麻3两，杏仁（去皮尖）3两，射干3两，防己3两，黄芩3两，大黄3两，芒消3两，柴胡3两，芍药4两，甘草（炙）4两。', '以水9升，煮取3升，分服。', '《外台》卷三十四引《集验方》', '妒乳乳痈；附骨疽。', '忌海藻，菘菜。', '-', '升麻', '[\'连翘\', \'升麻\', \'杏仁\', \'皮尖\', \'射干\', \'防己\', \'黄芩\', \'大黄\', \'两芒\', \'消两\', \'柴胡\', \'芍药\', \'甘草\']');
INSERT INTO `prescript` VALUES (59753, '20943', '拔疔膏', '野菊花1钱5分，山慈姑1钱5分，升麻（瓦炙）1钱5分，血竭1钱5分，天花粉1钱，七叶1枝花，紫花地丁3钱，木耳3钱，皂角刺（各瓦炙）3钱，朱砂（水飞，净）3钱，川贝母（去心）3钱，知母（瓦炙，或用黄酒煮透，焙干亦可）3钱，蟾酥3钱（酒化，不见火），生甘草5分，麝香5分，蓖麻子肉1两（去壳衣，捣烂用）。', '用时先将银针刺破疔根，入此膏少许，掩以膏药1对。周时疔自拔出矣。', '《经目屡验良方》', '疔疮。', '-', '-', '升麻', '[\'野菊花\', \'钱分\', \'山慈姑\', \'钱分\', \'升麻\', \'瓦炙钱分\', \'血竭\', \'钱分\', \'天花粉\', \'叶枝\', \'紫花地丁\', \'木耳\', \'皂角刺\', \'各瓦炙钱\', \'朱砂\', \'水飞净\', \'川贝母\', \'心钱\', \'知母\', \'黄酒\', \'焙干\', \'亦可\', \'蟾酥\', \'酒化\', \'不见\', \'生甘草\', \'麝香\', \'蓖麻子\', \'两去\', \'捣烂\']');
INSERT INTO `prescript` VALUES (59754, '20748', '大黄汤', '大黄（锉，炒）1两，栀子仁半两，犀角屑半两，栝楼根2两，升麻半两，黄芩（去黑心）半两，甘草（炙，锉）3分。', '每服5钱匕，用水1盏半，煎至1盏，滤去滓，空心、日午温服。', '《圣济总录》卷一八三', '疏利毒气。主乳石发动，痈疽发背。', '-', '-', '升麻', '[\'大黄\', \'栀子\', \'半两\', \'犀角\', \'半两\', \'栝楼\', \'根两\', \'升麻\', \'半两\', \'黄芩\', \'黑心\', \'半两\', \'甘草\']');
INSERT INTO `prescript` VALUES (59755, '20716', '和气饮', '干姜1分，葛根2钱，升麻2钱，熟大黄1钱半，枳壳1钱半，桔梗1钱，苍术1钱，白芍7分，甘草8分，当归4分，半夏4分，白芷4分，茯苓4分，小茴5分，川椒15粒。', '本方方名，《中国医学大辞典》引作“和气汤”。', '《杂病源流犀烛》卷二十七', '肾气上逆，先脊痛，继及背与肩。', '-', '-', '升麻', '[\'干姜\', \'葛根\', \'升麻\', \'钱熟\', \'大黄\', \'钱半\', \'枳壳\', \'钱半\', \'桔梗\', \'苍术\', \'白芍\', \'甘草\', \'当归\', \'半夏\', \'白芷\', \'茯苓\', \'分小茴分川椒粒\']');
INSERT INTO `prescript` VALUES (59756, '20714', '八物茜根汤', '茜根3两，升麻3两，犀角3两，桔梗1两，黄柏1两，黄芩1两，地榆4两，白蘘荷4两。', '茜根散（《圣惠》卷五十六）、茜根饮（《圣济总录》卷七十七）、茜根汤（《圣济总录》卷一四七）。', '方出《千金》卷二十四，名见《千金》卷十五', '中蛊毒，下血状如鸡肝，腹中搅痛难忍者。', '-', '-', '升麻', '[\'茜根\', \'升麻\', \'犀角\', \'桔梗\', \'黄柏\', \'黄芩\', \'地榆\', \'两白\', \'蘘荷\']');
INSERT INTO `prescript` VALUES (59757, '20709', '柏叶散', '柏叶半两，麻黄（去根节）半两，山栀子（去皮）半两，枳壳（去瓤，锉，麸炒）半两，羌活（去芦）半两，羊肝石半两，白蒺藜（炒去角）半两，升麻半两，子芩（去皮）半两，防风（去钗股）半两，牛蒡子（隔纸炒）半两，荆芥穗半两，茺蔚子半两，大黄（湿纸裹，甑上蒸）半两，苦参1两，乌蛇1条（酒浸，去皮骨，焙干）。', '每服2钱，温水调下，1日7-8服。', '《本事》卷三', '疠风。', '-', '-', '升麻', '[\'柏叶\', \'半两\', \'麻黄\', \'根节\', \'半两\', \'栀子\', \'去皮\', \'半两\', \'枳壳\', \'半两\', \'羌活\', \'去芦\', \'半两\', \'羊肝\', \'半两\', \'蒺藜\', \'半两\', \'升麻\', \'半两\', \'子芩\', \'去皮\', \'半两\', \'防风\', \'去钗股\', \'半两\', \'牛蒡子\', \'隔纸\', \'半两\', \'荆芥\', \'半两\', \'茺蔚子\', \'半两\', \'大黄\', \'湿纸\', \'半两\', \'苦参\', \'两乌蛇\', \'条酒\', \'去皮\', \'焙干\']');
INSERT INTO `prescript` VALUES (59758, '20696', '钩藤散', '钩藤半两，人参1分（去芦头），子芩1分，蚱蝉3枚（微炙），犀角屑1分，甘草半两（炙微赤，锉），川升麻半两，石膏半两，川大黄1分（锉碎，微炒）。', '每服1钱，以水1小盏，煎至5分，去滓，入竹沥半合，牛黄末1字，服之。', '《圣惠》卷八十五', '小儿壮热发痫，或时时四肢抽掣，多吐白沫。', '-', '-', '升麻', '[\'钩藤\', \'半两\', \'人参\', \'分去\', \'头子\', \'蚱蝉\', \'犀角\', \'甘草\', \'半两\', \'炙微\', \'锉川\', \'升麻\', \'半两\', \'石膏\', \'半两\', \'大黄\']');
INSERT INTO `prescript` VALUES (59759, '20695', '麦门冬丸', '麦门冬5两（去心），大黄1两，苦参1两，萎蕤1两，栀子（擘）1两，五加皮1两，黄芩1两，生犀屑1两，芍药1两，升麻1两，大青3分，甘草（炙）3分。', '每服15丸，食后以蜜水送下，渐加至20丸。', '《千金翼》卷二十二', '诸石发动，口干寒热，似鬼神病。', '-', '-', '升麻', '[\'麦门冬\', \'两去\', \'大黄\', \'苦参\', \'萎蕤\', \'栀子\', \'五加皮\', \'黄芩\', \'两生\', \'犀屑\', \'芍药\', \'升麻\', \'两大青分\', \'甘草\', \'炙分\']');
INSERT INTO `prescript` VALUES (59760, '20690', '胡黄连丸', '胡黄连1两，犀角（镑）1两，鳖甲（醋炙，去裙襕）1两，诃黎勒皮（半生半熟）1两，桔梗（锉炒）1两1分，升麻（锉）1两1分，地骨皮1两1分，知母（焙）1两1分，黄芩（去黑心）1两1分，甘草（炙、锉）3分，白茯苓（去黑皮）3分，人参3分，栝楼1个（大者），柴胡（去苗）1两半。', '每服20丸，食后以乌梅煎童子小便送下；如腹痛，用糯米饮下。', '《圣济总录》卷八十七', '劳热，骨节烦疼，心膈躁闷。', '-', '-', '升麻', '[\'胡黄连\', \'犀角\', \'鳖甲\', \'两诃黎勒皮\', \'半生半熟\', \'桔梗\', \'两分\', \'升麻\', \'两分\', \'地骨皮\', \'两分\', \'知母\', \'两分\', \'黄芩\', \'黑心\', \'两分\', \'甘草\', \'白茯苓\', \'黑皮\', \'人参\', \'栝楼\', \'个大者\', \'柴胡\', \'去苗\', \'两半\']');
INSERT INTO `prescript` VALUES (59761, '20646', '和荣散坚丸', '川芎1钱，白芍（酒炒）1钱，当归1钱，茯苓1钱，熟地1钱，陈皮1钱，桔梗1钱，香附1钱，白术（土炒）1钱，人参5钱，甘草（炙）5钱，海粉5钱，昆布5钱，贝母（去心）5钱，升麻3钱，红花3钱，夏枯草（熬汤，再加红蜜4两，再熬成膏）1斤。', '每服3钱，食远白滚水送下。身热，加黄芩、柴胡；自汗、盗汗，去升麻，倍人参，加黄耆；饮食无味，加藿香、砂仁；饮食不化，加山楂、麦芽；胸膈痞闷，加泽泻、木香；咳嗽，痰气不清，加杏仁、麦冬；口干作渴，加知母、五味子；睡眠不宁，加黄柏、远志、枣仁；惊悸健忘，加茯神、石菖蒲；有汗恶寒，加薄荷、半夏；无汗恶寒，加苍术、藿香；妇人经事不调，加延胡索、丹皮；腹胀不宽，加厚朴、大腹皮。', '《金鉴》卷六十四', '调和荣血，散坚开郁。主失荣证。多生于耳前后或肩项，初起状如痰核，推之不动，坚硬如石，皮色如常，日渐长大。', '-', '-', '升麻', '[\'川芎\', \'白芍\', \'当归\', \'茯苓\', \'熟地\', \'陈皮\', \'桔梗\', \'香附\', \'白术\', \'人参\', \'甘草\', \'海粉\', \'昆布\', \'贝母\', \'心钱\', \'升麻\', \'钱红\', \'花钱\', \'夏枯草\', \'熬汤\', \'加红蜜\', \'成膏斤\']');
INSERT INTO `prescript` VALUES (59762, '20627', '和血益气汤', '黄耆2钱，人参1钱5分，甘草1钱，苍术1钱，柴胡5分，升麻5分，木香4分，陈皮8分。', '上(口父)咀。水2钟，煎1钟，食远温服。有嗽，人参减去5分。', '《杏苑》卷五', '口唇干燥，小便数，舌上赤脉。', '-', '-', '升麻', '[\'黄耆\', \'人参\', \'钱分\', \'甘草\', \'苍术\', \'柴胡\', \'升麻\', \'木香\', \'陈皮分\']');
INSERT INTO `prescript` VALUES (59763, '20600', '沉香散', '沉香1两，麦门冬1两（去心），木香1两，川升麻1两，麻黄3两（去根节），川大黄1两。', '每服4钱，以水1中盏，煎至6分，去滓，不拘时候温服。', '《圣惠》卷六十二', '发背，肿如杏，或如鸡子者。', '-', '-', '升麻', '[\'沉香\', \'麦门冬\', \'两去\', \'木香\', \'两川\', \'升麻\', \'麻黄\', \'两去\', \'根节川\', \'大黄\']');
INSERT INTO `prescript` VALUES (59764, '20558', '虎睛丸', '虎睛1对（细研），朱砂半两（细研，水飞），露蜂房半两（微炙），麻黄半两（去根节），子芩半两，钩藤半两，防葵1两，川大黄1两（锉碎，微炒），龙齿1两（细研），栀子仁1两，银箔30片（细研），麝香1分（细研），羌活3分，柴胡3分（去苗），白鲜皮3分，川升麻3分，雷丸3分，沙参3分（去芦头），石膏1两（细研，水飞过），牛黄1分（细研），蚱蝉1两（去翅足，微炒或炙），蛇蜕皮1分（锉，微炒），天麻半两，甘草半两（炙微赤，锉）。', '丹砂丸（《圣济总录》卷一七一）。', '《圣惠》卷八十五', '小儿5岁至7岁，发癫痫，无时发动，口出白沫，遗失大小便不觉。', '-', '-', '升麻', '[\'虎睛\', \'细研\', \'朱砂\', \'半两\', \'细研\', \'水飞\', \'露蜂房\', \'半两\', \'麻黄\', \'半两\', \'节子\', \'半两\', \'钩藤\', \'半两\', \'防葵\', \'两川\', \'大黄\', \'龙齿\', \'两细研\', \'栀子\', \'仁两银\', \'箔片\', \'细研\', \'麝香\', \'分细研\', \'羌活\', \'柴胡\', \'分去\', \'白鲜皮\', \'分川\', \'升麻\', \'雷丸\', \'沙参\', \'分去\', \'芦头\', \'石膏\', \'两细\', \'研水\', \'飞过\', \'牛黄\', \'分细研\', \'蚱蝉\', \'两去\', \'足微\', \'蜕皮\', \'天麻\', \'半两\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (59765, '20514', '沉香散', '沉香1钱，麝香1钱，细辛半两，升麻2钱半，藁本2钱半，藿香叶2钱半，甘松2钱半，白芷2钱半，石膏4两，寒水石2两。', '沉醉香散（《普济方》卷七十）、沉香白牙散（《丹溪心法附余》卷十二）。', '《御药院方》卷九', '揩齿莹净令白。主口臭。', '-', '-', '升麻', '[\'沉香\', \'麝香\', \'细辛\', \'半两\', \'升麻\', \'钱半\', \'藁本\', \'钱半\', \'藿香\', \'叶钱\', \'半甘松\', \'白芷\', \'钱半\', \'石膏\', \'寒水石\']');
INSERT INTO `prescript` VALUES (59766, '20506', '麦门冬丸', '麦门冬5两（去心），干地黄3两，蜀升麻5两，黄芩5两，栝楼7两，苦参8两，人参3两，黄连5两，黄柏5两。', '每服20丸，以饮送下，1日2次。加至50-60丸。', '《外台》卷十一引《近效极要论》', '消渴，口舌干燥。', '忌芜荑、猪肉、冷水。', '-', '升麻', '[\'麦门冬\', \'两去\', \'心干\', \'地黄\', \'两蜀\', \'升麻\', \'黄芩\', \'栝楼\', \'苦参\', \'人参\', \'黄连\', \'黄柏\']');
INSERT INTO `prescript` VALUES (59767, '20480', '胡黄连散', '胡黄连1分，知母1分，鳖甲半两（涂醋，炙令黄，去裙襕），柴胡半两（去苗），地骨皮1分，黄芩1两，栀子仁1分，川升麻1分，犀角屑1分，甘草1分（炙微赤，锉），杏仁1分（汤浸，去皮尖仁双，麸炒微黄）。', '每服1钱，以水1小盏，煎至5分，去滓温服，不拘时候。', '《圣惠》卷八十八', '小儿骨热瘦瘁，心神烦躁，不得睡卧。', '-', '-', '升麻', '[\'胡黄连\', \'知母\', \'鳖甲\', \'半两\', \'涂醋\', \'炙令\', \'黄去\', \'柴胡\', \'半两\', \'去苗\', \'地骨皮\', \'黄芩\', \'栀子\', \'仁分川\', \'升麻\', \'犀角\', \'甘草\', \'分炙微\', \'杏仁\', \'分汤\', \'浸去\', \'皮尖\', \'仁双\', \'微黄\']');
INSERT INTO `prescript` VALUES (59768, '20413', '麦门冬丸', '麦门冬（去心，焙）1两，虎睛1对（微炙），龙齿（研）1两，金箔（研）100片，银箔（研）100片，石膏（研）1两，升麻1两，枳实（麸炒）1两，生姜（切，焙）1两，白茯苓（去黑皮）1两，人参（锉）1两，玄参3分，萎蕤（炒）3分，芍药3分，甘草（炙，锉）3分，远志（去心）3分，柏子仁（生用）半两，薤白（细切，焙干）半两，牛黄（别研）半两。', '每服20丸，煎地骨皮汤送下，日3夜1，不拘时候。', '《圣济总录》卷十五', '安魂定志。主风癫狂乱失心。', '-', '-', '升麻', '[\'麦门冬\', \'两虎睛\', \'龙齿\', \'研两\', \'金箔\', \'研片\', \'银箔\', \'研片\', \'石膏\', \'研两\', \'升麻\', \'枳实\', \'生姜\', \'切焙\', \'白茯苓\', \'黑皮\', \'人参\', \'玄参\', \'萎蕤\', \'炒分\', \'芍药\', \'甘草\', \'远志\', \'心分\', \'柏子仁\', \'生用\', \'半两\', \'薤白\', \'细切\', \'焙干\', \'半两\', \'牛黄\', \'别研\', \'半两\']');
INSERT INTO `prescript` VALUES (59769, '20409', '连翘散', '连翘子3分，独活（去芦头）3分，木香3分，射干3分，甘草（炙，锉）1两1分，桑寄生（锉）1两1分，升麻1两1分，鸡舌香1两1分，沉香1两1分，乳香（研）1两1分，大黄（锉，炒）1两1分，麝香（研）1分。', '每服5钱匕，水1盏半，煎至8分，入淡竹沥半合，去滓，空心温服。快利3-5行为度，未利再服。', '《圣济总录》卷一三一', '发背肿、痈疽，恶风结脓血。', '-', '-', '升麻', '[\'连翘\', \'子分\', \'独活\', \'芦头分\', \'木香\', \'射干\', \'甘草\', \'两分\', \'桑寄生\', \'两分\', \'升麻\', \'两分\', \'鸡舌香\', \'两分\', \'沉香\', \'两分\', \'乳香\', \'两分\', \'大黄\', \'两分\', \'麝香\', \'研分\']');
INSERT INTO `prescript` VALUES (59770, '20365', '理中汤', '人参1钱，炙甘草1钱，绿升麻1钱，漂白术2钱。', '加煨姜3片，大枣3个，水煎服。', '《幼幼集成》卷五', '痘已现形，而吐泻不止。', '-', '-', '升麻', '[\'人参\', \'甘草\', \'升麻\', \'漂白\']');
INSERT INTO `prescript` VALUES (59771, '20355', '败毒散', '升麻4分，干葛4分，紫苏4分，川芎4分，防风4分，荆芥4分，前胡6分，桔梗6分，枳壳6分，牛蒡2钱，连翘2钱，虫退3分，山楂1钱，木香3分，白芷5分，地骨皮5分（又方去干葛，加紫草）。', '-', '《种痘新书》卷四', '解毒定痛。主痘疮毒气壅盛而腹痛者，其痛稍缓，有作有止，频频叫痛，在脐以下痛，或连腰而痛，面赤唇紫，手足不冷。', '-', '-', '升麻', '[\'升麻\', \'分干葛分\', \'紫苏\', \'川芎\', \'防风\', \'荆芥\', \'分前\', \'胡分\', \'桔梗\', \'枳壳\', \'牛蒡\', \'连翘\', \'钱虫\', \'退分\', \'山楂\', \'木香\', \'白芷\', \'地骨皮\', \'方去\', \'干葛加\', \'紫草\']');
INSERT INTO `prescript` VALUES (59772, '20342', '刺蓟散', '刺蓟1两，川升麻1两，大青6分，紫苏茎叶1两，赤芍药1两半，犀角屑3分，川朴消1两，生干地黄1两，甘草3分（炙微赤，锉），子芩1两半。', '每服4钱，以水1中盏，煎至6分，去滓温服，不拘时候。', '《圣惠》卷十八', '热病吐血，并衄血不止，头面俱热。', '-', '-', '升麻', '[\'两川\', \'升麻\', \'两大青分\', \'紫苏\', \'芍药\', \'两半\', \'犀角\', \'分川朴\', \'消两生\', \'地黄\', \'甘草\', \'分炙微\', \'锉子\', \'两半\']');
INSERT INTO `prescript` VALUES (59773, '20314', '大乌犀散', '犀角（镑）1分，羚羊角（镑）1分，龙脑（研）1分，麝香（研）1分，雄黄（研）1分，熊胆（研）1分，牛黄（研）1分，乳香（研）1分，阿魏（研）1分，丹砂（研）1分，水银（与丹砂同研，慢火搅匀，结砂子，研）1分，干蝎（酒炒）1分，猪牙皂荚（酥炙，去皮、子）1分，乌头（炮裂，去皮脐）1分，附子（炮裂，去皮脐）1分，白附子（炮）1分，干姜（炮）1分，天麻1两，升麻1两，独活（去芦头）1两，狗脊（去土）1两，细辛（去苗叶）1两，秦艽（去苗土）1两，芎1两，杜仲（去粗皮，切，炒）1两，当归（切，焙）1两，厚朴（去粗', '每服1钱半匕，加至2钱匕，豆淋酒调下，生姜酒投。甚者用米醋与水同煎服之。', '《圣济总录》卷六', '破伤中风。', '-', '-', '升麻', '[\'犀角\', \'羚羊角\', \'龙脑\', \'研分\', \'麝香\', \'研分\', \'雄黄\', \'研分\', \'熊胆\', \'研分\', \'牛黄\', \'研分\', \'乳香\', \'研分\', \'阿魏\', \'研分\', \'丹砂\', \'研分\', \'水银\', \'丹砂\', \'同研\', \'慢火\', \'搅匀\', \'砂子\', \'研分\', \'干蝎酒\', \'炒分\', \'猪牙皂\', \'皮子\', \'乌头\', \'炮裂\', \'去皮\', \'脐分\', \'附子\', \'炮裂\', \'去皮\', \'脐分\', \'白附子\', \'炮分\', \'干姜\', \'炮分\', \'天麻\', \'升麻\', \'独活\', \'芦头\', \'狗脊\', \'细辛\', \'苗叶\', \'秦艽\', \'苗土\', \'两芎\', \'杜仲\', \'粗皮切\', \'当归\', \'切焙\', \'厚朴\']');
INSERT INTO `prescript` VALUES (59774, '20297', '参术饮', '人参3钱，黄耆2钱，白术1钱，甘草（炙）5分，白豆蔻8分，陈皮7分，木香1钱，地榆1钱，升麻1钱，粟壳8分，当归（全用）1钱，砂仁7分，泽泻8分。', '上(口父)咀。水煎，温服。', '《杏苑》卷四', '补中益气，温养脾胃。主中气下陷，脾胃虚败，久痢不止，滑脱不固，四肢倦怠，精神短少，饮食不消。', '-', '-', '升麻', '[\'人参\', \'黄耆\', \'白术\', \'甘草\', \'炙分\', \'白豆蔻\', \'陈皮分\', \'木香\', \'钱地\', \'榆钱\', \'升麻\', \'钱粟壳\', \'当归\', \'用钱\', \'砂仁\', \'泽泻\']');
INSERT INTO `prescript` VALUES (59775, '20287', '扶元宣解汤', '生耆1钱，当归1钱，升麻8分，柴胡8分，川芎4分，桔梗4分，陈皮4分，牛蒡7分，甘草3分，木通5分，防风5分。', '如头痛，加薄荷、藁本、白芷；如咳嗽；加半夏、炒芩；如腹痛，加厚朴、香附、木香。', '《种痘新书》卷四', '痘疮外感风寒，憎寒壮热，而体性怯弱者。', '-', '-', '升麻', '[\'生耆\', \'当归\', \'升麻\', \'柴胡\', \'川芎\', \'桔梗\', \'陈皮分\', \'牛蒡\', \'甘草\', \'木通\', \'防风\']');
INSERT INTO `prescript` VALUES (59776, '20243', '和血息火汤', '升麻1钱，当归5钱，黄耆3钱，防风3分，秦艽1钱，白芷5分，桂枝3分，天花粉2钱，甘草1钱，麦冬3钱，玄参5钱。', '水煎服。1剂轻，2剂而喎斜正矣。', '《辨证录》卷二', '和气血，解火。主中风。由入室向火，一边热而一边寒，遂致左颊出汗，偶尔出户，为贼风所袭，觉右颊拘急，口喎于右。', '-', '-', '升麻', '[\'升麻\', \'当归\', \'黄耆\', \'防风\', \'秦艽\', \'白芷\', \'桂枝\', \'天花粉\', \'甘草\', \'麦冬\', \'玄参\']');
INSERT INTO `prescript` VALUES (59777, '20217', '麦门冬汤', '麦门冬（去心，焙）3分，赤茯苓（去黑皮）3分，生干地黄（焙）3分，石膏（碎）3分，升麻3分，人参3分，知母（焙）3分，芎3分，山栀子仁3分，小麦半升，黄耆（炙，锉）1两，甘草（炙）1两，枳实（麸炒）1两，芍药1两，黄芩（去黑心）1两半，前胡（去芦头）1两半。', '每服5钱匕，水2盏，加生姜5片，大枣2枚（擘破），竹叶10片，同煎至1盏，去滓温服，不拘时候。', '《圣济总录》卷一八三', '乳石发动，痈疽发背热渴。', '-', '-', '升麻', '[\'麦门冬\', \'心焙分\', \'茯苓\', \'黑皮\', \'分生\', \'地黄\', \'焙分\', \'石膏\', \'碎分\', \'升麻\', \'人参\', \'知母\', \'焙分\', \'分山\', \'栀子\', \'仁分\', \'小麦\', \'半升\', \'黄耆\', \'甘草\', \'枳实\', \'芍药\', \'黄芩\', \'黑心\', \'两半\', \'胡去\', \'芦头\', \'两半\']');
INSERT INTO `prescript` VALUES (59778, '20118', '丁香丸', '麝香1分（细研），沉香1分，白檀香1分，龙脑1分（细研），煎香半两，鸡舌香半两，丁香半两，黄熟香半两，鸡骨香半两，甘松香半两，川升麻3分，郁金香3分。', '每日空心以盐汤嚼下3丸。留1半散不和，每日揩齿了，以散子重揩咽津。', '《圣惠》卷四十', '令人遍身俱香。主', '-', '-', '升麻', '[\'麝香\', \'分细研\', \'沉香\', \'分白\', \'檀香\', \'龙脑\', \'分细研\', \'半两\', \'鸡舌香\', \'半两\', \'丁香\', \'半两\', \'黄熟\', \'半两\', \'鸡骨\', \'半两\', \'松香\', \'半两\', \'升麻\', \'郁金香\']');
INSERT INTO `prescript` VALUES (59779, '20099', '茯神散', '茯神1两，木通1两（锉），黄连1两（去须），麦门冬1两（去心），川升麻1两，知母1两，子芩1两，川芒消1两，羚羊角屑3分。', '羚羊角汤（《圣济总录》卷四十三）。', '《圣惠》卷四', '心实热，口干烦渴，眼卧不安。', '-', '-', '升麻', '[\'茯神\', \'木通\', \'黄连\', \'两去\', \'麦门冬\', \'两去\', \'心川\', \'升麻\', \'知母\', \'两子\', \'两川芒\', \'消两\', \'羚羊角\']');
INSERT INTO `prescript` VALUES (59780, '20096', '金牙酒', '金牙（碎，绵裹）4两，细辛4两，草（炙）4两，干地黄4两，干姜4两，防风4两，附子（炮）4两，蛇床子4两，蒴藋4两，升麻4两，人参3两，独活1斤，牛膝5两，石斛5两。', '上以酒4斗渍之7日，饮2-3合，稍加之，以知为度。', '《医心方》卷八引唐临方', '脚气屈弱，或不能语者。', '-', '-', '升麻', '[\'金牙\', \'碎绵裹\', \'细辛\', \'两草\', \'两干\', \'地黄\', \'干姜\', \'防风\', \'附子\', \'蛇床子\', \'升麻\', \'人参\', \'独活\', \'牛膝\', \'石斛\']');
INSERT INTO `prescript` VALUES (59781, '20082', '连翘汤', '连翘1两半，玄参1两半，木香1两半，昆布（洗去成，焙）1两半，枳壳（去瓤，麸炒）1两半，犀角（镑）1两半，柴胡（去苗）1两，甘草（炙，锉）1两，木通（锉）1两，芍药1两，黄芩（去黑心）1两，沉香（锉）1两，当归（切，焙）1两，升麻1两。', '每服5钱匕，水2盏，入生姜1枣大（拍碎），柳枝2寸长1握，细锉，煎至1盏，去滓温服，空心、日午、夜卧各1服。', '《圣济总录》卷一二六', '男女长幼瘰疬结核，在项腋下，项强背痛。', '-', '-', '升麻', '[\'连翘\', \'两半\', \'玄参\', \'两半\', \'木香\', \'两半\', \'昆布\', \'洗去\', \'两半\', \'枳壳\', \'两半\', \'犀角\', \'两半\', \'柴胡\', \'甘草\', \'木通\', \'芍药\', \'黄芩\', \'黑心\', \'沉香\', \'当归\', \'切焙\', \'升麻\']');
INSERT INTO `prescript` VALUES (59782, '20036', '败毒和中散', '连翘7分（去心，研碎），防风7分，荆芥7分，黄连1分（不用酒炒），牛蒡子7分（拣净，炒香，研碎），桔梗6分，陈枳壳5分（炒），前胡1钱，紫草茸5分（酒洗，研末），小川芎7分，升麻3分，木通7分，蝉蜕12只，如有闰月13只（洗净，晒，去头足），麦门冬1钱（去心），甘草2分（生，去皮）。', '加生姜1片，灯心50寸，水煎服。若毒火内盛，大便结燥，2-3日不解，小便短涩又黄，加酒炒大黄8分；毒火盛，加酒炒黄芩2分。', '《痘疹传心录》卷十九', '清热解毒疏利。主发热1日，即见痘苗，极痘稠密，大热蒸蒸，烦躁昏沉，不省人事，唇焦口渴。', '-', '-', '升麻', '[\'连翘\', \'分去\', \'研碎\', \'防风\', \'荆芥\', \'黄连\', \'不用\', \'牛蒡子\', \'分拣\', \'研碎\', \'桔梗\', \'分陈\', \'枳壳\', \'胡钱\', \'紫草茸\', \'分酒\', \'研末\', \'川芎\', \'升麻\', \'木通\', \'蝉蜕\', \'闰月\', \'洗净\', \'头足\', \'麦门冬\', \'甘草\', \'去皮\']');
INSERT INTO `prescript` VALUES (59783, '19998', '赤茯苓散', '赤茯苓半两，前胡3分（去芦头），白鲜皮1两，瞿麦1两，子芩半两，栀子仁半两，滑石2两，川升麻3分，木通1两半（锉）。', '每服3钱，水一中盏，煎至6分，去滓温服，不拘时候。', '《圣惠》卷十六', '时气，有时寒热，四肢沉重，口不知味，胸中哕塞，小便不通。', '-', '-', '升麻', '[\'茯苓\', \'半两\', \'胡分\', \'芦头\', \'白鲜皮\', \'瞿麦\', \'两子\', \'半两\', \'栀子\', \'半两\', \'滑石\', \'两川\', \'升麻\', \'木通\', \'两半\']');
INSERT INTO `prescript` VALUES (59784, '19991', '保命丹', '朱砂1两，珍珠2钱，南星1两，麻黄（去根节）半两，白附子（炮）半两，雄黄半两，龙脑半两，琥珀3钱，僵蚕（炒）1两，犀角（镑）1两，麦门冬（去心）1两，枳壳1两，地骨皮1两，神曲1两，茯神1两，远志（去心）1两，人参1两，柴胡1两，金箔1薄片，牛黄3钱，天麻半两，脑子少许，麝香少许，胆矾半两，牙消4钱，毫车1两，天竺黄1两，防风1两，甘草1两，桔梗1两，白术1两，升麻1两，蝉退半两，黄芩2两，荆芥2两。', '每服1丸，薄荷汤化下，不拘时候。更加川乌（炮，去皮脐）、姜制半夏、白芷、川芎各1两；猪牙皂1两，和前药丸服，尤妙。', '《直指附遗》卷三引《千金》', '诸风痪疭，不能语言，心忪健忘，恍惚去来，头目晕眩，胸中烦郁，痰涎壅塞，抑气攻心，精神昏愦；心气不足，神志不定，惊恐怕怖，悲忧惨蹙，虚烦少睡，喜怒不时，或发狂癫，神情昏乱；及小儿惊痫，惊风抽搐不定；及大人暗风，并羊癫、猪癫发叫。', '忌猪、羊、虾、核桃动风引痰之物及猪羊血。', '-', '升麻', '[\'朱砂\', \'珍珠\', \'南星\', \'麻黄\', \'根节\', \'半两\', \'白附子\', \'半两\', \'雄黄\', \'半两\', \'龙脑\', \'半两\', \'琥珀\', \'僵蚕\', \'犀角\', \'麦门冬\', \'枳壳\', \'地骨皮\', \'神曲\', \'茯神\', \'远志\', \'人参\', \'柴胡\', \'金箔\', \'薄片\', \'牛黄\', \'天麻\', \'半两\', \'脑子\', \'少许\', \'麝香\', \'少许\', \'胆矾\', \'半两\', \'消钱\', \'毫车\', \'天竺黄\', \'防风\', \'甘草\', \'桔梗\', \'白术\', \'升麻\', \'两蝉\', \'半两\', \'黄芩\', \'荆芥\']');
INSERT INTO `prescript` VALUES (59785, '19979', '麻黄汤', '麻黄2两，射干2两，人参2两，茯苓2两，防己2两，前胡2两，枳实2两，半夏1两，犀角1两，羚羊角1两，青木香1两，橘皮1两，杏仁1两，升麻1两，生姜5两，独活3两，吴茱萸1升。', '上（口父）咀。以水1斗1升，煮取4升，分5次服，每服相去20里久，中间进少粥，以助胃气。此汤两口服1剂，取病气退乃止。若热盛喘烦者，加石膏6两，生麦门冬1升，去吴茱萸；若心下坚，加鳖甲1两。', '方出《千金》卷七引苏长史方，名见《普济方》卷二四四', '肿已消，仍有脚气者。', '-', '-', '升麻', '[\'麻黄\', \'射干\', \'人参\', \'茯苓\', \'防己\', \'两前\', \'胡两\', \'枳实\', \'半夏\', \'犀角\', \'羚羊角\', \'青木香\', \'橘皮\', \'杏仁\', \'升麻\', \'生姜\', \'独活\', \'吴茱萸\']');
INSERT INTO `prescript` VALUES (59786, '19975', '昆布散', '昆布3两，海藻3两，人参3两，玄参3两，橘皮3两，升麻3两，芎2两，桂心2两，干姜2两，小麦1升半（醋1升半，渍之1宿，出，晒醋尽，止）。', '每服方寸匕，渐加至2匕，酒送下，1日3次。', '《外台》卷七引《广济方》', '腹内诸气胀满。', '忌热面、炙肉、生葱、蒜、粘食等物。', '-', '升麻', '[\'昆布\', \'海藻\', \'人参\', \'玄参\', \'橘皮\', \'升麻\', \'两芎\', \'两桂心\', \'干姜\', \'小麦\', \'升半醋\', \'升半渍\', \'宿出\', \'尽止\']');
INSERT INTO `prescript` VALUES (59787, '19960', '麦门冬丸', '麦门冬（去心，焙）3两，地骨皮3两，山芋3两，山茱萸3两，蔓荆实（去白皮）3两，人参3两，防风（去叉）3两，沙参3两，芍药3两，枳壳（去瓢，麸炒）3两，升麻3两，赤茯苓（去黑皮）3两，甘菊花3两，玄参3两，羌活（去芦头）3两，龙胆半两。', '每服20-30丸，温酒送下。', '《圣济总录》卷十三', '热毒风发，起即欲卧，卧即欲起，心神烦闷。', '-', '-', '升麻', '[\'麦门冬\', \'地骨皮\', \'山芋\', \'山茱萸\', \'两蔓\', \'荆实\', \'白皮\', \'人参\', \'防风\', \'沙参\', \'芍药\', \'枳壳\', \'升麻\', \'两赤\', \'茯苓\', \'黑皮\', \'甘菊花\', \'玄参\', \'羌活\', \'芦头\', \'龙胆\', \'半两\']');
INSERT INTO `prescript` VALUES (59788, '19919', '独活散', '独活（去芦头）1两半，玄参1两，生犀角屑2两，升麻3两，恶实根（锉）半两，豉2合，生干地黄（锉）半两。', '每服3钱匕，空腹煎米饮调下，1日2次。', '《圣济总录》卷十', '历节风。', '-', '-', '升麻', '[\'独活\', \'芦头\', \'两半\', \'玄参\', \'两生\', \'犀角\', \'升麻\', \'两恶\', \'实根\', \'半两\', \'豉合生\', \'地黄\', \'半两\']');
INSERT INTO `prescript` VALUES (59789, '19857', '大木香散', '木香1两，地骨皮2两，玄参2两，甘草1两（生锉），川升麻2两，川大黄1两（锉碎，微炒）。', '木香散（《普济方》卷二八九）。', '《圣惠》卷六十二', '发背毒肿如杏，烦热疼痛。', '-', '-', '升麻', '[\'木香\', \'地骨皮\', \'玄参\', \'甘草\', \'两生\', \'锉川\', \'升麻\', \'两川\', \'大黄\']');
INSERT INTO `prescript` VALUES (59790, '19787', '参耆救母汤', '人参1两，黄耆1两，当归2两，升麻5分，龟版1个，母丁香3枚。', '水煎服。', '《辨证录》卷十二', '妇人生产6-7日，胞水已破，子不见下，其子已死于腹中。', '-', '-', '升麻', '[\'人参\', \'两黄耆\', \'当归\', \'升麻\', \'分龟\', \'版个\', \'母丁香\']');
INSERT INTO `prescript` VALUES (59791, '19785', '攻邪遏流汤', '升麻1钱，当归5钱，黄芩2钱，瓜蒌2钱，金银花1两，炙甘草2钱，连翘3钱，秦艽2钱，苍耳1钱，马蔺根1钱，牛膝1钱，牵牛1钱。', '水3碗，煎8分半，空腹服。', '《洞天奥旨》卷六', '子母流注疮毒。', '-', '-', '升麻', '[\'升麻\', \'当归\', \'黄芩\', \'钱瓜\', \'金银花\', \'甘草\', \'连翘\', \'秦艽\', \'苍耳\', \'马蔺\', \'根钱\', \'牛膝\', \'牵牛\']');
