INSERT INTO `prescript` VALUES (59793, '19743', '参耆补气汤', '黄耆1钱，茯苓1钱，人参1钱，石菖蒲5分，黄柏（酒制）5分，甘草（炙）5分，知母8分，白术8分，当归8分，橘皮8分，升麻4分，柴胡4分。', '上(口父)咀。水煎8分，食远热服。精血少，加熟地黄1钱。', '《杏苑》卷六', '气虚耳鸣。', '-', '-', '升麻', '[\'黄耆\', \'茯苓\', \'人参\', \'石菖蒲\', \'黄柏\', \'酒制\', \'甘草\', \'炙分\', \'知母\', \'白术\', \'当归\', \'橘皮\', \'升麻\', \'柴胡\']');
INSERT INTO `prescript` VALUES (59794, '19721', '牢牙乌髭方', '旱莲草2两半（此草有2种，1种是紫菊花，炉火客用之；此1种，再就北人始识之，《本草》中名鲤肠草，《孙其人千金方》名金陵草，浙人谓之莲子草，其子若小莲蓬故也），芝麻莘3两（此是压油了麻枯饼是也），诃子20个（并核锉），不蛀皂角3梃，月蚕沙2两，青盐3两半，川升麻3两半。', '本方方名，《本草纲目》引作“旱莲散”。', '《寿亲养老》卷四', '牢牙乌髭。主', '-', '-', '升麻', '[\'旱莲草\', \'两半\', \'此草\', \'种种\', \'紫菊花\', \'炉火\', \'客用\', \'此种\', \'北人\', \'始识\', \'本草\', \'中名\', \'鲤肠草\', \'孙其人\', \'千金方\', \'金陵\', \'浙人\', \'谓之\', \'莲子\', \'其子\', \'莲蓬\', \'芝麻\', \'莘两此\', \'压油\', \'枯饼\', \'诃子\', \'蛀皂角\', \'蚕沙\', \'青盐\', \'两半\', \'升麻\', \'两半\']');
