INSERT INTO `prescript` VALUES (59797, '19627', '钓藤丸', '钓藤半两，川升麻半两，乌犀（镑）半两，黄芩（去黑心并皮）半两，玄参半两，茯神半两，防风（去芦头）半两，秦艽半两，槟榔半两，黄连半两，大黄半两，地骨皮半两，天竺黄半两，甘草（炙）半两，牙消半两，麦门冬（去心）半两，龙齿半两，琥珀半两，青黛半两，珍珠（另研）半两，麝香半两，牛黄半两，丹砂（另研）半两，龙胆半两，蜣螂（去头足翅）3个，蝉（去头足翅，生用）3个，人参1两，金箔40片，银箔40片（同研），虎睛1对（酒浸，焙干）。', '每1岁儿服20-30丸，人参汤或薄荷汤送下。', '《御药院方》卷十一', '小儿风痫，时发时止，经年累月不休。', '-', '-', '升麻', '[\'钓藤\', \'半两\', \'升麻\', \'半两\', \'乌犀\', \'半两\', \'黄芩\', \'黑心\', \'半两\', \'玄参\', \'半两\', \'茯神\', \'半两\', \'防风\', \'芦头\', \'半两\', \'秦艽\', \'半两\', \'槟榔\', \'半两\', \'黄连\', \'半两\', \'大黄\', \'半两\', \'地骨皮\', \'半两\', \'天竺黄\', \'半两\', \'甘草\', \'半两\', \'牙消\', \'半两\', \'麦门冬\', \'去心\', \'半两\', \'龙齿\', \'半两\', \'琥珀\', \'半两\', \'青黛\', \'半两\', \'珍珠\', \'另研\', \'半两\', \'麝香\', \'半两\', \'牛黄\', \'半两\', \'丹砂\', \'另研\', \'半两\', \'龙胆\', \'半两\', \'蜣螂\', \'去头\', \'足翅个\', \'头足\', \'生用\', \'人参\', \'金箔\', \'片银\', \'箔片\', \'研虎睛\', \'焙干\']');
