INSERT INTO `prescript` VALUES (59800, '19449', '大黄饮子', '川大黄1两（锉碎，微炒），杏仁1两（汤浸，去皮尖双仁，麸炒微黄），栀子仁1两，川升麻1两，枳实1两（麸炒微黄），黄芩1两，生地黄2两，人参半两（去芦头），甘草半两（炙微赤，锉）。', '《医统》有乌梅一个，无枳实，有枳壳。', '《圣惠》卷五十八', '身有大热，热毒流于四肢，骨节急痛不可忍，腹中烦满，大便涩难。', '-', '-', '升麻', '[\'大黄\', \'杏仁\', \'两汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\', \'栀子\', \'仁两川\', \'升麻\', \'枳实\', \'两麸\', \'微黄\', \'黄芩\', \'生地黄\', \'人参\', \'半两\', \'芦头\', \'甘草\', \'半两\', \'炙微\']');
