INSERT INTO `prescript` VALUES (59802, '19374', '大黄汤', '大黄3两，常山3两，升麻3两，甘草（炙）3两。', '以水7升，煮取2升半，分3服，发前尽服。任取吐利。', '《备急方》引蒋家方（见《外台》卷五）', '患瘴热实，兼吐利者。', '忌海藻、菘菜、生葱、生菜。', '-', '升麻', '[\'大黄\', \'常山\', \'升麻\', \'甘草\']');
