INSERT INTO `prescript` VALUES (59804, '19343', '连翘散', '防风2钱，元参2钱，白蔹1钱，芒消1钱，大黄1钱，射干1钱，升麻5分，白芍1钱，甘草5分，杏仁20粒。', '本方名连翘散，但方中无连翘，疑脱。', '《金鉴》卷四十九', '妒乳。', '-', '-', '升麻', '[\'防风\', \'钱元\', \'参钱\', \'白蔹\', \'钱芒\', \'消钱\', \'大黄\', \'射干\', \'升麻\', \'白芍\', \'甘草\', \'杏仁\']');
