INSERT INTO `prescript` VALUES (59807, '19303', '活血汤', '红花3分，蔓荆子5分，细辛5分，生地黄（夏月加之）1钱，熟地黄1钱，藁本1钱5分，川芎1钱5分，防风2钱，羌活2钱，独活2钱，甘草（炙）2钱，柴胡（去苗）2钱，当归身（酒洗）2钱，葛根2钱，白芍药（炒）3钱，升麻3钱。', '上（口父）咀。每服5钱，清水2盏，煎至1盏，去滓，食前稍热服。', '《中国医学大辞典》引《全生指迷方》', '补血养血，生血益阳。主发热，自汗，盗汗，头晕口干，四肢无力；妇女崩漏太多，昏冒不省。', '-', '-', '升麻', '[\'红花\', \'蔓荆子\', \'细辛\', \'生地黄\', \'夏月\', \'加之\', \'熟地黄\', \'藁本\', \'钱分\', \'川芎\', \'钱分\', \'防风\', \'羌活\', \'独活\', \'甘草\', \'柴胡\', \'苗钱\', \'当归\', \'身酒\', \'洗钱\', \'葛根\', \'钱白\', \'芍药\', \'升麻\']');
INSERT INTO `prescript` VALUES (59808, '19256', '陈橘皮汤', '陈橘皮（汤浸，去白，焙）1两，柴胡（去苗）1两，半夏（汤洗7遍去滑）3分，枳壳（去瓤，麸炒）3分，诃黎勒皮3分，木香半两，升麻半两，五味子半两。', '每服5钱匕，水1盏半，加生姜5片，煎取7分，去滓温服。', '《圣济总录》卷六十七', '胸胁短气妨闷，不下食。', '-', '-', '升麻', '[\'橘皮\', \'汤浸\', \'柴胡\', \'半夏\', \'洗遍\', \'滑分\', \'枳壳\', \'分诃黎勒\', \'皮分\', \'木香\', \'半两\', \'升麻\', \'半两\', \'五味子\', \'半两\']');
