INSERT INTO `prescript` VALUES (59810, '19219', '补心麦门冬丸', '麦门冬（去心，焙）1两半，石菖蒲1两，远志（去心）1两半，人参1两，白茯苓（去黑皮）1两，熟干地黄1两半，桂（去粗皮）半两，天门冬（去心，焙）1两半，黄连（去须）1两半，升麻1两半。', '麦门冬丸（《普济方》卷十九）。', '《圣济总录》卷八十六', '兼开心气，使人多记不忘。主心劳多惊悸，心气不足。', '-', '-', '升麻', '[\'麦门冬\', \'两半\', \'石菖蒲\', \'远志\', \'去心\', \'两半\', \'人参\', \'白茯苓\', \'黑皮\', \'两熟\', \'地黄\', \'两半\', \'桂去\', \'粗皮\', \'半两\', \'天门冬\', \'两半\', \'黄连\', \'两半\', \'升麻\', \'两半\']');
