INSERT INTO `prescript` VALUES (59812, '19142', '混元膏', '羚羊血5钱，没药5钱，漏芦3钱，红花3钱，大黄2钱，麝香3钱，升麻3钱，白及5钱，生栀子2钱，甘草2钱，明雄黄5钱，白敛3钱。', '敷于患处。', '《金鉴》卷八十八', '消青紫肿痛。主打扑损伤，骨碎筋翻，瘀血凝聚疼痛者。', '-', '-', '升麻', '[\'羚羊\', \'血钱\', \'没药\', \'漏芦\', \'钱红\', \'花钱\', \'大黄\', \'麝香\', \'升麻\', \'钱白及\', \'钱生\', \'栀子\', \'甘草\', \'钱明\', \'雄黄\', \'钱白\', \'敛钱\']');
INSERT INTO `prescript` VALUES (59813, '19133', '茯神散', '茯神3分，犀角屑半两，龙齿1两，川升麻半两，麦门冬1两（去心），玄参半两，甜甘根1两（锉），黄芩3分，黄连1两（去须）。', '每服3钱，以水1中盏，煎至5分，去滓，下朴消1钱，地黄汁1合，搅令匀，不拘时候温服。', '《圣惠》卷十七', '热病发狂，心热烦闷，多惊，不得卧睡。', '-', '-', '升麻', '[\'茯神\', \'犀角\', \'半两\', \'龙齿\', \'两川\', \'升麻\', \'半两\', \'麦门冬\', \'两去\', \'玄参\', \'半两\', \'甜甘根\', \'黄芩\', \'黄连\', \'两去\']');
INSERT INTO `prescript` VALUES (59814, '19041', '柴胡调经汤', '炙甘草3分，当归身3分，葛根3分，独活5分，藁本5分，升麻5分，柴胡7分，羌活1钱，苍术1钱，红花少许。', '升阳调经汤（《医略六书》卷二十六）。', '《兰室秘藏》卷中', '经水不止，鲜红，项筋急，脑痛，脊骨强痛。', '-', '-', '升麻', '[\'甘草\', \'当归\', \'身分\', \'葛根\', \'独活\', \'藁本\', \'升麻\', \'柴胡\', \'羌活\', \'苍术\', \'红花\', \'少许\']');
INSERT INTO `prescript` VALUES (59815, '18905', '麻黄汤', '麻黄2两，石膏1两半，贝齿5个（无亦得），升麻1两，甘草1两，芍药1两，杏仁40个。', '每服5钱，以水2盏，煎至8分，温服。取汗，止后服。自汗者，去麻黄，加葛根2两。', '《伤寒总病论》卷四', '天行1-2日。', '-', '-', '升麻', '[\'麻黄\', \'石膏\', \'两半\', \'贝齿\', \'升麻\', \'甘草\', \'芍药\', \'杏仁\']');
