INSERT INTO `prescript` VALUES (59817, '18880', '茯神丸', '茯神半两，麦门冬半两（去心，焙），犀角屑1分，栀子仁1分，白鲜皮1分，川升麻1分，玄参1分，车前子1分，铁粉半两（细研），朱砂半两（细研）。', '每服5丸，以温水送下，不拘时候。', '《圣惠》卷八十四', '安镇心神。主小儿伤寒后，余热不除，心神不安。小儿风热潮作，肌体烦倦，不思饮食。', '-', '-', '升麻', '[\'茯神\', \'半两\', \'麦门冬\', \'半两\', \'犀角\', \'栀子\', \'仁分\', \'白鲜皮\', \'分川\', \'升麻\', \'玄参\', \'车前子\', \'铁粉\', \'半两\', \'细研\', \'朱砂\', \'半两\', \'细研\']');
