INSERT INTO `prescript` VALUES (59819, '18854', '大五香汤', '鸡舌香5两，沉香5两，藿香5两，熏陆香1钱，麝香（研）1钱，甘草（炙）3分，吴茱萸（汤浸，焙，洗）3分，细辛（去苗叶）半两，桂（去粗皮）半两，升麻1两1分。', '每服5钱匕，水1盏半，煎至1盏，去滓温服，1日3次。若心腹闷，当急服。', '《圣济总录》卷一三六', '毒气在肌肉之中，肿痛寒热，急者数日杀人。', '-', '-', '升麻', '[\'鸡舌香\', \'沉香\', \'藿香\', \'两熏陆\', \'香钱\', \'麝香\', \'研钱\', \'甘草\', \'炙分\', \'吴茱萸\', \'汤浸\', \'洗分\', \'细辛\', \'苗叶\', \'半两\', \'桂去\', \'粗皮\', \'半两\', \'升麻\', \'两分\']');
