INSERT INTO `prescript` VALUES (59825, '18710', '经验石膏汤', '升麻1钱，知母1钱，石膏1钱半，大黄（酒蒸）2钱，山栀8分，薄荷8分，茯苓8分，连翘8分，朴消6分，甘草5分。', '石膏汤（《疡医大全》卷十六）。', '《赤水玄珠》卷三', '胃有实热，牙痛或上牙肿痛。', '-', '-', '升麻', '[\'升麻\', \'知母\', \'石膏\', \'钱半大\', \'黄酒\', \'蒸钱山\', \'栀分\', \'薄荷\', \'茯苓\', \'连翘\', \'分朴\', \'消分\', \'甘草\']');
INSERT INTO `prescript` VALUES (59826, '18696', '麦门冬汤', '麦门冬1钱，葛根（去皮）1钱，升麻（去须）4分，赤芍药（酒炒）6分，茯苓6分，炙甘草4分，石膏（煅）1钱半。', '水煎服。', '《景岳全书》卷六十三引万氏方', '表邪内热，咳嗽甚者。麻疹咳嗽。', '-', '-', '升麻', '[\'麦门冬\', \'葛根\', \'去皮\', \'升麻\', \'赤芍\', \'药酒\', \'炒分\', \'茯苓\', \'甘草\', \'石膏\']');
INSERT INTO `prescript` VALUES (59827, '18692', '法制温半夏', '齐半夏2斤（用河水洗7返后用），白矾1斤（为末），好酒1瓶，川升麻4两，丁皮4两，缩砂仁4两，草豆蔻仁4两，甘草4两。', '每服1粒，细嚼，生姜汤下。不拘时候。', '《御药院方》卷五', '痰饮。', '-', '-', '升麻', '[\'半夏\', \'斤用\', \'河水\', \'洗返\', \'白矾\', \'酒瓶\', \'升麻\', \'两丁皮\', \'两缩\', \'砂仁\', \'草豆蔻\', \'甘草\']');
INSERT INTO `prescript` VALUES (59828, '18678', '鬼箭羽散', '鬼箭羽1分，真珠末1分，桃仁（汤浸，去皮尖双仁，麸炒微黄）、川大黄1两（锉，微炒），羚羊角屑半两，桔梗（去芦头）半两，川朴消半两，川升麻半两，赤芍药半两，柴胡（去苗）半两，黄芩半两。', '鬼箭羽汤（《圣济总录》卷一七七）。《圣济总录》有鬼臼。', '《圣惠》卷八十三', '小儿中恶，心坚强，卒痛欲困。', '-', '-', '升麻', '[\'鬼箭羽\', \'真珠\', \'末分\', \'桃仁\', \'汤浸\', \'皮尖\', \'双仁麸\', \'微黄\', \'大黄\', \'羚羊角\', \'半两\', \'桔梗\', \'芦头\', \'半两\', \'川朴消\', \'半两\', \'升麻\', \'半两\', \'芍药\', \'半两\', \'柴胡\', \'去苗\', \'半两\', \'黄芩\', \'半两\']');
INSERT INTO `prescript` VALUES (59829, '18654', '荆防败毒散', '荆芥穗1钱，北防风1钱，净连翘1钱，陈枳壳1钱，绿升麻1钱，南薄荷1钱，川羌活1钱，川独活1钱，粉干葛1钱，川木通1钱，金银花1钱，片黄芩1钱，正川芎1钱，黑栀仁1钱，炙甘草1钱。', '上身肿，加葱3茎，下身肿，加灯芯10茎，水煎服。', '《幼幼集成》卷四', '小儿疮疥毒气内陷，肚腹作肿。', '-', '-', '升麻', '[\'荆芥\', \'钱北\', \'防风\', \'钱净\', \'连翘\', \'枳壳\', \'升麻\', \'钱南\', \'薄荷\', \'钱川\', \'羌活\', \'钱川\', \'独活\', \'钱粉\', \'干葛钱\', \'川木通\', \'金银\', \'花钱\', \'黄芩\', \'钱正\', \'川芎\', \'栀仁\', \'甘草\']');
INSERT INTO `prescript` VALUES (59830, '18625', '举陷参耆煎', '玄参2钱，黄耆2钱，炒白术1钱，茯苓1钱，陈皮1钱，柴胡1钱，升麻1钱，炙甘草5分，泽泻2钱。', '姜、枣、灶心土为引，水煎服。', '《重订通俗伤寒论》', '妄下阴脱。凡伤寒温热攻下太过，脾胃受伤，心中懊憹，起卧不安，下泻不止者。', '-', '-', '升麻', '[\'玄参\', \'黄耆\', \'白术\', \'茯苓\', \'陈皮\', \'柴胡\', \'升麻\', \'甘草\', \'泽泻\']');
INSERT INTO `prescript` VALUES (59831, '18619', '定心神牛黄丸', '牛黄（别研）半两，珍珠末（别研）半两，琥珀（别捣罗）半两，铁粉（别研）半两，天竺黄（别研）半两，龙齿（别研）半两，金箔70片（与水银同研），银箔70片（与水银同研），水银（与金、银箔同研）半两，犀角（镑）半两，丹砂（与水银、金箔、银箔同研）半两，露蜂房（炙）1两，龙胆（去土）半两，升麻半两，防风（去叉）半两，黄芩（去黑心）半两，钩藤半两，知母（细切，焙）1两，天门冬（去心，焙）1两，白芍药1两，茯神（去木）1两，甘草（炙，锉）1两，菖蒲（九节者，米醋浸，刮去皮，切，酒炒）1两，麦门冬（去心，焙）1两，干蝎', '本方方名，《普济方》引作“牛黄丸”。', '《圣济总录》卷十五', '风邪变成癫痫，时时发动，不知人事。', '-', '-', '升麻', '[\'牛黄\', \'别研\', \'半两\', \'珍珠\', \'末别研\', \'半两\', \'琥珀\', \'捣罗\', \'半两\', \'铁粉\', \'别研\', \'半两\', \'天竺黄\', \'别研\', \'半两\', \'龙齿\', \'别研\', \'半两\', \'金箔\', \'水银\', \'同研\', \'箔片\', \'水银\', \'同研\', \'水银\', \'金银箔\', \'同研\', \'半两\', \'犀角\', \'半两\', \'丹砂\', \'水银\', \'金箔\', \'银箔\', \'同研\', \'半两\', \'露蜂房\', \'龙胆\', \'半两\', \'升麻\', \'半两\', \'防风\', \'半两\', \'黄芩\', \'黑心\', \'半两\', \'钩藤\', \'半两\', \'知母\', \'细切\', \'天门冬\', \'两白\', \'芍药\', \'茯神\', \'甘草\', \'菖蒲\', \'九节\', \'米醋\', \'去皮\', \'切酒\', \'麦门冬\', \'两干\']');
INSERT INTO `prescript` VALUES (59832, '18600', '常山丸', '常山（细锉）3分，乌梅肉（炒）半两，甘草（炙，锉）半两，鳖甲（去裙襕，醋浸炙）半两，萎蕤半两，石膏（碎）1两，知母（焙）3分，蜀漆叶3分，白薇3分，升麻3分，地骨皮3分，麦门冬（去心，焙）3分，豉（炒）1合。', '每服10丸，空腹米饮送下；未发前再服，渐加丸数。以愈为度。', '《圣济总录》卷三十五', '劳疟，积劳寒热。', '-', '-', '升麻', '[\'常山\', \'乌梅\', \'半两\', \'甘草\', \'半两\', \'鳖甲\', \'半两\', \'萎蕤\', \'半两\', \'石膏\', \'知母\', \'焙分\', \'漆叶分\', \'白薇分\', \'升麻\', \'地骨皮\', \'麦门冬\', \'分豉\', \'炒合\']');
INSERT INTO `prescript` VALUES (59833, '18557', '大黄汤', '大黄2两，甘草2两，炙黄芩2两，升麻2两，栀子100枚。', '大黄散（《圣惠》卷六十一）、升麻汤（《圣济总录》卷一三一）。', '《肘后方》卷五', '发背上初欲肿，热毒生疖，五脏壅滞。', '-', '-', '升麻', '[\'大黄\', \'甘草\', \'黄芩\', \'升麻\', \'栀子\']');
INSERT INTO `prescript` VALUES (59834, '18510', '茯苓汤', '白茯苓（去黑皮）3两，薏苡仁（炒）4两，丹参2两半，独活（去芦头）2两半，防风（去叉）2两半，牛膝（酒浸，切，焙）3两，防己3两，五加皮3两，黄耆3两，枳壳（去瓤，麸炒）3两，升麻3两，麻黄（去根节）4两，羚羊角（镑）2两，桂（去粗皮）1两半，石膏（研如粉）10两。', '每服5钱匕，以水1盏半，浸1宿，来晨煎取8分，去滓，空腹温服，日2次。心腹气胀，加连皮大腹1颗；心胸虚热，加麦门冬（去心）1分；小腹痛，加芍药、黄芩各1两；心胸有痰，加旋覆花1分；肺气咳嗽，加杏仁（去皮尖）14枚、桑根白皮1分；小便数，加杜仲末1分；言语謇涩，加附子1钱；小便涩，加木通1分；肾虚耳聋，加磁石末1钱匕；不睡，加酸枣仁末1钱；烦渴不止，加栝楼1分。', '《圣济总录》卷八十四', '江东春夏暑湿郁蒸，毒气攻击，脚气发动，两脚酸疼，或浮热肿满，或皮毛焦干，或脚疼不能久立，筋急抽痛，时冲心闷，胸膈痰积，恶心欲吐，四肢（疒帬）痹，十指不仁，腹胀气妨，头旋目眩眼暗。', '-', '-', '升麻', '[\'白茯苓\', \'黑皮\', \'薏苡仁\', \'丹参\', \'两半\', \'独活\', \'芦头\', \'两半\', \'防风\', \'两半\', \'牛膝\', \'浸切\', \'防己\', \'五加皮\', \'两黄耆\', \'枳壳\', \'升麻\', \'麻黄\', \'根节\', \'羚羊角\', \'两桂去\', \'粗皮\', \'两半\', \'石膏\', \'研如粉\']');
INSERT INTO `prescript` VALUES (59835, '18507', '定神琥珀丸', '琥珀（捣研）1两，珍珠（捣研末）1两，牛黄（研）1两，铁粉（研）1两，天竺黄（捣研）1两，龙齿（研）1两，腻粉（研）3分，犀角（镑）3分，甘草（炙，锉）3分，露蜂房（微炙）3分，龙胆3分，升麻3分，麦门冬（去心，焙）3分，丹砂（研）3分，防风（去叉）3分，黄芩（去黑心）3分，钩藤3分，人参3分，远志（去心）3分，知母（焙）3分，天门冬（去心，焙）3分，菖蒲（九节者，去须，米泔浸，切，焙）3分，白芍药3分，茯神（去木）3分，干蝎（酒炒）1两半，麝香（研）1分，金箔100片，银箔100片（与金箔、丹砂同研断星', '每服20丸，食后、临卧煎甜竹叶汤送下，日二夜一。', '《圣济总录》卷五', '心中风发动不知，渐成癫痫，惊悸恍惚。', '-', '-', '升麻', '[\'琥珀\', \'捣研\', \'珍珠\', \'研末\', \'牛黄\', \'研两\', \'铁粉\', \'研两\', \'天竺黄\', \'捣研\', \'龙齿\', \'研两腻\', \'研分\', \'犀角\', \'甘草\', \'露蜂房\', \'微炙分\', \'龙胆\', \'升麻\', \'麦门冬\', \'心焙分\', \'丹砂\', \'研分\', \'防风\', \'去叉分\', \'黄芩\', \'黑心\', \'钩藤\', \'人参\', \'远志\', \'心分\', \'知母\', \'焙分\', \'天门冬\', \'心焙分\', \'菖蒲\', \'九节\', \'浸切\', \'分白\', \'芍药\', \'茯神\', \'木分\', \'干蝎酒\', \'两半\', \'麝香\', \'研分\', \'金箔\', \'片银\', \'箔片\', \'金箔\', \'丹砂\', \'同研\', \'断星\']');
INSERT INTO `prescript` VALUES (59836, '18496', '茯苓汤', '赤茯苓（去黑皮）1两，桂（去粗皮）1两，黄芩（去黑心）1两，升麻半两，常山半两，蜀漆甘草（炙，锉）半两。', '每服3钱匕，水1盏，煎至7分，去滓温服，不拘时候。', '《圣济总录》卷三十七', '疟病发热，烦躁，身面皆黄，小便不利。', '-', '-', '升麻', '[\'茯苓\', \'黑皮\', \'两桂去\', \'粗皮\', \'黄芩\', \'黑心\', \'升麻\', \'半两\', \'常山\', \'半两\', \'甘草\', \'半两\']');
INSERT INTO `prescript` VALUES (59837, '18478', '茯苓汤', '茯苓5两，黄芩5两，栀子仁5两，芒消5两，赤石脂2两，升麻2两，紫菀2两，生麦门冬5两（去心），竹叶（切）1升，香豉1升（熬），石膏8两（碎，绵裹），生地黄（切）1升。', '以水9升，煮取2升，去滓，下芒消，分3服。', '《外台》卷十六引《删繁方》', '消热，止血气，调脉理中。主脉实热极，血气伤心，使心好生赫怒，口为色变赤，言语不快。', '忌酢物，芜荑。', '-', '升麻', '[\'茯苓\', \'黄芩\', \'栀子\', \'仁两芒\', \'消两\', \'赤石脂\', \'升麻\', \'紫菀\', \'两生\', \'麦门冬\', \'两去\', \'竹叶\', \'切升\', \'香豉\', \'升熬\', \'石膏\', \'两碎\', \'绵裹\', \'生地黄\', \'切升\']');
INSERT INTO `prescript` VALUES (59838, '18477', '大麻仁散', '大麻仁1两（炒令黄），川大黄1两（锉碎，微炒），射干1两，菖蒲1两，甘草半两（炙微赤，锉），麻黄1两（去根节），川升麻1两。', '每服4钱，以水1中盏，加生姜半分，豉100粒，煎至6分，去滓温服，不拘时候。', '《圣惠》卷四十六', '岭南瘴毒脚气，头面及脚肿，乍寒乍热，有似疟状，或气上逼心，烦闷喘嗽。', '-', '-', '升麻', '[\'麻仁\', \'炒令\', \'黄川\', \'大黄\', \'射干\', \'菖蒲\', \'甘草\', \'半两\', \'炙微\', \'麻黄\', \'两去\', \'根节川\', \'升麻\']');
INSERT INTO `prescript` VALUES (59839, '18347', '麦门冬丸', '麦门冬（去心，焙）2两，木通（锉）半两，人参半两，五味子半两，黄耆（锉）半两，羌活（去芦头）半两，防风（去叉）半两，生干地黄（焙）半两，黄芩（去黑心）半两，桑上寄生半两，茯神（去木）半两，天雄（炮裂，去皮脐）半两，升麻半两，泽兰半两，枳壳（去瓤，麸炒令黄）3分，大黄（锉，微炒）3分，当归（切，焙）1分。', '每服20丸，空心温酒送下，渐加至30丸，以愈为度。先用诸汤药涂敷拓，后服此。', '《圣济总录》卷一二八', '乳痈。', '-', '-', '升麻', '[\'麦门冬\', \'木通\', \'半两\', \'人参\', \'半两\', \'五味子\', \'半两\', \'黄耆\', \'半两\', \'羌活\', \'芦头\', \'半两\', \'防风\', \'半两\', \'生干\', \'地黄\', \'半两\', \'黄芩\', \'黑心\', \'半两\', \'桑上\', \'寄生\', \'半两\', \'茯神\', \'半两\', \'天雄\', \'炮裂\', \'去皮\', \'半两\', \'升麻\', \'半两\', \'泽兰\', \'半两\', \'枳壳\', \'炒令\', \'黄分\', \'大黄\', \'锉微\', \'炒分\', \'当归\', \'切焙分\']');
INSERT INTO `prescript` VALUES (59840, '18327', '柴胡汤', '柴胡（去苗）1两，前胡（去芦头）1两，桔梗（炒）1两，贝母（去心）1两，牡丹皮1两，黄芩（去黑心）1两，麻黄（去根节）1两，甘草（炙，锉）1钱，枳壳（去瓤，麸炒）1钱，升麻半两。', '每服3钱匕，水1盏，煎2-3沸，去滓，食后、临卧服。', '《圣济总录》卷十三', '因于露风成寒热，精神昏困，肢节烦疼。', '-', '-', '升麻', '[\'柴胡\', \'去苗\', \'两前\', \'胡去\', \'芦头\', \'桔梗\', \'贝母\', \'牡丹皮\', \'黄芩\', \'黑心\', \'麻黄\', \'根节\', \'甘草\', \'枳壳\', \'升麻\', \'半两\']');
INSERT INTO `prescript` VALUES (59841, '18320', '浮萍煎膏', '浮萍草1两，川升麻1两，黄柏1两，甘草1两（半生用）。', '每服半匙，含化咽津。', '《圣惠》卷三十六', '口舌生疮，久不愈者。', '-', '-', '升麻', '[\'浮萍\', \'两川\', \'升麻\', \'黄柏\', \'甘草\', \'半生\']');
INSERT INTO `prescript` VALUES (59842, '18307', '凉肢散', '茯苓5钱，薏仁5钱，玄参5钱，甘草1钱，升麻1钱，炒荆芥1钱，甘菊3钱，麦冬3钱，天花粉2钱。', '水煎服。', '《辨证录》卷二', '肌肉热板生风，体上如鼠走，唇口反裂，久则缩入，遍身皮毛尽发红黑。', '-', '-', '升麻', '[\'茯苓\', \'薏仁\', \'玄参\', \'甘草\', \'升麻\', \'荆芥\', \'甘菊\', \'麦冬\', \'天花粉\']');
INSERT INTO `prescript` VALUES (59843, '18287', '复生丸', '当归5钱，白芍5钱，西芎5钱，升麻5钱，葛根5钱，甘草5钱，嫩紫草茸1两，辰砂1两2钱。', '每服1丸，冰、雪、雨三水送下；如无，河水亦可，或糯米汤1匙送下亦可。5日以前可服。', '《回春》卷七', '解痘毒。主小儿痘疹不起发，紫黑陷伏，或痘疹初作，已发或未发者。', '-', '-', '升麻', '[\'当归\', \'白芍\', \'钱西\', \'升麻\', \'葛根\', \'甘草\', \'钱嫩\', \'紫草茸\', \'辰砂\']');
INSERT INTO `prescript` VALUES (59844, '18237', '定神安志丸', '金箔100片，银箔100片（和合），石膏（研）8分，龙齿（研）8分，铁精（研）8分，地骨白皮8分，茯神8分，黄芩8分，生干地黄8分，升麻8分，茯苓8分，玄参8分，人参8分，虎睛1具（微炙），牛黄（生）4两，生姜屑4两，麦门冬1钱（去心），枳实（炙）6分，甘草（炙）6分，葳蕤6分，芍药6分，远志（去心）5分，柏子仁5分，白鲜皮5分。', '每服20丸，渐加至30丸，食讫少时煮生枸杞根汤送下，1日2次。', '《普济方》卷一○一', '风邪狂乱失心。', '不利热曲、海藻、菘菜、芜荑、炙肉、醋、蒜、粘食、陈臭、油腻。', '-', '升麻', '[\'金箔\', \'片银\', \'箔片\', \'石膏\', \'研分\', \'龙齿\', \'研分\', \'精研\', \'分地\', \'白皮\', \'茯神\', \'黄芩\', \'分生\', \'地黄\', \'升麻\', \'茯苓\', \'玄参\', \'人参\', \'分虎睛\', \'具微\', \'牛黄\', \'生两\', \'生姜\', \'麦门冬\', \'枳实\', \'炙分\', \'甘草\', \'炙分\', \'葳蕤\', \'芍药\', \'远志\', \'心分\', \'柏子仁\', \'白鲜皮\']');
INSERT INTO `prescript` VALUES (59845, '18203', '麦门冬汤', '生麦门冬（去心）1两半，萎蕤1两半，秦皮（去粗皮）1两半，赤茯苓（去黑皮）1两半，大黄（生用）1两，升麻1两。', '每服5钱匕，水1盏半，加竹叶10片，煎至8分，去滓，下朴消末1钱匕，更煎令沸，空腹温服。', '《圣济总录》卷一○二', '肝实热，毒气上熏，目赤痛痒。', '-', '-', '升麻', '[\'麦门冬\', \'去心\', \'两半\', \'萎蕤\', \'两半\', \'秦皮\', \'粗皮\', \'两半\', \'茯苓\', \'黑皮\', \'两半\', \'大黄\', \'生用\', \'升麻\']');
INSERT INTO `prescript` VALUES (59846, '18200', '钩藤散', '钩藤3分，白茯苓3分，黄芩3分，川升麻3分，白鲜皮3分，龙齿1两，玄参1两，石膏1两，寒水石2两。', '每服1钱，以水1小盏，煎至5分，去滓，加竹沥半合，搅令匀，重煎1-2沸，分温2服。', '《圣惠》卷八十五', '小儿忽得惊痫，壮热口燥。', '-', '-', '升麻', '[\'钩藤\', \'白茯苓\', \'黄芩\', \'分川\', \'升麻\', \'白鲜皮\', \'龙齿\', \'玄参\', \'石膏\', \'寒水石\']');
INSERT INTO `prescript` VALUES (59847, '18193', '和解汤', '羌活1两，防风1两，川芎1两，人参1两，干葛半两，川升麻（轻者）半两，甘草（微炙）半两。', '和解散（《朱氏集验方》卷十一）、和劝汤（《疡医大全》卷三十三）《婴童百问》有芍药半两。', '《百一》卷十九', '解表和中。主小儿四时感冒寒邪，壮热烦躁，鼻塞多涕，惊悸自汗，肢节疼痛，及麸疮、痘疮已发或未发者。痘出充满红活，而热不退者；寒战咬牙，属热者；不泻而渴，属实者。', '-', '-', '升麻', '[\'羌活\', \'防风\', \'川芎\', \'人参\', \'两干葛\', \'半两\', \'升麻\', \'轻者\', \'半两\', \'甘草\', \'半两\']');
INSERT INTO `prescript` VALUES (59848, '18170', '大青煎', '大青1两，升麻1两，射干（去毛）1两，苦竹叶1两，山栀子仁1两，黄柏（去粗皮，蜜炙）半两，玄参（坚者）3分，蔷薇根2两，生地黄汁半斤，白蜜半斤。', '用水5升，煎至1升，去滓，下蜜、地黄汁，搅匀，再煎如稠饧，以净器盛。每服半匙，含化咽津，不拘时候。', '《圣济总录》卷三十', '伤寒后，下冷上热，口舌生疮。', '-', '-', '升麻', '[\'大青\', \'升麻\', \'射干\', \'苦竹叶\', \'两山\', \'栀子\', \'黄柏\', \'粗皮蜜\', \'半两\', \'玄参\', \'坚者\', \'蔷薇\', \'根两\', \'生地黄\', \'半斤\', \'白蜜\', \'半斤\']');
INSERT INTO `prescript` VALUES (59849, '18154', '常山饮', '常山1两，豉1合，葛根（锉）3分，柴胡（去苗）3分，升麻3分，牡蛎（熬）3分，大黄（锉，炒）3分，前胡（去头芦）3分，知母（焙）3分，山栀子仁3分，枳壳（去瓤，麸炒）3分，半夏（汤洗7遍去滑，炒）半两，麦门冬（去心，焙）半两，桃仁（去皮尖双仁，炒）半两，甘草（炙）半两，犀角（镑）半两。', '每用5钱匕，水1盏半，入葱、薤白各3寸，切碎，煎取1盏，去滓，分2服，发前及发时热服之。', '《圣济总录》卷三十四', '疟疾。先寒后热，四肢黄瘦，头痛。', '-', '-', '升麻', '[\'常山\', \'两豉\', \'葛根\', \'锉分\', \'柴胡\', \'苗分\', \'升麻\', \'牡蛎\', \'熬分\', \'大黄\', \'分前\', \'胡去\', \'芦分\', \'知母\', \'分山\', \'栀子\', \'仁分\', \'枳壳\', \'炒分\', \'半夏\', \'洗遍\', \'半两\', \'麦门冬\', \'半两\', \'桃仁\', \'皮尖\', \'双仁\', \'半两\', \'甘草\', \'半两\', \'犀角\', \'半两\']');
INSERT INTO `prescript` VALUES (59850, '18129', '大青散', '大青1两，栀子仁1两，川大黄（锉碎，微炒）1两，犀角屑1两，川升麻1两，甘草（炙微赤，锉）1两。', '每服5钱，以水1大盏，煎至5分，去滓温服，不拘时候。', '《圣惠》卷十', '伤寒壮热头痛，遍身发疮如豌豆。', '-', '-', '升麻', '[\'大青\', \'栀子\', \'仁两川\', \'大黄\', \'犀角\', \'两川\', \'升麻\', \'甘草\', \'炙微\']');
INSERT INTO `prescript` VALUES (59851, '18083', '茯苓汤', '赤茯苓（去黑皮）1两半，黄芩（去黑心）1两半，栀子仁1两半，赤石脂1两，升麻1两，紫菀（去苗土）1两，麦门冬（去心，焙）1两半，豉（炒）1合，石膏1两。', '每服5钱匕，水1盏半，加竹叶5片，煎至1盏，去滓，下芒消1钱匕，分2次温服，早食后、日午各1次。', '《圣济总录》卷九十二', '消热气，调血脉，理中。主脉极实热，血气伤心，好生嗔怒，口唇色变，言语不快。', '-', '-', '升麻', '[\'茯苓\', \'黑皮\', \'两半\', \'黄芩\', \'黑心\', \'两半\', \'栀子\', \'两半\', \'赤石脂\', \'升麻\', \'紫菀\', \'苗土\', \'麦门冬\', \'两半\', \'炒合\', \'石膏\']');
INSERT INTO `prescript` VALUES (59852, '18056', '柳花散', '柳花5-7钱，紫草1两2钱，升麻9钱，归身7钱半。', '每服7钱，葡萄煎汤调下。', '《赤水玄珠》卷二十八', '室女发热经行。', '-', '-', '升麻', '[\'柳花\', \'紫草\', \'升麻\', \'钱归身\']');
INSERT INTO `prescript` VALUES (59853, '17987', '活血通经汤', '芍药5分，升麻1钱，葛根1钱，人参1钱，当归身1钱，炙甘草1钱，酒黄柏2钱，桂枝2钱。', '都作1服，水2大盏，煎至1盏，热服，不拘时候。令暖房中近火摩搓其手。', '《兰宝秘藏》卷下', '活血通经，缓急润燥。主风气暴至，六脉俱弦甚，按之洪实有力，挛急，大便秘涩，面赤热。', '-', '-', '升麻', '[\'芍药\', \'升麻\', \'葛根\', \'人参\', \'当归\', \'身钱\', \'甘草\', \'钱酒\', \'黄柏\', \'桂枝\']');
INSERT INTO `prescript` VALUES (59854, '17954', '凉明饮', '黄连1钱，柴胡1钱，黄芩1钱，防风1钱，芍药1钱，生地1钱，当归5分，羌活5分，升麻5分，白芷5分，山栀子5分，川芎5分，甘草2分。', '水煎服。有赤脉，加菊花、木贼；白睛赤肿痛甚，加桑白皮；多泪不止，加荆芥、薄荷、菊花。', '《续名家方选》', '风热上冲，眼目赤肿，多泪不止。', '-', '-', '升麻', '[\'黄连\', \'柴胡\', \'黄芩\', \'防风\', \'芍药\', \'生地\', \'当归\', \'羌活\', \'升麻\', \'白芷\', \'分山\', \'栀子\', \'川芎\', \'甘草\']');
INSERT INTO `prescript` VALUES (59855, '17913', '金箔丸', '金箔50片（细研），银箔50片（细研），石膏1两，龙齿1两，铁粉1两（细研），人参1两（去芦头），茯神1两，远志1两（去心），朱砂1两（细研），防风1两（去芦头），黄芩1两，生干地黄1两，川升麻1两，地骨皮1两，玄参1两，犀角屑1两，虎睛1对（酒浸1宿，微炙），牛黄半两（细研），麦门冬1两半（去心，焙），枳实半两（麸炒微黄），甘草半两（炙微赤，锉）。', '每服20丸，以薄荷汤送下，不拘时候。', '《圣惠》卷二十', '安神定志。主风邪，狂乱失心。', '-', '-', '升麻', '[\'金箔\', \'片细\', \'研银\', \'箔片\', \'细研\', \'石膏\', \'龙齿\', \'铁粉\', \'两细研\', \'人参\', \'两去\', \'芦头\', \'茯神\', \'远志\', \'两去\', \'朱砂\', \'两细研\', \'防风\', \'两去\', \'芦头\', \'黄芩\', \'两生\', \'地黄\', \'两川\', \'升麻\', \'地骨皮\', \'玄参\', \'犀角\', \'两虎睛\', \'浸宿微\', \'牛黄\', \'半两\', \'细研\', \'麦门冬\', \'两半\', \'枳实\', \'半两\', \'微黄\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (59856, '17888', '参耆内托十宣散', '人参1钱5分，黄耆2钱，陈皮8分，甘草3分，升麻1钱，茯苓1钱，白术，泽泻2钱，当归2钱，川芎、生地、白芍、黄芩、乌药、前胡、黄柏、知母、天花粉。', '方中白术及当归以下九味用量原缺。冬天加桂，倘有余内症，因症加减。', '《疮疡经验全书》卷二', '流注发背。', '-', '-', '升麻', '[\'人参\', \'钱分\', \'黄耆\', \'陈皮分\', \'甘草\', \'升麻\', \'茯苓\', \'白术\', \'泽泻\', \'当归\', \'川芎\', \'生地\', \'白芍\', \'黄芩\', \'乌药\', \'黄柏\', \'知母\', \'天花粉\']');
INSERT INTO `prescript` VALUES (59857, '17885', '赤石脂汤', '赤石脂8两，乌梅20枚，栀子14枚，白术3两，升麻3两，廪米1升，干姜2两。', '以水1斗，煮米取熟，去米下药，煮取2升半，分为3服。', '《千金》卷二十', '下焦热或下痢脓血，烦闷恍惚。霍乱，下焦热结，或痢下脓血烦痛。', '-', '-', '升麻', '[\'赤石脂\', \'乌梅\', \'栀子\', \'白术\', \'升麻\', \'两廪\', \'干姜\']');
INSERT INTO `prescript` VALUES (59858, '17868', '大黄汤', '大黄（锉，炒）半两，升麻半两，栀子仁半两，朴消（别研）半两，枳壳（去瓤，麸炒）半两，黄耆（锉）半两。', '大黄散（《普济方》卷○五）。', '《圣济总录》卷一八二', '小儿痈疮，脏腑壅热，心神烦躁，大小便不利。', '-', '-', '升麻', '[\'大黄\', \'半两\', \'升麻\', \'半两\', \'栀子\', \'半两\', \'朴消\', \'别研\', \'半两\', \'枳壳\', \'半两\', \'黄耆\', \'半两\']');
INSERT INTO `prescript` VALUES (59859, '17806', '肥皂方', '角子糯肥皂1斤12两（去核），真排草1两5钱（如铁线者佳），绿升麻4两，白及5钱，楮实子2两半，白芷5钱，砂仁（带壳）5钱，糯米半升（另研），绿豆5钱（另研），天花粉5钱，白丁香2钱半，杏仁1两5钱（去皮，研如泥），猪胰子5个（另研），甘菊花5钱，红枣肉（去皮核）1两5钱，零陵香5钱，大片脑3钱，藿香3钱，广木香3两，宫粉1两半，梅桂7钱，南桂花1两半。', '照常洗面，润开水搽脸。久用斑滞自消，面如玉色。', '《鲁府禁方》卷四', '去垢，润肌，驻颜。主粉刺，花斑，雀子斑，及面上黑靥、皮肤燥痒。', '-', '-', '升麻', '[\'角子\', \'肥皂\', \'斤两\', \'去核\', \'真排\', \'铁线\', \'升麻\', \'两白及\', \'楮实子\', \'两半\', \'白芷\', \'砂仁\', \'带壳\', \'糯米\', \'半升\', \'另研\', \'绿豆\', \'另研\', \'天花粉\', \'钱白\', \'丁香\', \'钱半\', \'杏仁\', \'去皮\', \'研如泥\', \'胰子\', \'另研\', \'甘菊花\', \'红枣\', \'去皮\', \'核两钱\', \'零陵\', \'香钱\', \'大片\', \'脑钱\', \'藿香\', \'钱广\', \'木香\', \'宫粉\', \'两半\', \'梅桂钱\', \'桂花\', \'两半\']');
INSERT INTO `prescript` VALUES (59860, '17791', '胡桐泪散', '胡桐泪1两（烧赤，细研），石胆1两（细研），黄矾1两（烧灰，研），芦荟1两（细研），光明砂半两（细研），麝香1分（细研），川升麻1两，细辛3分，乱发灰1分，当归半两，牛膝半两（去苗），芎藭半两。', '每用先以甘草汤洗漱令净，后用药敷之，有涎即吐却，日3次。', '《圣惠》卷三十四', '齿漏疳，龈上生疮肿痛；足阳明胃经虚，风热所袭，传流齿牙，攻注龈肉，则至肿结妨闷，甚者与龈间津液相博，化为脓汁。', '-', '-', '升麻', '[\'胡桐泪\', \'两烧\', \'赤细\', \'研石胆\', \'两细\', \'研黄\', \'烧灰\', \'芦荟\', \'两细研\', \'光明\', \'半两\', \'细研\', \'麝香\', \'分细\', \'研川\', \'升麻\', \'细辛\', \'乱发\', \'灰分\', \'当归\', \'半两\', \'牛膝\', \'半两\', \'半两\']');
INSERT INTO `prescript` VALUES (59861, '17780', '独活散', '独活3分，汉防己半两，犀角屑半两，石膏1两，川升麻3分，黄芩3分，防风半两（去芦头），甘草半两（生用）。', '每服半两，以水1中盏，煎至5分，去滓，加竹沥半合，温服，1日3-4次。', '《圣惠》卷三十八', '乳石发动，生痈赤肿，毒气攻注，筋脉拘急，言语謇涩，心神烦躁。', '-', '-', '升麻', '[\'独活\', \'分汉\', \'防己\', \'半两\', \'犀角\', \'半两\', \'石膏\', \'两川\', \'升麻\', \'黄芩\', \'防风\', \'半两\', \'芦头\', \'甘草\', \'半两\', \'生用\']');
