INSERT INTO `prescript` VALUES (59863, '17765', '茴香安肾汤', '人参1钱，白术1钱，白茯苓（去皮）1钱，泽泻7分，茴香1钱（炒），破故纸1钱，黄柏8分，木香5分，槟榔1钱，乌药1钱，香附1钱（童便浸经宿），砂仁1钱，玄胡素5分，升麻3分，甘草（炙）5分，荔枝核1钱。', '饥时服。', '《古今医鉴》卷十', '左边偏坠，丸如鸡鸭子。', '-', '-', '升麻', '[\'人参\', \'白术\', \'白茯苓\', \'去皮\', \'泽泻\', \'茴香\', \'纸钱\', \'黄柏\', \'木香\', \'槟榔\', \'乌药\', \'香附\', \'童便\', \'浸经宿\', \'砂仁\', \'钱玄\', \'胡素分\', \'升麻\', \'甘草\', \'炙分\', \'荔枝核\']');
INSERT INTO `prescript` VALUES (59864, '17712', '结阴丸', '生地4两，白芍2两5钱，山药2两5钱，槐米1两5钱，川黄连1两5钱，黑荆芥1两5钱，茜草1两5钱，地榆1两5钱，乌梅肉1两，升麻1两，扁柏1两，罗汉松叶1两。', '每服3钱，早上空心吞服。', '《活人方》卷二', '清热凉血，祛风散滞。主脾虚气滞，肝虚血热，血随气而沉陷于阳明大肠，始为肠风脏毒，久则渗漏无度，传为结阴便血之症。', '-', '-', '升麻', '[\'生地\', \'白芍\', \'山药\', \'槐米\', \'两钱川\', \'黄连\', \'荆芥\', \'茜草\', \'地榆\', \'乌梅\', \'升麻\', \'扁柏\', \'罗汉松\', \'叶两\']');
INSERT INTO `prescript` VALUES (59865, '17667', '和血散痛汤', '羌活身1钱半，升麻1钱半，麻黄（去节）1钱半，桃仁10个，柴胡2钱，红花1分，归身1分，防风1钱，甘草（炙）2分，独活5分，猪苓5分，黄柏1钱，防己6分，知母（酒）1钱，黄连（酒）6分。', '和血散瘀汤（《证治宝鉴》卷十二）；方中黄连用量原缺，据《赤水玄珠》补。', '《医学纲目》卷十二引李东垣方', '和血止痛。主两手十指，一指疼了一指疼，疼后又肿，骨头里痛，膝痛，左膝痛了右膝痛，发时多则5日，少则3日，昼轻夜重，痛时觉热，行则痛轻，肿却重。', '-', '-', '升麻', '[\'羌活\', \'身钱\', \'升麻\', \'钱半\', \'麻黄\', \'去节\', \'桃仁\', \'柴胡\', \'红花\', \'分归\', \'身分\', \'防风\', \'甘草\', \'炙分\', \'独活\', \'猪苓\', \'黄柏\', \'防己\', \'知母\', \'酒钱\', \'黄连\', \'酒分\']');
