INSERT INTO `prescript` VALUES (59869, '17579', '独活散', '独活1两，川升麻1两，羚羊角屑1两，麻黄1两（去根节），防风1两（去芦头），桂心半两，附子3分（炮裂，去皮脐）。', '每服4钱，以水1中盏，煎至5分，去滓，加竹沥1合，更煎1-2沸，温服，不拘时候。', '《圣惠》卷四十五', '风毒脚气发盛，四肢皮肤及小腹顽痹不仁，言语謇涩，或至失音，心神昏愦。', '-', '-', '升麻', '[\'独活\', \'两川\', \'升麻\', \'羚羊角\', \'麻黄\', \'两去\', \'根节\', \'防风\', \'两去\', \'芦头\', \'桂心\', \'半两\', \'附子\', \'分炮裂\', \'去皮\']');
INSERT INTO `prescript` VALUES (59870, '17459', '茯神散', '茯神1两，木通1两（锉），川升麻1两，犀角屑半两，赤石脂1两，远志1两（去心），麦门冬1两半（去心，焙），桂心半两，甘草半两（炙微赤，锉）。', '每服4钱，以水1中盏，加竹茹1分，煎至6分，去滓，食后温服。', '《圣惠》卷二十六', '心劳实热，皮毛干焦，色无润泽，心神不安。', '-', '-', '升麻', '[\'茯神\', \'木通\', \'锉川\', \'升麻\', \'犀角\', \'半两\', \'赤石脂\', \'远志\', \'两去\', \'麦门冬\', \'两半\', \'心焙桂心\', \'半两\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (59871, '17457', '麦门冬汤', '麦门冬（去心）3钱，干葛3钱，人参（去芦）2钱，赤芍药2钱，升麻2钱，赤茯苓（去皮）2钱，甘草2钱，石膏末5钱。', '上（口父）咀。每服2钱，水1盏，煎7分，不拘时候温服。', '《活幼心书》卷下', '斑疹热毒，头痛烦闷，狂渴妄语。麻疹内外热盛，色紫黑者。', '-', '-', '升麻', '[\'麦门冬\', \'心钱\', \'干葛钱\', \'人参\', \'芦钱\', \'芍药\', \'升麻\', \'茯苓\', \'去皮\', \'甘草\', \'石膏\']');
INSERT INTO `prescript` VALUES (59872, '17425', '大青散', '大青半两，知母半两，柴胡半两（去苗），葛根半两（锉），甘草半两（炙微赤，锉），川升麻半两，石膏1两，黄芩半两，川芒消1分，赤芍药半两，栀子仁半两。', '每服1钱，以水1小盏，煎至5分，去滓温服，不拘时候。', '《圣惠》卷八十四', '小儿伤寒，头痛壮热，烦渴。', '-', '-', '升麻', '[\'大青\', \'半两\', \'知母\', \'半两\', \'柴胡\', \'半两\', \'去苗\', \'葛根\', \'半两\', \'甘草\', \'半两\', \'炙微\', \'锉川\', \'升麻\', \'半两\', \'石膏\', \'黄芩\', \'半两\', \'川芒\', \'消分\', \'芍药\', \'半两\', \'栀子\', \'半两\']');
INSERT INTO `prescript` VALUES (59873, '17401', '凉血解毒汤', '当归1钱1分，白芷5分，升麻4分，紫草1钱5分，红花1钱，赤芍1钱，桔梗8分，连翘1钱。', '加灯心20根，水煎服。', '《痘科金镜赋》卷六', '女人非经期出痘发热时而血忽至。', '重身者禁之。', '-', '升麻', '[\'当归\', \'钱分\', \'白芷\', \'升麻\', \'紫草\', \'分红\', \'花钱\', \'赤芍\', \'桔梗\', \'连翘\']');
INSERT INTO `prescript` VALUES (59874, '17398', '酒煮当归丸', '茴香5钱，黑附子（炮制，去皮脐）7钱，良姜7钱，当归1两（上4味锉如麻豆大，以上等好酒1升半同煎至酒尽，焙干），炙甘草5钱，苦楝（生用）5钱，丁香5钱，木香1钱，升麻1钱，柴胡2钱，炒黄盐3钱，全蝎3钱，延胡索4钱。', '每服50-70丸，空心淡醋汤送下。', '《兰室秘藏》卷中', '升阳胜湿。主(疒颓)疝，白带下注，脚气。腰以下如在冰雪中，以火焙炕，重重厚绵衣盖其上，犹寒冷不任，面白如枯鱼之象，肌肉如刀割削瘦。小便不止，与白带长流而不禁固，自不知不觉，面白目青，蓝如菜色，目（目巟）（目巟）无所见，身重如山，行步倚侧，不能安地，腿膝枯细，大便难秘，口不能言，无力之极，食不下，心下痞，烦心懊憹，不任其苦，面停垢，背恶寒，小便遗而不知，此上中下三阳真气俱虚竭。胃虚之极，哕呕不止，脉沉厥紧而涩，按之空虚。小腹下痛。', '-', '-', '升麻', '[\'茴香\', \'附子\', \'炮制\', \'去皮\', \'脐钱良\', \'姜钱\', \'当归\', \'两上\', \'麻豆\', \'以上\', \'升半同\', \'焙干\', \'甘草\', \'苦楝\', \'用钱\', \'丁香\', \'木香\', \'升麻\', \'柴胡\', \'黄盐\', \'全蝎\', \'延胡索\']');
INSERT INTO `prescript` VALUES (59875, '17390', '麻黄散', '麻黄1两（去根节），羚羊角屑1两，羌活1两，桂心半两，防风3分（去芦头），细辛3分，枳壳1两（麸炒微黄，去瓤），川升麻3分，甘草半两（炙微赤，锉）。', '每服3钱，以水1中盏，加生姜半分、薄荷3-7叶，煎至6分，去滓温服，不拘时候。', '《圣惠》卷六十九', '妇人中风，身如角弓反张，咽喉胸膈痰壅不利。', '-', '-', '升麻', '[\'麻黄\', \'两去\', \'根节\', \'羚羊角\', \'羌活\', \'两桂心\', \'半两\', \'防风\', \'分去\', \'芦头\', \'细辛\', \'枳壳\', \'两麸\', \'微黄\', \'升麻\', \'甘草\', \'半两\', \'炙微\']');
