INSERT INTO `prescript` VALUES (59877, '17381', '虎骨散', '虎头骨1两（涂酥，炙令黄），鳖甲1两（涂醋，炙微黄，去裙襕），川升麻1两，柴胡1分（去苗），桃仁1两（汤浸，去皮尖双仁，麸炒微黄），川大黄1两（锉碎，微炒），甘草1两（炙微赤，锉）。', '每服3钱，以水1中盏，煎至6分，去滓温服，不拘时候。', '《圣惠》卷三十一', '传尸羸瘦，肢节烦疼，不思饮食。', '忌苋莱。', '-', '升麻', '[\'头骨\', \'两涂\', \'炙令\', \'鳖甲\', \'两涂\', \'微黄\', \'升麻\', \'柴胡\', \'分去\', \'桃仁\', \'两汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\', \'大黄\', \'甘草\', \'两炙微\']');
INSERT INTO `prescript` VALUES (59878, '17246', '赤茯苓散', '赤茯苓1两，子芩3分，桑螵蛸3分（微炒），汉防己1分，羚羊角屑3分，射干半两，川升麻3分，川大黄3分（锉碎，微炒），瞿麦1两，大青2分，木通3分（锉）。', '每服3钱，以水一中盏，煎至6分，去滓温服，不拘时候。', '《圣惠》卷七', '膀胱实热，腹胀，小便不通，口舌干燥，咽肿不利。', '-', '-', '升麻', '[\'茯苓\', \'两子\', \'桑螵蛸\', \'分微\', \'炒汉\', \'防己\', \'羚羊角\', \'射干\', \'半两\', \'升麻\', \'分川\', \'大黄\', \'瞿麦\', \'两大青分\', \'木通\']');
INSERT INTO `prescript` VALUES (59879, '17216', '独活散', '独活1两，木香1两，射干1两，连翘1两，甘草1两（生，锉），桑寄生1两，川升麻1两，沉香1两，川大黄1两（生用）。', '每服4钱，以水1中盏，煎至6分，去滓，加竹沥半合，更煎1-2沸，放温服之，1日3次，得快利为度。', '《圣惠》卷六十四', '恶核风结肿毒，四肢烦热拘急。', '-', '-', '升麻', '[\'独活\', \'木香\', \'射干\', \'连翘\', \'甘草\', \'两生\', \'桑寄生\', \'两川\', \'升麻\', \'沉香\', \'两川\', \'大黄\', \'两生用\']');
INSERT INTO `prescript` VALUES (59880, '17193', '赤箭丸', '赤箭半两，天雄半两（炮裂，去皮脐），犀角屑半两，天南星半两（炮裂），白花蛇半两（酥拌，微炒），独活半两，防风半两（去芦头），芎半两，白附子半两（炮裂），川升麻半两，白术半两，白僵蚕半两（微炒），桑螵蛸半两（微炒），当归半两（锉，微炒），细辛半两，酸枣仁半两（微炒），萆薢半两，牛黄1分（研），朱砂1分（研），麝香1分（研），龙脑1分（研）。', '每服30丸，以豆淋酒送下，不拘时候。', '《圣惠》卷三', '肝脏中风，攻手足缓弱无力，口眼斜，精神不定，行步艰难。', '忌湿面、猪肉、羊血、毒鱼。', '-', '升麻', '[\'赤箭\', \'半两\', \'天雄\', \'半两\', \'炮裂\', \'去皮\', \'犀角\', \'半两\', \'天南星\', \'半两\', \'炮裂\', \'白花蛇\', \'半两\', \'独活\', \'半两\', \'防风\', \'半两\', \'芦头\', \'半两\', \'白附子\', \'半两\', \'炮裂川\', \'升麻\', \'半两\', \'白术\', \'半两\', \'白僵蚕\', \'半两\', \'桑螵蛸\', \'半两\', \'当归\', \'半两\', \'细辛\', \'半两\', \'酸枣仁\', \'半两\', \'半两\', \'牛黄\', \'分研\', \'朱砂\', \'分研\', \'麝香\', \'分研\', \'龙脑\', \'分研\']');
