INSERT INTO `prescript` VALUES (59911, '16334', '黄龙汤', '伏龙肝半两，当归3分（锉，微炒），甘草3分（炙微赤，锉），赤芍药3分，黄芩3分，川朴消3分，川升麻3分，生干地黄1两半。', '每服5钱，以水1大盏，入竹茹1分，煎至5分，去滓，不拘时候温服。', '《圣惠》卷十八', '去五脏热气。主热病鼻衄。', '-', '-', '升麻', '[\'伏龙肝\', \'半两\', \'当归\', \'甘草\', \'分炙微\', \'芍药\', \'黄芩\', \'分川朴\', \'消分川\', \'升麻\', \'分生\', \'地黄\', \'两半\']');
INSERT INTO `prescript` VALUES (59912, '16274', '虎睛丸', '虎睛1对（酒浸1宿，微炙，细研），朱砂1两（细研，水飞），铁粉1两，露蜂房半两（微炙），羌活半两，钩藤半两，牛黄半两（细研），蚱蝉4枚（去翅足，微炙），防葵半两，麻黄半两（去根节），龙齿1两（细研），川升麻半两，细辛1分，石膏1两（细研，水飞）。', '每服5丸，温水送下。', '《圣惠》卷八十五', '小儿癫痫，至大不愈，或发即口出白沫，并大小便出不知。', '-', '-', '升麻', '[\'虎睛\', \'浸宿\', \'微炙细研\', \'朱砂\', \'两细\', \'研水飞\', \'铁粉\', \'露蜂房\', \'半两\', \'羌活\', \'半两\', \'钩藤\', \'半两\', \'牛黄\', \'半两\', \'细研\', \'蚱蝉\', \'微炙防葵\', \'半两\', \'麻黄\', \'半两\', \'根节\', \'龙齿\', \'两细\', \'研川\', \'升麻\', \'半两\', \'细辛\', \'石膏\', \'两细\', \'研水飞\']');
INSERT INTO `prescript` VALUES (59913, '16258', '麦门冬汤', '麦门冬（去心）1两半，升麻1两，知母（锉，焙）1两，甘草（炙，锉）1两，鳖甲（醋炙，去裙襕）1两，柴胡（去苗）1两，前胡（去芦头）1两，桃仁（去皮尖双仁，炒研）1两，枳壳（去瓤，麸炒）1两，栀子（去皮）半两，芦根（锉）半两，乌梅肉（炒）半两，人参3分。', '每服3钱匕，水1盏半，加桃枝5寸，柳枝5寸（锉），生姜3片，煎至8分，去滓，入石膏末半钱匕，更煎沸，未发前1-2服。', '《圣济总录》卷三十六', '兼补心气。主肺疟。', '-', '-', '升麻', '[\'麦门冬\', \'去心\', \'两半\', \'升麻\', \'知母\', \'甘草\', \'鳖甲\', \'柴胡\', \'去苗\', \'两前\', \'胡去\', \'芦头\', \'桃仁\', \'皮尖\', \'双仁\', \'研两\', \'枳壳\', \'栀子\', \'去皮\', \'半两\', \'芦根\', \'半两\', \'乌梅\', \'半两\', \'人参\']');
INSERT INTO `prescript` VALUES (59914, '16248', '大黄散', '川大黄半两（锉碎，微炒），川升麻半两，栀子仁半两，川朴消半两，葵子半两。', '以水2大盏，煮取1盏3分，去滓，分温3服。以快利为度。', '《圣惠》卷六十一', '痈疽，脏腑壅热太过，心神烦闷，大小便不通。', '-', '-', '升麻', '[\'大黄\', \'半两\', \'碎微\', \'炒川\', \'升麻\', \'半两\', \'栀子\', \'半两\', \'川朴消\', \'半两\', \'葵子\', \'半两\']');
INSERT INTO `prescript` VALUES (59915, '16237', '大青煎', '大青半两，黄柏（去粗皮，蜜炙）半两，升麻半两，射干半两，蔷薇根半两，苦竹叶1握（细切），生地黄半两，玄参1两，白蜜2两，天门冬（去心，焙）半两。', '用水3升，煎取1升，去滓下蜜，再煎成膏。每服半匙，含化咽津，不拘时候。', '《圣济总录》卷一二四', '喉中热塞，及舌上腭生疮。', '-', '-', '升麻', '[\'大青\', \'半两\', \'黄柏\', \'粗皮蜜\', \'半两\', \'升麻\', \'半两\', \'射干\', \'半两\', \'蔷薇\', \'半两\', \'苦竹叶\', \'握细切\', \'生地黄\', \'半两\', \'玄参\', \'两白蜜\', \'天门冬\', \'半两\']');
INSERT INTO `prescript` VALUES (59916, '16173', '刺蓟散', '刺蓟半两，川升麻半两，鹿角胶半两（捣碎，炒令黄燥），羚羊角屑半两，青竹茹半两，当归半两（锉，微炒），生干地黄1两，甘草1分（生用）。', '以水2大盏半，煎至1盏半，去滓，分温5服，不拘时候。', '《圣惠》卷六', '肺壅热，吐血不止。', '-', '-', '升麻', '[\'半两\', \'升麻\', \'半两\', \'鹿角胶\', \'半两\', \'捣碎\', \'炒令\', \'黄燥\', \'羚羊角\', \'半两\', \'竹茹\', \'半两\', \'当归\', \'半两\', \'锉微\', \'炒生\', \'地黄\', \'甘草\', \'分生用\']');
INSERT INTO `prescript` VALUES (59917, '16151', '茯神煮散', '茯神36铢，麦门冬36铢，通草30铢，升麻30铢，紫菀18铢，桂心18铢，知母1两，赤石脂42铢，大枣20个，淡竹茹（鸡子大）1枚。', '茯神汤（《圣济总录》卷四十三）。本方方名，《千金方衍义》引作“茯神散”。', '《千金》卷十三', '心实热，口干烦渴，眼卧不安。', '忌生葱、酢物。', '-', '升麻', '[\'茯神\', \'麦门冬\', \'通草\', \'升麻\', \'紫菀\', \'桂心\', \'知母\', \'赤石脂\', \'大枣\', \'个淡\', \'竹茹\', \'鸡子\', \'大枚\']');
INSERT INTO `prescript` VALUES (59918, '16138', '羚羊角散', '羚羊角屑1两，川升麻1两，独活1两半，防风1两（去芦头），赤茯苓1两，川朴消1两，旋复花半两，子芩1两，石膏2两，槟榔2两。', '每服4钱，以水1中盏，煎至6分，去滓温服，不拘时候。', '《圣惠》卷四十五', '风毒脚气上攻，心膈壅闷，语言謇涩，头目烦疼，腹胁气滞，大小便难。', '-', '-', '升麻', '[\'羚羊角\', \'两川\', \'升麻\', \'独活\', \'两半\', \'防风\', \'两去\', \'芦头\', \'茯苓\', \'两川朴\', \'消两\', \'旋复花\', \'半两\', \'子芩\', \'石膏\', \'槟榔\']');
INSERT INTO `prescript` VALUES (59919, '16049', '大黄泻热汤', '大黄3两（切，别渍），黄芩3两，泽泻3两，升麻3两，羚羊角4两，栀子仁4两，生地黄汁1升，玄参8两，芒消3两。', '以水7升，先煮7味，取2升3合，下大黄更煎数沸，绞去滓，下消，分3服。', '《外台》卷六引《删繁方》', '开关格，通隔绝。主中焦实热闭塞，上下不通，隔绝关格，不吐不下，腹满膨膨，喘急。', '忌芜荑。', '-', '升麻', '[\'大黄\', \'两切\', \'别渍\', \'黄芩\', \'泽泻\', \'升麻\', \'羚羊角\', \'栀子\', \'生地黄\', \'汁升\', \'玄参\', \'两芒\', \'消两\']');
INSERT INTO `prescript` VALUES (59920, '16026', '除湿健脾汤', '白术（去芦，炒）1钱5分，苍术（术泔浸，炒）1钱，白茯苓（去皮）1钱，白芍（醋炒）1钱，当归8分，厚朴（去皮，姜炒）6分，陈皮8分，猪苓7分，泽泻7分，柴胡5分，升麻5分，防风（去芦）6分，甘草（炙）4分。', '加生姜3片，大枣1枚，水煎，早、晚热服。久泻，加南星（面包煨）7分。', '《回春》卷三', '久泻，色苍而齿疏，倦怠，食减下坠。', '-', '-', '升麻', '[\'白术\', \'去芦\', \'钱分\', \'苍术\', \'泔浸\', \'白茯苓\', \'去皮\', \'白芍\', \'当归\', \'厚朴\', \'去皮\', \'炒分\', \'陈皮分\', \'猪苓\', \'泽泻\', \'柴胡\', \'升麻\', \'防风\', \'芦分\', \'甘草\', \'炙分\']');
INSERT INTO `prescript` VALUES (59921, '16017', '丁香茱萸汤', '干生姜2分，黄柏2分，丁香5分，炙甘草5分，柴胡5分，橘皮5分，半夏5分，升麻7分，吴茱萸1钱，草豆蔻1钱，黄耆1钱，人参1钱，当归身1钱5分，苍术2钱。', '每服半两，水2盏，煎至1盏，去滓，食前稍热服。', '《脾胃论》卷下', '胃虚呕哕吐逆，膈咽不通。', '忌冷物。', '-', '升麻', '[\'生姜\', \'黄柏\', \'丁香\', \'甘草\', \'柴胡\', \'橘皮\', \'半夏\', \'升麻\', \'吴茱萸\', \'草豆蔻\', \'黄耆\', \'人参\', \'当归\', \'身钱分\', \'苍术\']');
INSERT INTO `prescript` VALUES (59922, '16003', '丁香吴茱萸汤', '吴茱萸1钱，草豆蔻1钱，人参1钱，苍术1钱，黄芩1钱，升麻7分，当归1钱半，柴胡5分，半夏5分，茯苓5分，干姜5分，丁香5分，甘草5分。', '每服半两，水2盏，煎至1盏，去滓，食前热服。', '《准绳·类方》卷三引东垣方', '胃寒呕吐哕。', '忌冷物。', '-', '升麻', '[\'吴茱萸\', \'草豆蔻\', \'人参\', \'苍术\', \'黄芩\', \'升麻\', \'当归\', \'钱半\', \'柴胡\', \'半夏\', \'茯苓\', \'干姜\', \'丁香\', \'甘草\']');
INSERT INTO `prescript` VALUES (59923, '15975', '金银补益汤', '金银花1两，生黄耆3钱，甘草1钱，人参3钱，白术2钱，陈皮1钱，升麻5分，柴胡1钱，当归3钱。', '水煎服。', '《洞天奥旨》卷十四', '疮疡，元气虚倦，口干发热。', '-', '-', '升麻', '[\'金银花\', \'两生\', \'黄耆\', \'甘草\', \'人参\', \'白术\', \'陈皮\', \'升麻\', \'柴胡\', \'当归\']');
INSERT INTO `prescript` VALUES (59924, '15970', '柴胡散', '柴胡（去苗）1两，黄芩1两，栀子仁1两，赤芍药1两，川升麻1两，麦门冬（去心）1两，甘草（炙微赤，锉）1两，玄参1两。', '每服4钱，以水1中盏，加淡竹叶14片，煎至6分，去滓，食后温服。', '《圣惠》卷三十三', '斑痘疮入眼，疼痛壮热，口干烦渴。', '-', '-', '升麻', '[\'柴胡\', \'黄芩\', \'栀子\', \'芍药\', \'两川\', \'升麻\', \'麦门冬\', \'甘草\', \'炙微\', \'玄参\']');
INSERT INTO `prescript` VALUES (59925, '15969', '黄耆汤', '黄耆2两（锉），人参2两，生干地黄2两（焙），甘草2两（炙，锉），白芍药2两，桂1两（去粗皮），黄芩1两（去黑心），赤茯苓（去黑皮）1两，升麻3两。', '每服5钱匕，水1盏半，加大枣3枚（擘破），生姜3片，同煎数沸，次下竹叶7片，更煎2-3沸，去滓取1盏，空心温服，日2次。', '《圣济总录》卷一八四', '调顺阴阳，去热益气。主乳石热盛，虚弱痞结羸瘦。', '-', '-', '升麻', '[\'黄耆\', \'人参\', \'两生\', \'地黄\', \'两焙\', \'甘草\', \'芍药\', \'两桂\', \'两去\', \'粗皮\', \'黄芩\', \'两去\', \'黑心\', \'茯苓\', \'黑皮\', \'升麻\']');
INSERT INTO `prescript` VALUES (59926, '15956', '调中益气汤', '人参1钱半，黄炙（蜜炙）3钱，白术（炒）1钱半，苍术（炒）1钱，当归2钱，白芷1钱，升麻5分，柴胡5分，陈皮1钱半，甘草5分。', '水煎去滓，温服。', '《医略六书》卷二十', '中虚湿伏，抑遏清阳，恶寒终日不罢，脉缓弱者。', '-', '-', '升麻', '[\'人参\', \'钱半\', \'黄炙蜜\', \'白术\', \'苍术\', \'当归\', \'白芷\', \'升麻\', \'柴胡\', \'陈皮\', \'甘草\']');
INSERT INTO `prescript` VALUES (59927, '15938', '鬼臼丸', '鬼臼半两（去须），川升麻3分，麝香1钱，柴胡1两（去苗）。', '每服10丸，以暖酒送下，日3次，不拘时候。', '《圣惠》卷五十五', '一切劳疾，飞尸，鬼疰等。', '-', '-', '升麻', '[\'鬼臼\', \'半两\', \'须川\', \'升麻\', \'麝香\', \'柴胡\', \'两去\']');
INSERT INTO `prescript` VALUES (59928, '15878', '蒌贝散', '栝蒌1钱，贝母（去心，研）1钱，南星1钱，甘草（生）1钱，连翘（去心）1钱（一方加青皮、升麻）。', '水2钟，煎8分，澄渣，加酒2分，食远服。', '《金鉴》卷六十六', '乳劳初肿气实者。', '-', '-', '升麻', '[\'贝母\', \'去心\', \'研钱\', \'南星\', \'甘草\', \'生钱\', \'连翘\', \'心钱\', \'一方\', \'青皮\', \'升麻\']');
INSERT INTO `prescript` VALUES (59929, '15853', '大青散', '黄芩2两，芍药2两，羚羊角（屑）2两，苦竹叶2两，黄柏2两，大青2两，升麻2两。', '以水7升，煎取2升，去滓，纳蜜2合搅，含冷吐，愈。', '《普济方》卷二九九引《肘后方》', '口疮。', '-', '-', '升麻', '[\'黄芩\', \'芍药\', \'羚羊角\', \'苦竹叶\', \'黄柏\', \'两大青\', \'升麻\']');
INSERT INTO `prescript` VALUES (59930, '15828', '虎骨散', '虎骨（酥炙令黄）1两，乌梅肉1两，赤茯苓（去黑皮，锉）1两，肉苁蓉（酒浸，切，焙）1两，人参1两，鳖甲（去裙襕，醋浸，炙）1两，甘草（炙）1两，芍药1两，当归（切，焙）3分，前胡（去芦头）3分，白术1两，豉（慢火微炒）1两，紫菀（去土）1两，黄耆（锉，焙）1两，常山（炒）1分，枳壳（去瓤，麸炒）1分，犀角（镑）1分，知母（切，焙）1分，升麻3分，柴胡（去苗）3分，桔梗（炒）3分，天灵盖（酥炙）3分，桂（去粗皮）3分，木香3分，桃仁（汤浸，去皮尖双仁，炒）49枚。', '每服2钱匕，温酒调下，空心、临卧各1服。若为丸，即炼蜜为丸，如梧桐子大。每服20-30丸，温酒送下。', '《圣济总录》卷十', '白虎风，走注疼痛，发歇不定，积年不效者。', '-', '-', '升麻', '[\'虎骨\', \'酥炙令\', \'乌梅\', \'茯苓\', \'黑皮\', \'肉苁蓉\', \'浸切\', \'人参\', \'鳖甲\', \'甘草\', \'芍药\', \'当归\', \'切焙\', \'分前\', \'胡去\', \'芦头分\', \'白术\', \'两豉\', \'慢火\', \'紫菀\', \'黄耆\', \'常山\', \'炒分\', \'枳壳\', \'炒分\', \'犀角\', \'知母\', \'切焙分\', \'升麻\', \'柴胡\', \'苗分\', \'桔梗\', \'炒分\', \'天灵盖\', \'分桂去\', \'粗皮分\', \'木香\', \'桃仁\', \'汤浸\', \'皮尖\', \'双仁\']');
INSERT INTO `prescript` VALUES (59931, '15773', '柴胡散', '柴胡1两（去苗），川升麻3分，黄芩3分，石膏2两，麦门冬1两（去心），犀角屑3分，葛根3分（锉），甘草3分（炙微赤，锉）。', '每服5钱，以水1大盏，加葱白2茎，竹叶2-7片，煎至5分，去滓温服，不拘时候。', '《圣惠》卷十六', '时气心神壅闷，烦渴不止。时气头痛，骨肉烦疼，口燥心闷，外寒内热，已自下利，虚热未退。', '-', '-', '升麻', '[\'柴胡\', \'两去\', \'苗川\', \'升麻\', \'黄芩\', \'石膏\', \'麦门冬\', \'两去\', \'犀角\', \'葛根\', \'甘草\', \'分炙微\']');
INSERT INTO `prescript` VALUES (59932, '15723', '复正散', '僵蚕7钱，清风藤7钱，生地黄7钱，白附子7钱，当归5钱，川芎5钱，何首乌5钱，防风5钱，白芷5钱，荆芥5钱，天麻5钱，蒺藜5钱，赤芍5钱，胡麻5钱，连翘5钱，桔梗5钱，藁本5钱，蔓荆子5钱，羌活3钱，全蝎3钱，升麻2钱，白僵蚕2钱，金银花1两，白花蛇2两（净）。', '上以好酒20斤，大坛盛封。早、晚随量饮之。', '《外科百效》卷下', '口眼歪斜，痫症。', '-', '-', '升麻', '[\'僵蚕\', \'清风\', \'藤钱\', \'生地黄\', \'白附子\', \'当归\', \'川芎\', \'何首乌\', \'防风\', \'白芷\', \'荆芥\', \'天麻\', \'蒺藜\', \'赤芍\', \'胡麻\', \'连翘\', \'桔梗\', \'藁本\', \'蔓荆子\', \'羌活\', \'全蝎\', \'升麻\', \'白僵蚕\', \'金银花\', \'白花蛇\', \'两净\']');
INSERT INTO `prescript` VALUES (59933, '15683', '桂心汤', '桂（去粗皮）3分，升麻1两，防风（去叉）1两，麻黄（去根节，煎，掠去沫，焙）1两，芎藭1两半，羚羊角（镑）1两半。', '每服3钱匕，水1盏，煎至7分，去滓，加竹沥半合，再煎2-3沸，温服，不拘时候。', '《圣济总录》卷一六一', '产后中风口，言语不利，筋脉拘急。', '-', '-', '升麻', '[\'桂去\', \'粗皮分\', \'升麻\', \'防风\', \'麻黄\', \'根节\', \'掠去\', \'两半\', \'羚羊角\', \'两半\']');
INSERT INTO `prescript` VALUES (59934, '15671', '羚羊角散', '羚羊角屑1两，红雪2两，旋复花半两，川升麻1两，紫苏茎叶1两，槟榔1两，枳壳1两（麸炒微黄，去瓤），麦门冬1两（去心），前胡1两（去芦头），独活1两，甘草半两（炙微赤，锉）。', '每服4钱，以水1中盏，加生姜半分、薄荷20叶，煎至6分，去滓温服，不拘时候。', '《圣惠》卷四十五', '脚气冲心，烦闷，眼前暗黑不识人。', '-', '-', '升麻', '[\'羚羊角\', \'两红雪\', \'旋复花\', \'半两\', \'升麻\', \'紫苏\', \'槟榔\', \'枳壳\', \'两麸\', \'微黄\', \'麦门冬\', \'两去\', \'心前\', \'胡两去\', \'芦头\', \'独活\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (59935, '15662', '大人参白虎汤', '人参1钱，石膏4钱，知母1钱5分，加升麻、防风、牛子、炒芩。', '-', '《种痘新书》卷十一', '毒气本盛，元气又亏，而麻疹出不快者。', '-', '-', '升麻', '[\'人参\', \'石膏\', \'知母\', \'分加\', \'升麻\', \'防风\', \'牛子\']');
INSERT INTO `prescript` VALUES (59936, '15655', '麻黄散', '麻黄3分（去根节），防风3分（去芦头），桂心半两，当归半两（锉碎，微炒），槟榔半两，黄芩3分，独活3分，甘草半两（炙微赤，锉），川升麻3分，犀角屑3分，赤茯苓等3分。', '每服4钱，以水1中盏，加生差半分，煎至6分，去滓温服，不拘时候。', '《圣惠》卷四十五', '脚气缓弱，顽痹少力，语涩心烦。', '-', '-', '升麻', '[\'麻黄\', \'分去\', \'根节\', \'防风\', \'分去\', \'芦头\', \'桂心\', \'半两\', \'当归\', \'半两\', \'槟榔\', \'半两\', \'黄芩\', \'独活\', \'甘草\', \'半两\', \'炙微\', \'锉川\', \'升麻\', \'犀角\', \'茯苓\']');
INSERT INTO `prescript` VALUES (59937, '15643', '瞿麦散', '瞿麦1两，葵子半两，木通1两（锉），黄连1两（去须），防风1两（去芦头），茯神2两，冬瓜仁1两，甘菊花半两，葳蕤1两，川升麻1两，地骨皮1两。', '每服4钱，以水1中盏，煎至6分，去滓，食前温服。', '《圣惠》卷五十八', '心热，小便难赤涩痛。', '-', '-', '升麻', '[\'瞿麦\', \'葵子\', \'半两\', \'木通\', \'黄连\', \'两去\', \'防风\', \'两去\', \'芦头\', \'茯神\', \'冬瓜\', \'甘菊花\', \'半两\', \'葳蕤\', \'两川\', \'升麻\', \'地骨皮\']');
INSERT INTO `prescript` VALUES (59938, '15632', '除风湿羌活汤', '羌活1两，防风（去芦）1钱，苍术（酒浸，去皮）1钱，黄耆1钱，升麻7分，炙甘草5分，独活5分，柴胡5分，川芎3分，黄柏3分，橘皮3分，藁本3分，泽泻（去须）3分，猪苓（去黑皮）3分，茯苓3分，黄连（去须）1分。', '本方方名，《玉机微义》引作“羌活汤”。', '《脾胃论》卷中', '风湿热痹，痿证，眩晕麻木。湿气胜，风证不退，眩晕麻木不已者。着痹。热痹，或痛风挟热者，肌肉热极，体上如鼠走，唇裂。', '-', '-', '升麻', '[\'羌活\', \'防风\', \'芦钱\', \'苍术\', \'酒浸\', \'去皮\', \'黄耆\', \'升麻\', \'甘草\', \'独活\', \'柴胡\', \'川芎\', \'黄柏\', \'橘皮\', \'藁本\', \'泽泻\', \'猪苓\', \'黑皮\', \'茯苓\', \'黄连\']');
INSERT INTO `prescript` VALUES (59939, '15608', '钩藤饮', '钩藤3分，蚱蝉（去头足翅，炙）2枚，犀角屑（微炒）半两，麦门冬（去心，焙）半两，升麻半两，石膏（捣碎）3分，柴胡（去苗）半两，甘草（微炙）1分。', '每服2钱匕，水1小盏，煎至6分，去滓，下竹沥半合，重煎3-5沸，分温3服。空心、午后、夜卧各1次。', '《圣济总录》卷一六九', '小儿惊热，睡眠不稳。', '-', '-', '升麻', '[\'钩藤\', \'蚱蝉\', \'头足\', \'犀角\', \'半两\', \'麦门冬\', \'半两\', \'升麻\', \'半两\', \'石膏\', \'捣碎\', \'柴胡\', \'去苗\', \'半两\', \'甘草\', \'微炙分\']');
INSERT INTO `prescript` VALUES (59940, '15607', '黄柏汤', '黄柏（去粗皮，炙）半两，升麻1两，木通（锉）1两，竹茹3分，麦门冬（去心焙）1两半，玄参1两，前胡（去芦头）3分，大青3分。', '每服3钱匕，水1盏，煎至7分，去浑，入芒消末1钱，搅令匀，温服。如鼻中有疮，以地黄汁少许滴鼻中，1日3-5次，不拘时候。要通利，加芒消；不欲利，去之。', '《圣济总录》卷一二三', '咽喉闭塞生疮，及干呕、头痛、食不下。', '-', '-', '升麻', '[\'黄柏\', \'粗皮\', \'半两\', \'升麻\', \'木通\', \'竹茹\', \'麦门冬\', \'两半\', \'玄参\', \'两前\', \'胡去\', \'芦头\', \'分大青分\']');
INSERT INTO `prescript` VALUES (59941, '15566', '黄耆散', '黄耆半两（锉），连翘半两，川升麻半两，玄参1分，丹参1分，露蜂房1分（微炙），枳壳半两（麸炒微黄，去瓤），甘草1分（炙微赤，锉）。', '每服1钱，以水1小盏，煎至5分，去滓，放温服。', '《圣惠》卷九十', '小儿疽肿及疮疖，身体壮热，口干心躁。', '-', '-', '升麻', '[\'黄耆\', \'半两\', \'连翘\', \'半两\', \'升麻\', \'半两\', \'玄参\', \'丹参\', \'露蜂房\', \'分微\', \'枳壳\', \'半两\', \'微黄\', \'甘草\', \'分炙微\']');
INSERT INTO `prescript` VALUES (59942, '15509', '黄柏汤', '黄柏（去粗皮，蜜炙）2两，龙胆1两半，黄连（去须）1两，升麻1两。', '每服5钱匕，水1盏半，煎至8分，去滓，时时含咽。', '《圣济总录》卷一八三', '乳石发口疮。', '-', '-', '升麻', '[\'黄柏\', \'粗皮蜜\', \'龙胆\', \'两半\', \'黄连\', \'升麻\']');
INSERT INTO `prescript` VALUES (59943, '15481', '大青散', '大青1两，蓝叶3分，川升麻3分，秦艽1两（去苗），栝楼根1两，川芒消1两半，茵陈3分，栀子仁半两，甘草半两（炙微赤，锉）。', '每服2钱，以水1中盏，煎至5分，去滓，入竹沥半合温服，不拘时候。', '《圣惠》卷十五', '时气6日，头痛壮热，心神烦乱，积热不散，或狂走不定。', '-', '-', '升麻', '[\'大青\', \'两蓝叶\', \'分川\', \'升麻\', \'秦艽\', \'两去\', \'栝楼\', \'根两川芒\', \'两半\', \'茵陈\', \'栀子\', \'半两\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (59944, '15408', '菊花散', '甘菊花1两，防风1两（去芦头），决明子1两，栀子仁1两，黄芩1两，车前子1两，川升麻1两，玄参1两，地骨皮1两，柴胡1两（去苗），麦门冬1两（去心），生干地黄1两，甘草1两（炙微赤，锉），羚羊角屑1两。', '每服3钱，以水1中盏，加淡竹叶2-7片，煎至6分，去滓，食后温服。', '《圣惠》卷三十二', '肝心壅热，眼涩痛。', '忌炙煿、油腻、热面、生果。', '-', '升麻', '[\'甘菊花\', \'防风\', \'两去\', \'芦头\', \'决明子\', \'栀子\', \'黄芩\', \'车前子\', \'两川\', \'升麻\', \'玄参\', \'地骨皮\', \'柴胡\', \'两去\', \'麦门冬\', \'两去\', \'心生\', \'地黄\', \'甘草\', \'两炙微\', \'羚羊角\']');
INSERT INTO `prescript` VALUES (59945, '15373', '羚羊角散', '羚羊角屑3分，犀角屑3分，赤芍药3分，黄连1分（去须），马牙消1分，朱砂（研细）1分，川升麻半两，牛黄半两（研细），天竺黄半两（研细），芎半两，当归半两（锉，微炒），甘草（炙微赤，锉）半两。', '每服1钱，煎竹叶汤放温调下。', '《圣惠》卷八十九', '小儿肝脏风热，上注眼目，赤肿疼痛。', '-', '-', '升麻', '[\'羚羊角\', \'犀角\', \'芍药\', \'黄连\', \'分去\', \'马牙\', \'消分\', \'朱砂\', \'细分\', \'升麻\', \'半两\', \'牛黄\', \'半两\', \'研细\', \'天竺黄\', \'半两\', \'研细\', \'半两\', \'当归\', \'半两\', \'甘草\', \'炙微\', \'半两\']');
INSERT INTO `prescript` VALUES (59946, '15371', '柳花散', '柳花7钱，紫草1两，升麻7钱，当归8钱，赤芍6钱。', '每服7钱，葡萄煎汤送下。', '《种痘新书》卷十', '女子出痘，火毒回炽，致血妄行，非经行之期于发热之时而经水忽至者。', '-', '-', '升麻', '[\'花钱\', \'紫草\', \'升麻\', \'当归\', \'赤芍\']');
INSERT INTO `prescript` VALUES (59947, '15339', '济阴煎', '川连1钱半，白术1钱半（炒），木通1钱半，当归3钱，川芎1钱，白芷1钱半，升麻8分，生地5钱，甘草8分。', '水煎，去滓温服。', '《医略六书》卷二十六', '阴疮。脓汁淋漓，脉数者。', '-', '-', '升麻', '[\'川连钱\', \'白术\', \'钱半\', \'木通\', \'钱半\', \'当归\', \'川芎\', \'白芷\', \'钱半\', \'升麻\', \'生地\', \'甘草\']');
INSERT INTO `prescript` VALUES (59948, '15327', '金枣丹', '雄黄1两，辰砂3钱，川乌（去皮尖）3钱，升麻3钱，蜈蚣3条，蟾酥3分，闹羊花3分，麝香6分。', '每服1丸，葱包，火煨葱熟为度，葱酒送下，尽醉发汗，忌风。如至重者肿或1块，再服2丸，不取汗，全消；如久破烂者，每服半丸，不必取汗，数服自愈。', '《虺后方》', '一切外科破烂，寒伤流注。', '-', '-', '升麻', '[\'雄黄\', \'辰砂\', \'川乌\', \'皮尖\', \'升麻\', \'蜈蚣\', \'蟾酥\', \'闹羊花\', \'麝香\']');
INSERT INTO `prescript` VALUES (59949, '15303', '调理益气汤', '黄耆（蜜水炙）1钱5分，党参1钱5分（焙），炒苍术1钱，橘红5分，木香（煨）4分，柴胡4分，升麻4分，白蔻肉3分，炙甘草3分。', '引加姜皮1分，小红枣3个，空心服。', '《古方汇精》卷一', '湿热所伤，体重烦闷，口失滋味，或痰嗽稠粘，寒热不调，体倦少食，脾虚泄泻；兼治虚人疟痢。', '-', '-', '升麻', '[\'黄耆\', \'蜜水\', \'钱分\', \'党参\', \'钱分\', \'苍术\', \'橘红\', \'木香\', \'柴胡\', \'升麻\', \'分白蔻\', \'肉分\', \'甘草\']');
INSERT INTO `prescript` VALUES (59950, '15294', '大青散', '大青3分，远志3分（去心），川升麻1两半，柴胡1两（去苗），黄芩1两，犀角屑3分，人参3分（去芦头），甘草半两（炙微赤，锉），芦根半两（锉）。', '每服4钱，以水1中盏，煎至5分，去滓温服，不拘时候。', '《圣惠》卷十', '伤寒邪热在胃，谵言妄语，身体壮热。', '-', '-', '升麻', '[\'大青分\', \'远志\', \'分去\', \'心川\', \'升麻\', \'两半\', \'柴胡\', \'两去\', \'黄芩\', \'犀角\', \'人参\', \'分去\', \'芦头\', \'甘草\', \'半两\', \'炙微\', \'芦根\', \'半两\']');
INSERT INTO `prescript` VALUES (59951, '15268', '桂枝加知母石膏升麻汤', '桂枝汤内加知母4钱半，石膏4钱半，升麻2钱。', '夏至后用。', '《伤寒图歌活人指掌》卷四', '热病。', '-', '-', '升麻', '[\'桂枝汤\', \'内加\', \'知母\', \'钱半\', \'石膏\', \'钱半\', \'升麻\']');
INSERT INTO `prescript` VALUES (59952, '15255', '大青丸', '大青1两，黄芩半两，蚤休半两，黄药半两（锉），黄连半两（去须），蔷薇根皮1两（锉），川升麻半两，栝楼根半两，知母半两，石青半两（细研），马牙消1两。', '绵裹1丸，含咽津。', '《圣惠》卷三十五', '咽喉肿痛，上焦实热，口舌生疮。', '-', '-', '升麻', '[\'大青\', \'黄芩\', \'半两\', \'蚤休\', \'半两\', \'黄药\', \'半两\', \'黄连\', \'半两\', \'蔷薇\', \'根皮\', \'锉川\', \'升麻\', \'半两\', \'栝楼\', \'半两\', \'知母\', \'半两\', \'石青\', \'半两\', \'细研\', \'马牙消\']');
INSERT INTO `prescript` VALUES (59953, '15195', '枸杞汤', '枸杞子（炒）半两，赤芍药1两半，山芋1两半，升麻1两半，蒺藜子（炒）2两，茯神（去木）2两，防风（去叉）1两。', '每服5钱匕，以水1盏半，煎取7分，加生地黄汁1合，去滓温服，临卧再服。', '《圣济总录》卷&mdash;一○', '风邪客于睑肤，令眼睑垂缓，甚则眼闭难开。', '-', '-', '升麻', '[\'枸杞子\', \'半两\', \'芍药\', \'两半\', \'山芋\', \'两半\', \'升麻\', \'两半\', \'蒺藜\', \'茯神\', \'防风\']');
INSERT INTO `prescript` VALUES (59954, '15150', '胡黄连丸', '胡黄连半两，柴胡1两（去苗），赤芍药3分，鳖甲2两（涂醋炙令黄，去裙襕），知母半两，犀角屑3分，川升麻半两，玄参半两，人参半两（去芦头），地骨皮3分，当归半两，杏仁3分（汤浸，去皮尖双仁，麸炒微黄），茯神3分，枳壳3分（麸炒微黄，去瓤），麦门冬1两半（去心，焙），紫菀3分（洗去苗土），川大黄3分（锉碎，微炒），甘草半两（炙微赤，锉），秦艽3分（去苗），槟榔半两，桔梗半两（去芦头）。', '每服30丸，以粥饮下，不拘时候。', '《圣惠》卷七十', '妇人热劳烦闷，四肢黄瘦疼痛，时有咳嗽，不欲饮食。', '-', '-', '升麻', '[\'胡黄连\', \'半两\', \'柴胡\', \'两去\', \'芍药\', \'鳖甲\', \'两涂\', \'炙令\', \'黄去\', \'知母\', \'半两\', \'犀角\', \'分川\', \'升麻\', \'半两\', \'玄参\', \'半两\', \'人参\', \'半两\', \'芦头\', \'地骨皮\', \'当归\', \'半两\', \'杏仁\', \'分汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\', \'茯神\', \'枳壳\', \'分麸\', \'微黄\', \'麦门冬\', \'两半\', \'紫菀\', \'分洗\', \'苗土\', \'大黄\', \'甘草\', \'半两\', \'炙微\', \'秦艽\', \'分去\', \'槟榔\', \'半两\', \'桔梗\', \'半两\', \'芦头\']');
INSERT INTO `prescript` VALUES (59955, '15110', '羚羊角散', '羚羊角屑1两，麦门冬2两（去心），大腹皮1两（锉），川大黄1两（锉碎，微炒），川升麻1两，柴胡1两（去苗），甘草半两（炙微赤，锉）。', '每服3钱，以水1中盏，煎至6分，去滓，入玄明粉1钱，搅令匀，温服，不拘时候。', '《圣惠》卷十六', '时气热毒在脏，大肠不通。', '-', '-', '升麻', '[\'羚羊角\', \'麦门冬\', \'两去\', \'大腹皮\', \'锉川\', \'大黄\', \'碎微\', \'炒川\', \'升麻\', \'柴胡\', \'两去\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (59956, '15093', '羚羊角汤', '羚羊角屑1两，防风1两（去叉），地骨皮1两，麦门冬1两（去心，焙），茯神（去木）1两，黄芩半两（去黑心），枳壳半两（去瓤，麸炒），蕤仁半两（汤浸，去皮），芒消半两，甘草（炙）3分，升麻3分，石膏2两。', '本方方名，《普济方》引作“羚羊角散”。', '《圣济总录》卷一○八', '风毒所攻，头目俱痛，及眉骨额角疼。', '-', '-', '升麻', '[\'羚羊角\', \'防风\', \'两去\', \'地骨皮\', \'麦门冬\', \'两去\', \'茯神\', \'黄芩\', \'半两\', \'黑心\', \'枳壳\', \'半两\', \'蕤仁\', \'半两\', \'汤浸\', \'去皮\', \'芒消\', \'半两\', \'甘草\', \'炙分\', \'升麻\', \'石膏\']');
INSERT INTO `prescript` VALUES (59957, '15065', '黄耆人参汤', '黄耆1钱（如自汗过多，更加1钱），升麻6分，人参5分（去芦），橘皮5分（不去白），麦门冬5分（去心），苍术5分（无汗更加5分），白术5分，黄柏3分（酒洗），炒曲3分，当归身2分（酒洗），炙甘草2分，五味子9个。', '上（口父）咀，都作1服。水2盏，煎至1盏，去滓，食远或空心稍热服。如心下痞闷，加黄连2分或3分；如胃脘当心痛，减大寒药，加草豆蔻仁5分；如胁下痛或缩急，加柴胡2分或3分；如头痛，目中溜火，加黄连2分或3分，川芎3分；如头痛，目不清利，上壅上热，加蔓荆子、川芎各3分，藁本、生地黄各2分，细辛1分；如气短，精神如梦寐之间，困乏无力，加五味子9个；如大便涩滞，隔1-2日不见者，致食少，食不下，血少，血中伏火而不得润也，加当归身、生地黄、麻子仁泥各5分，桃仁3枚（汤泡去皮尖，另研），如大便通行，所加之药勿再服；如', '《脾胃论》卷中', '助元气，理治庚辛之不足。主脾胃虚弱，上焦之气不足，遇夏天气热盛，损伤元气，怠惰嗜卧，四肢不收，精神不足，两脚痿软，遇早晚寒厥，日高之后阳气将旺，复热如火，乃阴阳气血俱不足，故或热厥而阴虚，或寒厥而气虚，口不知味，目中溜火，而视物（目荒）（目荒）无所见，小便频数，大便难而结秘，胃脘当心而痛，两胁痛或急缩，脐下周围如绳束之急，甚则如刀刺，腹难舒伸，胸中闭塞，时显呕哕，或有痰嗽，口沃白沫，舌强，腰、背、胛眼皆痛，头痛时作，食不下，或食入即饱，全不思食，自汗尤甚，若阴气覆在皮毛之上，皆天气之热助本病也，乃庚大肠、', '忌酒、湿面、大料物之类及过食冷物。', '-', '升麻', '[\'黄耆\', \'自汗\', \'过多\', \'更加\', \'升麻\', \'人参\', \'分去\', \'橘皮\', \'分不去\', \'麦门冬\', \'分去\', \'苍术\', \'分无汗\', \'更加\', \'白术\', \'黄柏\', \'分酒\', \'曲分\', \'当归\', \'身分\', \'酒洗\', \'甘草\', \'五味子\']');
