INSERT INTO `prescript` VALUES (59959, '15036', '表解散', '白附子1分，防风1分，川芎1分，全蝎1分，麻黄（去节）1分，荆芥1分，红花1分，当归1分，蝉蜕1分，薄荷1分，羌活1分，茯苓1分，甘草1分，升麻1分，朱砂（研）半钱，麝香少许。', '每服半钱，金银薄荷汤调下。', '《普济方》卷四○三', '天行水疹，腥臊未出，潮热，麻子。', '-', '-', '升麻', '[\'白附子\', \'防风\', \'川芎\', \'全蝎\', \'麻黄\', \'节分\', \'荆芥\', \'红花\', \'当归\', \'蝉蜕\', \'薄荷\', \'羌活\', \'茯苓\', \'甘草\', \'升麻\', \'朱砂\', \'研半钱\', \'麝香\', \'少许\']');
INSERT INTO `prescript` VALUES (59960, '15010', '茯苓汤', '生黄芩3分，当归身4分，肉桂5分，炙甘草5分，猪苓6分，茯苓6分，泽泻1钱，芍药1钱5分，苍术2钱，生姜2钱，升麻2钱，柴胡2钱。', '每服水2盏，煎至1盏，去滓，食前稍热服。', '《兰室秘藏》卷下', '因伤冷饭水泄，一夜走十行，变作白痢，次日其痢赤白，腹中（疒丂）痛，减食，热躁，四肢沉困无力。', '-', '-', '升麻', '[\'黄芩\', \'当归\', \'身分\', \'肉桂\', \'甘草\', \'猪苓\', \'茯苓\', \'泽泻\', \'芍药\', \'钱分\', \'苍术\', \'生姜\', \'升麻\', \'柴胡\']');
INSERT INTO `prescript` VALUES (59961, '14994', '麻黄散', '麻黄2两（去根节），当归1两（锉，微炒），川升麻1两，知母1两，赤芍药1两，天门冬1两（去心），桂心1两，赤茯苓1两，甘草1两（炙微赤，锉），石膏2两，白术1两，干姜1两（炮裂，锉）。', '每服5钱，以水1大盏，加生姜半分、大枣3枚，煎至5分，去滓温服，不拘时候。', '《圣惠》卷九', '伤寒7日大下后，脉沉迟，手足厥逆，喉咽不利，胸膈烦躁；唾脓血，泄利不止。', '-', '-', '升麻', '[\'麻黄\', \'两去\', \'根节\', \'当归\', \'炒川\', \'升麻\', \'知母\', \'两赤\', \'芍药\', \'天门冬\', \'两去\', \'心桂心\', \'茯苓\', \'甘草\', \'两炙微\', \'石膏\', \'白术\', \'干姜\', \'两炮裂\']');
INSERT INTO `prescript` VALUES (59962, '14963', '麻黄汤', '麻黄（去根节，汤煮掠去沫）1两，防风（去叉）3两，当归（切，焙）3两，赤茯苓（去黑皮）3两，升麻2两，芎2两，白术2两，芍药2两，麦门冬（去心，焙）2两，黄芩（去黑心）2两，桂（去粗皮）2两，甘草（炙，锉）2两，杏仁（汤浸，去皮尖双仁，炒黄）30枚。', '每服5钱匕，水1盏，酒半盏，入大枣1枚（擘破），煎至1盏，去滓温服，日3夜1。', '《圣济总录》卷八十一', '恶风毒，脚气痹弱。', '-', '-', '升麻', '[\'麻黄\', \'根节\', \'掠去\', \'防风\', \'当归\', \'切焙\', \'茯苓\', \'黑皮\', \'升麻\', \'两芎\', \'白术\', \'芍药\', \'麦门冬\', \'黄芩\', \'黑心\', \'两桂去\', \'粗皮\', \'甘草\', \'杏仁\', \'汤浸\', \'皮尖\', \'双仁\', \'黄枚\']');
INSERT INTO `prescript` VALUES (59963, '14958', '麻黄散', '麻黄1两（去根节），独活1两，防风1两（去芦头），桂心半两，芎3分，当归2两（锉，微炒），羚羊角屑半两，酸枣仁1两，川升麻半两，秦艽半两（去苗），杏仁3分（汤浸，去皮尖双仁，麸炒微黄），甘草半两（炙微赤，锉）。', '防风独活汤（《普济方》卷三三九）。', '《圣惠》卷七十四', '妊娠中风，身如角弓反张，口噤语涩。', '-', '-', '升麻', '[\'麻黄\', \'两去\', \'根节\', \'独活\', \'防风\', \'两去\', \'芦头\', \'桂心\', \'半两\', \'当归\', \'羚羊角\', \'半两\', \'酸枣仁\', \'两川\', \'升麻\', \'半两\', \'秦艽\', \'半两\', \'去苗\', \'杏仁\', \'分汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (59964, '14954', '独活丸', '独活1两，防风（去芦头）1两，芎1两，细辛1两，当归1两，沉香1两，生干地黄1两，鸡舌香半两，零陵香半两，川升麻半两，甘草（炙微赤，锉）半两。', '方中生干地黄，《圣济总录》作“黄芩”。', '《圣惠》卷三十四', '牙齿历蠧，齿根暗黑。', '-', '-', '升麻', '[\'独活\', \'防风\', \'芦头\', \'两芎\', \'细辛\', \'当归\', \'沉香\', \'两生\', \'地黄\', \'两鸡\', \'舌香\', \'半两\', \'零陵\', \'半两\', \'升麻\', \'半两\', \'甘草\', \'炙微\', \'半两\']');
INSERT INTO `prescript` VALUES (59965, '14901', '除湿补气汤', '黄耆3钱，黄柏6分，陈皮1钱半，泽泻1钱，升麻1钱，白芍2钱半，生甘草2钱，生芩4钱，炙草半分，五味子50个。', '水煎。稍热服。', '《蒿崖尊生》卷十二', '暑天热伤元气，手麻木。', '-', '-', '升麻', '[\'黄耆\', \'黄柏\', \'陈皮\', \'泽泻\', \'升麻\', \'白芍\', \'钱半\', \'生甘草\', \'钱生\', \'半分\', \'五味子\']');
INSERT INTO `prescript` VALUES (59966, '14891', '钩藤汤', '钩藤1两，黄芩（去黑心）1两，升麻1两，甘草（炙令赤色，锉）1两，鳖甲（去裙襕，醋浸，炙令黄色）半两，丁香半两，大黄（锉，微炒）4两。', '钩藤饮（《普济方》卷二三七）。', '《圣济总录》卷九十三', '传尸劳瘦，骨蒸、伏连、殗殜，命在须臾，精神尚爽。', '-', '-', '升麻', '[\'钩藤\', \'黄芩\', \'黑心\', \'升麻\', \'甘草\', \'炙令\', \'赤色\', \'鳖甲\', \'浸炙令\', \'黄色\', \'半两\', \'丁香\', \'半两\', \'大黄\']');
INSERT INTO `prescript` VALUES (59967, '14873', '胡麻丸', '天麻1两，升麻1两，白附子（炮）1两，人参1两，细辛1两，川芎1两，木贼1两，定风草1两，穿山甲（去土，炒黄色）1两，丹参1两，玄参1两，何首乌（酒浸）1两，紫参1两，蔓荆子1两，防风1两，威灵仙1两，全蜈蚣1对。', '每用2两，用胡麻1斤，炒香为末，拌匀，入蜜和丸，都分为90丸。每服1丸，细嚼，温浆水下，日进3服，不拘时候。淡白粥服120日，不再发。', '《御药院方》卷一', '大风。', '-', '-', '升麻', '[\'天麻\', \'升麻\', \'白附子\', \'人参\', \'细辛\', \'川芎\', \'木贼\', \'两定\', \'风草\', \'穿山甲\', \'黄色\', \'丹参\', \'玄参\', \'何首乌\', \'酒浸\', \'两紫参\', \'蔓荆子\', \'防风\', \'威灵仙\', \'两全\', \'蜈蚣\']');
INSERT INTO `prescript` VALUES (59968, '14858', '胡黄连丸', '胡黄连1两半，鳖甲1两半，犀角1两半，诃黎勒1两半，赤茯苓1两半，甘草（炙）1两半，黄芩1两，地骨皮1两，知母（炒）1两，桔梗1两，升麻1两，柴胡1两半，人参2分，栝楼1个。', '每服20丸，食后乌梅汤和童便下；如腹痛，糯米饮下。', '《医统》卷四十六', '热痨，骨节烦疼，心膈躁闷；亦治虚劳骨蒸。', '忌苋菜。', '-', '升麻', '[\'胡黄连\', \'两半\', \'鳖甲\', \'两半\', \'犀角\', \'两半\', \'黎勒\', \'两半\', \'茯苓\', \'两半\', \'甘草\', \'两半\', \'黄芩\', \'地骨皮\', \'知母\', \'桔梗\', \'升麻\', \'柴胡\', \'两半\', \'人参\', \'栝楼\']');
INSERT INTO `prescript` VALUES (59969, '14843', '茯神汤', '茯神（去木）1两，赤芍药1两，葛根（锉）1两，升麻1两半，地骨皮（锉）1两半，黄芩（去黑心）1两半，大黄（锉，炒）1两。', '每服4钱匕，水1盏半，煎至4分，去滓温服，食后、临卧各1次。', '《圣济总录》卷&mdash;一○', '斑疮入眼。', '-', '-', '升麻', '[\'茯神\', \'芍药\', \'葛根\', \'升麻\', \'两半\', \'地骨皮\', \'两半\', \'黄芩\', \'黑心\', \'两半\', \'大黄\']');
INSERT INTO `prescript` VALUES (59970, '14816', '大黄散', '川大黄1两半（锉碎，微炒），柴胡3分（去苗），赤芍药3分，鳖甲1两（涂醋，炙令黄，去裙襕），黄芩3分，犀角屑3分，川升麻3分，赤茯苓3分，知母3分，槟榔3分，杏仁3分（汤浸，去皮尖双仁麸炒微黄），木通1两（锉）。', '每服4钱，以水1中盏，煎至6分，去滓温服，不拘时候。如人行10里再服。以通利为度。', '《圣惠》卷十', '伤寒虽得汗后，热不除，心腹烦满，大小便秘涩。', '-', '-', '升麻', '[\'大黄\', \'两半\', \'柴胡\', \'分去\', \'芍药\', \'鳖甲\', \'两涂\', \'炙令\', \'黄去\', \'黄芩\', \'犀角\', \'分川\', \'升麻\', \'分赤\', \'茯苓\', \'知母\', \'槟榔\', \'杏仁\', \'分汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\', \'木通\']');
INSERT INTO `prescript` VALUES (59971, '14782', '黄芩散', '黄芩2两，白及2两，麻黄2两（去节），漏芦2两（真者），白薇2两，枳壳2两（麸炒，去瓤），升麻2两，白芍药2两，川当归2两，川牛膝2两，甘草2两，大黄5两。', '一云：痈疽发背等疾服此获安之后，宜常服四物汤交和黄耆建中汤，空心煎服，以御未来，恐后再作。未利，再服加芒消3钱。', '《普济方》卷二八三', '时行热毒而致痈疽发背，丹疹赤肿，恶肉变作赤色；及眼赤肿生障翳。', '-', '-', '升麻', '[\'黄芩\', \'两白及\', \'麻黄\', \'两去\', \'漏芦\', \'两真者\', \'白薇\', \'枳壳\', \'两麸\', \'升麻\', \'两白\', \'芍药\', \'两川\', \'当归\', \'川牛膝\', \'甘草\', \'大黄\']');
INSERT INTO `prescript` VALUES (59972, '14717', '活络流气饮', '白通草7分，白芷7分，桔梗7分，薄荷7分，川芎7分，猪牙皂7分，红兰花1钱，连翘1钱，当归1钱，羌活1钱，柴胡1钱，土木鳖肉1钱，威灵仙8分，升麻5分，生甘草4分。', '水煎，加酒半盏，食远服。素禀虚弱而脉微细者，加人参、黄耆；脉洪大者，加玄参、天花粉。', '《观聚方要补》卷八引《会元方》', '流注块，或痛或不痛者；或发乍寒乍热之流注风。', '-', '-', '升麻', '[\'通草\', \'白芷\', \'桔梗\', \'薄荷\', \'川芎\', \'猪牙皂\', \'分红\', \'兰花\', \'连翘\', \'当归\', \'羌活\', \'柴胡\', \'土木\', \'鳖肉\', \'威灵仙\', \'升麻\', \'生甘草\']');
